	[321] = { name = 'Swift Gray Steed', sourceText = '|cFFFFD200Vendor: |rCorporal Arthur Flew|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Cost: |r500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = 'A veteran of some of the toughest battles fought by the Argent Crusade.', },
	[322] = { name = 'Great Golden Kodo', sourceText = '|cFFFFD200Vendor: |rDoru Thunderhorn|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Cost: |r500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = 'An alpha member of the herd, this kodo is a prime example of the mighty creatures which thunder through the Southern Barrens.', },
	[323] = { name = 'Turbostrider', sourceText = '|cFFFFD200Vendor: |rRillie Spindlenut|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Cost: |r500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = 'The mirror\'s not so you can look back, but so your foes can see something besides your tailpipes. -Rillie Spindlenut', },
	[324] = { name = 'Swift Violet Ram', sourceText = '|cFFFFD200Vendor: |rDerrick Brindlebeard|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Cost: |r500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = 'The Barak Tor\'ol ram\'s thick bony skull and massive horns make it the ideal mount for charging through groups of armored enemies.', },
