	[467] = { name = 'Cataclysmic Gladiator\'s Twilight Drake', sourceText = '|cFFFFD200Legacy|r',  description = 'Heroes who proved their might in the gladiatorial arenas during the Cataclysm were awarded the most exceptional of the twilight drake broods.', },
	[468] = { name = 'Imperial Quilen', sourceText = '|cFFFFD200Promotion:|r Mists of Pandaria Collector\'s Edition',  description = 'Favorite pets of the ancient Mogu princes, Quilen make loyal and fearless companions and mounts.', },
	[469] = { name = 'Depleted-Kyparium Rocket', sourceText = '|cFFFFD200Profession: |rEngineering',  description = 'When Kyparite is depleted of its amber essence it becomes fairly resistant to the corrosion from rocket fuel.', },
	[470] = { name = 'Geosynchronous World Spinner', sourceText = '|cFFFFD200Profession: |rEngineering',  description = 'This rocket was originally designed to speed up the rotation of the world making for shorter nights.  Gnomes with insomnia can have lofty goals.', },
	[471] = { name = 'Onyx Cloud Serpent', sourceText = '|cFFFFD200Quest:|r Surprise Attack!|n|cFFFFD200Location:|r Townlong Steppes|n',  description = 'Onyx Cloud serpents are known to be more aggressive and daring than their cousins, the Heavenly Onyx Cloud Serpents.', },
	[472] = { name = 'Crimson Cloud Serpent', sourceText = '|cFFFFD200Achievement: |rGlory of the Pandaria Hero|n|cFFFFD200Category: |rDungeons & Raids',  description = 'Crimson Cloud Serpents are given to flights of fancy, striking out on a whim for seemingly random journeys, sometimes for days at a time.', },
	[473] = { name = 'Heavenly Onyx Cloud Serpent', sourceText = '|cFFFFD200Drop:|r Sha of Anger|n|cFFFFD200Location:|r Kun-Lai Summit',  description = 'Heavenly Onyx Cloud Serpents are born with violet scales covering the entirety of their body. Over time, these scales darken to the color of onyx.', },
