	[475] = { name = 'Heavenly Golden Cloud Serpent', sourceText = '|cFFFFD200Vendor: |rMistweaver Ku|n|cFFFFD200Zone: |rTimeless Isle|n|cFFFFD200Faction: |rEmperor Shaohao - Exalted',  description = 'Warmth and hope emanate from this great creature, which glows like a sunrise over the mountains.', },
