	[516] = { name = 'Pandaren Kite', sourceText = '|cFFFFD200Achievement: |rPandaren Ambassador|n|cFFFFD200Category: |rPandaria',  description = 'This token of honor is a gift from the Lorewalkers of Pandaria. Flying manual seemingly not included, however.', },
