	[521] = { name = 'Jade Pandaren Kite', sourceText = '|cFFFFD200Achievement: |rWe\'re Going to Need More Saddles|n|cFFFFD200Category: |rGeneral',  description = 'The residents of Pandaria salute your enthusiasm for wildlife preservation. They do have concerns about housing 150 animals per visit, though.', },
