	[523] = { name = 'Swift Windsteed', sourceText = '|cFFFFD200In-Game Shop|r',  description = 'One of Pandaria\'s most elusive creatures, said to bring good fortune to its rider.', },
	[526] = { name = 'Grand Armored Gryphon', sourceText = '|cFFFFD200Vendor: |rAgent Malley|n|cFFFFD200Zone: |rKrasarang Wilds|n|cFFFFD200Faction: |rOperation: Shieldwall - Exalted|n|cFFFFD200Cost: |r2000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = 'This noble gryphon is as staunch a battle companion as any member of the Alliance could ask for.', },
