	[528] = { name = 'Grand Gryphon', sourceText = '|cFFFFD200Quest: |rThe Silence|n|cFFFFD200Zone: |rKun-Lai Summit',  description = 'Gryphons\' keen eyesight allows them to see over vast distances and warn their riders of any dangers that lie ahead.', },
	[529] = { name = 'Grand Wyvern', sourceText = '|cFFFFD200Quest: |rBreath of Darkest Shadow|n|cFFFFD200Zone: |rKun-Lai Summit',  description = 'Wyverns hail from some of the most inhospitable regions of Kalimdor, making them masters of adapting to any environment.', },
	[530] = { name = 'Armored Skyscreamer', sourceText = '|cFFFFD200Achievement: |rGlory of the Thundering Raider|n|cFFFFD200Category: |rDungeons & Raids',  description = 'Only those heroic souls who surmount every challenge the mogu tyrants place before them can proudly fly the skies on a war-prepared skyscreamer.', },
	[531] = { name = 'Spawn of Horridon', sourceText = '|cFFFFD200Drop: |rHorridon|n|cFFFFD200Zone: |rThrone of Thunder',  description = 'When you see a hurricane coming, you get out of the way. Horridon would fight the hurricane and win.', },
	[532] = { name = 'Ghastly Charger', sourceText = '|cFFFFD200Vendor: |rLandro Longshot|n|cFFFFD200Location:|r Booty Bay|n',  description = 'Not all of Medivh\'s victims are gone for good.', },
	[533] = { name = 'Cobalt Primordial Direhorn', sourceText = '|cFFFFD200Drop:|r Oondasta|n|cFFFFD200Location:|r Isle of Giants',  description = '"When ya fight alongside raptors, ya want a mount dey can\'t eat." -Unknown dinomancer', },
