	[546] = { name = 'Crimson Primal Direhorn', sourceText = '|cFFFFD200Vendor: |rVasarin Redmorn|n|cFFFFD200Zone: |rIsle of Thunder|n|cFFFFD200Faction: |rSunreaver Onslaught - Exalted|n|cFFFFD200Cost: |r3000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = 'Leave it to the blood elves to pick the red one.', },
