	[558] = { name = 'Kor\'kron War Wolf', sourceText = '|cFFFFD200Achievement: |rAhead of the Curve: Garrosh Hellscream (10/25 player)|n|cFFFFD200Category: |rFeats of Strength',  description = 'The Kor\'kron use only the mightiest wolves for their mounts, bedecking them in armor that would break the backs of lesser creatures.', },
