	[561] = { name = 'Thundering Onyx Cloud Serpent', sourceText = '|cFFFFD200Drop:|r Huolon|n|cFFFFD200Location:|r Timeless Isle',  description = 'This ebony cloud serpent is a lightning-wreathed vision of such rarity as to have driven many mortals to madness.', },
	[562] = { name = 'Tyrannical Gladiator\'s Cloud Serpent', sourceText = '|cFFFFD200Legacy|r',  description = 'These cloud serpents always look elegant and slim in classic black.', },
