	[571] = { name = 'Iron Skyreaver', sourceText = '|cFFFFD200In-Game Shop|r',  description = 'Iron Horde beastmasters bred this rylak for war and then girded it for battle in black steel and smoldering flame.', },
	[593] = { name = 'Warforged Nightmare', sourceText = '|cFFFFD200In-Game Shop|r',  description = 'A nightmarish steed of hardened steel and vengeful spirits, this companion will follow you to the depths of the Firelands and back without complaint.', },
