	[1245] = { name = 'Bloodflank Charger', sourceText = '|cFFFFD200Achievement:|r Two Sides to Every Tale|n',  description = 'Stolen from the Arathi Basin stables, this fire-hearted charger now steeds for the Horde.', },
	[1246] = { name = 'Ironclad Frostclaw', sourceText = '|cFFFFD200Achievement:|r Two Sides to Every Tale',  description = 'Captured in the snows of Alterac Valley, this bloodthirsty hunter now roars for the Alliance.', },
	[1247] = { name = 'Mechacycle Model W', sourceText = '|cFFFFD200Achievement: |rMecha-Done|n|cFFFFD200Category: |rBattle for Azeroth',  description = 'The rapid technological evolution of the descendents of Mimiron began with the introduction of a new gear, leading to such wonders as the tragically under-produced Model W.', },
	[1248] = { name = 'Junkheap Drifter', sourceText = '|cFFFFD200Drop: |rRustfeather|n|cFFFFD200Zone: |rMechagon',  description = 'When you really want to be noticed... and don\'t mind strapping yourself to a giant, barely-held-together wheel.', },
	[1249] = { name = 'Child of Torcali', sourceText = '|cFFFFD200Quest:|r Wander Not Alone|n|cFFFFD200Zone: |rZuldazar',  description = 'This daughter of the great direhorn loa, Torcali, never lost faith that her keeper would help her grow to her full potential.', },
	[1250] = { name = 'Mollie', sourceText = '|cFFFFD200Drop: |rDunegorger Kraulok|n|cFFFFD200Zone: |rVol\'dun',  description = 'The kind nature of alpacas makes them perfect as companions for the intrepid adventurer.', },
	[1252] = { name = 'Mechagon Peacekeeper', sourceText = '|cFFFFD200Drop: |rHK-8 Aerial Oppression Unit|n|cFFFFD200Zone: |rOperation: Mechagon (Mythic)',  description = 'Keeps the peace by turning you into pieces.', },
	[1253] = { name = 'Scrapforged Mechaspider', sourceText = '|cFFFFD200Quest: |rDrive It Away Today|n|cFFFFD200Zone: |rMechagon',  description = 'You built this yourself, you should be proud, even though It\'s smoky, sparky, and loud.', },
	[1254] = { name = 'Rustbolt Resistor', sourceText = '|cFFFFD200Vendor: |rStolen Royal Vendorbot|n|cFFFFD200Zone: |rMechagon|n|cFFFFD200Faction: |rRustbolt Resistance - Exalted|n|cFFFFD200Cost: |r524288|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = '"R E S I S T" is carved into the seat in crude letters, along with several, more profane things.', },
	[1255] = { name = 'Deepcoral Snapdragon', sourceText = '|cFFFFD200Quest: |rWild Tame|n|cFFFFD200Zone: |rNazjatar',  description = 'The snapdragon is a quiet hunter in the water, but is far less stealthy on land.', },
	[1256] = { name = 'Snapdragon Kelpstalker', sourceText = '|cFFFFD200Quest: |rGood Girl|n|cFFFFD200Zone: |rNazjatar',  description = 'When a snapdragon bites you, the worry isn\'t about the bite itself, but the poison that will begin to eat away at the wound.', },
	[1257] = { name = 'Silent Glider', sourceText = '|cFFFFD200Drop: |rSoundless|n|cFFFFD200Zone: |rNazjatar',  description = 'Gasses naturally stored within the creature allowed it to survive within the depths, those same gasses also allow them to effortlessly float through the air.', },
	[1258] = { name = 'Fabious', sourceText = '|cFFFFD200Drop:|r Fabious|n',  description = 'He emerges from the water, fins swaying lazily in the breeze, sun-kissed scales glittering. He is... Fabious.', },
	[1260] = { name = 'Crimson Tidestallion', sourceText = '|cFFFFD200Vendor: |rMrrl|n|cFFFFD200Zone: |rNazjatar|n|cFFFFD200Cost: |r4|Hitem:169783|h|TInterface/ICONS/INV_Misc_Coin_12.blp:0|t|h 2|Hitem:169780|h|TInterface/ICONS/INV_Alchemy_80_AlchemistStone02.BLP:0|t|h 1|Hitem:170100|h|TInterface/ICONS/INV_Misc_Food_111_IcefinFillet.blp:0|t|h|n',  description = 'The blood-red coloration of these creatures is ideal for hiding in large coral formations.', },
	[1262] = { name = 'Inkscale Deepseeker', sourceText = '|cFFFFD200Vendor: |rCrafticus Mindbender|n|cFFFFD200Zone: |rNazjatar|n|cFFFFD200Cost: |r150|Hitem:168802|h|TInterface/ICONS/INV_misc_token_pvp01.BLP:0|t|h|n|n|cFFFFD200Vendor: |rDazzerian|n|cFFFFD200Zone: |rNazjatar|n|cFFFFD200Cost: |r150|Hitem:168802|h|TInterface/ICONS/INV_misc_token_pvp01.BLP:0|t|h',  description = 'Schools of tidestallions will viciously slam their muscular chests into their prey to take them down.', },
	[1265] = { name = 'Uncorrupted Voidwing', sourceText = '|cFFFFD200Quest:|r Lost Flight|n',  description = 'This spawn of Vexiona, cleansed by Wrathion, comes from a flight with a fractured past and uncertain future.', },
	[1266] = { name = 'Alabaster Stormtalon', sourceText = '|cFFFFD200In-Game Shop|r|n',  description = 'Chiseled by the stonemasons of Stormwind, this stone gryphon symbolizes the unbending courage of the Alliance.', },
	[1267] = { name = 'Alabaster Thunderwing', sourceText = '|cFFFFD200In-Game Shop|r|n',  description = 'Chiseled by the stoneworkers of Orgrimmar, this stone wind rider is a symbol of the unwavering strength of the Horde.', },
	[1269] = { name = 'Swift Spectral Fathom Ray', sourceText = '|cFFFFD200Legacy|r',  description = 'Even the dead need a lift every now and then.', },
	[1270] = { name = 'Swift Spectral Magnetocraft', sourceText = '|cFFFFD200Legacy|r',  description = 'Even the dead need a lift every now and then.', },
	[1271] = { name = 'Swift Spectral Armored Gryphon', sourceText = '|cFFFFD200Legacy|r',  description = 'Even the dead need a lift every now and then.', },
	[1272] = { name = 'Swift Spectral Pterrordax', sourceText = '|cFFFFD200Legacy|r',  description = 'Even the dead need a lift every now and then.', },
	[1282] = { name = 'Black Serpent of N\'Zoth', sourceText = '|cFFFFD200Achievement:|r Through the Depths of Visions',  description = 'The black serpents of N\'Zoth are the harbingers of his ultimate vision becoming reality. Only those servents or enemies that can tread this space have any chance of wrangling them.', },
	[1283] = { name = 'Mechagon Mechanostrider', sourceText = '|cFFFFD200Achievement: |rAllied Races: Mechagnome',  description = 'The best mount, logically, serves multiple purposes -- sometimes you need to get places AND reach something high up once you get there.', },
	[1285] = { name = 'Frostwolf Snarler', sourceText = '|cFFFFD200Achievement: |rAlterac Valley of Olde|n|cFFFFD200Category: |rAlterac Valley',  description = 'Raised in the Alterac Mountains by the Frostwolf Clan, trained to bite dwarves.', },
	[1286] = { name = 'Caravan Hyena', sourceText = '|cFFFFD200Achievement: |rAllied Races: Vulpera',  description = 'The canopy atop this dune-wanderer can be stored at your campsite, should you desire to feel the wind in your fur.', },
	[1287] = { name = 'Explorer\'s Jungle Hopper', sourceText = '|cFFFFD200Recruit-a-Friend|r ',  description = 'For when you\'re making a crash landing in the middle of an uncharted swamp because It\'s the only way to access that ancient temple.', },
	[1288] = { name = 'Explorer\'s Dunetrekker', sourceText = '|cFFFFD200Recruit-a-Friend|r ',  description = 'The surly dunetrekker can easily carry a set of explorers to the nearest ruins and back without the need to rest.', },
	[1289] = { name = 'Ensorcelled Everwyrm', sourceText = '|cFFFFD200Promotion:|r Shadowlands Heroic Edition',  description = 'Anima is a fundamental force that infuses the Shadowlands. It flows through many creatures, but is also traded, bartered for, and fought over.', },
	[1290] = { name = 'Squeakers, the Trickster', sourceText = '|cFFFFD200In Game Shop|r|n',  description = 'The locals in Halfhill swear that whenever a batch of grain is missing, a farmworking instrument is misplaced, or a bushel of berries can\'t be found, you can hear a mischievous squeaking, and are reminded to slow down and accept what life gives you.', },
	[1291] = { name = 'Lucky Yun', sourceText = '|cFFFFD200In Game Shop|r|n',  description = 'The Pandaren believe that merely rubbing one\'s hands on the shiny hide of the ox will grant prosperity, the challenge is catching it.', },
	[1292] = { name = 'Stormpike Battle Ram', sourceText = '|cFFFFD200Achievement: |rAlterac Valley of Olde|n|cFFFFD200Category: |rAlterac Valley',  description = 'Raised in the Alterac Mountains by the Stormpike Clan, trained to headbutt orcs.', },
	[1293] = { name = 'Ny\'alotha Allseer', sourceText = '|cFFFFD200Drop:|r N\'Zoth the Corruptor|n|cFFFFD200Zone:|r Ny\'alotha, the Waking City|n|cFFFFD200Difficulty:|r Mythic',  description = 'To find him, drown yourself in the Circle of Stars.', },
	[1297] = { name = 'Clutch of Ha-Li', sourceText = '|cFFFFD200Drop: |rHa-Li|n|cFFFFD200Zone: |rVale of Eternal Blossoms',  description = 'The brood of Ha-Li are comfortable in the blue skies of Pandaria, hunting unsuspecting prey.', },
	[1298] = { name = 'Hopecrusher Gargon', sourceText = '|cFFFFD200Covenant: |rVenthyr|n|cFFFFD200Zone: |rRevendreth',  description = 'Once the loyal mount of a House Briarbane lord, it turned on the lord and his retinue. Members of House Briarbane now whisper of a dark Gargon lying in wait even baiting venthyr who venture through the darker parts of the Chalice District of Revendreth.', },
	[1299] = { name = 'Battle Gargon Vrednic', sourceText = '|cFFFFD200Covenant: |rVenthyr|n|cFFFFD200Zone: |rRevendreth',  description = 'Prince Renathal\'s loyal gargon, Vrednic has been by his side for millennia. Adorned in his Sinvyr armor, Vrednic\'s mere presence instills terror to all those who lay eyes on him as he ushers his rider into battle.', },
	[1302] = { name = 'Dreamlight Runestag', sourceText = '|cFFFFD200Covenant: |rNight Fae|n|cFFFFD200Zone: |rArdenweald',  description = 'Runestags are granted to those who have pledged their service to the Winter Queen and the Court of Night.', },
	[1303] = { name = 'Enchanted Dreamlight Runestag', sourceText = '|cFFFFD200Covenant: |rNight Fae|n|cFFFFD200Zone: |rArdenweald',  description = 'The majestic runestags are capable of siphoning anima from the ambient light of Ardenweald with their elaborate antlers. The anima is used as nourishment and to power the runestag\'s speed and magic.', },
	[1304] = { name = 'Mawsworn Soulhunter', sourceText = '|cFFFFD200Drop: |rGorged Shadehound|n|cFFFFD200Zone: |rThe Maw',  description = 'Soulhunters are used to track wayward souls across the wastes that flee from their dark fates, and are one of the only creatures that feel comfortable traversing the maw.', },
	[1305] = { name = 'Darkwarren Hardshell', sourceText = '|cFFFFD200Adventures:|r Shadowlands',  description = 'The Darkwarren Hardshell\'s chitin is sturdy enough to withstand mortar shells and dragon\'s fire. It will also conviently consume anything lacking anima, if needed.', },
	[1306] = { name = 'Swift Gloomhoof', sourceText = '|cFFFFD200Drop: |rNight Mare|n|cFFFFD200Zone: |rArdenweald',  description = 'The ballads of Ardenweald claim that the birth of gloomrunner heralds the passing of a particularly benevolent wild god. Seeing a gloomrunner is therefore a blessing and a curse.', },
	[1307] = { name = 'Sundancer', sourceText = '|cFFFFD200Drop:|r Sundancer|n|cFFFFD200Zone:|r Bastion|n',  description = 'This sun-dappled courser gallops on lightbeams with carefree abandon.', },
	[1309] = { name = 'Chittering Animite', sourceText = '|cFFFFD200Adventures:|r Shadowlands|n',  description = 'These exceptionally agile fliers make excellent mounts once they\'ve been trained not to sap every ounce of anima from their rider.', },
	[1310] = { name = 'Horrid Dredwing', sourceText = '|cFFFFD200Drop: |rHarika the Horrid|n|cFFFFD200Covenant: |rVenthyr|n|cFFFFD200Zone: |rRevendreth',  description = 'Harika the Horrid terrorized the denizens of the Forest Ward, and with her demise her broodlings can now take this horror to denizens farther afield.', },
	[1311] = { name = 'Ivory Cloud Serpent', sourceText = '|cFFFFD200Drop:|r Ivory Cloud Serpent|n|cFFFFD200Zone:|r Vale of Eternal Blossoms',  description = 'Ivory Cloud Serpents are often mistaken for roaring thunder as they tear through the skies with blistering speed.', },
	[1312] = { name = 'Gargantuan Grrloc', sourceText = '|cFFFFD200In-Game Shop|r |n',  description = 'It\'s the biggest murloc you\'ve ever seen!', },
	[1313] = { name = 'Rajani Warserpent', sourceText = '|cFFFFD200Vendor: |rZhang Ku|n|cFFFFD200Zone: |rVale of Eternal Blossoms|n|cFFFFD200Cost: |r1|Hitem:174230|h|TInterface/ICONS/INV_Misc_MonsterScales_18.blp:0|t|h',  description = 'Fierce companions of the followers of Ra-den, the Rajani Warserpents are a force to be reckoned with.', },
	[1314] = { name = 'Drake of the Four Winds', sourceText = '|cFFFFD200Drop: |rIshak of the Four Winds|n|cFFFFD200Zone: |rUldum',  description = 'Rocks erode, fires sputter, but wind remains powerful regardless of the passage of time. -Phaoris', },
	[1315] = { name = 'Mail Muncher', sourceText = '|cFFFFD200Drop: |rMail Muncher|n|cFFFFD200Zone: |rHorrific Visions',  description = 'Worming its way through the Postmaster\'s network, this serpent of N\'Zoth deprived Azeroth of its primary magical correspondence.', },
	[1317] = { name = 'Waste Marauder', sourceText = '|cFFFFD200Drop: |rRotfeaster|n|cFFFFD200Zone: |rUldum',  description = 'Larger than most of its kin, this vulture has shown sick and strong beasts alike why they need to fear the skies.', },
	[1318] = { name = 'Wastewander Skyterror', sourceText = '|cFFFFD200Vendor: |rProvisioner Qorra|n|cFFFFD200Zone: |rUldum|n|cFFFFD200Faction: |rUldum Accord - Exalted|n|cFFFFD200Cost: |r24000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = 'Doubting a Wastewander Skyterror\'s capabilities is a quick way to find yourself missing a limb.', },
	[1319] = { name = 'Malevolent Drone', sourceText = '|cFFFFD200Drop:|r Corpse Eater|n|cFFFFD200Zone: |rUldum',  description = 'The Aqir drones feast on heroes and cowards alike to grow their ranks.', },
	[1320] = { name = 'Shadowbarb Drone', sourceText = '|cFFFFD200Quest: |rMy Own Drone|n|cFFFFD200Zone: |rUldum',  description = 'Void, jelly, and a very full belly is all this drone needs.', },
	[1321] = { name = 'Wicked Swarmer', sourceText = '|cFFFFD200Vendor: |rWrathion|n|cFFFFD200Zone: |rChamber of Heart|n|cFFFFD200Cost: |r100000|Hcurrency:1719|h|TInterface/ICONS/Archaeology_5_0_MoguCoin.blp:0|t|h',  description = 'When the Void reached out, the Aqir were formed in its terrible image.', },
	[1322] = { name = 'Wriggling Parasite', sourceText = '|cFFFFD200Achievement: |rGlory of the Ny\'alotha Raider|n|cFFFFD200Category: |rDungeons & Raids',  description = 'It\'s so wiggly! Just looking at it move is chaotic and unnerving.', },
	[1324] = { name = 'Elusive Quickhoof', sourceText = '|cFFFFD200Zone: |rVol\'dun',  description = 'The kind nature of alpacas makes them perfect as companions for the intrepid adventurer.', },
	[1326] = { name = 'Awakened Mindborer', sourceText = '|cFFFFD200Achievement: |rBattle for Azeroth Keystone Master: Season Four|n|cFFFFD200Category: |rDungeons',  description = 'It may look menacing right now, but It\'s actually kind of cute when It\'s asleep.', },
	[1327] = { name = 'Ren\'s Stalwart Hound', sourceText = '|cFFFFD200Drop: |rHoundlord Ren|n|cFFFFD200Zone: |rVale of Eternal Blossoms',  description = 'Houndlord Ren used his strongest quilen to carry him across Pandaria to subjugate the Vale for the Mogu empire.', },
	[1328] = { name = 'Xinlao', sourceText = '|cFFFFD200Drop: |rAnh-De the Loyal|n|cFFFFD200Zone: |rVale of Eternal Blossoms',  description = 'The only thing stronger than a quilen\'s loyalty is its fierce devotion to making sure it covers its owner in kisses.', },
	[1329] = { name = 'Springfur Alpaca', sourceText = '|cFFFFD200Quest: |rAlpaca It In|n|cFFFFD200Zone: |rUldum',  description = 'The kind nature of alpacas makes them perfect as companions for the intrepid adventurer.', },
	[1332] = { name = 'Silky Shimmermoth', sourceText = '|cFFFFD200Vendor: |rMaster Clerk Salorn|n|cFFFFD200Zone: |rArdenweald|n|cFFFFD200Cost: |r5000|Hcurrency:1813|h|TInterface/ICONS/Spell_AnimaBastion_Orb.BLP:0|t|h',  description = 'The moths of Ardenweald play a vital role in the ecosystem, carrying spores, anima, and pollen from one part of the forest to the other, providing nourishment and variation to the denizens living below.', },
	[1346] = { name = 'Steamscale Incinerator', sourceText = '|cFFFFD200In-Game Shop|r|n',  description = 'A mad gnomish inventor had a dream to ride a dragon, so he created this metal monstrosity. He powered it with a trapped demon, in order to create "an authentic dragon soul," and did not survive its activation.', },
	[1350] = { name = 'Colossal Slaughterclaw', sourceText = '|cFFFFD200Drop: |rSupplies of the Undying Army|n|cFFFFD200Zone: |rMaldraxxus',  description = 'Only the most stalwart stable masters of Maldraxxus are able to break the will of the colossal slaughterclaws, giving all champions of the Undying Army a fearsome ally in the Shadowlands.', },
	[1351] = { name = 'Vicious War Spider', sourceText = '|cFFFFD200PvP:|r Shadowlands: Season 1|nRated Arena and Rated Battleground Season Reward',  description = 'When being a courageous member of the Alliance doesn\'t strike the right amount of fear into your opponent, add a giant spider.', },
	[1352] = { name = 'Vicious War Spider', sourceText = '|cFFFFD200PvP:|r Shadowlands: Season 1|nRated Arena and Rated Battleground Season Reward',  description = 'When being a bellowing member of the Horde doesn\'t strike the right amount of fear into your opponent, add a giant spider.', },
	[1354] = { name = 'Shadeleaf Runestag', sourceText = '|cFFFFD200Covenant: |rNight Fae|n|cFFFFD200Zone: |rArdenweald',  description = 'The shadeleaf runestag are bred for their ferocity as well as their beauty by the Wild Hunt. They feel no fear charging into the den of any predator.', },
	[1355] = { name = 'Wakener\'s Runestag', sourceText = '|cFFFFD200Covenant: |rNight Fae|n|cFFFFD200Zone: |rArdenweald',  description = 'Runestags enjoy lying near wildseeds. The Wakeners believe they can hear the sleepers\' dreams. These runestags display a range of emotions in their vigil, signaling the dreams or nightmares of the spirits within.', },
	[1356] = { name = 'Winterborn Runestag', sourceText = '|cFFFFD200Covenant: |rNight Fae|n|cFFFFD200Zone: |rArdenweald',  description = 'Winterborn runestags are blessed by the Winter Queen herself. Their runes flare brilliantly when predators are nearby, warning the entire herd.', },
	[1357] = { name = 'Enchanted Shadeleaf Runestag', sourceText = '|cFFFFD200Covenant: |rNight Fae|n|cFFFFD200Zone: |rArdenweald',  description = 'An shadeleaf runestag is capable of goring creatures as powerful as a gorm matriarch in defense of its herd. The Wild Hunt ride these mounts into dangerous corners of the Shadowlands, when their domain must be defended from those who would threaten the wilds.', },
	[1358] = { name = 'Enchanted Wakener\'s Runestag', sourceText = '|cFFFFD200Covenant: |rNight Fae|n|cFFFFD200Zone: |rArdenweald',  description = 'Wakener runestags fiercely protect their wildseed groves. When the groves starting to fail, many runestags went berserk.', },
	[1359] = { name = 'Enchanted Winterborn Runestag', sourceText = '|cFFFFD200Covenant: |rNight Fae|n|cFFFFD200Zone: |rArdenweald',  description = 'Winterborn runestags are the favored mount of the Winter Queen herself, and she only bestows such a creature on her most favored subjects.', },
	[1360] = { name = 'Shimmermist Runner', sourceText = '|cFFFFD200Area: |rMistveil Tangle|n|cFFFFD200Zone: |rArdenweald',  description = 'Mischievous spriggans and the valerunners have a natural enmity. No valerunner would deign to allow a spriggan rider because they can sense their inherent malevolence. This angers the trickster spriggans who take twisted enjoyment in playing cruel pranks on these majestic creatures.', },
	[1361] = { name = 'Duskflutter Ardenmoth', sourceText = '|cFFFFD200Vendor: |rAithlyn|n|cFFFFD200Zone: |rArdenweald|n|cFFFFD200Faction: |rThe Wild Hunt - Exalted|n|cFFFFD200Cost: |r30000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = 'These ardenmoths prefer the dark and damp forests of northwest Ardenweald, where fungal colonies are perpetually blooming.', },
	[1362] = { name = 'Spinemaw Gladechewer', sourceText = '|cFFFFD200Drop: |rGormtamer Tizo|n|cFFFFD200Zone: |rArdenweald',  description = 'Spriggan riders tie bits of anima-less detritus to the end of sticks in front of their gorm mounts to steer. It\'s... an imperfect art.', },
	[1363] = { name = 'Sinful Gladiator\'s Soul Eater', sourceText = '|cFFFFD200Achievement: |rSinful Gladiator: Shadowlands Season 1|n|cFFFFD200Category: |rPlayer vs. Player',  description = 'This mount shows a proclivity for snacking upon the souls cast out of Revendreth.', },
	[1364] = { name = 'War-Bred Tauralus', sourceText = '|cFFFFD200Covenant: |rNecrolord|n|cFFFFD200Zone: |rMaldraxxus',  description = 'The soldiers of Maldraxxus protect the Shadowlands, and require a mount that is both capable and calm when faced with overwhelming odds.', },
	[1365] = { name = 'Plaguerot Tauralus', sourceText = '|cFFFFD200Covenant: |rNecrolord|n|cFFFFD200Zone: |rMaldraxxus',  description = 'The tauralus is considered courageous, though scholars of Maldraxxus have long wondered if their lack of fear is simply due to the tiny size of their brains.', },
	[1366] = { name = 'Bonehoof Tauralus', sourceText = '|cFFFFD200Drop: |rTahonta|n|cFFFFD200Covenant: |rNecrolord|n|cFFFFD200Zone: |rMaldraxxus',  description = 'There are quite a few reasons that you do not want to stand behind a tauralus, the hooves and whip-like tails being the least of them.', },
	[1367] = { name = 'Chosen Tauralus', sourceText = '|cFFFFD200Achievement: |rThe Gang\'s All Here|n|cFFFFD200Category: |rCovenant Sanctums',  description = 'Even an unarmored tauralus is a valuable ally in battle, its enormous horns capable of piercing even plate armor.', },
	[1368] = { name = 'Armored War-Bred Tauralus', sourceText = '|cFFFFD200Covenant: |rNecrolord|n|cFFFFD200Zone: |rMaldraxxus',  description = 'The tauralus that serve the armies of Maldraxxus have had all traits of compassion bred out of them, and are only adorned in armor when they show that no sword or spell can stop them from carrying their rider to victory.', },
	[1369] = { name = 'Armored Plaguerot Tauralus', sourceText = '|cFFFFD200Covenant: |rNecrolord|n|cFFFFD200Vendor: |rSu Zettai|n|cFFFFD200Zone: |rMaldraxxus|n|cFFFFD200Cost: |r3000|Hcurrency:1813|h|TInterface/ICONS/Spell_AnimaBastion_Orb.BLP:0|t|h',  description = 'When fully armored, a tauralus can take an incredible amount of damage before falling, making it a valuable ally in battle.', },
	[1370] = { name = 'Armored Bonehoof Tauralus', sourceText = '|cFFFFD200Drop: |rSabriel the Bonecleaver|n|cFFFFD200Covenant: |rNecrolord|n|cFFFFD200Zone: |rMaldraxxus',  description = 'A favored mount of the House of the Chosen, the tauralus only gets more angry and vicious when exposed to battle.', },
	[1371] = { name = 'Armored Chosen Tauralus', sourceText = '|cFFFFD200Achievement: |rThings To Do When You\'re Dead|n|cFFFFD200Category: |rCovenant Sanctums',  description = 'The tauralus has two tails, which are both absolutely necessary for whipping away buzzing pests like the bloodlouse.', },
	[1372] = { name = 'Blisterback Bloodtusk', sourceText = '|cFFFFD200Drop:|r Warbringer Mal\'korak|n|cFFFFD200Zone: |rMaldraxxus',  description = 'The House of the Chosen train their mounts in all manner of difficult circumstances, so that no matter the battle, they will never falter.', },
	[1373] = { name = 'Gorespine', sourceText = '|cFFFFD200Drop: |rNerissa Heartless|n|cFFFFD200Zone: |rMaldraxxus',  description = 'Nerissa trampled over countless bodies on the back of Gorespine as she rose to prominence as the commander of the Lichsworn forces for the House of Rituals.', },
	[1375] = { name = 'Lurid Bloodtusk', sourceText = '|cFFFFD200Vendor: |rNalcorn Talsen|n|cFFFFD200Zone: |rMaldraxxus|n|cFFFFD200Faction: |rThe Undying Army - Exalted|n|cFFFFD200Cost: |r30000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = 'Only bloodtusks that survive the Contest of Conquest are deemed worthy enough to become a mount of the Undying Army.', },
	[1376] = { name = 'Silvertip Dredwing', sourceText = '|cFFFFD200Covenant Feature: |rThe Ember Court|n|cFFFFD200Covenant: |rVenthyr|n|n|cFFFFD200Covenant Feature: |rQueen\'s Conservatory|n|cFFFFD200Covenant: |rNight Fae|n|n|cFFFFD200Covenant Feature: |rPath of Ascension|n|cFFFFD200Covenant: |rKyrian|n|n|cFFFFD200Covenant Feature: |rAbomination Factory|n|cFFFFD200Covenant: |rNecrolord',  description = 'Bred by the Revendreth prince, Renathal, these rare dredwing are fiercely loyal and will only let those deemed worthy by the prince himself to interact with them.', },
	[1377] = { name = 'Rampart Screecher', sourceText = '|cFFFFD200Achievement: |rGlory of the Nathria Raider|cFFFFD200Category: |rDungeons & Raids',  description = 'Roosting among the ramparts of Castle Nathria, these giant dredbats act as sentinels and are trained to take down threats approaching the castle from the air.', },
	[1378] = { name = 'Harvester\'s Dredwing', sourceText = '|cFFFFD200Drop:|r Harvester\'s War Chest|n|cFFFFD200Zone:|r The Maw ',  description = 'The dredwings of the Harvesters fear no mawsworn, and will gleefully sink their teeth into anything that threatens Sinfall.', },
	[1379] = { name = 'Endmire Flyer', sourceText = '|cFFFFD200Drop: |rFamu the Infinite|n|cFFFFD200Zone: |rRevendreth',  description = 'Attracted through portals through which little is known these flying devourers have found a home in the Endmire of Revendreth feeding on the latent anima.', },
	[1382] = { name = 'Inquisition Gargon', sourceText = '|cFFFFD200Covenant: |rVenthyr|n|cFFFFD200Zone: |rRevendreth',  description = 'The gargon of the Inquisitors are some of the most vicious ever carved. The Avowed seek to recapture these beasts and return them to the righteous duty of assisting in the purifying of souls rather than the perversions performed at the hands of the Inquisitors.', },
	[1384] = { name = 'Sinfall Gargon', sourceText = '|cFFFFD200Covenant: |rVenthyr|n|cFFFFD200Zone: |rRevendreth',  description = 'The gargon are the loyal mounts and companions of the Venthyr and Renathal has found many abandoned at Sinfall, awaiting reawakening and assignment to worthy members of the covenant.', },
	[1385] = { name = 'Crypt Gargon', sourceText = '|cFFFFD200Covenant: |rVenthyr|n|cFFFFD200Zone: |rRevendreth',  description = 'Loyal hounds of the Curator, these gargon patrol the crypts and catacombs deterring both trespassing and escape.', },
	[1387] = { name = 'Desire\'s Battle Gargon', sourceText = '|cFFFFD200Covenant: |rVenthyr|n|cFFFFD200Zone: |rRevendreth',  description = 'The Countess maintains a small stable of elegantly adorned gargon mounts for her sole use. Many a rival has been taken in by the finery to their peril as she personally oversees their deadly training.', },
	[1388] = { name = 'Gravestone Battle Gargon', sourceText = '|cFFFFD200Covenant: |rVenthyr|n|cFFFFD200Zone: |rRevendreth',  description = 'Representative of a new loyal batch of gargon, these battle ready beasts will take Renathal\'s most loyal compatriots into the final battles against the Jailer.', },
	[1389] = { name = 'Battle Gargon Silessa', sourceText = '|cFFFFD200Covenant: |rVenthyr|n|cFFFFD200Zone: |rRevendreth',  description = 'Silessa fought valiantly alongside her master in the great incursion by the Light, following the battle her master set her to guard over a vault room of great value.', },
	[1391] = { name = 'Loyal Gorger', sourceText = '|cFFFFD200Quest: |rA New Pack',  description = 'Crawling forth from the cracks between the realms, the devourers have a voracious appetite for anima. Little is known of their origins, but all denizens of the Shadowlands see their recent boldness as a sign of the end times.', },
	[1392] = { name = 'Pale Acidmaw', sourceText = '|cFFFFD200Covenant Feature: |rThe Ember Court|n|cFFFFD200Covenant: |rVenthyr|n|n|cFFFFD200Covenant Feature: |rQueen\'s Conservatory|n|cFFFFD200Covenant: |rNight Fae|n|n|cFFFFD200Covenant Feature: |rPath of Ascension|n|cFFFFD200Covenant: |rKyrian|n|n|cFFFFD200Covenant Feature: |rAbomination Factory|n|cFFFFD200Covenant: |rNecrolord',  description = 'The acid present in the ichor of this creature sapped away the color from its shell, leaving it a striking shade of snowy white.', },
	[1393] = { name = 'Wild Glimmerfur Prowler', sourceText = '|cFFFFD200Drop: |rValfir the Unrelenting|n|cFFFFD200Covenant: |rNight Fae|n|cFFFFD200Zone: |rArdenweald',  description = 'This prowler\'s brilliant green coat distinguishes it from the blues and purples of Ardenweald, making it much easier for others of its kind to see through the midnight woods.', },
	[1394] = { name = 'Phalynx of Loyalty', sourceText = '|cFFFFD200Covenant: |rKyrian|n|cFFFFD200Zone: |rBastion',  description = 'Some within the Ascended keep a phalynx as a pet. While they may not be as furry as a larion, they are much less likely to eat your other pets.', },
	[1395] = { name = 'Phalynx of Humility', sourceText = '|cFFFFD200Covenant: |rKyrian|n|cFFFFD200Zone: |rBastion',  description = 'The phalynx does not fetch, though it is not known why this is.', },
	[1396] = { name = 'Phalynx of Courage', sourceText = '|cFFFFD200Covenant: |rKyrian|n|cFFFFD200Zone: |rBastion',  description = 'A phalynx is considered both a worthy companion and a lithe opponent to test one\'s reflexes against.', },
	[1397] = { name = 'Wildseed Cradle', sourceText = '|cFFFFD200Treasure: |rCache of the Moon|n|cFFFFD200Zone: |rArdenweald',  description = 'The most precious cargo in all Ardenweald, the wildseeds, are cradled by this lovingly-crafted cart. It is imbued with anima to allow it to hover over the ground.', },
	[1398] = { name = 'Phalynx of Purity', sourceText = '|cFFFFD200Covenant: |rKyrian|n|cFFFFD200Zone: |rBastion',  description = 'Little is known about the origination of phalynx technology, the machinations of which are taught more akin to religion than engineering.', },
	[1399] = { name = 'Eternal Phalynx of Purity', sourceText = '|cFFFFD200Covenant: |rKyrian|n|cFFFFD200Zone: |rBastion',  description = 'The armored phalynx is a favorite companion of the mightiest warriors of the Ascended as it is both fearless and vicious in battle.', },
	[1400] = { name = 'Eternal Phalynx of Courage', sourceText = '|cFFFFD200Covenant: |rKyrian|n|cFFFFD200Zone: |rBastion',  description = 'Many bars of gildenite are required to forge one of these sparkling mounts, but the sight of them alone is worth the gathering time.', },
	[1401] = { name = 'Eternal Phalynx of Loyalty', sourceText = '|cFFFFD200Covenant: |rKyrian|n|cFFFFD200Zone: |rBastion',  description = 'A marvel of steward engineering, the armored phalynx is a rare and awe-inspiring sight.', },
	[1402] = { name = 'Eternal Phalynx of Humility', sourceText = '|cFFFFD200Covenant: |rKyrian|n|cFFFFD200Zone: |rBastion',  description = 'When an armored phalynx is forged, stewards clap their hammers together in reverence of the skill required in its creation.', },
	[1404] = { name = 'Silverwind Larion', sourceText = '|cFFFFD200Treasure: |rGift of the Silver Wind|n|cFFFFD200Zone: |rBastion',  description = 'This gift of the silvery winds is only bestowed upon those that search high and low for the wisdom of Bastion.', },
	[1405] = { name = 'Restoration Deathwalker', sourceText = '|cFFFFD200Achievement: |rShadowlands Keystone Master: Season Four|n|cFFFFD200Category: |rDungeons',  description = 'With the death of the Jailer and the ascension of a new Arbiter, the Shadowlands is returning to its great Purpose. Some deathwalkers recognize the role that mortal heroes have played, and are willing to serve as mounts.', },
	[1406] = { name = 'Marrowfang', sourceText = '|cFFFFD200Drop:|r Nalthor the Rimebinder|n|cFFFFD200Zone: |rThe Necrotic Wake (Mythic)|n|n|cFFFFD200Drop:|r Challenger\'s Cache|n|cFFFFD200Zone: |rThe Necrotic Wake',  description = 'Marrowfang, Bonefang\'s progeny, was respected by Amarth the Harvester for her insatiable appetite, even if it disrupted any experiments that the sadistic commander wanted to run.', },
	[1407] = { name = 'Callow Flayedwing', sourceText = '|cFFFFD200Zone: |rMaldraxxus',  description = 'Flayedwings are a necessary force in the cycle of life for the Shadowlands, acting as both prey and predator for the harsh environment of Maldraxxus.', },
	[1408] = { name = 'Gruesome Flayedwing', sourceText = '|cFFFFD200Covenant Feature: |rThe Ember Court|n|cFFFFD200Covenant: |rVenthyr|n|n|cFFFFD200Covenant Feature: |rQueen\'s Conservatory|n|cFFFFD200Covenant: |rNight Fae|n|n|cFFFFD200Covenant Feature: |rPath of Ascension|n|cFFFFD200Covenant: |rKyrian|n|n|cFFFFD200Covenant Feature: |rAbomination Factory|n|cFFFFD200Covenant: |rNecrolord',  description = 'The flayedwings of Maldraxxus are prized for their ability to soar effortlessly through the skies of the Shadowlands, allowing their rider a surprise opportunity to strike at their foes or support their allies.', },
	[1409] = { name = 'Bonesewn Fleshroc', sourceText = '|cFFFFD200Profession:|r Abominable Stitching|n|cFFFFD200Zone:|r Maldraxxus',  description = 'Only expert flesh-stitchers who have demonstrated mastery over the magics of Maldraxxus are powerful enough to construct a deathroc of this size.', },
	[1410] = { name = 'Hulking Deathroc', sourceText = '|cFFFFD200Drop: |rViolet Mistake|n|cFFFFD200Zone: |rMaldraxxus',  description = 'Deathrocs are stitched monstrosities from the discarded viscera, bone, and organs that were not required to construct soldiers for the House of Constructs.', },
	[1411] = { name = 'Predatory Plagueroc', sourceText = '|cFFFFD200Drop: |rGieger|n|cFFFFD200Covenant: |rNecrolord|n|cFFFFD200Zone: |rMaldraxxus',  description = 'Geiger\'s everlasting hunger is legendary amonst the House of Constructs, and no beast is too large or bony to fit inside its massive stomach.', },
	[1413] = { name = 'Dauntless Duskrunner', sourceText = '|cFFFFD200Covenant Feature: |rThe Ember Court|n|cFFFFD200Covenant: |rVenthyr|n|n|cFFFFD200Covenant Feature: |rQueen\'s Conservatory|n|cFFFFD200Covenant: |rNight Fae|n|n|cFFFFD200Covenant Feature: |rPath of Ascension|n|cFFFFD200Covenant: |rKyrian|n|n|cFFFFD200Covenant Feature: |rAbomination Factory|n|cFFFFD200Covenant: |rNecrolord',  description = 'The Path of Ascension is a challenging trial, and those that display all of the facets the Kyrian hold dear will be duly rewarded.', },
	[1414] = { name = 'Sinrunner Blanchy', sourceText = '|cFFFFD200NPC:|r Dead Blanchy |n|cFFFFD200Location:|r Revendreth|n |n',  description = 'In life, Blanchy appeared to be an affectionate, well-behaved horse of excellent disposition and appetite. What evils must she have committed in secret to have earned her place among the condemned souls of Revendreth?', },
	[1415] = { name = 'Arboreal Gulper', sourceText = '|cFFFFD200Drop: |rHumon\'gozz|n|cFFFFD200Zone: |rArdenweald',  description = 'The wood and foliage growing atop this toad is a natural part of its body, helping it siphon anima from the air to nourish itself.', },
	[1416] = { name = 'Mawsworn Charger', sourceText = '|cFFFFD200Achievement:|r Flawless Master|n|cFFFFD200Zone:|r Torghast, Tower of the Damned',  description = 'The fate of all horse mounts.', },
	[1417] = { name = 'Hand of Hrestimorak', sourceText = '|cFFFFD200Achievement:|r Glory of the Dominant Raider|n|cFFFFD200Zone:|r Sanctum of Domination',  description = 'The treachery of Hrestimorak was infamous, betraying his people to claim power. Facing damnation in the Maw, he tried to subvert the Jailer... a foolish act that would earn him an eternity of torment.', },
	[1419] = { name = 'Sintouched Deathwalker', sourceText = '|cFFFFD200Achievement: |rShadowlands Keystone Master: Season One|n|cFFFFD200Category: |rDungeons',  description = 'Forged from the sins of countless diabolical dungeon denizens. Only those with the skills to overcome them may hold its reins.', },
	[1420] = { name = 'Umbral Scythehorn', sourceText = '|cFFFFD200Vendor: |rSpindlenose|n|cFFFFD200Zone: |rArdenweald|n',  description = 'Umbral gorm are the most docile of all gorm, but they also grow the largest horns and mandibles. They are probably so docile because other creatures avoid riling them.', },
	[1421] = { name = 'Court Sinrunner', sourceText = '|cFFFFD200Vendor: |rMistress Mihaela|n|cFFFFD200Zone: |rRevendreth|n|cFFFFD200Faction: |rCourt of Harvesters - Exalted|n|cFFFFD200Cost: |r30000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = 'Sinrunners were once the steeds of dark souls, carried in the wake of either their sins or their masters\' sins to Revendreth. While some chose to run free, many continue to serve darkness in death as they did in life.', },
	[1422] = { name = 'Warstitched Darkhound', sourceText = '|cFFFFD200Adventures:|r Shadowlands',  description = 'These warhounds are crafted by the House of Constructs to carry any rider into the thick of battle, rending and tearing enemy mounts all the while.', },
	[1423] = { name = 'Highwind Darkmane', sourceText = '|cFFFFD200Adventures:|r Shadowlands',  description = 'The Forsworn and their mounts take on a darker aspect upon renouncing the path laid forth by the Archon. While tame enough to ride, the Highwind Darkmane has a fiercely independent streak.', },
	[1424] = { name = 'Snowstorm', sourceText = '|cFFFFD200Blizzard\'s 30th Anniversary|r ',  description = 'He comes from the ice and snow, under the Azeroth sun where adventurers go.', },
	[1425] = { name = 'Gilded Prowler', sourceText = '|cFFFFD200Vendor: |rAdjutant Nikos|n|cFFFFD200Zone: |rBastion|n|cFFFFD200Cost: |r30000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = 'The mighty larion is an apex predator of Bastion, but that does not stop it from enjoying a nice nap in the sunny fields.', },
	[1426] = { name = 'Ascended Skymane', sourceText = '|cFFFFD200Treasure: |rCache of the Ascended|n|cFFFFD200Zone: |rBastion',  description = 'Only those that have proven themselves against the combined might of the Ascended will be able to ride this mount.', },
	[1428] = { name = 'Amber Ardenmoth', sourceText = '|cFFFFD200Drop:|r Wild Hunt Supplies|n|cFFFFD200Zone:|r Ardenweald|n',  description = 'The brilliant orange hue of the amber ardenmoth signals to predators that it is dangerous to eat. Consuming one of these creatures will put a predator into a dreamless sleep for years.', },
	[1429] = { name = 'Vibrant Flutterwing', sourceText = '|cFFFFD200Vendor: |rCortinarius|n|cFFFFD200Zone: |rArdenweald',  description = 'These minty green moths are very friendly. They particularly enjoy having their head fuzz vigorously brushed. They also enjoy music and poetry.', },
	[1430] = { name = 'Desertwing Hunter', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = 'In service of the great Purpose, desertwing hunters were designed to fill distant skies of forgotten realms of the Shadowlands.', },
	[1431] = { name = 'Pale Regal Cervid', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = 'Looking at these majestic creatures, It\'s not difficult to see how the idea of cervids developed into the stags found on Azeroth and other mortal worlds.', },
	[1433] = { name = 'Vespoid Flutterer', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = 'A flutterer is an overgrown vespoid worker created to carry a rider. Rather than looking to a queen for leadership, it looks to its rider to guide it in all things.', },
	[1434] = { name = 'Deepstar Aurelid', sourceText = '|cFFFFD200Drop:|r Hirukon|n|cFFFFD200Zone:|r Zereth Mortis',  description = 'As one of the last remaining polyps of an elusive creature, this aurelid is at home in the water as much as the air.', },
	[1436] = { name = 'Battle-Hardened Aquilon', sourceText = '|cFFFFD200Covenant: |rKyrian|n|cFFFFD200Vendor: |rDuchess Mynx|n|cFFFFD200Zone: |rKorthia|n|cFFFFD200Cost: |r1000|Hcurrency:1767|h|TInterface/ICONS/INV_Stygia.BLP:0|t|h|n|cFFFFD200Achievement: |rOn the Offensive',  description = 'The aquilon can be a valuable ally in combat, with capabilities exceeding those of the phalynx.', },
	[1437] = { name = 'Battle-Bound Warhound', sourceText = '|cFFFFD200Drop: |rTheater of Pain|n|cFFFFD200Zone: |rMaldraxxus',  description = 'Warhounds find comfort in conflict, and the Contest of Conquest is the perfect place to find these beasts honing their abilities.', },
	[1438] = { name = 'Bulbous Necroray', sourceText = '|cFFFFD200Drop:|r Oozing Necroray Egg|n',  description = 'The necrorays of Maldraxxus prefer to congregate around the enormous decaying mushrooms of the region.', },
	[1439] = { name = 'Infested Necroray', sourceText = '|cFFFFD200Drop:|r Oozing Necroray Egg|n',  description = 'Necrorays lay their eggs in small clusters within the frills and hollows of decaying fungal blooms.', },
	[1440] = { name = 'Pestilent Necroray', sourceText = '|cFFFFD200Drop:|r Oozing Necroray Egg|n',  description = 'When a necroray hatches, spores fly everywhere, making a huge mess.', },
	[1441] = { name = 'Bound Shadehound', sourceText = '|cFFFFD200Quest:|r Feral Shadehound|n|cFFFFD200Zone: |rThe Maw',  description = 'Created by deciphering and performing powerful runic magic, this Shadehound is bound to its creator.', },
	[1442] = { name = 'Corridor Creeper', sourceText = '|cFFFFD200Achievement: |rTwisting Corridors: Layer 8|n|cFFFFD200Category: |rTorghast',  description = 'The loyal hounds of the Jailer creep along the corridors in search of the scent of souls.', },
	[1443] = { name = 'Voracious Gorger', sourceText = '|cFFFFD200Achievement: |rGlory of the Shadowlands Hero|n|cFFFFD200Category: |rDungeons & Raids',  description = 'The devourers are presumed to have their origins in the In-Between, a space between the lands of the Shadowlands that few have explored.', },
	[1444] = { name = 'Viridian Phase-Hunter', sourceText = '|cFFFFD200Promotion|r',  description = 'Hailing from a species already adept at natural camouflage, the otherworldly energies that transformed Draenor into Outland gave these creatures the ability to shift through space.', },
	[1445] = { name = 'Slime Serpent', sourceText = '|cFFFFD200Drop: |rSlime Serpent|n|cFFFFD200Zone: |rPlaguefall',  description = 'This mysterious creature lurks beneath the slime pools of Plaguefall. No one knows where it came from.', },
	[1446] = { name = 'Tazavesh Gearglider', sourceText = '|cFFFFD200Achievement: |rFake It \'Til You Make It|n|cFFFFD200Category: |rShadowlands Dungeon',  description = 'Brokers of all types use these gliders to travel around Tazavesh and similar nearby outposts, and carry their goods as well.', },
	[1448] = { name = 'Serenade', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = 'These beautiful helicids emerge only at night. When they are pleased, their shells hum with a joyful melody.', },
	[1449] = { name = 'Lord of the Corpseflies', sourceText = '|cFFFFD200Covenant: |rNecrolord|n|cFFFFD200Drop:|r Fleshwing|n|cFFFFD200Zone:|r Korthia ',  description = 'To be the Lord of the Corpseflies, you\'ve got to lay a LOT of eggs.', },
	[1450] = { name = 'Soaring Razorwing', sourceText = '|cFFFFD200Vendor: |rArchivist Roh-Suir|n|cFFFFD200Zone: |rKorthia|n|cFFFFD200Faction: |rThe Archivists\' Codex - Tier 6|n|cFFFFD200Cost: |r5000|Hcurrency:1931|h|TInterface/ICONS/INV_Misc_PaperBundle04a.BLP:0|t|h',  description = 'The hide of the razorwing is light, allowing it to fly, but also quite tough, allowing them to survive passage into the Maw.', },
	[1451] = { name = 'Vicious War Croaker', sourceText = '|cFFFFD200PvP:|r Shadowlands: Season 3|nRated Arena and Rated Battleground Season Reward',  description = 'These creatures are so enthusiastic when charging into battle that they will croak out something that sounds very much like "FOR THE HORDE!"', },
	[1452] = { name = 'Vicious War Croaker', sourceText = '|cFFFFD200PvP:|r Shadowlands: Season 3|nRated Arena and Rated Battleground Season Reward',  description = 'These creatures are so enthusiastic when charging into battle that they will croak out something that sounds very much like "FOR THE ALLIANCE!"', },
	[1454] = { name = 'Tamed Mauler', sourceText = '|cFFFFD200Drop:|r Supplies of the Archivist\'s Codex|n|cFFFFD200Zone:|r Korthia |n',  description = 'When it comes to taming devourers, all it takes is a little anima and a lot of patience. And a lot of healing potions.', },
	[1455] = { name = 'Beryl Shardhide', sourceText = '|cFFFFD200Drop:|r Death\'s Advance Supplies|n|cFFFFD200Zone:|r Korthia',  description = 'These shardhides find it easy to hide in the underbrush, and it is rare to find one that is tamed.', },
	[1456] = { name = 'Sapphire Skyblazer', sourceText = '|cFFFFD200Promotion|r |n',  description = 'These brilliant blue birds burn brighter (and hotter) than their orange cousins, though the origin of both remains a mystery.', },
	[1458] = { name = 'Wandering Ancient', sourceText = '|cFFFFD200Promotion|r',  description = 'A staunch defender of the forests of Azeroth, the ancients are also known for their glorious canopies of leaves, which change color with the seasons.', },
	[1459] = { name = 'Vicious War Gorm', sourceText = '|cFFFFD200PvP:|r Shadowlands: Season 2|nRated Arena and Rated Battleground Season Reward',  description = 'The gorm love eating the corpses of your enemies, so this seemed like a natural fit.', },
	[1460] = { name = 'Vicious War Gorm', sourceText = '|cFFFFD200PvP:|r Shadowlands: Season 2|nRated Arena and Rated Battleground Season Reward',  description = 'The gorm love eating the corpses of your enemies, so this seemed like a natural fit.', },
	[1465] = { name = 'Vicious Warstalker', sourceText = '|cFFFFD200PvP:|r Shadowlands: Season 4|nRated Arena and Rated Battleground Season Reward',  description = 'Trample the Horde to dust while riding a majestic protoform wolf! For the Alliance!', },
	[1466] = { name = 'Vicious Warstalker', sourceText = '|cFFFFD200PvP:|r Shadowlands: Season 4|nRated Arena and Rated Battleground Season Reward',  description = 'Trample the Alliance to dust while riding a majestic protoform wolf! For the Horde!', },
	[1467] = { name = 'Noble Bruffalon', sourceText = '|cFFFFD200Vendor: |rStorykeeper Ashekh|n|cFFFFD200Zone: |rThe Forbidden Reach|n|cFFFFD200Cost: |r100000|Hcurrency:2118|h|TInterface/ICONS/INV_Misc_Powder_Thorium.blp:0|t|h',  description = 'It is a great honor to be trusted with one of the Maruuk\'s loyal hunting companions. This Bruffalon is reliable and steadfast through any hardship.', },
	[1469] = { name = 'Magmashell', sourceText = '|cFFFFD200Item: |rEmpty Magma Shell|n|cFFFFD200Zone: |rThe Waking Shores',  description = 'Who\'s a good little new-forged snail made entirely of elemental lava? You are! Yes, you!', },
	[1471] = { name = 'Vengeance', sourceText = '|cFFFFD200Drop:|r Lady Sylvanas Windrunner|n|cFFFFD200Zone:|r Sanctum of Domination|n|cFFFFD200Difficulty:|r Mythic',  description = 'Sylvanas lost a part of herself the day Arthas took her life, and the darkness within her grew to fill the void.', },
	[1475] = { name = 'Hand of Bahmethra', sourceText = '|cFFFFD200Drop:|r Tormentors of Torghast|n|cFFFFD200Zone:|r The Maw|n',  description = 'The most wicked and cruel of her kin, Bahmethra endured the full measure of the Banished One\'s punishments before her fall.', },
	[1476] = { name = 'Wild Hunt Legsplitter', sourceText = '|cFFFFD200Drop:|r War Chest of the Wild Hunt|n|cFFFFD200Zone:|r The Maw',  description = 'Integrated into the depleted forces of the Wild Hunt, the fawn riders quickly found that the gorm\'s powerful mandibles were perfect for cutting down close enemy ranks.', },
	[1477] = { name = 'Undying Darkhound', sourceText = '|cFFFFD200Drop:|r War Chest of the Undying Army|n|cFFFFD200Zone:|r The Maw ',  description = 'The Undying Darkhound\'s parts have graced many battlefields, yet this latest battle may have been the first time they all fought together.', },
	[1478] = { name = 'Skyskin Hornstrider', sourceText = '|cFFFFD200Vendor:|r Brendormi|n|cFFFFD200Cost:|r 150|Hitem:202039|h|TInterface/ICONS/INV_misc_stormlordsfavor.blp:0|t|h 3000|Hcurrency:2118|h|TInterface/ICONS/INV_Misc_Powder_Thorium.blp:0|t|h',  description = 'The instability of primalist storms in potential timelines sometimes causes creatures to absorb the energies from the very storms. These creatures tend to be docile towards those who are able to face the fury of these chronomatic anomalies.', },
	[1480] = { name = 'Unchained Gladiator\'s Soul Eater', sourceText = '|cFFFFD200Achievement: |rGladiator: Shadowlands Season 2|n|cFFFFD200Category: |rPlayer vs. Player',  description = 'This soul eater\'s gaze unnerves you. Best not meet its eyes for too long.', },
	[1481] = { name = 'Cartel Master\'s Gearglider', sourceText = '|cFFFFD200Drop:|r So\'leah|n|cFFFFD200Zone:|r Tazavesh, the Veiled Market|n',  description = 'Only the richest cartel leaders are able to afford the metals required to craft the Cartel Master\'s Gearglider.', },
	[1484] = { name = 'Ardenweald Wilderling', sourceText = '|cFFFFD200Covenant: |rNight Fae|n|cFFFFD200Zone: |rArdenweald',  description = 'As anima flows to the distant wealds of Ardenweald once more, the wilderlings awaken and cautiously emerge from their canopy burrows.', },
	[1485] = { name = 'Autumnal Wilderling', sourceText = '|cFFFFD200Covenant: |rNight Fae|n|cFFFFD200Vendor: |rElwyn|n|cFFFFD200Zone: |rArdenweald|n|cFFFFD200Cost: |r7500|Hcurrency:1813|h|TInterface/ICONS/Spell_AnimaBastion_Orb.BLP:0|t|h',  description = 'The wilderlings are benevolent, magical creatures of Ardenweald, and are drawn to souls that are strong and pure.', },
	[1486] = { name = 'Winter Wilderling', sourceText = '|cFFFFD200Covenant: |rNight Fae|n|cFFFFD200Vendor: |rDuchess Mynx|n|cFFFFD200Zone: |rKorthia|n|cFFFFD200Cost: |r1000|Hcurrency:1767|h|TInterface/ICONS/INV_Stygia.BLP:0|t|h|n|cFFFFD200Achievement: |rOn the Offensive',  description = 'Wilderlings serve as support units in battle, preserving their allies and rendering enemy magics useless.', },
	[1487] = { name = 'Summer Wilderling', sourceText = '|cFFFFD200Covenant: |rNight Fae|n|cFFFFD200Drop:|r Escaped Wilderling|n|cFFFFD200Zone:|r Korthia |n',  description = 'Stubborn and rebellious, this wilderling is loyal to very few. But those few are bonded for life.', },
	[1489] = { name = 'Obsidian Gravewing', sourceText = '|cFFFFD200Covenant: |rVenthyr|n|cFFFFD200Vendor: |rChachi the Artiste|n|cFFFFD200Zone: |rRevendreth|n|cFFFFD200Cost: |r7500|Hcurrency:1813|h|TInterface/ICONS/Spell_AnimaBastion_Orb.BLP:0|t|h',  description = 'In the darkened skies of Revendreth, you cannot see these creatures coming until it is too late.', },
	[1490] = { name = 'Sinfall Gravewing', sourceText = '|cFFFFD200Covenant: |rVenthyr|n|cFFFFD200Zone: |rRevendreth',  description = 'With anima once again flowing in Sinfall, the Stonewright has been able to infuse these Stoneborn, making them valuable allies in the battles to come.', },
	[1491] = { name = 'Pale Gravewing', sourceText = '|cFFFFD200Covenant: |rVenthyr|n|cFFFFD200Vendor: |rDuchess Mynx|n|cFFFFD200Zone: |rKorthia|n|cFFFFD200Cost: |r1000|Hcurrency:1767|h|TInterface/ICONS/INV_Stygia.BLP:0|t|h|n|cFFFFD200Achievement: |rOn the Offensive',  description = 'Gravewings know no fear and are nearly indestructible -- perfect for an assault on the Maw.', },
	[1492] = { name = 'Elysian Aquilon', sourceText = '|cFFFFD200Covenant: |rKyrian|n|cFFFFD200Zone: |rBastion',  description = 'With anima once again flowing to the Eternal Forge in Bastion, the plans for the aquilon centurion could be crafted once more.', },
	[1493] = { name = 'Forsworn Aquilon', sourceText = '|cFFFFD200Covenant: |rKyrian|n|cFFFFD200Drop:|r Wild Worldcracker|n|cFFFFD200Zone:|r Korthia |n',  description = 'Originally produced by the Forsworn with stolen blueprints, these powerful centurion are now earning a reputation across the Shadowlands.', },
	[1494] = { name = 'Ascendant\'s Aquilon', sourceText = '|cFFFFD200Covenant: |rKyrian|n|cFFFFD200Vendor: |rAdjutant Galos|n|cFFFFD200Zone: |rBastion|n|cFFFFD200Cost: |r7500|Hcurrency:1813|h|TInterface/ICONS/Spell_AnimaBastion_Orb.BLP:0|t|h',  description = 'The aquilon can rapidly transport aspirants, stewards, and even ascended to a location. Hey, sometimes your wings get tired.', },
	[1495] = { name = 'Maldraxxian Corpsefly', sourceText = '|cFFFFD200Covenant: |rNecrolord|n|cFFFFD200Zone: |rMaldraxxus',  description = 'Corpseflies were once found throughout Maldraxxus, but many died out in the absence of anima. With anima flowing once again, they swarm.', },
	[1496] = { name = 'Regal Corpsefly', sourceText = '|cFFFFD200Covenant: |rNecrolord|n|cFFFFD200Vendor: |rSu Zettai|n|cFFFFD200Zone: |rMaldraxxus|n|cFFFFD200Cost: |r7500|Hcurrency:1813|h|TInterface/ICONS/Spell_AnimaBastion_Orb.BLP:0|t|h',  description = 'Only the forces of Maldraxxus would call a corpsefly "regal."', },
	[1497] = { name = 'Battlefield Swarmer', sourceText = '|cFFFFD200Covenant: |rNecrolord|n|cFFFFD200Vendor: |rDuchess Mynx|n|cFFFFD200Zone: |rKorthia|n|cFFFFD200Cost: |r1000|Hcurrency:1767|h|TInterface/ICONS/INV_Stygia.BLP:0|t|h|n|cFFFFD200Achievement: |rOn the Offensive',  description = 'Corpseflies are strongly attracted to the smell of dead bodies and will swarm an active battlefield as soon as the first corpse falls.', },
	[1500] = { name = 'Sanctum Gloomcharger', sourceText = '|cFFFFD200Drop:|r The Nine|n|cFFFFD200Zone:|r Sanctum of Domination',  description = 'Only the Jailer\'s most powerful lieutenants are gifted these steeds to stalk the halls of the Tower.', },
	[1501] = { name = 'Soulbound Gloomcharger', sourceText = '|cFFFFD200Drop:|r Mysterious Gift from Ve\'nari|n|cFFFFD200Zone:|r The Maw',  description = 'Ve\'nari has many methods of sneaking around the Maw and disrupting the Jailer\'s plans, including binding his own minions.', },
	[1502] = { name = 'Fallen Charger', sourceText = '|cFFFFD200Drop:|r Fallen Charger|n|cFFFFD200Zone:|r The Maw',  description = 'The charger\'s screams echo across the Maw as it continues a never-ending ride across the land, forever looking for escape from the endless torments it has been forced to endure.', },
	[1503] = { name = 'Hand of Nilganihmaht', sourceText = '|cFFFFD200Zone:|r The Maw',  description = 'Of all the giants who dared defy the Jailer, Nilganihmaht was the most arrogant. For this transgression, the giant\'s severed hand was hidden within a veil of torments, and the rings that could summon it back were scattered across the Maw.', },
	[1504] = { name = 'Hand of Salaranga', sourceText = '|cFFFFD200Achievement:|r Breaking the Chains|n|cFFFFD200Zone:|r Korthia and The Maw',  description = 'Salaranga was the most cunning of her kin and hid amongst the Beastwarrens. She struck at the Banished One with a horde of bestial allies. It did not matter.', },
	[1505] = { name = 'Amber Shardhide', sourceText = '|cFFFFD200Vendor: |rDuchess Mynx|n|cFFFFD200Zone: |rKorthia|n|cFFFFD200Faction: |rDeath\'s Advance - Revered|n|cFFFFD200Cost: |r5000|Hcurrency:1767|h|TInterface/ICONS/INV_Stygia.BLP:0|t|h',  description = 'The bright colors of Korthian wildlife indicate that they have either few predators, or are tough enough to fight back.', },
	[1506] = { name = 'Crimson Shardhide', sourceText = '|cFFFFD200Drop:|r Malbog|n|cFFFFD200Zone:|r Korthia',  description = 'When Korthia was pulled into the maw, only a few hardy species survived.', },
	[1507] = { name = 'Darkmaul', sourceText = '|cFFFFD200Zone:|r Korthia',  description = 'An older shardhide, Darkmaul was relatively tame and friendly around Korthians. He was thought lost to the calamity.', },
	[1508] = { name = 'Fierce Razorwing', sourceText = '|cFFFFD200Drop:|r Death\'s Advance Supplies|n|cFFFFD200Zone:|r Korthia',  description = 'Razorwings generally stay away from other Korthian species, but with time and patience, they can be tamed as well.', },
	[1509] = { name = 'Garnet Razorwing', sourceText = '|cFFFFD200Drop:|r Reliwik the Defiant|n|cFFFFD200Zone:|r Korthia',  description = 'Korthia was once known for its bright red flowers. Several species were known for consuming or rolling in them, and taking on bright red coloration as a result.', },
	[1510] = { name = 'Dusklight Razorwing', sourceText = '|cFFFFD200Zone:|r Korthia',  description = 'Razorwings can be seen throughout Korthia, making their nests on high hills and in trees.', },
	[1511] = { name = 'Maelie, the Wanderer', sourceText = '|cFFFFD200Zone:|r Korthia',  description = 'A curious and inquisitive doe, she doesn\'t mean to worry her master, she just gets distracted easily. Especially with all of the new things to see on Korthia.', },
	[1513] = { name = 'Sarge\'s Tale', sourceText = '|cFFFFD200Promotion|r|n',  description = 'Hold on a sec, Sarge isn\'t that large, someone must have spun a tall tale in the Hearthstone Tavern...', },
	[1514] = { name = 'Rampaging Mauler', sourceText = '|cFFFFD200Drop:|r Konthrogz the Obliterator|n|cFFFFD200Zone:|r Korthia',  description = 'One would think controlling a rampaging mount would be difficult, but if you can learn to control the direction of the rampage it can be quite useful.', },
	[1520] = { name = 'Soultwisted Deathwalker', sourceText = '|cFFFFD200Achievement: |rShadowlands Keystone Master: Season Two|n|cFFFFD200Category: |rDungeons',  description = 'A howling vortex of tormented souls, ridden only by those who wish to demonstrate their impressive tolerance for pain and suffering.', },
	[1521] = { name = 'Val\'sharah Hippogryph', sourceText = '|cFFFFD200Vendor: |rAridormi|n|cFFFFD200Zone: |rDalaran|n|cFFFFD200Cost: |r5000|Hcurrency:1166|h|TInterface/ICONS/PVECurrency-Justice.blp:0|t|h',  description = 'These majestic hippogryphs are as close to a being of the Emerald Dream as any resident of Azeroth can be, and do not bond with a rider easily.', },
	[1522] = { name = 'Heartlight Vombata', sourceText = '|cFFFFD200Vendor: |rVilo|n|cFFFFD200Zone: |rZereth Mortis|n|cFFFFD200Faction: |rThe Enlightened - Exalted|n|cFFFFD200Cost: |r5000|Hcurrency:1813|h|TInterface/ICONS/Spell_AnimaBastion_Orb.BLP:0|t|h',  description = 'The Enlightened revere all creations of the First Ones, yet for many, the vombata of Zereth Mortis are the most beloved. These creatures are equal parts trusted beast of burden and beloved companion.', },
	[1523] = { name = 'Curious Crystalsniffer', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = 'Most vombata enjoy tunneling through the ground, but these sniffers seem to have an unerring ability to locate crystals buried deep underground and will congregate around large deposits.', },
	[1524] = { name = 'Darkened Vombata', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = 'The schematic to synthesize these darkened vombata seem to have appeared as a reaction to the Mawsworn invasion. The resulting vombata look different--darker, much like the invaders--but behave normally.', },
	[1525] = { name = 'Adorned Vombata', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = 'Red-colored vombata are considered lucky by the Enlightened. It was a red vombata that was present when they first arrived in Zereth Mortis.', },
	[1526] = { name = 'Deathrunner', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = 'These wild cervids are only truly at peace when running through--or between--the lands of Death.', },
	[1528] = { name = 'Sundered Zerethsteed', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = 'Whatever the purpose of these cervids once was, it is long forgotten. Though they are not Dominated, they have been corrupted by the unintended presence of Mawsworn in the sacred realm of Zereth Mortis.', },
	[1529] = { name = 'Anointed Protostag', sourceText = '|cFFFFD200Vendor: |rVilo|n|cFFFFD200Zone: |rZereth Mortis|n|cFFFFD200Faction: |rThe Enlightened - Revered|n|cFFFFD200Cost: |r5000|Hcurrency:1813|h|TInterface/ICONS/Spell_AnimaBastion_Orb.BLP:0|t|h',  description = 'Only those most esteemed by the Enlightened are trusted with the reins of this rare cervid.', },
	[1531] = { name = 'Wen Lo, the River\'s Edge', sourceText = '|cFFFFD200In Game Shop|r|n',  description = 'The rivers of Pandaria, like Wen Lo, are unpredictable in nature. They may remain calm, bringing water to the crops, or flood, bringing ruin. Wen Lo reminds us that we must adapt to the challenges of life.', },
	[1532] = { name = 'Soaring Spelltome', sourceText = '|cFFFFD200Achievement: |rA Tour of Towers|n|cFFFFD200Category: |rTimewalking',  description = 'In the forgotten depths of the Dalaran library, this oversized tome was found flapping madly around the room. Open the pages of this arcane-infused book and hitch a ride to magical adventure.', },
	[1533] = { name = 'Forged Spiteflyer', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = 'There\'s nothing that can make you feel as alive quite like riding through the realm of Death while atop a being that hates you.', },
	[1534] = { name = 'Buzz', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = 'Buzz buzz buzz, buzz buzz! Buzz buzz buzz buzz buzz. Buzz buzz buzz, buzz buzz buzz.', },
	[1535] = { name = 'Bronzewing Vespoid', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = 'Bronzewing vespoids are the result of attempting to synthesize a vespoid queen with no other members of a new hive. While no mortal has yet discovered how to synthesize a hive, the bronzewings are reliable mounts just as they are.', },
	[1536] = { name = 'Mawdapted Raptora', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = 'Since the Jailer\'s incursion, new raptora have been synthesized by Zereth Mortis that are capable of resisting the corrupting influence of the Maw.', },
	[1537] = { name = 'Raptora Swooper', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = 'The raptora swooper is one of the most vicious iterations of its kind, perfectly suited for its ecological niche. Needing no further adjustments, the ones synthesized today are identical to those created in the past.', },
	[1538] = { name = 'Bronze Helicid', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = 'Some helicids of Zereth Mortis have strangely metallic shells. The ones who do are generally a bronze color, indistinct from the metal any novice miner could work, save that this creature\'s construction far surpasses the skill of any novice.', },
	[1539] = { name = 'Unsuccessful Prototype Fleetpod', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = 'This creature was not designed by the First Ones, but instead by an unknown being attempting to create a very fast runner. Needless to say the attempt failed, because this mount is very slow.', },
	[1540] = { name = 'Scarlet Helicid', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = 'This helicid\'s unusual reddish tint is clearly intentional in its design, but the only other difference between these and other helicids is the unusual lack of any ooze.', },
	[1541] = { name = 'Genesis Crawler', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = 'The Enlightened who have observed tarachnids believe that this variety may be some of the first beings to ever walk the land of Zereth Mortis.', },
	[1542] = { name = 'Tarachnid Creeper', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = 'These tarachnids make their home in the darkest corners of Zereth Mortis, coming out only to hunt (or, sometimes, carry a rider around).', },
	[1543] = { name = 'Ineffable Skitterer', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = 'These tarachnids move with a purpose unknown to others, answering only to a higher calling. Whatever that may be, it appears to include routine assistance to mortals who need a ride. For now.', },
	[1544] = { name = 'Wastewarped Deathwalker', sourceText = '|cFFFFD200Achievement: |rShadowlands Keystone Master: Season Three|n|cFFFFD200Category: |rDungeons',  description = 'Raw swirling energy bound together into a cohesive form, it will only bend to the will of those strong enough to bind it.', },
	[1545] = { name = 'Divine Kiss of Ohn\'ahra', sourceText = '|cFFFFD200Quest:|r A Whispering Breeze|n|cFFFFD200Zone:|r Ohn\'ahran Plains',  description = '...as though welcomed by the wind itself.', },
	[1546] = { name = 'Iskaara Trader\'s Ottuk', sourceText = '|cFFFFD200Vendor: |rTattukiaka|n|cFFFFD200Zone: |rThe Azure Span|n|cFFFFD200Cost: |r1|Hitem:195502|h|TInterface/ICONS/INV_10_DungeonJewelry_Primalist_Trinket_4_Earth.BLP:0|t|h 1|Hitem:195496|h|TInterface/ICONS/INV_10_DungeonJewelry_Primalist_Necklace_1_Air.BLP:0|t|h',  description = 'There are no guarantees when you purchase an ottuk from Tattukiaka. At least they\'re cute.', },
	[1547] = { name = 'Goldplate Bufonid', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = 'This armored bufonid is always ready to leap into battle... as long as that battle is really, really close. In a pinch, it can leap multiple times and eventually reach the battle.', },
	[1549] = { name = 'Shimmering Aurelid', sourceText = '|cFFFFD200Achievement: |rGlory of the Sepulcher Raider|n|cFFFFD200Category: |rDungeons & Raids',  description = 'Though it was once created to defend the Sepulcher of the First Ones, with its primary function now completed, it now obediently follows your command.', },
	[1551] = { name = 'Cryptic Aurelid', sourceText = '|cFFFFD200Achievement: |rFrom A to Zereth|n|cFFFFD200Category: |rShadowlands',  description = 'The Cyphers of the First Ones are beyond any mortal understanding. A curious effect of the language is that, for reasons unknown, some aurelids are drawn to mortals who aspire to unlock its mystery.', },
	[1552] = { name = 'Carcinized Zerethsteed', sourceText = '|cFFFFD200Quest:|r Leading Motives',  description = 'The few of these mysterious creatures that remain appear to be impossibly old, almost timeless. Perhaps they were created by the First Ones, because no schematic to synthesize these has yet been discovered.', },
	[1553] = { name = 'Liberated Slyvern', sourceText = '|cFFFFD200Drop: |rBreezebiter|n|cFFFFD200Zone: |rThe Azure Span',  description = 'It\'s difficult to tell because It\'s far too angry, but this slyvern is actually glad It\'s not owned by gnolls anymore.', },
	[1556] = { name = 'Tangled Dreamweaver', sourceText = '|cFFFFD200Promotion:|r Dragonflight Heroic Edition|n',  description = 'The last of the sleepers are finally awakening, bringing the dreams of the dragons back to the lands.', },
	[1563] = { name = 'Highland Drake', sourceText = '|cFFFFD200Achievement:|r Dragon Isles: Highland Drake|n',  description = 'The drakes of Azure Span follow a strict personal code of honor as they explore the isles anew.', },
	[1564] = { name = 'Colossal Soulshredder Mawrat', sourceText = '|cFFFFD200Achievement:|r Flawless Master (Layer 16)|n|cFFFFD200Category:|r Torghast|n',  description = 'The incredible prowess you have demonstrated within the Jailer\'s Tower has instilled sufficient respect into this fearsome creature, enabling its use as a mount.', },
	[1565] = { name = 'Colossal Umbrahide Mawrat', sourceText = '|cFFFFD200Drop:|r Torghast Layers 13 or higher|n|cFFFFD200Location:|r Torghast|n',  description = 'This disturbingly large Mawrat is an anomaly amongst its species. These Colossal Mawrats lurk only in the most distant and darkest reaches of Torghast.', },
	[1566] = { name = 'Colossal Ebonclaw Mawrat', sourceText = '|cFFFFD200Achievement:|r The Jailer\'s Gauntlet: Layer 4|n|cFFFFD200Category:|r Torghast|n',  description = 'This vicious and cunning creature has grown to its astonishing size by not only feasting upon the weakened souls in the Jailer\'s Tower, but also vulnerable Mawsworn.', },
	[1567] = { name = 'Lightforged Ruinstrider', sourceText = '',  description = '', },
	[1568] = { name = 'Lightforged Ruinstrider', sourceText = '|cFFFFD200Paladin|r |n',  description = 'A loyal steed to the Lightforged Draenei.', },
	[1569] = { name = 'Patient Bufonid', sourceText = '|cFFFFD200Quest: |rThe Patient Bufonid|n|cFFFFD200Zone: |rZereth Mortis',  description = 'Some bufonids will sit perfectly still for hours or days at a time if left to their own devices. Fortunately, they\'re also quite agreeable if a rider would like to move more often than that.', },
	[1570] = { name = 'Prototype Leaper', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = 'An ancient schematic allows for the instantiation of this early, but functional, bufonid design.', },
	[1571] = { name = 'Russet Bufonid', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = 'Russet is a rare color among bufonids that is normally only seen on specimens that are tainted by other cosmic forces, or synthesized from a perplexing schematic that invokes that color.', },
	[1572] = { name = 'Cosmic Gladiator\'s Soul Eater', sourceText = '|cFFFFD200Achievement: |rGladiator: Shadowlands Season 3|n|cFFFFD200Category: |rPlayer vs. Player',  description = 'This soul eater\'s gaze unnerves you. Best not meet its eyes for too long.', },
	[1573] = { name = 'Magenta Cloud Serpent', sourceText = '|cFFFFD200Trading Post|r',  description = 'More affectionate than their cousins, Magenta Cloud Serpents are known to form close bonds with their riders.', },
	[1574] = { name = 'Crusty Crawler', sourceText = '|cFFFFD200Trading Post|r',  description = 'This rare giant crab has taken a break from its nautical nonsense deep under the sea to explore the surface.', },
	[1575] = { name = 'Quawks', sourceText = '|cFFFFD200Trading Post|r',  description = 'Squawks\'s older (and somehow louder) brother appears to tolerate your presence. For now. You should probably track down some crackers while you still can.', },
	[1576] = { name = 'Jigglesworth Sr.', sourceText = '|cFFFFD200Achievement:|r Fates of the Shadowlands Raids|n',  description = 'It\'s unclear how the saddle stays atop this formerly feline friend, but somehow the House of Plagues made it work. Science.', },
	[1577] = { name = 'Ash\'adar, Harbinger of Dawn', sourceText = '|cFFFFD200Trading Post|r',  description = 'Kaldorei legends whisper of a relentless hunter prowling amongst the deepest shadows of Kalimdor, never sleeping. By the light of the sun or the moon, Ash\'adar now hunts with you.', },
	[1578] = { name = '[DND] Test Mount JZB', sourceText = '|cFFFFD200Vendor: |rUncle Bigpocket|cFFFFD200Zone: |rKun-Lai Summit|cFFFFD200Cost: |r120000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = '', },
	[1579] = { name = 'Coral-Stalker Waveray', sourceText = '|cFFFFD200Trading Post|r',  description = 'This species of ray seems to have evolved to stalk its prey amidst the brightly colored corals of the seas.', },
	[1580] = { name = 'Heartbond Lupine', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = 'From the moment of its creation, this packless lupine has held nothing more than deepest affection and loyalty towards you.', },
	[1581] = { name = 'Nether-Gorged Greatwyrm', sourceText = '|cFFFFD200In-Game Shop|r|n',  description = 'Few mana wyrms find sufficiently stable sources of arcane power to reach such a size. A greatwyrm\'s appetite for mana is unmatched.', },
	[1582] = { name = 'Savage Green Battle Turtle', sourceText = '|cFFFFD200Trading Post|r',  description = 'Turtle too slow to chase down your foes? Just add cannons. Nothing can outrun a cannon.', },
	[1583] = { name = 'Armored Siege Kodo', sourceText = '|cFFFFD200In-Game Shop|r',  description = 'Only the strongest and most fearless of kodos can be trained to become living siege weapons. Even the highest walls won\'t keep them out for long.', },
	[1584] = { name = 'Colossal Plaguespew Mawrat', sourceText = '|cFFFFD200Drop:|r Rhuv, Gorger of Ruin|n',  description = 'These rodents became Iska\'s infamous sentries, scouting and carrying her plague across the Maw. Their presence is only followed by ruin.', },
	[1585] = { name = 'Colossal Wraithbound Mawrat', sourceText = '|cFFFFD200Drop:|r Mawsworn Supply Chest|n',  description = 'Infused by the energies of tormented souls, these creatures have adopted ghostlike abilities that make them quite elusive.', },
	[1586] = { name = 'Armored Golden Pterrordax', sourceText = '|cFFFFD200Trading Post|r',  description = 'Painted and adorned with gold, these Pterrordax patrol the skies over the tomb of Atal\'Dazar.', },
	[1587] = { name = 'Zereth Overseer', sourceText = '|cFFFFD200Drop: |rThe Jailer|n|cFFFFD200Zone: |rSepulcher of the First Ones|n|cFFFFD200Difficulty: |rMythic',  description = 'Enigmatic and aloof, this construct of the First Ones flies on a graceful stream of perfect geometric understanding.', },
	[1588] = { name = 'Winding Slitherdrake', sourceText = '|cFFFFD200Achievement:|r A Serpentine Discovery',  description = 'These drakes feel at home in the depths of the earth and the far reaches of the sky.', },
	[1589] = { name = 'Renewed Proto-Drake', sourceText = '|cFFFFD200Achievement:|r Dragon Isles: A New Friend',  description = 'As the isles awaken, these proto-drakes answer the call to soar the skies alongside mortals.', },
	[1590] = { name = 'Windborne Velocidrake', sourceText = '|cFFFFD200Achievement:|r Dragon Isles: Windborne Velocidrake',  description = 'The long talons of the velocidrake give it an edge on the plains.', },
	[1591] = { name = 'Cliffside Wylderdrake', sourceText = '|cFFFFD200Achievement:|r Dragon Isles: Cliffside Wylderdrake',  description = 'Often roosting along the steep cliff faces of Thaldraszus, they\'re swift to the skies in times of danger.', },
	[1594] = { name = 'Jade, Bright Foreseer', sourceText = '|cFFFFD200In Game Shop|r|n',  description = 'The presence of this majestic Pandaren rabbit spirit brings joy and happiness and is heralded as a wonderful omen. Legend says that in its eyes, you can glimpse the greatest happiness of your life, even if it is yet to come.', },
	[1595] = { name = 'Cerulean Marsh Hopper', sourceText = '|cFFFFD200Trading Post|r',  description = 'Gottum\'s expansion into export markets was inevitable with news of the Trading Post reaching even into the depths of the Frogmarsh.', },
	[1596] = { name = 'Amalgam of Rage', sourceText = '|cFFFFD200Promotion:|r Diablo IV',  description = 'The fearsome beast ridden by Astaroth himself is now yours to command.', },
	[1597] = { name = 'Grimhowl', sourceText = '|cFFFFD200Quest: |rGood Fiery Boy|n|cFFFFD200Zone: |rBlackrock Depths',  description = 'Otherwise known as The Flame Woof, the Fetcher of the Igneous, Molten Core\'s Best Friend, Forgehound Maximus, the Best Fiery Boy, and "oh no is that an AXE on his face!?"', },
	[1599] = { name = 'Eternal Gladiator\'s Soul Eater', sourceText = '|cFFFFD200Achievement: |rGladiator: Shadowlands Season 4|n|cFFFFD200Category: |rPlayer vs. Player',  description = 'This soul eater\'s gaze unnerves you. Best not meet its eyes for too long.', },
	[1600] = { name = 'Elusive Emerald Hawkstrider', sourceText = '|cFFFFD200Quest: |rBlood Knight|n|cFFFFD200Zone: |rGhostlands|n|n|cFFFFD200Quest: |rVictory for the Sin\'dorei|n|cFFFFD200Zone: |rGhostlands',  description = 'Unseen for many years, this brightly hued hawkstrider emerged from hiding to help defend the Ghostlands from the Scourge. Its prowess shines as brilliantly as its feathers.', },
	[1602] = { name = 'Tuskarr Shoreglider', sourceText = '|cFFFFD200Promotion|r|n',  description = 'The Tuskarr learned long ago that some of the bones of the sea creatures they hunted were light and buoyant, making for effective glider materials.', },
	[1603] = { name = 'Subterranean Magmammoth', sourceText = '|cFFFFD200Vendor: |rSacratros|n|cFFFFD200Zone: |rZaralek Cavern|n|cFFFFD200Cost: |r100|Hitem:204727|h|TInterface/ICONS/INV_Misc_CuriousCoin.BLP:0|t|h',  description = 'Awakened from a long slumber, this beast has yet to see the light of day.', },
	[1605] = { name = 'Dragon Isles Drake Model Test', sourceText = '|cFFFFD200Achievement:|r Dragon Isles Drake|n',  description = 'NYI', },
	[1607] = { name = 'Swift Spectral Drake', sourceText = '|cFFFFD200Legacy|r',  description = 'Even the dead need a lift every now and then.', },
	[1612] = { name = 'Loyal Magmammoth', sourceText = '|cFFFFD200Vendor: |rYries Lightfingers|n|cFFFFD200Zone: |rThe Waking Shores',  description = 'Liberated from the Qalashi and harnessed by your ingenuity (and your harness), this magmammoth is ready to lava stomp wherever you want to go.', },
	[1615] = { name = 'Tamed Skitterfly', sourceText = '|cFFFFD200Faction:|r Dragonscale Expedition|n|cFFFFD200Renown:|r 25|n|cFFFFD200Vendor:|r Granpap Whiskers|n|cFFFFD200Location:|r Dragonscale Basecamp',  description = 'It didn\'t take long for Granpap Whiskers to tame the skitterflies around the Dragonscale Basecamp and set them to helping move Expedition personnel and cargo.', },
	[1616] = { name = 'Azure Skitterfly', sourceText = '|cFFFFD200Faction:|r Dragonscale Expedition|n|cFFFFD200Renown:|r 25|n|cFFFFD200Vendor:|r Granpap Whiskers|n|cFFFFD200Location:|r Dragonscale Basecamp|n|cFFFFD200Cost:|r 5|Hitem:201401|h|TInterface/ICONS/INV_Icon_Feather05d.BLP:0|t|h 20|Hitem:193053|h|TInterface/ICONS/INV_Feather_11.blp:0|t|h 750|Hcurrency:2003|h|TInterface/ICONS/INV_Faction_WarResources.BLP:0|t|h',  description = 'Once Granpap Whiskers finally taught them how to take riders, the azure skitterflies proved to bond closely and loyally to their riders.', },
	[1617] = { name = 'Verdant Skitterfly', sourceText = '|cFFFFD200Faction:|r Dragonscale Expedition|n|cFFFFD200Renown:|r 25|n|cFFFFD200Treasure:|r Expedition Scout\'s Pack',  description = 'Verdant skitterflies are the perfect mount for the scouts of the Dragonscale Expedition scouts, as they are fast and blend into the foliage of the Dragon Isles.', },
	[1619] = { name = 'Ancient Salamanther', sourceText = '|cFFFFD200Drop:|r The Forbidden Reach|n',  description = 'Due to their preference for dark caves and murky waters, sightings of this unique breed of salamanther are so rare that many researchers thought they had gone extinct.', },
	[1622] = { name = 'Stormhide Salamanther', sourceText = '|cFFFFD200Vendor:|r Mythressa|n|cFFFFD200Cost:|r 2000|Hcurrency:2118|h|TInterface/ICONS/INV_Misc_Powder_Thorium.blp:0|t|h',  description = 'Sometimes when storm energies infuse a creature, it simply... becomes larger and more placid. Few salamanthers are willing to be ridden, but these children of the storm are a notable exception.', },
	[1623] = { name = 'Seething Slug', sourceText = '|cFFFFD200Treasure: |rSeething Cache|n|cFFFFD200Zone: |rZaralek Cavern',  description = 'Drawn forth from its abyss by the rage of the Zaqali.', },
	[1626] = { name = 'Shellack', sourceText = '|cFFFFD200Achievement:|r Glory of the Dragonflight Hero|n',  description = 'Fed by victory and hardened by glory, this snail is nothing less than a tribute to mastery.', },
	[1627] = { name = 'Gooey Snailemental', sourceText = '|cFFFFD200Drop:|r The Forbidden Reach|n',  description = 'Formed from the leftover elemental energies of the Primal Storms, this magical mollusk is faster than it appears.', },
	[1629] = { name = 'Scrappy Worldsnail', sourceText = '|cFFFFD200Vendor: |rDealer Vexil|n|cFFFFD200Zone: |rThe Waking Shores|n|cFFFFD200Cost: |r1000|Hitem:202173|h|TInterface/ICONS/Creatureportrait_Twilightshammer_lava_magicball.blp:0|t|h',  description = 'For the Worldbreakers, a portable footstool. For you, a hardy mount.', },
	[1634] = { name = 'Mossy Mammoth', sourceText = '|cFFFFD200Drop:|r Zskera Vaults|n',  description = 'Preserved for thousands of years in stone, bronze magic has nearly aged this mammoth back into a fossil.', },
	[1635] = { name = 'Plainswalker Bearer', sourceText = '|cFFFFD200World Event:|r Centaur Hunts (first hunt of each week)',  description = 'The mammoths of the Ohn\'ahran Plains are unaccustomed to carrying people, as their centaur neighbors do not typically ride mounts. They can learn to bear riders, however. Some even do so enthusiastically.', },
	[1639] = { name = 'Lizi, Thunderspine Tramper', sourceText = '|cFFFFD200Quest: |rTo Tame A Thunderspine|n|cFFFFD200Zone: |rOhn\'ahran Plains',  description = 'Nothing will stop Lizi from her mission to eat all the world\'s fruit.', },
	[1644] = { name = 'Raging Magmammoth', sourceText = '|cFFFFD200Achievement: |rGlory of the Vault Raider|n|cFFFFD200Category: |rDungeons & Raids',  description = 'Most lava flows. This lava charges.', },
	[1645] = { name = 'Renewed Magmammoth', sourceText = '|cFFFFD200Drop:|r Dreamsurges',  description = 'Reforged after destruction at the hands of the Druids of the Flame, this magmammoth is ready to stomp any foes in its path.', },
	[1653] = { name = 'Brown War Ottuk', sourceText = '|cFFFFD200Faction:|r Iskaara Tuskarr|n|cFFFFD200Renown:|r 30|n|cFFFFD200Vendor:|r Tatto|n|cFFFFD200Location:|r Iskaara|n|cFFFFD200Cost:|r 5|Hitem:201403|h|TInterface/ICONS/ACHIevement_Reputation_Tuskarr.blp:0|t|h 5|Hitem:201400|h|TInterface/ICONS/TRADE_ARCHAEOLOGY_SHARK JAWS.BLP:0|t|h 1000|Hcurrency:2003|h|TInterface/ICONS/INV_Faction_WarResources.BLP:0|t|h',  description = 'The tuskarr of Iskaara are loathe to war against anyone, but many tuskarr and their allies have nonetheless taken up the spear with a war ottuk beside them.', },
	[1654] = { name = 'Otterworldly Ottuk Carrier', sourceText = '|cFFFFD200Achievement: |rThanks for the Carry!|n|cFFFFD200Category: |rMounts|n',  description = 'Some ottuks get into the spirit of carrying people around more than others.', },
	[1655] = { name = 'Yellow War Ottuk', sourceText = '|cFFFFD200Faction:|r Iskaara Tuskarr|n|cFFFFD200Renown:|r 30|n|cFFFFD200Vendor:|r Tatto|n|cFFFFD200Location:|r Iskaara|n|cFFFFD200Cost:|r 5|Hitem:201403|h|TInterface/ICONS/ACHIevement_Reputation_Tuskarr.blp:0|t|h 5|Hitem:201400|h|TInterface/ICONS/TRADE_ARCHAEOLOGY_SHARK JAWS.BLP:0|t|h 1000|Hcurrency:2003|h|TInterface/ICONS/INV_Faction_WarResources.BLP:0|t|h',  description = 'The yellow ottuks of the Azure Span are sleek, sneaky, and smart. Those trained for war are the finest tuskarr cavalry mounts, equally capable on the ground and in the waters.', },
	[1656] = { name = 'Otto', sourceText = '|cFFFFD200Quest:|r The Way to an Otto\'s Heart|n',  description = 'Relax. The future will unfold as it was meant to, one way or an otter.', },
	[1657] = { name = 'Brown Scouting Ottuk', sourceText = '|cFFFFD200Faction:|r Iskaara Tuskarr|n|cFFFFD200Renown:|r 25|n|cFFFFD200Vendor:|r Tatto|n|cFFFFD200Location:|r Iskaara|n|cFFFFD200Cost:|r 2|Hitem:201403|h|TInterface/ICONS/ACHIevement_Reputation_Tuskarr.blp:0|t|h 2|Hitem:201400|h|TInterface/ICONS/TRADE_ARCHAEOLOGY_SHARK JAWS.BLP:0|t|h 750|Hcurrency:2003|h|TInterface/ICONS/INV_Faction_WarResources.BLP:0|t|h',  description = 'Wily and playful in their natural habitat, ottuks possess keen eyes that can scout far afield as ably as they watch for predators.', },
	[1658] = { name = 'Ivory Trader\'s Ottuk', sourceText = '|cFFFFD200Vendor: |rTattukiaka|n|cFFFFD200Zone: |rThe Azure Span|n|cFFFFD200Cost: |r1|Hitem:193696|h|TInterface/ICONS/INV_10_DungeonJewelry_Primalist_Ring_1_Earth.BLP:0|t|h 1|Hitem:193633|h|TInterface/ICONS/INV_10_DungeonJewelry_Tuskarr_Ring_2_Color4.BLP:0|t|h 1|Hitem:193708|h|TInterface/ICONS/INV_10_DungeonJewelry_Explorer_Ring_1_Color1.BLP:0|t|h',  description = 'No adult knows why some ottuks are white, but any tuskarr child can tell you: It\'s because they eat too much snow.', },
	[1659] = { name = 'Yellow Scouting Ottuk', sourceText = '|cFFFFD200Faction:|r Iskaara Tuskarr|n|cFFFFD200Renown:|r 25|n|cFFFFD200Vendor:|r Tatto|n|cFFFFD200Location:|r Iskaara|n|cFFFFD200Cost:|r 2|Hitem:201403|h|TInterface/ICONS/ACHIevement_Reputation_Tuskarr.blp:0|t|h 2|Hitem:201400|h|TInterface/ICONS/TRADE_ARCHAEOLOGY_SHARK JAWS.BLP:0|t|h 750|Hcurrency:2003|h|TInterface/ICONS/INV_Faction_WarResources.BLP:0|t|h',  description = 'The yellow scouting ottuk love to hide and they blend in well to their surroundings. When not \'playing\' hide and seek with their riders, they are perfect companions when scouting enemy encampments.', },
	[1660] = { name = 'Crimson Gladiator\'s Drake', sourceText = '|cFFFFD200Achievement:|r Gladiator: Dragonflight Season 1|n|cFFFFD200Category:|r Player vs. Player',  description = 'Forged in the fires of conquest, and armored to the teeth.', },
	[1662] = { name = 'Telix the Stormhorn', sourceText = '|cFFFFD200Promotion|r |n',  description = 'Once a simple beetle of the Forbidden Reach, Telix became infused with storm magic when Raszageth was unleashed. With its newly heightened senses, Telix roams the Dragon Isles alongside the Dragonscale Expedition--and you!', },
	[1664] = { name = 'Guardian Vorquin', sourceText = '|cFFFFD200Vendor: |rTreysh|n|cFFFFD200Zone: |rThe Forbidden Reach|n|cFFFFD200Cost: |r80|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|n|cFFFFD200Vendor: |rTethalash|n|cFFFFD200Zone: |rValdrakken|n|cFFFFD200Cost: |r80|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = 'Trained using ancient dracthyr methods, these armored vorquin are the pinnacle of tactical mounted ground warfare.', },
	[1665] = { name = 'Swift Armored Vorquin', sourceText = '|cFFFFD200Vendor: |rTreysh|n|cFFFFD200Zone: |rThe Forbidden Reach|n|cFFFFD200Cost: |r80|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|n|cFFFFD200Vendor: |rTethalash|n|cFFFFD200Zone: |rValdrakken|n|cFFFFD200Cost: |r80|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = 'Trained using ancient dracthyr methods, these armored vorquin are the pinnacle of tactical mounted ground warfare.', },
	[1667] = { name = 'Armored Vorquin Leystrider', sourceText = '|cFFFFD200Vendor: |rTreysh|n|cFFFFD200Zone: |rThe Forbidden Reach|n|cFFFFD200Cost: |r80|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|n|cFFFFD200Vendor: |rTethalash|n|cFFFFD200Zone: |rValdrakken|n|cFFFFD200Cost: |r80|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = 'Trained using ancient dracthyr methods, these armored vorquin are the pinnacle of tactical mounted ground warfare.', },
	[1668] = { name = 'Majestic Armored Vorquin', sourceText = '|cFFFFD200Vendor: |rTreysh|n|cFFFFD200Zone: |rThe Forbidden Reach|n|cFFFFD200Cost: |r80|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|n|cFFFFD200Vendor: |rTethalash|n|cFFFFD200Zone: |rValdrakken|n|cFFFFD200Cost: |r80|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = 'Trained using ancient dracthyr methods, these armored vorquin are the pinnacle of tactical mounted ground warfare.', },
	[1671] = { name = 'Duskwing Ohuna', sourceText = '|cFFFFD200Vendor: |rCelestine of the Harvest|n|cFFFFD200Zone: |rThe Waking Shores|n|cFFFFD200Zone: |rOhn\'ahran Plains|n|cFFFFD200Zone: |rThe Azure Span|n|cFFFFD200Zone: |rThaldraszus|n|cFFFFD200Cost: |r1000|Hitem:207026|h|TInterface/ICONS/INV_Enchant_DustStrange.blp:0|t|h',  description = 'If you look to the night sky and see the clouds whisk by don\'t panic. It may just be the illusive Duskwing Ohuna.', },
	[1672] = { name = 'Zenet Hatchling', sourceText = '|cFFFFD200Drop:|r Zenet Egg|n|cFFFFD200Zone:|r Ohn\'ahran Plains',  description = 'The hardest winds begin with a gust and a flap.', },
	[1674] = { name = 'Temperamental Skyclaw', sourceText = '|cFFFFD200Vendor:|r Zon\'Wogi|n|cFFFFD200Zone:|r The Azure Span|n|n',  description = 'Slyverns are rarely comfortable acting as mounts. Only those willing to feed their gluttonous appetites and keep a careful eye on them should attempt to ride.', },
	[1679] = { name = 'Frostbrood Proto-Wyrm', sourceText = '|cFFFFD200Promotion:|r Wrath of the Lich King Classic|n',  description = 'The same magic of undeath that raised the Frostbrood into the Lich King\'s service has infused this primal wyrm. Its loyalty and ferocity now serve those who defend Azeroth.', },
	[1681] = { name = 'Hailstorm Armoredon', sourceText = '|cFFFFD200Achievement: |rDragonflight Keystone Master: Season One|n|cFFFFD200Category: |rDungeons',  description = 'Born in the middle of an ice storm, this armoredon prefers its meals partially frozen.', },
	[1683] = { name = 'Crimson Vorquin', sourceText = '|cFFFFD200Vendor: |rTreysh|n|cFFFFD200Zone: |rThe Forbidden Reach|n|cFFFFD200Cost: |r40|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|n|cFFFFD200Vendor: |rTethalash|n|cFFFFD200Zone: |rValdrakken|n|cFFFFD200Cost: |r40|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = 'Respected for their resilience in battle, even against dragonfire, these noble beasts are the prized mounts of the dracthyr cavalry.', },
	[1684] = { name = 'Sapphire Vorquin', sourceText = '|cFFFFD200Vendor: |rTreysh|n|cFFFFD200Zone: |rThe Forbidden Reach|n|cFFFFD200Cost: |r40|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|n|cFFFFD200Vendor: |rTethalash|n|cFFFFD200Zone: |rValdrakken|n|cFFFFD200Cost: |r40|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = 'Respected for their resilience in battle, even against dragonfire, these noble beasts are the prized mounts of the dracthyr cavalry.', },
	[1685] = { name = 'Bronze Vorquin', sourceText = '|cFFFFD200Vendor: |rTreysh|n|cFFFFD200Zone: |rThe Forbidden Reach|n|cFFFFD200Cost: |r40|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|n|cFFFFD200Vendor: |rTethalash|n|cFFFFD200Zone: |rValdrakken|n|cFFFFD200Cost: |r40|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = 'Respected for their resilience in battle, even against dragonfire, these noble beasts are the prized mounts of the dracthyr cavalry.', },
	[1686] = { name = 'Obsidian Vorquin', sourceText = '|cFFFFD200Vendor: |rTreysh|n|cFFFFD200Zone: |rThe Forbidden Reach|n|cFFFFD200Cost: |r40|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|n|cFFFFD200Vendor: |rTethalash|n|cFFFFD200Zone: |rValdrakken|n|cFFFFD200Cost: |r40|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = 'Respected for their resilience in battle, even against dragonfire, these noble beasts are the prized mounts of the dracthyr cavalry.', },
	[1688] = { name = 'Vicious Sabertooth', sourceText = '|cFFFFD200PvP:|r Dragonflight: Season 1|nRated Arena and Rated Battleground Season Reward',  description = 'The elite Alliance forces that use these sabertooth mounts use a battle cry that translates from Darnassian as "here kitty kitty".', },
	[1689] = { name = 'Vicious Sabertooth', sourceText = '|cFFFFD200PvP:|r Dragonflight: Season 1|nRated Arena and Rated Battleground Season Reward',  description = 'The elite Horde forces that use these sabertooth mounts use a battle cry that translates from Thalassian as "pspspspsps".', },
	[1690] = { name = 'Whelpling', sourceText = '',  description = '', },
	[1692] = { name = 'Wondrous Wavewhisker', sourceText = '|cFFFFD200In-Game Shop|r|n',  description = 'They say one of these magical fish is born when a fisherfolk dreams of the one that got away... and grows bigger with each retelling of the tale.', },
	[1698] = { name = 'Rocket Shredder 9001', sourceText = '|cFFFFD200Recruit-a-Friend|r |n',  description = 'The salesgoblin always tells people about the RS-9001\'s huge variety of attachments, but somehow neglects to mention that you never get to pick the attachments it\'ll have when you mount up.', },
	[1725] = { name = 'Inferno Armoredon', sourceText = '|cFFFFD200Achievement: |rDragonflight Keystone Master: Season Two|n|cFFFFD200Category: |rDungeons',  description = 'Its tail smells like campfire but its breath smells like brimstone.', },
	[1727] = { name = 'Tarecgosa\'s Visage', sourceText = '|cFFFFD200Quest:|r Rhapsody in Blue',  description = '"Farewell, Tarecgosa. A dear sister, hero, student, and teacher. Though blood does not bind us, you will always be family. May your soul find its place soaring among the stars."', },
	[1729] = { name = 'Big Slick in the City', sourceText = '|cFFFFD200Quest:|r Snailed It|n|cFFFFD200Zone: |rZaralek Cavern',  description = 'Big heart, big dreams, Big Slick.', },
	[1730] = { name = 'Igneous Shalewing', sourceText = '|cFFFFD200Vendor: |rSaccratros|n|cFFFFD200Zone: |rZaralek Cavern|n|cFFFFD200Cost: |r400|Hitem:204727|h|TInterface/ICONS/INV_Misc_CuriousCoin.BLP:0|t|h',  description = 'You\'d be forgiven for mistaking this creature for a flying volcanic rock. Because to some extent, it is.', },
	[1732] = { name = 'Cobalt Shalewing', sourceText = '|cFFFFD200Drop: |rKaroktra|n|cFFFFD200Zone: |rZaralek Cavern',  description = 'Brood of Karoktra, raised in the underlight of Zaralek Cavern.', },
	[1734] = { name = 'Shadowflame Shalewing', sourceText = '|cFFFFD200Achievement: |rGlory of the Aberrus Raider',  description = 'Born from the magma, its carapace was originally molten blobs cooled to form dark-colored volcanic rock. Being imbued by shadowflame has only solidified its unbreakable nature.', },
	[1735] = { name = 'Cataloged Shalewing', sourceText = '|cFFFFD200Zone: |rZaralek Cavern|n|cFFFFD200Public Event: |rResearchers Under Fire',  description = 'The first instance of a flaming shalewing ever discovered. Nine out of ten researchers agree: It\'s hot!', },
	[1736] = { name = 'Boulder Hauler', sourceText = '|cFFFFD200Vendor: |rPonzo|n|cFFFFD200Zone: |rZaralek Cavern|n|cFFFFD200Cost: |r85|Hitem:205188|h|TInterface/ICONS/INV_Stone_WeightStone_08.blp:0|t|h',  description = 'These shalewings have been specially trained to carry large amounts of Barter Boulders for the niffen\'s bartering activities.', },
	[1737] = { name = 'Sandy Shalewing', sourceText = '|cFFFFD200Achievement: |rMaster of Troubled Timeways|n|cFFFFD200Category: |rTimewalking',  description = 'The patterns on the wings of that creature resemble sand flowing through an hourglass. One could gaze upon them for ages...', },
	[1738] = { name = 'Morsel Sniffer', sourceText = '|cFFFFD200Vendor: |rHarlowe Marl|n|cFFFFD200Zone: |rZaralek Cavern|n|cFFFFD200Cost: |r800|Hcurrency:2003|h|TInterface/ICONS/INV_Faction_WarResources.BLP:0|t|h',  description = 'A breed of shalewings trained by the niffen to find the deepest of morsels.', },
	[1739] = { name = 'Obsidian Gladiator\'s Slitherdrake', sourceText = '|cFFFFD200Achievement:|r Gladiator: Dragonflight Season 2|n|cFFFFD200Category:|r Player vs. Player',  description = 'This mischievous drake enjoys flying through geysers and volcanic plumes.', },
	[1740] = { name = 'Vicious War Snail', sourceText = '|cFFFFD200PvP:|r Dragonflight: Season 2|nRated Arena and Rated Battleground Season Reward',  description = 'The Alliance uses these ravenous snail mounts to traverse over any obstacle in battlefields.', },
	[1741] = { name = 'Vicious War Snail', sourceText = '|cFFFFD200PvP:|r Dragonflight: Season 2|nRated Arena and Rated Battleground Season Reward',  description = 'The Horde uses these ravenous snail mounts to traverse over any obstacle in battlefields.', },
	[1742] = { name = 'Felcrystal Scorpion', sourceText = '|cFFFFD200Trading Post|r',  description = 'The results of a fel infusion experiment with one of Skorpyron\'s offspring.', },
	[1744] = { name = 'Grotto Netherwing Drake', sourceText = '|cFFFFD200Quest:|r Dragon Keeping|n|cFFFFD200Zone: |rEmerald Dream',  description = 'After traveling to Outland and back, It\'s only natural these drakes seek out new skies.', },
	[1771] = { name = 'Highland Drake', sourceText = '|cFFFFD200Achievement:|r Dragon Isles: Highland Drake|n',  description = 'The drakes of Azure Span follow a strict personal code of honor as they explore the isles anew.', },
	[1772] = { name = 'Scarlet Pterrordax', sourceText = '|cFFFFD200Quest:|r Time to Fly|n',  description = 'This pterrordax was so eager to take to the skies that its handler couldn\'t finish training it.', },
	[1773] = { name = 'Harbor Gryphon', sourceText = '|cFFFFD200Quest:|r Time to Fly|n',  description = 'This gryphon was the runt of its pride, left behind when its brethren were sent for training. Today, however, it is ready to prove its worth.', },
	[1774] = { name = 'Valiance', sourceText = '|cFFFFD200Discovery:|r Valiance|n|cFFFFD200Location:|r Naxxramas',  description = 'Most undead steeds have their riders chosen for them. Valiance, on the other hand, chooses its own rider.', },
	[1776] = { name = 'White War Wolf', sourceText = '|cFFFFD200Horde |r|n|cFFFFD200Drop: |rTime Rifts|n|cFFFFD200Vendor: |rWarden Krizzik|n|cFFFFD200Zone: |rTyrhold Reservoir|n|cFFFFD200Cost: |r3000|Hcurrency:2594|h|TInterface/ICONS/TimelessCoin_yellow.BLP:0|t|h|n|n|cFFFFD200Alliance |r|n|cFFFFD200Item: |rGift of the White War Wolf',  description = 'Can a wolf\'s hunger ever be satiated? These lupine brutes seek and devour the enemies of the Horde.', },
	[1777] = { name = 'Ravenous Black Gryphon', sourceText = '|cFFFFD200Alliance |r|n|cFFFFD200Drop: |rTime Rifts|n|cFFFFD200Vendor: |rWarden Krizzik|n|cFFFFD200Zone: |rTyrhold Reservoir|n|cFFFFD200Cost: |r3000|Hcurrency:2594|h|TInterface/ICONS/TimelessCoin_yellow.BLP:0|t|h|n|n|cFFFFD200Horde |r|n|cFFFFD200Item: |rGift of the Ravenous Black Gryphon',  description = 'War corrupted these once-proud creatures. The susurration of their wings now whispers omens of imminent demise.', },
	[1778] = { name = 'Gold-Toed Albatross', sourceText = '|cFFFFD200Drop: |rTime Rifts|n|cFFFFD200Vendor: |rSorotis|n|cFFFFD200Zone: |rTyrhold Reservoir|n|cFFFFD200Cost: |r3000|Hcurrency:2594|h|TInterface/ICONS/TimelessCoin_yellow.BLP:0|t|h',  description = 'Soaring from a distant timeway, this never-before-seen avian variant was born in a world of order.', },
	[1779] = { name = 'Felstorm Dragon', sourceText = '|cFFFFD200Drop: |rTime Rifts|n|cFFFFD200Vendor: |rFalara Nightsong|n|cFFFFD200Zone: |rTyrhold Reservoir|n|cFFFFD200Cost: |r3000|Hcurrency:2594|h|TInterface/ICONS/TimelessCoin_yellow.BLP:0|t|h',  description = 'The Burning Legion defeated dragons in a distant timeway. These foul fel wyrms chose corruption and servitude over destruction.', },
	[1781] = { name = 'Sulfur Hound', sourceText = '|cFFFFD200Drop: |rTime Rifts|n|cFFFFD200Vendor: |rProvisioner Qorra|n|cFFFFD200Zone: |rTyrhold Reservoir|n|cFFFFD200Cost: |r3000|Hcurrency:2594|h|TInterface/ICONS/TimelessCoin_yellow.BLP:0|t|h',  description = 'The Black Empire subjugated these humbled and hateful hounds. Utterly obedient, they still serve the strong.', },
	[1782] = { name = 'Perfected Juggernaut', sourceText = '|cFFFFD200Drop: |rTime Rifts|n|cFFFFD200Vendor: |rGill the Drill|n|cFFFFD200Zone: |rTyrhold Reservoir|n|cFFFFD200Cost: |r3000|Hcurrency:2594|h|TInterface/ICONS/TimelessCoin_yellow.BLP:0|t|h',  description = 'Perfection, like beauty, is in the eye of the beholder. But eyesight can be surgically enhanced.', },
	[1783] = { name = 'Scourgebound Vanquisher', sourceText = '|cFFFFD200Drop: |rTime Rifts|n|cFFFFD200Vendor: |rBaron Silver|n|cFFFFD200Zone: |rTyrhold Reservoir|n|cFFFFD200Cost: |r3000|Hcurrency:2594|h|TInterface/ICONS/TimelessCoin_yellow.BLP:0|t|h',  description = 'Once bound in service to the Scourge, these undead dragons exist only to spread death, disease, and destruction.', },
	[1784] = { name = 'Royal Swarmer', sourceText = '|cFFFFD200Trading Post|r',  description = 'These drones will swarm anyone who dares approach their queen uninvited.', },
	[1785] = { name = 'Ancestral Clefthoof', sourceText = '|cFFFFD200Trading Post|r|n',  description = 'This clefthoof has seen ages pass, witnessing everything and patiently teaching many to ride.', },
	[1786] = { name = 'Renewed Proto-Drake', sourceText = '|cFFFFD200Achievement:|r Dragon Isles: A New Friend',  description = 'As the isles awaken, these proto-drakes answer the call to soar the skies alongside mortals.', },
	[1787] = { name = 'Windborne Velocidrake', sourceText = '|cFFFFD200Achievement:|r Dragon Isles: Windborne Velocidrake',  description = 'The long talons of the velocidrake give it an edge on the plains.', },
	[1788] = { name = 'Cliffside Wylderdrake', sourceText = '|cFFFFD200Achievement:|r Dragon Isles: Cliffside Wylderdrake',  description = 'Often roosting along the steep cliff faces of Thaldraszus, they\'re swift to the skies in times of danger.', },
	[1789] = { name = 'Winding Slitherdrake', sourceText = '|cFFFFD200Achievement:|r A Serpentine Discovery',  description = 'These drakes feel at home in the depths of the earth and the far reaches of the sky.', },
	[1794] = { name = 'Pattie', sourceText = '|cFFFFD200Achievement: |rWhodunnit?|n|cFFFFD200Event: |rSecrets of Azeroth',  description = 'Preservationist Kathos encountered Pattie on her first assignment to Vol\'dun. Pattie developed an affinity to Preservationist work but is eager to delve deeper, helping adventurers in their sleuthing.', },
	[1796] = { name = 'Whelpling', sourceText = '',  description = '', },
	[1797] = { name = 'Ginormous Grrloc', sourceText = '|cFFFFD200In-Game Shop|r |n',  description = 'It\'s the second biggest murloc you\'ve ever seen!', },
	[1798] = { name = 'Azure Worldchiller', sourceText = '|cFFFFD200Drop: |rDoomwalker|n|cFFFFD200Zone: |rTanaris',  description = 'In the corners of Azeroth, stories are told of a dreaded dragon who swept a pall of torment over the land. In some versions of this story, his fiery presence brought cataclysmic death and destruction worldwide. In other versions, he is blue.', },
	[1799] = { name = 'Eve\'s Ghastly Rider', sourceText = '|cFFFFD200Trading Post|r',  description = 'The preferred transportation of the Fenlow family (especially during Hallow\'s End).', },
	[1801] = { name = 'Verdant Armoredon', sourceText = '|cFFFFD200Achievement: |rDragonflight Keystone Master: Season Three|n|cFFFFD200Category: |rDungeons',  description = 'The gnarled horns of this armoredon are far stronger than bone.', },
	[1808] = { name = 'Blossoming Dreamstag', sourceText = '|cFFFFD200Vendor: |rTalisa Whisperbloom|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1|Hitem:211376|h|TInterface/ICONS/INV_Misc_TrailofFlowers.blp:0|t|h',  description = 'The pelts of young dreamstags flower with the vibrant colors of the dream.', },
	[1809] = { name = 'Suntouched Dreamstag', sourceText = '|cFFFFD200Vendor: |rMoon Priestess Lasara|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1200|Hcurrency:2003|h|TInterface/ICONS/INV_Faction_WarResources.BLP:0|t|h',  description = 'The sun\'s radiant energy nurtures these dreamstags, helping them rapidly reach maturity.', },
	[1810] = { name = 'Rekindled Dreamstag', sourceText = '|cFFFFD200Vendor: |rTalisa Whisperbloom|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1|Hitem:211376|h|TInterface/ICONS/INV_Misc_TrailofFlowers.blp:0|t|h',  description = 'Ashes of fallen leaves are fertile soil for dreamstags to arise from.', },
	[1811] = { name = 'Lunar Dreamstag', sourceText = '|cFFFFD200Vendor: |rMoon Priestess Lasara|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1200|Hcurrency:2003|h|TInterface/ICONS/INV_Faction_WarResources.BLP:0|t|h',  description = 'The moon\'s pale light is reflected in the pelt of these elusive nocturnal creatures.', },
	[1813] = { name = 'Mimiron\'s Jumpjets', sourceText = '|cFFFFD200World Event:|r Secrets of Azeroth|n',  description = 'Created as a prototype by Mimiron. The pieces were stolen from his workshop, sold, and lost to time. That is until now..', },
	[1814] = { name = 'Shadow Dusk Dreamsaber', sourceText = '|cFFFFD200Achievement: |rGlory of the Dream Raider|n|cFFFFD200Category: |rDungeons & Raids',  description = 'A dreamsaber strikes quickly from the shadows, capturing small animals and slipping back into darkness without a sound. The lucky few who have seen a feeding dreamsaber can see them revel in lazily toying with their prey before beginning their meal.', },
	[1815] = { name = 'Winter Night Dreamsaber', sourceText = '|cFFFFD200Drop:|r Dreamseed Cache|n|cFFFFD200Zone:|r Emerald Dream|n',  description = 'These nocturnal dreamsabers carry the chill of winter with them and will often hibernate for days on end if not otherwise awoken.', },
	[1816] = { name = 'Evening Sun Dreamsaber', sourceText = '|cFFFFD200Vendor: |rTalisa Whisperbloom|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1|Hitem:211376|h|TInterface/ICONS/INV_Misc_TrailofFlowers.blp:0|t|h',  description = 'These dreamsabers are ferocious in battle, yet lethargic and often found lazily outstretched upon open fields. After soaking in the warmth of daylight, their flame flares that much brighter.', },
	[1817] = { name = 'Morning Flourish Dreamsaber', sourceText = '|cFFFFD200Vendor: |rTalisa Whisperbloom|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1|Hitem:211376|h|TInterface/ICONS/INV_Misc_TrailofFlowers.blp:0|t|h',  description = 'Known for the lavish energy that emanates from them, these sabers are notorious tricksters. Their roar scatters the early birds that wake them.', },
	[1818] = { name = 'Anu\'relos, Flame\'s Guidance', sourceText = '|cFFFFD200Drop: |rFyrakk|n|cFFFFD200Zone: |rAmirdrassil, the Dream\'s Hope (Mythic)',  description = 'Though her feathers flicker with flame\'s destructive potential, they do not scorch, but simply radiate a comfortable heat.', },
	[1819] = { name = 'Vicious Moonbeast', sourceText = '|cFFFFD200PvP:|r Dragonflight: Season 3|nRated Arena and Rated Battleground Season Reward',  description = 'Unlike most of its kind, this moonbeast has a taste for Horde blood and an affinity for the color blue.', },
	[1820] = { name = 'Vicious Moonbeast', sourceText = '|cFFFFD200PvP:|r Dragonflight: Season 3|nRated Arena and Rated Battleground Season Reward',  description = 'Unlike most of its kind, this moonbeast has a taste for Alliance blood and an affinity for the color red.', },
	[1830] = { name = 'Flourishing Whimsydrake', sourceText = '|cFFFFD200Quest:|r Emerald Welcome|n|cFFFFD200Zone: |rEmerald Dream',  description = 'These playful protectors of the Emerald Dream may be larger than their faerie dragon relatives, but they are no less mischievous.', },
	[1831] = { name = 'Verdant Gladiator\'s Slitherdrake', sourceText = '|cFFFFD200Achievement:|r Gladiator: Dragonflight Season 3|n|cFFFFD200Category:|r Player vs. Player',  description = 'This drake visited many heroes in their dreams before joining your cause.', },
	[1833] = { name = 'Springtide Dreamtalon', sourceText = '|cFFFFD200Vendor: |rTalisa Whisperbloom|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1|Hitem:211376|h|TInterface/ICONS/INV_Misc_TrailofFlowers.blp:0|t|h',  description = 'Surging with the kind of energy and enthusiasm that can make anyone envious.', },
	[1834] = { name = 'Ochre Dreamtalon', sourceText = '|cFFFFD200Quest: |rA Little Hope is Never Without Worth|n|cFFFFD200Zone: |rEmerald Dream',  description = 'Playful dreamtalons often decorate themselves in a myriad of colors that only autumn offers.', },
	[1835] = { name = 'Snowfluff Dreamtalon', sourceText = '|cFFFFD200Vendor: |rTalisa Whisperbloom|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1|Hitem:211376|h|TInterface/ICONS/INV_Misc_TrailofFlowers.blp:0|t|h',  description = 'As lighthearted and gentle as the snow, they can bounce from one snowdrift to the next without leaving a print.', },
	[1837] = { name = 'Delugen', sourceText = '|cFFFFD200Vendor: |rElianna|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1|Hcurrency:2777|h|TInterface/ICONS/INV_Legion_Faction_DreamWeavers.BLP:0|t|h',  description = 'Imagine being so engrossed by a dream that it feels real; some might even call it a delusion.', },
	[1838] = { name = 'Talont', sourceText = '|cFFFFD200Vendor: |rElianna|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1|Hcurrency:2777|h|TInterface/ICONS/INV_Legion_Faction_DreamWeavers.BLP:0|t|h',  description = 'Some daydreams are filled with wondrous possibilities, but shatter when reminded of reality\'s limitations. Some dreams are not so burdened, even when they carry a rider.', },
	[1839] = { name = 'Stargrazer', sourceText = '|cFFFFD200Vendor: |rElianna|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1|Hcurrency:2777|h|TInterface/ICONS/INV_Legion_Faction_DreamWeavers.BLP:0|t|h',  description = 'One can\'t help but think what it might feel like to pluck the stars from their fixture in the sky. Perhaps, Stargrazer will show us how someday.', },
	[1841] = { name = 'Crimson Glimmerfur', sourceText = '|cFFFFD200Trading Post|r',  description = 'Gilnean legend states this red-and-white fox only crosses the Winter Veil during one season.', },
	[1938] = { name = 'Mammyth', sourceText = '|cFFFFD200Vendor: |rElianna|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1|Hcurrency:2777|h|TInterface/ICONS/INV_Legion_Faction_DreamWeavers.BLP:0|t|h',  description = 'In the Dream, sometimes the myth is more real than its basis.', },
	[1939] = { name = 'Imagiwing', sourceText = '|cFFFFD200Vendor: |rElianna|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1|Hcurrency:2777|h|TInterface/ICONS/INV_Legion_Faction_DreamWeavers.BLP:0|t|h',  description = 'There\'s nothing more beautiful than watching your imagination take flight.', },
	[1940] = { name = 'Salatrancer', sourceText = '|cFFFFD200Vendor: |rElianna|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1|Hcurrency:2777|h|TInterface/ICONS/INV_Legion_Faction_DreamWeavers.BLP:0|t|h',  description = 'While most salamanthers\' attention jumps freely from one attraction to the next, this one enjoys long periods of deep observation.', },
	[1942] = { name = 'Jeweled Copper Scarab', sourceText = '|cFFFFD200Trading Post|r',  description = 'The rulers of desert kingdoms would attune crystals to their personal mounts for quick summoning.', },
	[1944] = { name = 'Golden Regal Scarab', sourceText = '|cFFFFD200|r',  description = 'Plated with gold and socketed with emeralds, this scarab is fit to fly royalty in style.', },
	[1517] = { name = 'Bound Blizzard', sourceText = '|cFFFFD200Promotion:|r |n',  description = 'A flurrious, fridgid friend, frost-flecked for frozen fun.', },
}

addon.petData = {
	[39] = { description = 'A mechanical squirrel\'s logic center tells it to collect and store both nuts and bolts for the winter.', sourceText = '|cFFFFD200Profession: |rEngineering', creatureID = 2671, },
	[40] = { description = 'Donni Anthania plans to have a bombay buried with her when she dies. A wise adventurer can put its talents to much better use.', sourceText = '|cFFFFD200Vendor: |rDonni Anthania|n|cFFFFD200Zone: |rElwynn Forest|n|cFFFFD200Cost: |r40|TINTERFACE/MONEYFRAME/UI-SILVERICON.BLP:0|t', creatureID = 7385, },
	[41] = { description = 'Donni Anthania invites these cats to her tea parties. But she doesn\'t serve their favorite drink: the tears of their enemies.', sourceText = '|cFFFFD200Vendor: |rDonni Anthania|n|cFFFFD200Zone: |rElwynn Forest|n|cFFFFD200Cost: |r40|TINTERFACE/MONEYFRAME/UI-SILVERICON.BLP:0|t', creatureID = 7384, },
	[42] = { description = 'Old Alterac saying: \'A cat has nine lives, but needs only one.\'', sourceText = '|cFFFFD200Drop: |rWorld Drop|n|cFFFFD200Zone: |rHillsbrad Foothills', creatureID = 7383, },
	[43] = { description = 'The last person who tried to housebreak this cat quickly learned that a soiled rug is better than a shredded everything-else.', sourceText = '|cFFFFD200Vendor: |rDonni Anthania, Steven Lisbane|n|cFFFFD200Zone: |rElwynn Forest|n|cFFFFD200Cost: |r40|TINTERFACE/MONEYFRAME/UI-SILVERICON.BLP:0|t', creatureID = 7382, },
	[44] = { description = 'Known for its blue eyes. Also considered a delicacy by giant murlocs.', sourceText = '|cFFFFD200Vendor: |rDealer Rashaad|n|cFFFFD200Zone: |rNetherstorm|n|cFFFFD200Cost: |r60|TINTERFACE/MONEYFRAME/UI-SILVERICON.BLP:0|t', creatureID = 7380, },
	[45] = { description = 'Sleeping is this cat\'s second favorite activity. The first is yawning.', sourceText = '|cFFFFD200Vendor: |rDonni Anthania|n|cFFFFD200Zone: |rElwynn Forest|n|cFFFFD200Cost: |r40|TINTERFACE/MONEYFRAME/UI-SILVERICON.BLP:0|t', creatureID = 7381, },
	[46] = { description = '\'The queen had three kittens. The first gave her a juicy rat. The second, a tasty hare. And the white, her favorite, presented the head of her rival.\'', sourceText = '|cFFFFD200Vendor: |rLil Timmy|n|cFFFFD200Zone: |rStormwind City|n|cFFFFD200Cost: |r60|TINTERFACE/MONEYFRAME/UI-SILVERICON.BLP:0|t', creatureID = 7386, },
	[47] = { description = 'Some say this clever bird can be taught to speak--but it\'s smart enough to keep its beak shut.', sourceText = '|cFFFFD200Vendor: |rHarry No-Hooks, Narkk|n|cFFFFD200Zone: |rThe Cape of Stranglethorn|n|cFFFFD200Cost: |r40|TINTERFACE/MONEYFRAME/UI-SILVERICON.BLP:0|t', creatureID = 7390, },
	[49] = { description = 'The jungle trolls train these birds to mimic calls for help in order to lure unsuspecting travelers into traps.', sourceText = '|cFFFFD200Drop: |rWorld Drop|n|cFFFFD200Zone: |rNorthern Stranglethorn, The Cape of Stranglethorn', creatureID = 7391, },
	[50] = { description = 'Favored pet of the Defias pirates, this colorful bird is handy for remembering passwords, grocery lists, and cracker recipes.', sourceText = '|cFFFFD200Drop: |rDefias Pirate|n|cFFFFD200Zone: |rThe Deadmines', creatureID = 7387, },
	[51] = { description = 'Favored pet of the goblins of Booty Bay, this colorful bird is renowned for its ability to count coins, tally budgets, and lie about contracts.', sourceText = '|cFFFFD200Vendor: |rNarkk, Harry No-Hooks|n|cFFFFD200Zone: |rThe Cape of Stranglethorn|n|cFFFFD200Cost: |r40|TINTERFACE/MONEYFRAME/UI-SILVERICON.BLP:0|t|n|n|cFFFFD200Vendor: |rDealer Rashaad|n|cFFFFD200Zone: |rNetherstorm|n|cFFFFD200Cost: |r40|TINTERFACE/MONEYFRAME/UI-SILVERICON.BLP:0|t', creatureID = 7389, },
	[52] = { description = 'It thinks you taste like chicken too.', sourceText = '|cFFFFD200Vendor: |r"Plucky" Johnson|n|cFFFFD200Zone: |rThousand Needles|n|cFFFFD200Cost: |r1|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t', creatureID = 7394, },
	[55] = { description = 'The cockroach is usually an impulse buy. Its owners have a hard time parting with it even after numerous attempts.', sourceText = '|cFFFFD200Vendor: |rDealer Rashaad|n|cFFFFD200Zone: |rNetherstorm|n|cFFFFD200Cost: |r50|TINTERFACE/MONEYFRAME/UI-SILVERICON.BLP:0|t|n|n|cFFFFD200Vendor: |rJeremiah Payson|n|cFFFFD200Zone: |rTirisfal Glades|n|cFFFFD200Cost: |r50|TINTERFACE/MONEYFRAME/UI-SILVERICON.BLP:0|t', creatureID = 7395, },
	[56] = { description = 'Hopes that someday it can grow up to destroy villages, just like mommy.', sourceText = '|cFFFFD200Drop: |rWhelplings|n|cFFFFD200Zone: |rWetlands, Dustwallow Marsh, Badlands, Burning Steppes', creatureID = 7543, },
	[57] = { description = 'Like other members of the blue dragonflight, these whimsical little critters display an affinity to arcane magic.', sourceText = '|cFFFFD200Drop: |rWorld Drop|n|cFFFFD200Zone: |rWinterspring', creatureID = 7547, },
	[58] = { description = 'Hailing from the Wetlands, this young dragon is just learning how to hunt, breathe fire, and go on cute destructive rampages.', sourceText = '|cFFFFD200Drop: |rWorld Drop|n|cFFFFD200Zone: |rWetlands', creatureID = 7544, },
	[59] = { description = 'Once bred and raised by the green dragon Itharius, this unique breed of dragon has settled in the jungles of Feralas.', sourceText = '|cFFFFD200Drop: |rNoxious Whelp|n|cFFFFD200Zone: |rFeralas', creatureID = 7545, },
	[64] = { description = 'Vendors at the Darkmoon Faire offer strange and exotic wonders. They also sell wood frogs.', sourceText = '|cFFFFD200Vendor: |rFlik|n|cFFFFD200Zone: |rDarkmoon Island|n|cFFFFD200Cost: |r1|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t', creatureID = 7550, },
	[65] = { description = 'Known for their powerful legs and keen eyesight.', sourceText = '|cFFFFD200Vendor: |rFlik|n|cFFFFD200Zone: |rDarkmoon Island|n|cFFFFD200Cost: |r1|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t', creatureID = 7549, },
	[67] = { description = 'This dark-feathered bird of prey is often seen as a harbinger of doom amongst the druids of Teldrassil.', sourceText = '|cFFFFD200Vendor: |rShylenai|n|cFFFFD200Zone: |rTeldrassil|n|cFFFFD200Cost: |r50|TINTERFACE/MONEYFRAME/UI-SILVERICON.BLP:0|t', creatureID = 7555, },
	[68] = { description = 'Night elf children are given an owl feather on their first birthday as a token of good luck.', sourceText = '|cFFFFD200Vendor: |rShylenai|n|cFFFFD200Zone: |rTeldrassil|n|cFFFFD200Cost: |r50|TINTERFACE/MONEYFRAME/UI-SILVERICON.BLP:0|t', creatureID = 7553, },
	[69] = { description = 'Winterspring\'s furbolg prize the brilliant white feathers of these majestic creatures for ritual purposes.', sourceText = '|cFFFFD200Pet Battle: |rWinterspring|n|cFFFFD200Season: |rWinter', creatureID = 7554, },
	[121] = { description = 'Gurky is a born performer, though she remains elusive, making public appearances only on the rarest of occasions.', sourceText = '|cFFFFD200Promotion:|r EU Fansite Promotion|n', creatureID = 16069, },
	[70] = { description = 'A complex system of burrows protects the prairie dog from its neighbors\' massive hooves.', sourceText = '|cFFFFD200Vendor: |rHalpa, Naleen|n|cFFFFD200Zone: |rMulgore|n|cFFFFD200Cost: |r50|TINTERFACE/MONEYFRAME/UI-SILVERICON.BLP:0|t', creatureID = 14421, },
	[72] = { description = 'If you go chasing snowshoe rabbits, you know you\'re going to fall.', sourceText = '|cFFFFD200Vendor: |rYarlyn Amberstill|n|cFFFFD200Zone: |rDun Morogh|n|cFFFFD200Cost: |r20|TINTERFACE/MONEYFRAME/UI-SILVERICON.BLP:0|t', creatureID = 7560, },
	[74] = { description = 'A favored companion of Kirin Tor magi, this reptile makes one wonder whether it\'s truly a snake or something else altogether.', sourceText = '|cFFFFD200Vendor: |rBreanni|n|cFFFFD200Zone: |rDalaran|n|cFFFFD200Cost: |r50|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t', creatureID = 7561, },
	[75] = { description = 'Xan\'tish fearlessly tracks, captures, and trains these noble snakes so they will aid their comrades in battle.', sourceText = '|cFFFFD200Vendor: |rXan\'tish|n|cFFFFD200Zone: |rOrgrimmar|n|cFFFFD200Cost: |r50|TINTERFACE/MONEYFRAME/UI-SILVERICON.BLP:0|t', creatureID = 7565, },
	[77] = { description = 'The brown snake is native to Horde-controlled territories. It seems unremarkable until its victims are within range.', sourceText = '|cFFFFD200Vendor: |rXan\'tish|n|cFFFFD200Zone: |rOrgrimmar|n|cFFFFD200Cost: |r50|TINTERFACE/MONEYFRAME/UI-SILVERICON.BLP:0|t', creatureID = 7562, },
	[78] = { description = 'The crimson snake is favored among the Darkspear trolls for not only its vicious nature, but also its steadfast loyalty.', sourceText = '|cFFFFD200Vendor: |rDealer Rashaad|n|cFFFFD200Zone: |rNetherstorm|n|cFFFFD200Cost: |r50|TINTERFACE/MONEYFRAME/UI-SILVERICON.BLP:0|t|n|n|cFFFFD200Vendor: |rXan\'tish|n|cFFFFD200Zone: |rOrgrimmar|n|cFFFFD200Cost: |r50|TINTERFACE/MONEYFRAME/UI-SILVERICON.BLP:0|t', creatureID = 7567, },
	[83] = { description = 'Not to worry; the combat mechanisms and homing logic have been disabled on this unit, I think. --Oglethorpe Obnoticus', sourceText = '|cFFFFD200Quest:|r An OOX of Your Own|n|cFFFFD200Zone:|r Booty Bay|n', creatureID = 8376, },
	[84] = { description = 'Don\'t call this bird chicken unless you want egg on your face.', sourceText = '|cFFFFD200Quest: |rCLUCK!|n|cFFFFD200Zone: |rWestfall', creatureID = 30379, },
	[85] = { description = 'Possibly explosive, definitely adorable. Keep away from open flame.', sourceText = '|cFFFFD200Profession: |rEngineering', creatureID = 9656, },
	[86] = { description = 'This tiny peacekeeper security bot is often outfitted with the latest arcane nullifiers and crowd pummelers. Not for the environmentally minded.', sourceText = '|cFFFFD200Profession: |rEngineering', creatureID = 9657, },
	[87] = { description = 'These adorable critters love snuggling with their owners after a long day of brutal, bloody battle.', sourceText = '|cFFFFD200Drop: |rWorld Drop|n|cFFFFD200Zone: |rFeralas', creatureID = 9662, },
	[89] = { description = 'Worgs are the favored companions of orcs and are fiercely loyal on the battlefield.', sourceText = '|cFFFFD200Drop: |rQuartermaster Zigris|n|cFFFFD200Zone: |rLower Blackrock Spire', creatureID = 10259, },
	[90] = { description = 'Although born in the warm heights of Blackrock Spire, the smolderweb hatchling can survive most environments.', sourceText = '|cFFFFD200Drop: |rMother Smolderweb|n|cFFFFD200Zone: |rLower Blackrock Spire', creatureID = 10598, },
	[92] = { description = 'This pint-sized panda cub is highly sought after by adventurers around Azeroth - despite its narcoleptic nature.', sourceText = '|cFFFFD200Promotion:|r World of Warcraft Collector\'s Edition', creatureID = 11325, },
	[93] = { description = 'AND THE HEAVENS SHALL TREMBLE, AND MAN SHALL WEEP, AND THE END OF DAYS SHALL--wait, why are you so tall?', sourceText = '|cFFFFD200Promotion:|r World of Warcraft Collectors Edition', creatureID = 11326, },
	[94] = { description = 'A small, fierce member of the Zerg swarm, the zergling is not to be trifled with. Very dangerous in high numbers.', sourceText = '|cFFFFD200Promotion:|r World of Warcraft Collectors Edition', creatureID = 11327, },
	[95] = { description = 'Talented engineers have crafted an exact replica of a wood frog, at only ten times the cost of buying the real thing.', sourceText = '|cFFFFD200Profession: |rEngineering', creatureID = 12419, },
	[106] = { description = 'The legendary ale frog was believed to be extinct until the strange brews at the Darkmoon Faire brought them out of hiding.', sourceText = '|cFFFFD200World Event:|r Darkmoon Faire', creatureID = 14878, },
	[107] = { description = 'Appearances of this beloved and ultra-reclusive murloc are sure to draw a crowd.', sourceText = '|cFFFFD200Promotion:|r BlizzCon 2005', creatureID = 15186, },
	[111] = { description = 'Lurky\'s charming disposition and lively antics are guaranteed to soothe frustration and lighten even the darkest moods.', sourceText = '|cFFFFD200Promotion:|r Burning Crusade Collector\'s Edition (EU only)|n', creatureID = 15358, },
	[114] = { description = 'Warning: Wear gloves and goggles while handling disgusting oozeling. DO NOT WASH. Will stain most armor.', sourceText = '|cFFFFD200Drop:|r World Drop|n|cFFFFD200Creature:|r Oozes, Slimes and Worms', creatureID = 15429, },
	[115] = { description = 'After birth, newborn sharks quickly flee their hungry mothers. Their early independence makes them dangerous, even while young.', sourceText = '|cFFFFD200Profession:|r Fishing|n|cFFFFD200Vendor:|r Nat Pagle|n|cFFFFD200Zone:|r Garrison, Fishing Shack|n|cFFFFD200Cost:|r 50|TINTERFACE/ICONS/INV_misc_coin_19.blp:0|t', creatureID = 86445, },
	[116] = { description = 'This clever toy is equipped with a secret switch to disable its \'tranquil\' mode. Luckily, even the engineers who built it don\'t know how to find it.', sourceText = '|cFFFFD200Profession: |rEngineering', creatureID = 15699, },
	[117] = { description = 'He loves to dance and wave and play with you! He melts after Winter Veil, but don\'t worry; he\'ll be back again someday!', sourceText = '|cFFFFD200World Event:|r Feast of Winter Veil|n', creatureID = 15710, },
	[118] = { description = 'Would rather not spend any more time inside a wrapped holiday gift box.', sourceText = '|cFFFFD200World Event:|r Feast of Winter Veil', creatureID = 15706, },
	[119] = { description = 'These off-season allies work tirelessly to prepare for the Feast of Winter Veil, and would appreciate not being disturbed the rest of the year.', sourceText = '|cFFFFD200World Event:|r Feast of Winter Veil', creatureID = 15698, },
	[120] = { description = 'During the majestic Winter Veil celebration, these faithful companions spread joy throughout the land. The rest of the year, they are busy.', sourceText = '|cFFFFD200World Event:|r Feast of Winter Veil', creatureID = 15705, },
	[122] = { description = 'The power of love can be a dangerous thing, especially when wielded by an airborne, ill-tempered goblin.', sourceText = '|cFFFFD200World Event:|r Love is in the Air|n|cFFFFD200Vendor: |rLovely Merchant|n|cFFFFD200Cost: |r40|TINTERFACE/ICONS/INV_ValentinesCard01:0|t|n', creatureID = 16085, },
	[124] = { description = 'This cub shares many traits with its relatives, but it\'s in a league of its own with its singing ability.', sourceText = '|cFFFFD200Promotion:|r iCoke', creatureID = 16456, },
	[125] = { description = 'Slow and steady wins the race.', sourceText = '|cFFFFD200World Event:|r Children\'s Week', creatureID = 16547, },
	[126] = { description = 'Many assume \'Wiggles\' refers to the motion of this pig\'s posterior. They discover the true meaning when they see their own entrails.', sourceText = '|cFFFFD200World Event:|r Children\'s Week', creatureID = 16548, },
	[127] = { description = 'A clever tactician, Whiskers will feign death to fool predators and bill collectors.', sourceText = '|cFFFFD200World Event:|r Children\'s Week', creatureID = 16549, },
	[128] = { description = 'This little piece of summer smells of warm evenings and lightning storms. If you look closely, you might be able to make out a face.', sourceText = '|cFFFFD200World Event:|r Midsummer Fire Festival|n|cFFFFD200Vendor: |rMidsummer Merchant|n|cFFFFD200Cost: |r350|TINTERFACE/ICONS/INV_SummerFest_FireFlower:0|t', creatureID = 16701, },
	[130] = { description = 'After a harpy incursion in Feralas, several hippogryph nesting grounds were attacked. Hundreds of hatchlings were orphaned, and they have been entrusted to the care of the druids on Sardor Isle.', sourceText = '|cFFFFD200Trading Card Game:|r Heroes of Azeroth', creatureID = 17255, },
	[131] = { description = 'Offspring of the nether drakes of Outland, this young dragon is still growing into its role as hunter, protector, and adorable terror of the skies.', sourceText = '|cFFFFD200Promotion:|r Burning Crusade Collector\'s Edition', creatureID = 18381, },
	[132] = { description = 'Only magical wishes make magical crawdads. If you wish for a fish, fish for a wish.', sourceText = '|cFFFFD200Fishing:|r Fishing (430)|n|cFFFFD200Zone:|r Terokkar Forest (Fishing Nodes)', creatureID = 18839, },
	[136] = { description = 'The ethereal Rashaad began breeding these enigmatic creatures after learning of their ability to feed on arcane energies.', sourceText = '|cFFFFD200Vendor: |rDealer Rashaad|n|cFFFFD200Zone: |rNetherstorm|n|cFFFFD200Cost: |r40|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t', creatureID = 20408, },
	[137] = { description = 'This bunny is the cutest thing its opponent will ever see. it\'ll also be the last.', sourceText = '|cFFFFD200Vendor: |rDealer Rashaad|n|cFFFFD200Zone: |rNetherstorm|n|cFFFFD200Cost: |r10|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t', creatureID = 20472, },
	[138] = { description = 'Delicate denizen of Azuremyst Isle, this magical insect has been rumored to possess healing abilities.', sourceText = '|cFFFFD200Vendor: |rSixx|n|cFFFFD200Zone: |rThe Exodar|n|cFFFFD200Cost: |r50|TINTERFACE/MONEYFRAME/UI-SILVERICON.BLP:0|t', creatureID = 21010, },
	[139] = { description = 'A product of exposure to wild magical energies, this insect is the unexpectedly colorful result of Azerothian moth species being released into the eco-domes of the Netherstorm.', sourceText = '|cFFFFD200Vendor: |rDealer Rashaad|n|cFFFFD200Zone: |rNetherstorm|n|cFFFFD200Cost: |r10|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t', creatureID = 21009, },
	[140] = { description = 'Prefers flying during thunderstorms, which are common in Azuremyst Isle.', sourceText = '|cFFFFD200Vendor: |rSixx|n|cFFFFD200Zone: |rThe Exodar|n|cFFFFD200Cost: |r50|TINTERFACE/MONEYFRAME/UI-SILVERICON.BLP:0|t', creatureID = 21008, },
	[141] = { description = 'Prefers flying during foggy nights, which are common in Azuremyst Isle.', sourceText = '|cFFFFD200Vendor: |rSixx|n|cFFFFD200Zone: |rThe Exodar|n|cFFFFD200Cost: |r50|TINTERFACE/MONEYFRAME/UI-SILVERICON.BLP:0|t', creatureID = 21018, },
	[142] = { description = 'Once prized by dragonhawk breeders in Silvermoon. Stands out at night to predators, making it necessary to gain awareness at an early age.', sourceText = '|cFFFFD200Vendor: |rJilanne|n|cFFFFD200Zone: |rEversong Woods|n|cFFFFD200Cost: |r50|TINTERFACE/MONEYFRAME/UI-SILVERICON.BLP:0|t', creatureID = 21055, },
	[143] = { description = 'Mortal enemies with silver dragonhawks; on pretty good terms with golden dragonhawks.', sourceText = '|cFFFFD200Vendor: |rJilanne|n|cFFFFD200Zone: |rEversong Woods|n|cFFFFD200Cost: |r50|TINTERFACE/MONEYFRAME/UI-SILVERICON.BLP:0|t', creatureID = 21064, },
	[144] = { description = 'Ferocious species of dragonhawk, often seen diving down on prey from high in the sky.', sourceText = '|cFFFFD200Vendor: |rJilanne|n|cFFFFD200Zone: |rEversong Woods|n|cFFFFD200Cost: |r50|TINTERFACE/MONEYFRAME/UI-SILVERICON.BLP:0|t', creatureID = 21063, },
	[145] = { description = 'Raised by the few dealers still willing to trade in Netherstorm.', sourceText = '|cFFFFD200Vendor: |rDealer Rashaad|n|cFFFFD200Zone: |rNetherstorm|n|cFFFFD200Cost: |r10|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t', creatureID = 21056, },
	[146] = { description = 'Commonly used by sporelings as a light source as they navigate the waterways of Zangarmarsh, this luminescent insect helps them avoid the dangerous bogflare needlers.', sourceText = '|cFFFFD200Drop: |rBogflare Needler|n|cFFFFD200Zone: |rZangarmarsh', creatureID = 21076, },
	[149] = { description = 'The magical energies released during Skywing\'s transformation back into his true arakkoa form liberated this poor creature, a fledgling bird imprisoned by Luanga in order to power his fowl curse.', sourceText = '|cFFFFD200Quest:|r Skywing|n|cFFFFD200Zone:|r Terokkar Forest|n|n', creatureID = 22445, },
	[153] = { description = 'Many a drunken Brewfest goer has told the tale of this mysterious creature, but few remember that tale in the morning.', sourceText = '|cFFFFD200World Event:|r Brewfest', creatureID = 22943, },
	[155] = { description = 'Lucky squeals when he\'s angry or when he\'s eating. Either way, you don\'t want to bother him.', sourceText = '|cFFFFD200Promotion:|r World Wide Invitational 2007|n', creatureID = 23198, },
	[156] = { description = 'Although loath to part with Bananas, Landro realized it would be safer if the pet was far from Booty Bay when his father, King Mukla, discovered he was missing.', sourceText = '|cFFFFD200Trading Card Game:|r Through the Dark Portal', creatureID = 23234, },
	[157] = { description = 'he\'s slightly evil but extremely narcoleptic. Keep him away from critters!', sourceText = '|cFFFFD200World Event:|r Children\'s Week', creatureID = 23231, },
	[891] = { description = '', sourceText = '', creatureID = 66154, },
	[158] = { description = 'The sin\'dorei use hawkstriders as mounts, but this little one was deemed unfit after being diagnosed with numerous anxiety disorders.', sourceText = '|cFFFFD200World Event:|r Children\'s Week', creatureID = 23258, },
	[159] = { description = 'Domesticated elekks are trained as mounts by the draenei people. In the wild they are hunted for their ivory tusks.', sourceText = '|cFFFFD200World Event:|r Children\'s Week', creatureID = 23266, },
	[160] = { description = 'Stinker uses his aroma to repel predators and attract cats. he\'s much more successful with the former than the latter.', sourceText = '|cFFFFD200Achievement: |rShop Smart, Shop Pet...Smart|n|cFFFFD200Category: |rPet Battles', creatureID = 23274, },
	[162] = { description = 'This mischievous gourd likes to pop out of the ground and scare you right out of your pants. Hail to the pumpkin song!', sourceText = '|cFFFFD200World Event:|r Hallow\'s End', creatureID = 23909, },
	[163] = { description = 'The crocolisk\'s hide and meat are prized by hunters, but the beast is ferocious and seldom taken by surprise.', sourceText = '|cFFFFD200Profession: |rFishing|n|cFFFFD200Zone: |rShattrath', creatureID = 24388, },
	[164] = { description = 'Muckbreath is just like other crocolisks, except he has an excellent sense of humor.', sourceText = '|cFFFFD200Profession:|r Fishing|n|cFFFFD200Zone:|r Shattrath', creatureID = 24389, },
	[165] = { description = 'Amani witch doctors have turned many victims into frogs. Some never recover.', sourceText = '|cFFFFD200Drop:|r Forest Frog|n|cFFFFD200Zone:|r Zul\'Aman', creatureID = 24480, },
	[166] = { description = 'A shy creature, the pink elekk will only make its presence known to those that have befriended it, or to anyone too intoxicated to harm it.', sourceText = '|cFFFFD200World Event:|r Brewfest|n|cFFFFD200Vendor: |rBelbi Quikswitch|n|cFFFFD200Zone: |rDun Morogh|n|cFFFFD200Cost: |r100|TINTERFACE/ICONS/INV_Misc_Coin_01:0|t|n|n|cFFFFD200Vendor: |rBliz Fixwidget|n|cFFFFD200Zone: |rDurotar|n|cFFFFD200Cost: |r100|TINTERFACE/ICONS/INV_Misc_Coin_01:0|t', creatureID = 24753, },
	[167] = { description = 'Wild sporebats are vicious creatures, but if tamed at a young age, they can be loyal companions.', sourceText = '|cFFFFD200Vendor: |rMycah|n|cFFFFD200Zone: |rZangarmarsh|n|cFFFFD200Faction: |rSporeggar - Exalted|n|cFFFFD200Cost: |r30|TINTERFACE/ICONS/INV_Mushroom_02:0|t', creatureID = 25062, },
	[168] = { description = 'When the Burning Legion invaded Outland, Landro Longshot contributed this design to the war effort. Both the Alliance and Horde passed on production.', sourceText = '|cFFFFD200Trading Card Game:|r March of the Legion', creatureID = 25109, },
	[169] = { description = 'Much quieter than a real dragon.', sourceText = '|cFFFFD200Trading Card Game:|r March of the Legion', creatureID = 25110, },
	[170] = { description = 'This golden pig honors its ancestors by giving its allies good fortune.', sourceText = '|cFFFFD200Promotion:|r Recruit-A-Friend|n', creatureID = 25146, },
	[171] = { description = 'Druids think silver pigs have a special bond with golden pigs, as both breeds appeared on Azeroth at the same time.', sourceText = '|cFFFFD200Promotion:|r Recruit-A-Friend', creatureID = 25147, },
	[172] = { description = 'Elder shaman have warned that wisps of Ragnaros, loyal servants of the Firelord, have taken to the Molten Front.  Vigilance is advised.', sourceText = '|cFFFFD200Vendor:|r Zen\'Vorka|n|cFFFFD200Zone:|r Molten Front|n|cFFFFD200Cost: |r30|TINTERFACE/ICONS/INV_MISC_MARKOFTHEWORLDTREE.BLP:0|t', creatureID = 25706, },
	[173] = { description = 'Crocolisks are six-legged amphibian predators found in coastal waters. Archaeologists believe they may be descended from the diemetradons of Un\'goro Crater.', sourceText = '|cFFFFD200Profession: |rFishing|n|cFFFFD200Zone: |rShattrath', creatureID = 26050, },
	[174] = { description = 'After studying the crocolisk species for several years, the famed angler Nat Pagle determined that baby crocolisks are mostly harmless.', sourceText = '|cFFFFD200Profession: |rFishing|n|cFFFFD200Zone: |rShattrath', creatureID = 26056, },
	[175] = { description = 'A child of phoenix god Al\'ar, the hatchling takes flight immediately after birth.', sourceText = '|cFFFFD200Drop: |rKael\'thas Sunstrider|n|cFFFFD200Zone: |rMagisters\' Terrace', creatureID = 26119, },
	[179] = { description = 'These stunning creatures, awarded to heroes for victory in battle, represent bravery and strength.', sourceText = '|cFFFFD200Promotion:|r Battleground Event', creatureID = 27217, },
	[180] = { description = 'Only heroes who have proven their valor through great acts are gifted one of these mystical serpentine creatures.', sourceText = '|cFFFFD200Promotion:|r China PVP Event', creatureID = 27346, },
	[183] = { description = 'Ethereal soul-traders will travel with those who can free the essences they need from unruly hosts.', sourceText = '|cFFFFD200Trading Card Game:|r The Hunt for Illidan', creatureID = 27914, },
	[186] = { description = 'Native to the Terokkar Forest, nether rays spawn during the rainy season. Out of the hundreds of offspring resulting from one hatching, very few survive to adulthood.', sourceText = '|cFFFFD200Vendor: |rGrella|n|cFFFFD200Zone: |rTerokkar Forest|n|cFFFFD200Faction: |rSha\'tari Skyguard - Exalted|n|cFFFFD200Cost: |r40|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t', creatureID = 28470, },
	[187] = { description = 'Vampire bats have been trained as scouts and mounts by the Forsaken of the Undercity and the Amani trolls of Zul\'Aman.', sourceText = '|cFFFFD200Drop: |rPrince Tenris Mirkblood|n|cFFFFD200Zone: |rKarazhan', creatureID = 28513, },
	[188] = { description = 'Accidentally created when the Lich King\'s deathly powers raised Rimefang from undeath.', sourceText = '|cFFFFD200Promotion:|r Wrath of the Lich King Collector\'s Edition', creatureID = 28883, },
	[189] = { description = '\'I am Justice itself!\'', sourceText = '|cFFFFD200Promotion:|r World Wide Invitational 2008', creatureID = 29089, },
	[190] = { description = 'When it senses the impending death of a nearby creature, the skull will emit an ear-piercing cackle.', sourceText = '|cFFFFD200Vendor: |rDarahir|n|cFFFFD200Zone: |rCrystalsong Forest|n|cFFFFD200Cost: |r40|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t', creatureID = 29147, },
	[191] = { description = 'This mechanical warrior is a favorite gift during Winter Veil. Batteries not included, but rockets definitely are!', sourceText = '|cFFFFD200World Event:|r Feast of Winter Veil', creatureID = 24968, },
	[192] = { description = 'Mr. Chilly\'s appearance evokes a more civilized era, when duelists battled in tuxedos.', sourceText = '|cFFFFD200Promotion:|r WoW/Blizzard Account Merger', creatureID = 29726, },
	[892] = { description = '', sourceText = '', creatureID = 66155, },
	[193] = { description = 'You wouldn\'t want to meet this rat in a dark alley. Or anywhere else, for that matter.', sourceText = '|cFFFFD200Profession:|r Fishing|n|cFFFFD200Zone:|r Dalaran', creatureID = 31575, },
	[194] = { description = 'Unlike most of Geen the gorloc\'s \'mysterious eggs,\' this one actually contained a living thing.', sourceText = '|cFFFFD200Drop: |rMysterious Egg', creatureID = 32589, },
	[195] = { description = 'The rare albino tickbird is revered amongst the Oracles. It is seen as a sign of heavy rainstorms, good grub harvests, and hibernating wolvar.', sourceText = '|cFFFFD200Drop: |rMysterious Egg', creatureID = 32590, },
	[196] = { description = 'These voracious whelps grow at an incredible rate and can devour nearly twice their weight in raw meat per day.', sourceText = '|cFFFFD200Drop: |rMysterious Egg', creatureID = 32592, },
	[197] = { description = 'The cobras of Azeroth are renowned for their fast-acting venom and swift strikes.', sourceText = '|cFFFFD200Drop: |rMysterious Egg', creatureID = 32591, },
	[198] = { description = 'Notable for its wide eyes and sassy slide.', sourceText = '|cFFFFD200Vendor: |rSairuk|n|cFFFFD200Zone: |rDragonblight|n|cFFFFD200Faction: |rThe Kalu\'ak - Exalted|n|cFFFFD200Cost: |r12|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|n|cFFFFD200Vendor: |rTanaika|n|cFFFFD200Zone: |rHowling Fjord|n|cFFFFD200Faction: |rThe Kalu\'ak - Exalted|n|cFFFFD200Cost: |r12|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t', creatureID = 32595, },
	[199] = { description = 'To prevent apprentices from secretly using the familiar to complete their chores, the archmagi placed the ritual required to summon the creature across the pages of several spellbooks hidden throughout Dalaran.', sourceText = '|cFFFFD200Achievement:|r Higher Learning|n|cFFFFD200Category: |rGeneral', creatureID = 32643, },
	[200] = { description = 'Spring rabbits symbolize fertility, and they love to demonstrate why.', sourceText = '|cFFFFD200World Event:|r Noblegarden|n|cFFFFD200Vendor: |rNoblegarden Merchant|n|cFFFFD200Cost: |r100|TINTERFACE/ICONS/Achievement_Noblegarden_Chocolate_Egg:0|t', creatureID = 32791, },
	[201] = { description = 'he\'s humble, clairvoyant, and delicious with cranberry sauce.', sourceText = '|cFFFFD200World Event:|r Pilgrim\'s Bounty', creatureID = 32818, },
	[202] = { description = 'This rare bear is bred for its small size and perpetually youthful appearance.', sourceText = '|cFFFFD200Achievement: |rWoW\'s 4th Anniversary|n|cFFFFD200Category: |rFeats of Strength', creatureID = 32841, },
	[203] = { description = 'She likes the company of friends. Seventy-five of them, to be precise.', sourceText = '|cFFFFD200Achievement: |rLil\' Game Hunter|n|cFFFFD200Category: |rPet Battles', creatureID = 32939, },
	[204] = { description = 'It always wakes up dancing. What a happy little tree!', sourceText = '|cFFFFD200Vendor: |rRook Hawkfist|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Cost: |r40|TINTERFACE/ICONS/Ability_Paladin_ArtofWar.blp:0|t', creatureID = 33188, },
	[205] = { description = 'The bears of Dun Morogh are prized for their hardiness, loyalty, and combat prowess. Their time among the dwarves does little to curb their ferocity.', sourceText = '|cFFFFD200Vendor: |rDerrick Brindlebeard|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Cost: |r40|TINTERFACE/ICONS/Ability_Paladin_ArtofWar.blp:0|t ', creatureID = 33194, },
	[206] = { description = 'Can sense an opponent\'s heartbeat through echolocation.', sourceText = '|cFFFFD200Vendor: |rEliza Killian|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Cost: |r40|TINTERFACE/ICONS/Ability_Paladin_ArtofWar.blp:0|t', creatureID = 33197, },
	[207] = { description = 'The volatile Durotar scorpion is a fierce desert predator. It\'s known for only occasionally stinging its owner.', sourceText = '|cFFFFD200Vendor: |rFreka Bloodaxe|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Cost: |r40|TINTERFACE/ICONS/Ability_Paladin_ArtofWar.blp:0|t ', creatureID = 33198, },
	[209] = { description = 'Unlike the polymorphed variety, trueborn sheep can be quite scary.', sourceText = '|cFFFFD200Vendor: |rCorporal Arthur Flew|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Cost: |r40|TINTERFACE/ICONS/Ability_Paladin_ArtofWar.blp:0|t ', creatureID = 33200, },
	[210] = { description = 'A tallstrider\'s speed on open ground is unrivaled, so predators rely on stealth and pack tactics when hunting them.', sourceText = '|cFFFFD200Vendor: |rDoru Thunderhorn|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Cost: |r40|TINTERFACE/ICONS/Ability_Paladin_ArtofWar.blp:0|t ', creatureID = 33219, },
	[211] = { description = 'The strand crawler has adapted to the northern cold by hibernating under the island beaches.', sourceText = '|cFFFFD200Profession: |rFishing|n|cFFFFD200Zone: |rNorthrend, Stormwind, Orgrimmar', creatureID = 33226, },
	[212] = { description = 'The long-term effects of the contamination caused by the Exodar are still under observation.', sourceText = '|cFFFFD200Vendor: |rIrisee|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Cost: |r40|TINTERFACE/ICONS/Ability_Paladin_ArtofWar.blp:0|t |n', creatureID = 33205, },
	[213] = { description = 'Created by a blood elf apprentice, this broom would be extremely useful if anyone could figure out how to make it stop.', sourceText = '|cFFFFD200Vendor: |rTrellis Morningsun|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Cost: |r40|TINTERFACE/ICONS/Ability_Paladin_ArtofWar.blp:0|t ', creatureID = 33227, },
	[214] = { description = 'Many of Stormwind\'s youth joined the Argent Crusade as squires, hoping to one day serve the Holy Light in battle.', sourceText = '|cFFFFD200Quest:|r A Champion Rises|n|cFFFFD200Zone:|r Icecrown', creatureID = 33238, },
	[215] = { description = 'Used as a training mount for young gnomes, the Mechanopeep has none of the speed but all of the charm of its bigger brothers.', sourceText = '|cFFFFD200Vendor: |rRillie Spindlenut|n|cFFFFD200Zone: |rIcecrown', creatureID = 33274, },
	[216] = { description = 'These young orcs strive to embody the Argent Crusade\'s virtues: valor, strength, and integrity.', sourceText = '|cFFFFD200Quest:|r A Champion Rises|n|cFFFFD200Zone:|r Icecrown', creatureID = 33239, },
	[217] = { description = 'Murkimus is the most fearsome of all murloc arena warriors. And for a time, the only one.', sourceText = '|cFFFFD200Promotion:|r Arena Tournament', creatureID = 33578, },
	[218] = { description = 'This is actually three pets in one. You won\'t know what you\'ll get until you summon it!', sourceText = '|cFFFFD200Vendor: |rSamamba|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Cost: |r40|TINTERFACE/ICONS/Ability_Paladin_ArtofWar.blp:0|t|n', creatureID = 33810, },
	[220] = { description = 'A solemn reminder of what was lost when the Worldbreaker changed the face of Azeroth.', sourceText = '|cFFFFD200Quest: |rRemembrance of Auberdine|n|cFFFFD200Zone: |rDarkshore', creatureID = 34278, },
	[224] = { description = 'Many have mocked this cat before battle. Their last words are usually, \'Here, kitty, kitty.\'', sourceText = '|cFFFFD200Vendor: |rBreanni|n|cFFFFD200Zone: |rCrystalsong Forest|n|cFFFFD200Cost: |r50|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t', creatureID = 34364, },
	[225] = { description = 'The bouncy gurloc is a distant cousin of the murloc.', sourceText = '|cFFFFD200World Event:|r Children\'s Week', creatureID = 33530, },
	[226] = { description = 'Despite their smaller stature, the wolvar are some of the most aggressive creatures in Northrend. This often makes their curious nature intimidating.', sourceText = '|cFFFFD200World Event:|r Children\'s Week', creatureID = 33529, },
	[227] = { description = 'A fighting robot fueled by delicious red or blue electrolytes.', sourceText = '|cFFFFD200Promotion:|r Mountain Dew Promotion', creatureID = 34587, },
	[228] = { description = 'Only the rarest of murlocs is able to successfully cross dimensional barriers. Fewer still return with the kind of advanced armor and weaponry possessed by Grunty.', sourceText = '|cFFFFD200Promotion:|r BlizzCon 2009', creatureID = 34694, },
	[229] = { description = 'This extremely rare species of mana wyrm is prized for its unique coloration and even temperament.', sourceText = '|cFFFFD200Vendor: |rHiren Loresong|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Faction: |rThe Silver Covenant - Exalted|n|cFFFFD200Cost: |r40|TINTERFACE/ICONS/Ability_Paladin_ArtofWar.blp:0|t|n', creatureID = 34724, },
	[231] = { description = 'Considered to be lucky, tigers carved of jade are prized, but taming a jade tiger itself is an even greater prize.', sourceText = '|cFFFFD200Promotion:|r Recruit-A-Friend', creatureID = 34930, },
	[232] = { description = 'Rarely leaves nest until maturity. Enjoys conducting pranks on the nearby Razormaw nest.', sourceText = '|cFFFFD200Drop: |rDart\'s Nest|n|cFFFFD200Zone: |rDustwallow Marsh', creatureID = 35396, },
	[233] = { description = 'The demand for this purple-skinned breed has drastically reduced the raptor population near the Wailing Caverns.', sourceText = '|cFFFFD200Drop: |rDeviate Guardian, Deviate Ravager|n|cFFFFD200Zone: |rWailing Caverns', creatureID = 35395, },
	[234] = { description = 'This crafty raptor was born in the icy wastes of Northrend. Its harsh upbringing gives it strength when many others would perish.', sourceText = '|cFFFFD200Drop: |rGundrak Raptor|n|cFFFFD200Zone: |rZul\'Drak', creatureID = 35400, },
	[235] = { description = 'This offspring of Takk the Leaper is incredibly agile and can maneuver through difficult environments with ease.', sourceText = '|cFFFFD200Drop: |rTakk\'s Nest|n|cFFFFD200Zone: |rThe Barrens', creatureID = 35387, },
	[236] = { description = 'This hatchling is coveted for its unique coloration. Despite Breanni\'s loving care, the raptor\'s feral nature cannot be restrained.', sourceText = '|cFFFFD200Vendor: |rBreanni|n|cFFFFD200Zone: |rCrystalsong Forest|n|cFFFFD200Cost: |r50|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t', creatureID = 35399, },
	[237] = { description = 'Larger than other raptor infants, these hatchlings grow into towering reptilian beasts capable of massive devastation.', sourceText = '|cFFFFD200Drop: |rRavasaur Matriarch\'s Nest|n|cFFFFD200Zone: |rUn\'goro Crater', creatureID = 35397, },
	[238] = { description = 'Razormaw eggs are protected fiercely by their mothers. Their cuteness belies their inherited ferocity.', sourceText = '|cFFFFD200Drop: |rRazormaw Matriarch\'s Nest|n|cFFFFD200Zone: |rWetlands', creatureID = 35398, },
	[239] = { description = 'Razzashi raptors are among the cleverest predators in the jungle, making them favored by the Gurubashi trolls.', sourceText = '|cFFFFD200Drop: |rWorld Drop|n|cFFFFD200Zone: |rNorthern Stranglethorn, The Cape of Stranglethorn', creatureID = 35394, },
	[240] = { description = 'The onyx panther is invisible at night, like a stalking shadow, and disappears with the dawn.', sourceText = '|cFFFFD200Promotion:|r Korea World Event', creatureID = 35468, },
	[241] = { description = 'Seeing these kites soaring above Northrend\'s shores is a clear sign that tuskarr are nearby.', sourceText = '|cFFFFD200Trading Card Game:|r Scourgewar', creatureID = 36482, },
	[242] = { description = 'Affectionate and curious, these kittens seem to find the most peculiar hiding places.', sourceText = '|cFFFFD200Trading Card Game:|r Scourgewar', creatureID = 36511, },
	[243] = { description = 'Spawn of Onyxia, this young dragon has decided that it doesn\'t want to follow in its grandfathers\' cataclysmic footsteps.', sourceText = '|cFFFFD200Achievement: |rWoW\'s 5th Anniversary|n|cFFFFD200Category: |rFeats of Strength', creatureID = 36607, },
	[244] = { description = 'Found in the Firelands and the Molten Core, core hounds can trace their lineage back to the alpha male, Magmadar.', sourceText = '|cFFFFD200Promotion:|r Authenticator Account Link|n', creatureID = 36871, },
	[245] = { description = 'A gift from the Wildhammer dwarves to the heroes of the Alliance, these hatchlings are descendants of the same gryphons ridden by Falstad and his entourage into Grim Batol.', sourceText = '|cFFFFD200In-Game Shop|r', creatureID = 36908, },
	[246] = { description = 'Thorg No-Legs never paid heed to the old orc saying, \'Don\'t pull a wyvern\'s tail.\'', sourceText = '|cFFFFD200In-Game Shop|r', creatureID = 36909, },
	[247] = { description = 'A rarely seen family of tigers, many believe (perhaps incorrectly) that some residual magic or other event is responsible for the unique color.', sourceText = '|cFFFFD200Promotion:|r Recruit-A-Friend', creatureID = 36910, },
	[248] = { description = 'The pandaren monk is a fearless traveling companion, constantly training in his art... unless you offer him a drink. He considers it rude to refuse.', sourceText = '|cFFFFD200In-Game Shop|r', creatureID = 36911, },
	[249] = { description = 'In life, Kel\'Thuzad betrayed the Kirin Tor and created the Cult of the Damned. In death, he serves the Scourge as the Archlich Lord of Naxxramas.', sourceText = '|cFFFFD200In-Game Shop|r', creatureID = 36979, },
	[250] = { description = 'Although small, pugs are very dangerous when provoked.', sourceText = '|cFFFFD200Achievement: |rLooking For Multitudes|n|cFFFFD200Category: |rDungeons & Raids', creatureID = 37865, },
	[251] = { description = 'This, um, \'adorable\' little guy likes to play with critters. Just don\'t let them get too close.', sourceText = '|cFFFFD200World Event:|r Love is in the Air', creatureID = 38374, },
	[253] = { description = 'While not nearly as destructive as its original master, the Ice Lord Ahune, the frostling has been known to lob chilly snowballs at unsuspecting adventurers.', sourceText = '|cFFFFD200World Event:|r Midsummer Fire Festival', creatureID = 40198, },
	[254] = { description = 'Jepetto\'s top-selling toy this season, the bot\'s new blue chassis has children across Azeroth scrambling for this feisty battle bot.', sourceText = '|cFFFFD200Vendor: |rWorld Vendors|n|cFFFFD200Cost: |r50|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t', creatureID = 40295, },
	[255] = { description = 'Breanni in Dalaran, one of the world\'s foremost collectors of exotic pets, will only award the mystical Celestial Dragon to fellow collectors whose obsession rivals her own.', sourceText = '|cFFFFD200Achievement: |rLittlest Pet Shop|n|cFFFFD200Category: |rPet Battles', creatureID = 40624, },
	[256] = { description = 'Modeled after the huge titan prototype in Ulduar, this volatile little construct plays hard with its toys.', sourceText = '|cFFFFD200In-Game Shop|r', creatureID = 40703, },
	[257] = { description = 'Murkimus is the most fearsome of all murloc arena warriors. And the only one.', sourceText = '', creatureID = 40721, },
	[258] = { description = 'Powerful artillery of the Terran army. The Thor is always the first one in and the last one out!', sourceText = '|cFFFFD200Promotion:|r StarCraft II: Wings of Liberty Collector\'s Edition', creatureID = 42078, },
	[259] = { description = 'Unlike its gold counterpart, the blue mini jouster takes a leisurely pace when jabbing its opponent\'s eyeballs.', sourceText = '|cFFFFD200Quest:|r Egg Wave|n|cFFFFD200Zone:|r Mount Hyjal|n|n', creatureID = 42177, },
	[260] = { description = 'This critter is infamous for its ability to unleash a fast and furious eyeball jabbing.', sourceText = '|cFFFFD200Quest:|r Egg Wave|n|cFFFFD200Zone:|r Mount Hyjal|n|n', creatureID = 42183, },
	[261] = { description = 'Goblin engineering at its finest. Blueprints based on the massive fel reaver in Hellfire Peninsula.', sourceText = '|cFFFFD200Profession: |rEngineering', creatureID = 43800, },
	[262] = { description = 'The gold standard for sturdy gnomish design, this mechanical critter is the perfect companion for the lonely engineer on the go.', sourceText = '|cFFFFD200Profession: |rEngineering', creatureID = 43916, },
	[264] = { description = 'This appears to be nothing more than the paw of a small primate. A closer inspection shows that the hand has been dried by centuries of exposure to the sands of Uldum.', sourceText = '|cFFFFD200Profession: |rArchaeology', creatureID = 45128, },
	[265] = { description = 'An adventurous little elemental, Pebble escaped hungry gyreworms in the Crumbling Depths, braved the emptiness of the Twisting Nether, and ultimately found himself rescued from a mailbox.', sourceText = '|cFFFFD200Achievement: |rRock Lover|n|cFFFFD200Category: |rQuests', creatureID = 45247, },
	[266] = { description = 'You stare at her, and she just stares right back. So clever; sometimes it seems as if she\'s figuring out how to open doors.', sourceText = '|cFFFFD200Profession: |rArchaeology', creatureID = 45340, },
	[267] = { description = 'Reduces the likelihood of being eaten by monsters when you enter pitch-black places.', sourceText = '|cFFFFD200Profession:|r Cataclysm Enchanting (75)|n|cFFFFD200Formula:|r Enchanted Lantern', creatureID = 46898, },
	[268] = { description = 'Nations tremble under his shadow - the Destroyer, the one-time Aspect of Earth, and the monster responsible for breaking Azeroth asunder.  Now in a convenient travel-size!', sourceText = '|cFFFFD200Promotion:|r Cataclysm Collector\'s Edition', creatureID = 46896, },
	[270] = { description = 'Fire hatchlings are born from the ashes of a phoenix. Dark hatchlings emerge from their shadows.', sourceText = '|cFFFFD200Vendor:|r Guild Vendor|n|cFFFFD200Zone:|r Stormwind, Orgrimmar|n|cFFFFD200Cost: |r300|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n', creatureID = 47944, },
	[271] = { description = 'This bird is a salty veteran of the endless naval battles surrounding Tol Barad--a veteran whose grown plump from picking through the leftovers, that is.', sourceText = '|cFFFFD200Vendor: |rQuartermaster Brazie|n|cFFFFD200Zone: |rTol Barad Peninsula|n|cFFFFD200Faction: |rBaradin\'s Wardens - Honored|n|cFFFFD200Cost: |r50|TINTERFACE/ICONS/Achievement_Zone_TolBarad.blp:0|t', creatureID = 48107, },
	[272] = { description = 'Rolls into a protective ball when it feels threatened. Or when its mother calls.', sourceText = '|cFFFFD200Vendor:|r Guild Vendor|n|cFFFFD200Zone:|r Stormwind, Orgrimmar|n|cFFFFD200Cost: |r300|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n', creatureID = 48242, },
	[277] = { description = 'Thought to be an original construct of the titans, the clockwork gnome has some gears smaller than the eye can see.', sourceText = '|cFFFFD200Profession: |rArchaeology', creatureID = 48609, },
	[278] = { description = 'The fighting in Tol Barad has made it difficult to find these kits, and numerous would-be owners have decimated the fox population there as a result.', sourceText = '|cFFFFD200Drop: |rBaradin Fox|n|cFFFFD200Zone: |rTol Barad Peninsula', creatureID = 48641, },
	[279] = { description = 'The elemental shale spider is no true arachnid, but it has durability and ferocity that make it feared even in Deepholm.', sourceText = '|cFFFFD200Drop: |rJadefang|n|cFFFFD200Zone: |rDeepholm', creatureID = 48982, },
	[280] = { description = 'Guild pages must be fearless since they are called upon to announce their guild\'s victories over the Horde in battle.', sourceText = '|cFFFFD200Vendor: |rGuild Vendor|n|cFFFFD200Zone:|r Stormwind, Orgrimmar|n|cFFFFD200Cost: |r300|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|cFFFFD200Cooldown: |r8 hrs', creatureID = 49586, },
	[281] = { description = 'When a guild earns enough glory by defeating Alliance champions, some orcs will come to share the glory by serving the guild.', sourceText = '|cFFFFD200Vendor: |rGuild Vendor|n|cFFFFD200Zone:|r Stormwind, Orgrimmar|n|cFFFFD200Cost: |r300|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|cFFFFD200Cooldown: |r8 hrs', creatureID = 49588, },
	[282] = { description = 'When guilds have accumulated enough wealth, brave soldiers are often called to bear their standards.', sourceText = '|cFFFFD200Vendor: |rGuild Vendor|n|cFFFFD200Zone:|r Stormwind, Orgrimmar|n|cFFFFD200Cost: |r500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|cFFFFD200Cooldown: |r4 hrs', creatureID = 49587, },
	[283] = { description = 'Wealthy guilds draw the attention of soldiers willing to carry their standards into battle.', sourceText = '|cFFFFD200Vendor: |rGuild Vendor|n|cFFFFD200Zone:|r Stormwind, Orgrimmar|n|cFFFFD200Cost: |r500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|cFFFFD200Cooldown: |r4 hrs', creatureID = 49590, },
	[285] = { description = 'Always \'ready to play,\' Landro\'s Lil\' XT is almost indistinguishable from the original. Prone to tantrums and narcolepsy.', sourceText = '|cFFFFD200Trading Card Game: |rWorldbreaker|n', creatureID = 50468, },
	[893] = { description = '', sourceText = '', creatureID = 66156, },
	[286] = { description = 'The grubs of the Plaguelands are a side effect of necromantic corruption and the massive number of corpses there. Mr. Grubbs is from a particularly acrobatic breed.', sourceText = '|cFFFFD200World Drop:|r Eastern Plaguelands (requires Fiona\'s Lucky Charm)', creatureID = 50586, },
	[287] = { description = 'After swarming around open lava pits during the day, these strange insects emit a fiery glow at night.', sourceText = '|cFFFFD200Quest:|r SEVEN! YUP!, Not Fireflies, Flameflies|n|cFFFFD200Zone:|r Burning Steppes|n', creatureID = 51632, },
	[289] = { description = 'You\'ll find him in his shell, exactly where a snail has to be!', sourceText = '|cFFFFD200World Event:|r Children\'s Week', creatureID = 51635, },
	[291] = { description = 'This sweet flower will brighten your day with its warm smile and cheerful song! Also strong against undead.', sourceText = '|cFFFFD200Quest:|r Lawn of the Dead|n|cFFFFD200Zone:|r Hillsbrad Foothills|n', creatureID = 51090, },
	[292] = { description = 'Higitus figi- hockity pocki- abracadab- prestidig--oh, pinfeathers!', sourceText = '|cFFFFD200Profession: |rEnchanting', creatureID = 50545, },
	[293] = { description = 'Often confused by miners as a rare gem, the geode initially makes its presence known by emitting melodic reverberations.', sourceText = '|cFFFFD200Drop:|r Elementium Vein, Rich Elementium Vein|n|cFFFFD200Zone:|r Deepholm, Twilight Highlands, Uldum', creatureID = 50722, },
	[294] = { description = 'Deathwing\'s emergence transformed the whole of Azeroth; not even the ocean-dwelling murlocs were immune to his corrupt influence.', sourceText = '|cFFFFD200Promotion:|r BlizzCon 2010', creatureID = 51122, },
	[296] = { description = 'Moonkin are blessed by Elune and spread nature\'s grace wherever they travel. The festive fowls are always in the mood for a dance.', sourceText = '|cFFFFD200In-Game Shop:|r Alliance', creatureID = 51601, },
	[297] = { description = 'It\'s never too soon to summon this tiny aspect of the dread Firelord.', sourceText = '|cFFFFD200In-Game Shop|r', creatureID = 51600, },
	[298] = { description = 'The distinct roar of the moonkin is one of the most terrifying sounds in nature.', sourceText = '|cFFFFD200In-Game Shop:|r Horde', creatureID = 51649, },
	[301] = { description = 'It looked up at you with those huge black eyes and seemed to ask, \'Are you my mommy?\'', sourceText = '|cFFFFD200Quest:|r Some Good Will Come|n|cFFFFD200Zone:|r Northern Stranglethorn|n|n', creatureID = 52226, },
	[302] = { description = 'How the goblin Landro Longshot initially came into possession of the lichling remains a mystery, but he was all too eager to wager it in a game of cards.', sourceText = '|cFFFFD200Trading Card Game:|r Worldbreaker', creatureID = 52343, },
	[303] = { description = 'Elune made nightsabers silver and black so they could meld into the shadows on a moonlit night.', sourceText = '|cFFFFD200Trading Card Game:|r Twilight of the Dragons', creatureID = 52344, },
	[306] = { description = 'Winterspring cubs are happiest when frolicking in freshly fallen snow.', sourceText = '|cFFFFD200Vendor: |rMichelle De Rum|n|cFFFFD200Zone: |rWinterspring|n|cFFFFD200Cost: |r50|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t', creatureID = 52831, },
	[307] = { description = 'Born in the jungles of Stranglethorn, unlucky raptor hatchlings are often captured by the Gurubashi and taken to the troll city of Zul\'Gurub.', sourceText = '|cFFFFD200Quest: |rAn Old Friend|n|cFFFFD200Zone: |rZul\'Gurub', creatureID = 52894, },
	[308] = { description = 'Wild marsh walkers are dangerously aggressive, but they\'re loyal and affectionate if you manage to tame them.', sourceText = '|cFFFFD200World Event:|r Children\'s Week', creatureID = 53048, },
	[309] = { description = 'Pterrordax eggs are incubated underneath the fossils of their forebears, allowing the hatchlings to absorb their ancestors\' ferocity and intelligence.', sourceText = '|cFFFFD200Profession: |rArchaeology', creatureID = 53225, },
	[310] = { description = 'Voodoo figurines were often empowered by flasks of mojo, troll sweat, and the flesh of tribal enemies.', sourceText = '|cFFFFD200Profession: |rArchaeology', creatureID = 53232, },
	[311] = { description = 'Still a little clumsy, this tiny winged lion is learning how to be the best mount it can be!', sourceText = '|cFFFFD200In-Game Shop|r', creatureID = 53283, },
	[316] = { description = 'Not yet the fearsome creatures they grow to in adulthood, the Cenarion hatchlings have an almost harmless appearance that has caught novice trainers of the Cenarion Circle off guard.', sourceText = '|cFFFFD200In-Game Shop|r', creatureID = 53623, },
	[317] = { description = 'The cubs of Hyjal suffered much during the Cataclysm. On the bright side, the devastation also led to their newfound love of heights and daredevil stunts.', sourceText = '|cFFFFD200Vendor: |rVarlan Highbough|n|cFFFFD200Zone: |rMolten Front|n|cFFFFD200Cost: |r1500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t', creatureID = 53658, },
	[318] = { description = 'Native to the ashen soils of the Firelands, the lasher was transplanted to Azeroth with the help of the Guardians of Hyjal.', sourceText = '|cFFFFD200Vendor: |rAyla Shadowstorm|n|cFFFFD200Zone: |rMolten Front|n|cFFFFD200Cost: |r1500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t', creatureID = 53661, },
	[319] = { description = 'Ride with the moon (and this furry, whiskered witch) in the dead of night!', sourceText = '|cFFFFD200World Event:|r Hallow\'s End|n|cFFFFD200Vendor: |rWoim|n|cFFFFD200Zone: |rTirisfal Glades|n|cFFFFD200Cost: |r150|TINTERFACE/ICONS/achievement_halloween_candy_01:0|t|n|n|cFFFFD200Vendor: |rPippi|n|cFFFFD200Zone: |rStormwind City|n|cFFFFD200Cost: |r150|TINTERFACE/ICONS/achievement_halloween_candy_01:0|t', creatureID = 53884, },
	[320] = { description = 'A legendary member of the blue dragonflight.', sourceText = '|cFFFFD200Vendor: |rGuild Vendor|n|cFFFFD200Zone:|r Stormwind, Orgrimmar|n|cFFFFD200Cost: |r1500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n', creatureID = 54027, },
	[321] = { description = 'Previous owners of the crate have reported the disappearance of other pets from their collection.', sourceText = '|cFFFFD200World Event:|r Hallow\'s End', creatureID = 54128, },
	[323] = { description = 'Most squirrels hoard acorns for sustenance. Nuts, on the other hand, hoards acorns to stock its supply of ammunition.', sourceText = '|cFFFFD200Achievement: |rPetting Zoo|n|cFFFFD200Category: |rPet Battles', creatureID = 54227, },
	[325] = { description = 'One of the rarest creatures, this kaliri is set apart from others by its brilliant plumage and the lengths hunters will go to capture them.', sourceText = '|cFFFFD200Achievement: |rMenagerie|n|cFFFFD200Category: |rPet Battles', creatureID = 54374, },
	[894] = { description = '', sourceText = '', creatureID = 66329, },
	[328] = { description = 'You might think this fish out of water would be a weak opponent... until you see the intensity that lies within its protective bubble.', sourceText = '|cFFFFD200Trading Card Game:|r Throne of the Tides', creatureID = 54383, },
	[329] = { description = 'When you\'re confronting Murkablo or any of the Prime Murlocs, possession of a soulstone is strongly advised.', sourceText = '|cFFFFD200Promotion:|r BlizzCon 2011', creatureID = 54438, },
	[330] = { description = 'The monkeys of Azeroth are infamous tricksters, and these beloved companions of the Darkmoon Faire have taken that innate cunning to a whole new level.', sourceText = '|cFFFFD200Vendor: |rLhara|n|cFFFFD200Zone: |rDarkmoon Island|n|cFFFFD200Cost: |r90|TINTERFACE/ICONS/INV_misc_ticket_darkmoon_01:0|t |n', creatureID = 54491, },
	[331] = { description = 'Created to entertain children at the Darkmoon Faire, these magical balloons never lose their floatiness.', sourceText = '|cFFFFD200Quest:|r Blown Away|n|cFFFFD200Zone:|r Stormwind', creatureID = 54539, },
	[332] = { description = 'A magical wonder, these balloons will never deflate and are traditionally created in batches of ninety-nine.', sourceText = '|cFFFFD200Quest:|r Blown Away|n|cFFFFD200Zone:|r Orgrimmar|n', creatureID = 54541, },
	[333] = { description = 'The grell are embodiments of the wild side of nature. Tame ones are very rare.', sourceText = '|cFFFFD200Trading Card Game:|r Crown of the Heavens', creatureID = 54730, },
	[335] = { description = 'This shelled reptile bravely protects the home on its back with an unwaveringly happy disposition.', sourceText = '|cFFFFD200Vendor: |rLhara|n|cFFFFD200Zone: |rDarkmoon Island|n|cFFFFD200Cost: |r90|TINTERFACE/ICONS/INV_misc_ticket_darkmoon_01:0|t ', creatureID = 54487, },
	[336] = { description = 'It just floats along...', sourceText = '|cFFFFD200Vendor: |rLhara|n|cFFFFD200Zone: |rDarkmoon Island|n|cFFFFD200Cost: |r90|TINTERFACE/ICONS/INV_misc_ticket_darkmoon_01:0|t |n', creatureID = 55187, },
	[337] = { description = 'A stowaway from Greatfather Winter\'s workshop, this tiny elemental found himself trapped in a present that was stolen by the Abominable Greench.', sourceText = '|cFFFFD200World Event:|r Feast of Winter Veil', creatureID = 55215, },
	[338] = { description = 'A favorite of children across Azeroth, this tough little toy can withstand even the most aggressive imagination.', sourceText = '|cFFFFD200Vendor: |rLhara|n|cFFFFD200Zone: |rDarkmoon Island|n|cFFFFD200Cost: |r90|TINTERFACE/ICONS/INV_misc_ticket_darkmoon_01:0|t ', creatureID = 55356, },
	[339] = { description = 'Lacking the mines and rockets of its predecessor, this kid-friendly faire prize is a new favorite among Azeroth\'s children.', sourceText = '|cFFFFD200Vendor: |rLhara|n|cFFFFD200Zone: |rDarkmoon Island|n|cFFFFD200Cost: |r90|TINTERFACE/ICONS/INV_misc_ticket_darkmoon_01:0|t ', creatureID = 55367, },
	[340] = { description = 'Despite its diminutive size, it is impossible to miss a sea pony cutting through the water.', sourceText = '|cFFFFD200Profession: |rFishing|n|cFFFFD200Zone: |rDarkmoon Island', creatureID = 55386, },
	[341] = { description = 'These magical lanterns are acquired at the Lunar Festival, which celebrates the defeat of the Burning Legion by the free races of Azeroth.', sourceText = '|cFFFFD200World Event:|r Lunar Festival|n|cFFFFD200Vendor: |rValadar Starsong|n|cFFFFD200Zone: |rMoonglade|n|cFFFFD200Cost: |r50|TINTERFACE/ICONS/INV_Misc_ElvenCoins:0|t', creatureID = 55571, },
	[342] = { description = 'The light from each lantern honors the soul of an ancestor spirit. During the Lunar Festival, these ghostly elders pass along their wisdom to the current generation.', sourceText = '|cFFFFD200World Event:|r Lunar Festival|n|cFFFFD200Vendor: |rValadar Starsong|n|cFFFFD200Zone: |rMoonglade|n|cFFFFD200Cost: |r50|TINTERFACE/ICONS/INV_Misc_ElvenCoins:0|t', creatureID = 55574, },
	[343] = { description = 'This snuggly kitten loves to frolic, but sometimes it seems to be chasing things that aren\'t really there.', sourceText = '|cFFFFD200Vendor: |rLhara|n|cFFFFD200Zone: |rDarkmoon Island|n|cFFFFD200Cost: |r90|TINTERFACE/ICONS/INV_misc_ticket_darkmoon_01:0|t |n', creatureID = 56031, },
	[344] = { description = '', sourceText = '|cFFFFD200Vendor: |rCarl Goodup|n|cFFFFD200Zone: |rDarkmoon Island|n|cFFFFD200Cost: |r10|TINTERFACE/MONEYFRAME/UI-SILVERICON.BLP:0|t', creatureID = 56082, },
	[345] = { description = '', sourceText = '|cFFFFD200Vendor: |rCarl Goodup|n|cFFFFD200Zone: |rDarkmoon Island|n|cFFFFD200Cost: |r10|TINTERFACE/MONEYFRAME/UI-SILVERICON.BLP:0|t', creatureID = 56083, },
	[346] = { description = 'Fetish shaman are empowered by the spirits of their fallen ancestors. These malevolent spirits are bound to the shaman\'s tribal spear. Control this spear, and you control the shaman.', sourceText = '|cFFFFD200Promotion:|r Diablo 3 Collector\'s Edition', creatureID = 56266, },
	[347] = { description = 'The Soul of the Aspects is said to be possessed of mercurial moods ranging from stoic and deliberate to unpredictable and erratic, to loving and deeply pensive.', sourceText = '|cFFFFD200In-Game Shop|r', creatureID = 58163, },
	[348] = { description = 'Always watching. Always judging.', sourceText = '|cFFFFD200Trading Card Game: |rTimewalkers: War of the Ancients|n', creatureID = 59020, },
	[354] = { description = '', sourceText = '', creatureID = 59216, },
	[374] = { description = 'Offspring of the black sheep, the black lamb is only found in the forests of Elwynn.', sourceText = '|cFFFFD200Pet Battle:|r Elwynn Forest', creatureID = 60649, },
	[375] = { description = '', sourceText = '', creatureID = 62626, },
	[378] = { description = 'When you have two rabbits, you\'ll often soon have three.', sourceText = '|cFFFFD200Pet Battle: |rAzshara, Blade\'s Edge Mountains, Crystalsong Forest, Darkshore, Dun Morogh, Duskwood, Elwynn Forest, Eversong Woods, Feralas, Highmountain, Hillsbrad Foothills, Howling Fjord, Moonglade, Mount Hyjal, Mulgore, Nagrand, Redridge Mountains, Silvermoon City, Silverpine Forest, Stonetalon Mountains, Stormwind City, Tirisfal Glades, Western Plaguelands, Westfall, Wetlands', creatureID = 61080, },
	[379] = { description = 'Most people do not know the difference between a squirrel and a chipmunk. Most don\'t care.', sourceText = '|cFFFFD200Pet Battle: |rAmmen Vale, Ashenvale, Azshara, Blade\'s Edge Mountains, Crystalsong Forest, Darkshore, Dun Morogh, Duskwood, Dustwallow Marsh, Elwynn Forest, Feralas, Gilneas, Highmountain, Hillsbrad Foothills, Howling Fjord, Loch Modan, Magisters\' Terrace, Moonglade, Mount Hyjal, Nagrand, Ruins of Gilneas, Sholazar Basin, Silverpine Forest, Spires of Arak, Stormwind City, Teldrassil, Terokkar Forest, Tol Barad Peninsula, Twilight Highlands, Val\'sharah, Well of Eternity, Western Plaguelands, Westfall, Wetlands, Zul\'Aman', creatureID = 61081, },
	[380] = { description = 'It\'s hard to know what\'s more dangerous: the teeth or the tail.', sourceText = '|cFFFFD200Pet Battle: |rThe Jade Forest', creatureID = 62992, },
	[381] = { description = 'Despite the name you should absolutely never, ever pet it.', sourceText = '|cFFFFD200Drop:|r Sack of Pet Supplies|n', creatureID = 61086, },
	[382] = { description = 'This unique species of faerie dragon is not usually found on Azeroth.', sourceText = '|cFFFFD200Drop:|r Oddly-Colored Egg|n', creatureID = 61087, },
	[383] = { description = 'It seems like a trick to the eyes, but the eternal strider can walk across water, its small feet never breaking the surface.', sourceText = '|cFFFFD200Pet Battle: |rVale of Eternal Blossoms', creatureID = 61088, },
	[384] = { description = 'The thick pelt of the otter makes it adaptable to any climate, but it particularly prefers frigid waters where abalone is plentiful.', sourceText = '', creatureID = 61089, },
	[385] = { description = 'It seems that mice are completely incapable of resisting cheese even in the face of imprisonment, injury, or death.', sourceText = '|cFFFFD200Pet Battle: |rDuskwood, Dustwallow Marsh, Grizzly Hills, Mulgore, Netherstorm, Westfall, Wetlands', creatureID = 61143, },
	[386] = { description = 'Distinct from their tree dwelling cousins, these ground squirrels make their homes on the plains of Azeroth and beyond.', sourceText = '|cFFFFD200Pet Battle: |rArathi Highlands, Mulgore, Nagrand, Northern Barrens, Southern Barrens, Thunder Bluff, Westfall', creatureID = 61141, },
	[387] = { description = 'Due to recent accidents, the goblin cartels have prohibited anyone from bringing snakes on a zeppelin.', sourceText = '|cFFFFD200Pet Battle: |rDustwallow Marsh, Eversong Woods, Feralas, Ghostlands, Howling Fjord, Loch Modan, Nagrand, Northern Stranglethorn, Sholazar Basin, Silverpine Forest, Terokkar Forest, Westfall, Zangarmarsh, Zul\'Drak', creatureID = 61142, },
	[388] = { description = 'These arthropods line many Azerothian shores and seek to fulfill their one true desire: to pinch.', sourceText = '|cFFFFD200Pet Battle: |rAzshara, Borean Tundra, Howling Fjord, Krasarang Wilds, Nagrand (Draenor), Twilight Highlands, Westfall, The Waking Shores, The Azure Span', creatureID = 61158, },
	[389] = { description = 'The tiny harvester collects weeds and small grains that the larger reapers may have missed.', sourceText = '|cFFFFD200Pet Battle: |rWestfall', creatureID = 61160, },
	[390] = { description = 'When they look into those big brown eyes, some hunters have a hard time pulling the trigger. So they shoot from a distance.', sourceText = '', creatureID = 61750, },
	[391] = { description = 'Mountain cottontails can leap up a steep incline with speed you would not believe.', sourceText = '|cFFFFD200Pet Battle: |rHighmountain, Redridge Mountains', creatureID = 61167, },
	[392] = { description = 'The rats in Redridge grow fat from feasting on the bounty of the village larders.', sourceText = '|cFFFFD200Pet Battle: |rRedridge Mountains', creatureID = 61168, },
	[393] = { description = 'They were here long before us and will be here long after we are gone.', sourceText = '|cFFFFD200Pet Battle: |rBurning Steppes, Eastern Plaguelands, Gorgrond, Icecrown, Iron Docks, The Hinterlands, Twilight Highlands, Un\'goro Crater, Wetlands', creatureID = 61384, },
	[394] = { description = '"Little Bo Peep has lost her sheep, and doesn\'t know where to find them. Leave them alone, and they\'ll come OH MY GOD they\'re INSIDE THE HOUSE!"', sourceText = '', creatureID = 61170, },
	[395] = { description = 'Always wear a helm in the Redridge Mountains. Buzzards view humanoid hair as excellent nesting material.', sourceText = '|cFFFFD200Pet Battle: |rRedridge Mountains', creatureID = 61171, },
	[396] = { description = 'Native to Duskwood, these eight-legged critters are often used as ingredients in alchemy potions.', sourceText = '|cFFFFD200Pet Battle: |rDuskwood', creatureID = 61253, },
	[397] = { description = 'Skunks are known as solitary creatures for reasons which are hopefully obvious.', sourceText = '|cFFFFD200Pet Battle: |rAmmen Vale, Azshara, Bloodmyst Isle, Duskwood, Gilneas, Howling Fjord, Nagrand (Draenor), Terokkar Forest|n', creatureID = 61255, },
	[398] = { description = 'While many people believe black cats to be bad luck, black rats are held in higher esteem.', sourceText = '|cFFFFD200Pet Battle: |rBadlands, Duskwood, Dustwallow Marsh, Eastern Plaguelands, Thousand Needles, Twilight Highlands, Western Plaguelands, Wetlands|n', creatureID = 61257, },
	[399] = { description = 'Years ago, humans introduced these reptiles into Duskwood to cull the local rat population.', sourceText = '|cFFFFD200Pet Battle: |rDuskwood', creatureID = 61258, },
	[400] = { description = 'Widow spiderlings have plenty of time to explore Duskwood once they take care of their pesky family commitments.', sourceText = '|cFFFFD200Pet Battle: |rDuskwood|n|cFFFFD200Time: |rNight', creatureID = 61259, },
	[401] = { description = 'The low tide reveals the colonies of strand crabs and the sound of their clicking dance as they feed upon small creatures hidden in the sand.', sourceText = '|cFFFFD200Pet Battle: |rDarkshore, Dragonblight, Dun Morogh, Gilneas, Kezan, Northern Stranglethorn, Spires of Arak, Swamp of Sorrows, The Cape of Stranglethorn, The Lost Isles', creatureID = 61312, },
	[402] = { description = 'Prefers to dwell in wet, muddy areas and feed on Sorrowmoss nectar.', sourceText = '|cFFFFD200Pet Battle: |rSwamp of Sorrows', creatureID = 61370, },
	[403] = { description = 'Parrots can mimic speech, but have difficulty with meaningful conversation.', sourceText = '|cFFFFD200Pet Battle: |rNorthern Stranglethorn, Swamp of Sorrows, The Cape of Stranglethorn, Un\'goro Crater', creatureID = 61313, },
	[404] = { description = 'Thought by many to be nothing more than the subject of children\'s nursery rhymes, the long-tailed mole is very much real.', sourceText = '|cFFFFD200Pet Battle: |rDun Morogh, Northern Stranglethorn, The Cape of Stranglethorn, Un\'goro Crater', creatureID = 61317, },
	[405] = { description = 'Stranglethorn\'s Gurubashi trolls often capture these powerful beasts to keep as pets... or to cook as meals.', sourceText = '|cFFFFD200Pet Battle: |rNorthern Stranglethorn, Tanaan Jungle, The Cape of Stranglethorn, Un\'goro Crater', creatureID = 61318, },
	[406] = { description = 'A hardy insect that can dwell in just about any climate, the beetle is known to thrive on battlefields after the fighting has ended.', sourceText = '|cFFFFD200Pet Battle: |rBadlands, Eastern Plaguelands, Felwood, Northern Stranglethorn, Silithus, The Cape of Stranglethorn, Un\'goro Crater, The Waking Shores, The Azure Span, Thaldraszus', creatureID = 61319, },
	[895] = { description = '', sourceText = '', creatureID = 66330, },
	[407] = { description = 'This vicious species weaves elaborate webs throughout the dense jungles of Stranglethorn Vale.', sourceText = '|cFFFFD200Pet Battle: |rHighmountain, Northern Stranglethorn, Shadowmoon Valley, The Cape of Stranglethorn', creatureID = 61320, },
	[408] = { description = 'If a female lizard does not lay her clutch in a well camouflaged area, her eggs will likely be devoured by Lashtail Raptors.', sourceText = '|cFFFFD200Pet Battle: |rNorthern Stranglethorn, The Cape of Stranglethorn', creatureID = 61321, },
	[409] = { description = 'Parrots can\'t get enough of the fresh fish the Bloodscalp trolls eat. they\'re less interested in dry flatbreads.', sourceText = '|cFFFFD200Pet Battle: |rNorthern Stranglethorn', creatureID = 61322, },
	[410] = { description = 'Just like the rough and tumble sailors who live dockside, the wharf rats are feral, violent creatures.', sourceText = '|cFFFFD200Pet Battle: |rIron Docks, The Cape of Stranglethorn, Tol Barad Peninsula', creatureID = 61323, },
	[411] = { description = 'Young apes quickly learn to hang from branches with their feet, leaving both hands free to fling objects at interlopers.', sourceText = '|cFFFFD200Pet Battle: |rThe Cape of Stranglethorn|n|cFFFFD200Weather: |rRain', creatureID = 61324, },
	[412] = { description = 'The most common of the arachnid family, these critters spin their webs in nearly every corner of Azeroth.', sourceText = '|cFFFFD200Pet Battle: |rAhn\'kahet: The Old Kingdom, Azjol-Nerub, Azshara, Black Temple, Blasted Lands, Drak\'Tharon Keep, Dun Morogh, Dustwallow Marsh, Eastern Plaguelands, Elwynn Forest, Ghostlands, Gilneas, Gilneas City, Halls of Reflection, Hillsbrad Foothills, Howling Fjord, Icecrown Citadel, Karazhan, Naxxramas, Orgrimmar, Pit of Saron, Ruins of Gilneas, Shadowmoon Valley (Draenor), Stonetalon Mountains, Swamp of Sorrows, Teldrassil, The Culling of Stratholme, The Forge of Souls, The Hinterlands, The Storm Peaks, The Violet Hold, Tirisfal Glades, Utgarde Keep, Utgarde Pinnacle, Winterspring, Zul\'Aman, Zul\'Drak', creatureID = 61327, },
	[414] = { description = 'Venom capable of killing creatures up to 100 times its own body weight.', sourceText = '|cFFFFD200Pet Battle: |rBlade\'s Edge Mountains, Blasted Lands, Burning Steppes, Eastern Plaguelands, Hellfire Peninsula, Orgrimmar, Shadowmoon Valley, Silithus, Thousand Needles, Twilight Highlands', creatureID = 61326, },
	[415] = { description = 'Drawn to heat and the smell of smoke, the fire beetle\'s diet consists of ash.', sourceText = '|cFFFFD200Pet Battle: |rBlasted Lands, Burning Steppes, Mount Hyjal, Searing Gorge, Un\'goro Crater, The Waking Shores', creatureID = 61328, },
	[416] = { description = 'Despite its snapping claws and venom-dripping tail, it is the bite of the diminutive scorpling that is the greatest threat.', sourceText = '|cFFFFD200Pet Battle: |rBlasted Lands', creatureID = 61329, },
	[417] = { description = 'The intrepid archaeologist Harrison Jones has a well documented fear of rats.', sourceText = '|cFFFFD200Pet Battle: |rAmmen Vale, Arathi Basin, Arathi Highlands, Ashenvale, Azshara, Bloodmyst Isle, Crystalsong Forest, Darkshore, Desolace, Drak\'Tharon Keep, Dun Morogh, Frostfire Ridge, Ghostlands, Gilneas, Gilneas City, Highmountain, Hillsbrad Foothills, Howling Fjord, Karazhan, Loch Modan, Nagrand, Naxxramas, Ruins of Gilneas, Scarlet Monastery, Shattrath City, Silverpine Forest, Spires of Arak, Stonetalon Mountains, Stormwind City, Sunken Temple, Swamp of Sorrows, Terokkar Forest, The Cape of Stranglethorn, The Culling of Stratholme, The Deadmines, The Hinterlands, The Lost Isles, The Shattered Halls, The Violet Hold, Timeless Isle, Tirisfal Glades, Tanaan Jungle, The Waking Shores, The Azure Span', creatureID = 61366, },
	[418] = { description = 'Brought to Orgrimmar aboard merchant ships, these short-lived reptiles are considered pests by the city\'s residents.', sourceText = '|cFFFFD200Pet Battle: |rDurotar, Northern Stranglethorn, Orgrimmar, Swamp of Sorrows, Twilight Highlands, Wetlands', creatureID = 61367, },
	[419] = { description = 'This is a small frog.', sourceText = '|cFFFFD200Pet Battle: |rArathi Highlands, Darnassus, Desolace, Elwynn Forest, Eversong Woods, Ghostlands, Loch Modan, Northern Barrens, Southern Barrens, Swamp of Sorrows, Teldrassil, Zangarmarsh', creatureID = 61071, },
	[420] = { description = 'The song of late summer nights is the song of toads.', sourceText = '|cFFFFD200Pet Battle: |rAshenvale,  Durotar, Dustwallow Marsh, Eversong Woods, Felwood, Ghostlands, Hillsbrad Foothills, Howling Fjord, Nagrand, Orgrimmar, Silverpine Forest, Swamp of Sorrows, Teldrassil, Twilight Highlands, Wetlands', creatureID = 61369, },
	[421] = { description = 'Prefers to dwell in humid, tropical areas and feed on Kingsblood nectar.', sourceText = '|cFFFFD200Pet Battle: |rNorthern Stranglethorn, The Cape of Stranglethorn', creatureID = 61314, },
	[422] = { description = 'When hunting or evading predators, this snake can travel on the surface of water for short distances.', sourceText = '|cFFFFD200Pet Battle: |rSwamp of Sorrows', creatureID = 61372, },
	[423] = { description = 'The thick exoskeleton of this adaptable arthropod allows it to thrive in harsh environments.', sourceText = '|cFFFFD200Pet Battle: |rBurning Steppes, Searing Gorge', creatureID = 61383, },
	[424] = { description = 'A nuisance to most, roaches\' incredible survivability sets them apart from other species.', sourceText = '|cFFFFD200Pet Battle: |rAshenvale, Azshara, Desolace, Dun Morogh, Duskwood, Gilneas, Gilneas City, Howling Fjord, Icecrown Citadel, Kezan, Loch Modan, Northern Stranglethorn, Razorfen Downs, Redridge Mountains, Stonetalon Mountains, The Cape of Stranglethorn, The Culling of Stratholme, Thousand Needles, Tirisfal Glades|n', creatureID = 61169, },
	[425] = { description = 'This ill-tempered snake species makes its home around the fiery molten pits of the Burning Steppes.', sourceText = '|cFFFFD200Pet Battle: |rBurning Steppes, Shadowmoon Valley', creatureID = 61385, },
	[427] = { description = 'The rock-hard carapaces of these spiders allow them to survive in the Searing Gorge\'s blistering heat.', sourceText = '|cFFFFD200Pet Battle: |rSearing Gorge, Talador', creatureID = 61420, },
	[428] = { description = 'These blisteringly hot spiders camouflage themselves by blending in with oozing magma.', sourceText = '|cFFFFD200Pet Battle: |rSearing Gorge', creatureID = 61425, },
	[429] = { description = 'Beetles in the Burning Steppes roll magma into tiny balls and eat them once they cool off.', sourceText = '|cFFFFD200Pet Battle: |rBurning Steppes', creatureID = 61386, },
	[430] = { description = 'Tiny, autonomous machines created by the Titans to clean and maintain their mighty edifices.', sourceText = '|cFFFFD200Pet Battle: |rBadlands, Gorgrond, Tanaris', creatureID = 61438, },
	[431] = { description = 'Wildhammer dwarves train young gryphons by sending them out to hunt rattlesnakes in the Twilight Highlands.', sourceText = '|cFFFFD200Pet Battle: |rBadlands, Tanaris, Twilight Highlands', creatureID = 61439, },
	[432] = { description = 'Less venomous but more aggressive than other breeds of scorpion.', sourceText = '|cFFFFD200Pet Battle: |rBadlands, Tanaris, Terokkar Forest', creatureID = 61440, },
	[433] = { description = 'The coloration of the spiky lizard allows it to blend in seamlessly with the desert sands of Silithus.', sourceText = '|cFFFFD200Pet Battle: |rBadlands, Silithus', creatureID = 61441, },
	[434] = { description = 'When a ram lowers his head, it means he\'s preparing to charge. Or he could just be tired.', sourceText = '|cFFFFD200Pet Battle: |rArathi Highlands, Hillsbrad Foothills, Loch Modan, Ruins of Gilneas, Wetlands', creatureID = 61456, },
	[437] = { description = 'These rams hone their fighting skills by head-butting twigs and saplings near Loch Modan.', sourceText = '|cFFFFD200Pet Battle: |rLoch Modan', creatureID = 61459, },
	[438] = { description = 'This snake is often seen sunning on the exposed rocks high in the Badlands.', sourceText = '|cFFFFD200Pet Battle: |rBadlands', creatureID = 61443, },
	[439] = { description = 'These creatures are silent, save for the occasional sigh of otherworldly despair.', sourceText = '|cFFFFD200Pet Battle: |rDeadwind Pass|n|cFFFFD200Time: |rEarly Morning', creatureID = 61375, },
	[440] = { description = 'The thick snow near Dun Morogh acts as camouflage for snow cubs learning to hunt.', sourceText = '|cFFFFD200Pet Battle: |rDun Morogh', creatureID = 61689, },
	[441] = { description = 'The alpine hare can burrow into the permafrost and hibernate for weeks or even months.', sourceText = '|cFFFFD200Pet Battle: |rDun Morogh, Winterspring', creatureID = 61690, },
	[442] = { description = 'Most creatures cannot withstand a fraction of the harsh conditions that roaches can thrive in.', sourceText = '|cFFFFD200Pet Battle: |rDun Morogh', creatureID = 61691, },
	[443] = { description = 'Despite their idyllic surroundings, grasslands cottontails hop at surprising speed when evading their pursuers.', sourceText = '|cFFFFD200Pet Battle: |rArathi Highlands', creatureID = 61704, },
	[444] = { description = '', sourceText = '', creatureID = 61708, },
	[445] = { description = 'Twisters delight in sneaking up on unsuspecting creatures and surprising them with gusts up their nostrils.', sourceText = '|cFFFFD200Pet Battle: |rArathi Highlands', creatureID = 61703, },
	[446] = { description = 'Jade Oozelings glow in the dark, making them a welcome sight to workers in pitch-black mineshafts.', sourceText = '|cFFFFD200Pet Battle: |rThe Hinterlands', creatureID = 61718, },
	[447] = { description = 'Pay attention to deer crossings. Even a fawn will total a goblin trike.', sourceText = '|cFFFFD200Pet Battle: |rElwynn Forest, Teldrassil', creatureID = 61165, },
	[448] = { description = 'A carrot, a box, a stick, and some string. Wait a few hours and you\'ll have a hare.', sourceText = '|cFFFFD200Pet Battle: |rArathi Highlands, Durotar, The Hinterlands', creatureID = 61751, },
	[449] = { description = 'Distinct from their Azerothian relatives, these critters of Outland make their home in Blade\'s Edge Mountains.', sourceText = '|cFFFFD200Pet Battle: |rBlade\'s Edge Mountains, Gorgrond, The Hinterlands', creatureID = 61752, },
	[450] = { description = 'The preferred habitat of this creature is woodland areas where it can safely complete its metamorphosis.', sourceText = '|cFFFFD200Pet Battle: |rAshenvale, Ghostlands, Hillsbrad Foothills, Howling Fjord, The Hinterlands, Tirisfal Glades, Tanaan Jungle', creatureID = 61753, },
	[452] = { description = 'According to the tales of the night elves, the first red-tailed chipmunk was colored so after the theft and consumption of a sacred apple.', sourceText = '|cFFFFD200Pet Battle: |rDarnassus, Desolace, Hillsbrad Foothills, Talador, Teldrassil', creatureID = 61757, },
	[453] = { description = 'Infested cubs still listen for the telltale buzzing of bees even though they can no longer taste honey.', sourceText = '|cFFFFD200Pet Battle: |rHillsbrad Foothills', creatureID = 61758, },
	[454] = { description = 'Even the rats of Lordaeron fell to the apothecaries\' plague, chewing upon the tainted flesh of the dead.', sourceText = '|cFFFFD200Pet Battle: |rTirisfal Glades', creatureID = 61889, },
	[455] = { description = 'Undead squirrels have acquired a taste for the strange berries and mushrooms that now grow in Silverpine Forest.', sourceText = '|cFFFFD200Pet Battle: |rSilverpine Forest', creatureID = 61890, },
	[456] = { description = 'Curiously, this hawk uses its razor-sharp talons to peel the bark off trees as it searches for bugs, even though it has no need for sustenance.', sourceText = '|cFFFFD200Pet Battle: |rWestern Plaguelands', creatureID = 61826, },
	[457] = { description = 'It can swallow three times its own body weight in blood.', sourceText = '|cFFFFD200Pet Battle: |rEastern Plaguelands', creatureID = 61830, },
	[458] = { description = 'These small spirits find their undead neighbors to be poor company, so they typically wander Tirisfal Glades alone.', sourceText = '|cFFFFD200Pet Battle: |rTirisfal Glades', creatureID = 61905, },
	[459] = { description = 'After centuries of domestication, these furry critters inhabit nearly every human nation in the Eastern Kingdoms.', sourceText = '|cFFFFD200Pet Battle: |rArathi Highlands, Elwynn Forest, Eversong Woods, Netherstorm, Silvermoon City', creatureID = 62019, },
	[460] = { description = 'Small elemental saplings grow into treants, fierce defenders of forests and groves.', sourceText = '|cFFFFD200Pet Battle: |rEversong Woods', creatureID = 62020, },
	[461] = { description = 'This new arrival to Azeroth was spawned a short time ago. It seeks to thrive while awaiting its next phase of evolution.', sourceText = '|cFFFFD200Pet Battle: |rGhostlands', creatureID = 62022, },
	[462] = { description = '[DEBUG] This pet comes from the magical Test Land where it frolics all day in fields of structs.', sourceText = '|cFFFFD200Vendor: |rQuartermaster Brazie|n|cFFFFD200Zone: |rTol Barad Peninsula|n|cFFFFD200Faction: |rBaradin\'s Wardens - Honored', creatureID = 62035, },
	[463] = { description = 'The souls of slain crabs seek vengeance against the murlocs who ate them.', sourceText = '|cFFFFD200Pet Battle: |rGhostlands', creatureID = 62034, },
	[464] = { description = 'You can easily locate moths if you wear expensive garments near Ammen Vale. They can\'t resist the delicious taste of high fashion.', sourceText = '|cFFFFD200Pet Battle: |rAzuremyst Isle', creatureID = 62050, },
	[465] = { description = 'These voracious carnivores first arrived on Azeroth aboard the draenei\'s dimensional fortress, the Exodar.', sourceText = '|cFFFFD200Pet Battle: |rBloodmyst Isle', creatureID = 62051, },
	[466] = { description = 'The children of Orgrimmar frequently capture these lizards as pets.  The adults of Orgrimmar frequently step on them.', sourceText = '|cFFFFD200Pet Battle: |rDurotar, Orgrimmar', creatureID = 62114, },
	[467] = { description = 'A desert dwelling forager attracted to areas rich with dung.', sourceText = '|cFFFFD200Pet Battle: |rDurotar, Orgrimmar, Uldum', creatureID = 62115, },
	[468] = { description = 'These beetles often make their homes in quarries abandoned by the Horde war machine in Durotar.', sourceText = '|cFFFFD200Pet Battle: |rDurotar', creatureID = 62116, },
	[469] = { description = 'An enigmatic species, said to feed off the chaotic energies of the Old Gods.', sourceText = '|cFFFFD200Pet Battle: |rAzshara, Deepholm, Mount Hyjal', creatureID = 62118, },
	[470] = { description = 'Twisted by the Twilight Hammer\'s dark magic, these sinister critters are known to feed on their own young.', sourceText = '|cFFFFD200Pet Battle: |rAzshara, Deepholm, Twilight Highlands', creatureID = 62117, },
	[471] = { description = 'Originally built by an irate goblin to annoy neighbors with an early morning wake-up call.', sourceText = '|cFFFFD200Pet Battle: |rAzshara, Orgrimmar, Winterspring|n', creatureID = 62119, },
	[472] = { description = 'Considered by most to be a significant improvement to the 4000 series, there were nonetheless serious concerns about the amount of heat they produced.', sourceText = '|cFFFFD200Pet Battle: |rAzshara, Stonetalon Mountains, Winterspring|n', creatureID = 62120, },
	[473] = { description = 'Turtles are feared by the naga, who consider them bad luck and give them a wide berth on the shores of Azshara.', sourceText = '|cFFFFD200Pet Battle: |rAzshara', creatureID = 62121, },
	[474] = { description = 'These playful feline cubs practice their hunting skills by stalking each other through the Barrens.', sourceText = '|cFFFFD200Pet Battle: |rNorthern Barrens', creatureID = 62129, },
	[475] = { description = 'The barbaric centaur over-hunted giraffes for decades, but it was the tauren who saved this species from extinction.', sourceText = '|cFFFFD200Pet Battle: |rSouthern Barrens', creatureID = 62130, },
	[476] = { description = 'Most people only see gazelles from the rear.', sourceText = '|cFFFFD200Pet Battle: |rDesolace, Mulgore, Northern Barrens, Southern Barrens', creatureID = 62163, },
	[477] = { description = 'Young gazelles quickly learn to flee when they hear the war cries of tauren hunters in Mulgore.  They don\'t hear the good hunters, unfortunately.', sourceText = '|cFFFFD200Pet Battle: |rMulgore', creatureID = 62176, },
	[478] = { description = 'Prefers to dwell in cool, forested areas and feed on Mageroyal nectar.', sourceText = '|cFFFFD200Pet Battle: |rAshenvale, Darnassus, Desolace, Moonglade, Mount Hyjal, Teldrassil', creatureID = 62177, },
	[479] = { description = 'Their proximity to the magic of the elves has caused some of them unnatural long life. And in some elfin rabbits...stranger things.', sourceText = '|cFFFFD200Pet Battle: |rDarnassus, Desolace, Mount Hyjal, Teldrassil', creatureID = 62178, },
	[480] = { description = 'Prized by gem crafters, the undamaged carapace of a topaz hatchling can be used to create stunning inlays for rings and bracelets.', sourceText = '|cFFFFD200Pet Battle: |rDeepholm, Desolace|n', creatureID = 62181, },
	[482] = { description = 'Long ago, Mount Hyjal\'s druids tempered this species to live in peace alongside the region\'s other creatures.', sourceText = '|cFFFFD200Pet Battle: |rBlade\'s Edge Mountains, Desolace, Mount Hyjal, Silithus', creatureID = 62184, },
	[483] = { description = 'There are many scholars who question whether it is a toad or not.', sourceText = '|cFFFFD200Pet Battle: |rDesolace, Tanaan Jungle', creatureID = 62185, },
	[484] = { description = 'This cunning species tunnels beneath Uldum and erupts from the ground to ensnare its prey.', sourceText = '|cFFFFD200Pet Battle: |rDesolace, Silithus, Tanaris, Uldum', creatureID = 62186, },
	[485] = { description = 'When these sleepy critters aren\'t snoozing, they use their massive claws to dig for tasty insect snacks.', sourceText = '|cFFFFD200Pet Battle: |rDesolace|n|cFFFFD200Time: |rNight|n', creatureID = 62187, },
	[486] = { description = 'It\'s called a mule because of its big ears. Also been known to transport black market bloodthistle over the border to Quel\'Thalas.', sourceText = '|cFFFFD200Pet Battle: |rStonetalon Mountains', creatureID = 62188, },
	[487] = { description = 'Many an explorer has been entranced by the adorable nature of the alpine chipmunk, failing to notice its small teeth on frostbitten digits.', sourceText = '|cFFFFD200Pet Battle: |rHighmountain, Mount Hyjal, Stonetalon Mountains, Winterspring, The Azure Span|n', creatureID = 62189, },
	[488] = { description = 'Brave assassins sometimes capture these critters to use their highly toxic venom to poison weapons.', sourceText = '|cFFFFD200Pet Battle: |rStonetalon Mountains', creatureID = 62190, },
	[489] = { description = 'These vicious fire-breathing whelps escaped Onyxia\'s lair and fled into the surrounding region. Handle with extreme caution.', sourceText = '|cFFFFD200Pet Battle: |rDustwallow Marsh', creatureID = 62201, },
	[491] = { description = 'Originally bred by the Sandfury trolls as pets, these feral and savage felines prowl the deserts of Tanaris.', sourceText = '|cFFFFD200Pet Battle: |rTanaris', creatureID = 62257, },
	[492] = { description = 'A desert-dwelling insect which defends its eggs by spraying a foul, ogrelike scent.', sourceText = '|cFFFFD200Pet Battle: |rTanaris', creatureID = 62256, },
	[493] = { description = 'Hunts for food just below the water\'s surface on the coast of Darkshore.', sourceText = '|cFFFFD200Pet Battle: |rDarkshore', creatureID = 62246, },
	[494] = { description = 'These hatchlings entertain themselves by clacking their mandibles together in increasingly complex rhythms.', sourceText = '|cFFFFD200Pet Battle: |rTanaris|n|cFFFFD200Weather: |rSandstorm', creatureID = 62258, },
	[495] = { description = 'For every frog, a prince and princess in waiting.', sourceText = '|cFFFFD200Pet Battle: |rAshenvale', creatureID = 62312, },
	[496] = { description = 'Don\'t let their lack of a face fool you--these slimy critters are angry and just itching for a fight.', sourceText = '|cFFFFD200Pet Battle: |rAshenvale', creatureID = 62313, },
	[497] = { description = 'Even cockroaches were not strong enough to withstand the foul seepage of demonic corruption.', sourceText = '|cFFFFD200Pet Battle: |rFelwood, Shadowmoon Valley', creatureID = 62314, },
	[896] = { description = '', sourceText = '', creatureID = 66331, },
	[498] = { description = 'Prefers to dwell in corrupted woodlands and feed on Purple Lotus nectar.', sourceText = '|cFFFFD200Pet Battle: |rFelwood', creatureID = 62315, },
	[499] = { description = 'There are some things too dirty even for rats.', sourceText = '|cFFFFD200Pet Battle: |rFelwood', creatureID = 62316, },
	[500] = { description = 'These ill-tempered constructs of flame and rage sometimes rise from the remains of larger infernals who fall in battle.', sourceText = '|cFFFFD200Pet Battle: |rFelwood', creatureID = 62317, },
	[502] = { description = 'The dwarven explorer who first discovered the spotted bell frog could not decide between naming it after the unique spot pattern or the sound of the frog\'s mating ritual. She chose both.', sourceText = '|cFFFFD200Pet Battle: |rUn\'goro Crater', creatureID = 62370, },
	[503] = { description = 'Prefers to dwell in the shadow of Nordrassil and feed on Stormvine nectar.', sourceText = '|cFFFFD200Pet Battle: |rMoonglade, Mount Hyjal, Un\'goro Crater', creatureID = 62373, },
	[504] = { description = 'Diemetradons abandon their hatchlings in Un\'goro Crater to fend for themselves. Only the strongest survive.', sourceText = '|cFFFFD200Pet Battle: |rUn\'goro Crater', creatureID = 62375, },
	[505] = { description = 'Although most reptiles enjoy basking in sunlight, these scaly beasts love bathing in the glow of Azeroth\'s twin moons.', sourceText = '|cFFFFD200Pet Battle: |rThousand Needles', creatureID = 62255, },
	[506] = { description = 'These eight-legged critters have lurked in the shadowy cliffs of the Stonetalon Mountains for millennia.', sourceText = '|cFFFFD200Pet Battle: |rStonetalon Mountains', creatureID = 62191, },
	[507] = { description = 'Many owls nest high in the stone structures of Darnassus, hunting any pests that enter the night elves\' city.', sourceText = '|cFFFFD200Pet Battle: |rTeldrassil', creatureID = 62242, },
	[508] = { description = 'The fur of the darkshore bears owes to the almost perpetual gloom they live in, and their vision in the darkness is uncanny.', sourceText = '|cFFFFD200Pet Battle: |rDarkshore', creatureID = 62250, },
	[509] = { description = 'These small elementals cling to the backs of larger mire beasts, picking off tasty bugs and mites.', sourceText = '|cFFFFD200Pet Battle: |rWetlands', creatureID = 61686, },
	[510] = { description = 'Many a young gnomish inventor has made up for the death of a beloved pet by creating a mechanical friend in its image.', sourceText = '|cFFFFD200Pet Battle: |rKezan, Winterspring', creatureID = 62434, },
	[511] = { description = 'Native to Azeroth\'s desert regions, these resilient reptiles can survive for weeks without water.', sourceText = '|cFFFFD200Pet Battle: |rSilithus, Uldum', creatureID = 62523, },
	[512] = { description = 'Spawn of the nefarious Shrieker Scarabs of Ahn\'Qiraj, the hatchlings are much more docile (and quiet) than their parents.', sourceText = '|cFFFFD200Pet Battle: |rSilithus', creatureID = 62524, },
	[513] = { description = 'The squeaks used by these tiny insectoids may sound cute, but they\'re actually screaming for your death.', sourceText = '|cFFFFD200Pet Battle: |rSilithus|n|cFFFFD200Season: |rSummer', creatureID = 62526, },
	[514] = { description = 'A brash fel orc once tried to enslave these critters as his personal soldiers. Pieces of him are still scattered across Outland.', sourceText = '|cFFFFD200Pet Battle: |rHellfire Peninsula', creatureID = 62555, },
	[515] = { description = 'These pudgy little beings often get themselves into trouble while hunting down their favorite snack: glow caps.', sourceText = '|cFFFFD200Pet Battle: |rZangarmarsh', creatureID = 62564, },
	[517] = { description = 'After hatching, these twisted reptiles devour the weaker members of their clutches as an act of dominance.', sourceText = '|cFFFFD200Pet Battle: |rTerokkar Forest', creatureID = 62583, },
	[518] = { description = 'For ogres, comparing a female\'s hair to a clefthoof\'s shaggy coat is a term of great endearment.', sourceText = '|cFFFFD200Pet Battle: |rNagrand', creatureID = 62620, },
	[519] = { description = 'Born from demonic magic, these petulant little entities corrupt everything they touch with foul energies.', sourceText = '|cFFFFD200Pet Battle: |rShadowmoon Valley, Tanaan Jungle', creatureID = 62621, },
	[521] = { description = 'Adult nether rays are relatively docile, but their younglings are short-tempered and highly energetic beasts.', sourceText = '|cFFFFD200Pet Battle: |rNetherstorm', creatureID = 62627, },
	[523] = { description = 'Howling Fjord teems with the ever shifting cycle of life. When a creature dies, its body is quickly consumed by maggots, which in turn pass those nutrients on to a number of other creatures.', sourceText = '|cFFFFD200Pet Battle: |rHowling Fjord', creatureID = 62640, },
	[525] = { description = 'A key food source during the Feast of Winter Veil.', sourceText = '|cFFFFD200Pet Battle: |rHowling Fjord', creatureID = 62648, },
	[528] = { description = 'This grumpy species of basilisk roams the Blade\'s Edge Mountains, avoiding contact with other critters whenever possible.', sourceText = '|cFFFFD200Pet Battle: |rBlade\'s Edge Mountains', creatureID = 62628, },
	[529] = { description = 'There\'s an old vrykul saying that goes, \'Let sleeping worgs lie, unless you don\'t want your face anymore.\'', sourceText = '|cFFFFD200Pet Battle: |rHowling Fjord', creatureID = 62669, },
	[530] = { description = 'These living blobs of ooze lack even the most rudimentary form of intelligence, but that doesn\'t mean they can\'t be your friend.', sourceText = '|cFFFFD200Pet Battle: |rBorean Tundra', creatureID = 62697, },
	[532] = { description = 'The fierce wolvar hunt these ill-tempered brutes for their horns, which are then carved into talismans and other trinkets.', sourceText = '|cFFFFD200Pet Battle: |rSholazar Basin', creatureID = 62816, },
	[534] = { description = 'The taunka collect the feathers of these noble birds of prey to use in crude fortunetelling ceremonies.', sourceText = '|cFFFFD200Pet Battle: |rGrizzly Hills', creatureID = 62819, },
	[535] = { description = 'The trolls of Zul\'Drak inadvertently created these little beings while summoning water elementals to fight the Scourge.', sourceText = '|cFFFFD200Pet Battle: |rZul\'Drak', creatureID = 62820, },
	[536] = { description = 'Tundra penguins often gather in large groups on the ice for protection against aquatic threats. Unfortunately this makes them easy prey for humanoid hunters seeking meat, skins and other achievements.', sourceText = '|cFFFFD200Pet Battle: |rBorean Tundra, Dragonblight', creatureID = 62835, },
	[897] = { description = '', sourceText = '', creatureID = 66333, },
	[537] = { description = 'These skulking little creatures hatch in nests built around the ancient dragon bones of central Northrend.', sourceText = '|cFFFFD200Pet Battle: |rDragonblight', creatureID = 62852, },
	[538] = { description = 'Kel\'Thuzad once nursed a plague-infected whelp to adulthood, feeding it only the finest liquefied remains.', sourceText = '|cFFFFD200Pet Battle: |rIcecrown', creatureID = 62854, },
	[539] = { description = 'Grotto voles are often confused with mice. Never twice.', sourceText = '|cFFFFD200Pet Battle: |rMount Hyjal', creatureID = 62884, },
	[540] = { description = 'Where there is carrion, there will always be rats.', sourceText = '|cFFFFD200Pet Battle: |rMount Hyjal', creatureID = 62885, },
	[541] = { description = 'The outer member of the roaches\' eggs protect the larva from incineration. It is here in incubation that they gain the ability to resist the fire and heat of the volcanic climate.', sourceText = '|cFFFFD200Pet Battle: |rFrostfire Ridge, Mount Hyjal', creatureID = 62886, },
	[542] = { description = 'Flamboyantly colored and more exuberant than most frogs.', sourceText = '|cFFFFD200Pet Battle: |rUldum', creatureID = 62892, },
	[543] = { description = 'When the locusts swarm, you can hear nothing but the sound of their wings.', sourceText = '|cFFFFD200Pet Battle: |rUldum', creatureID = 62893, },
	[544] = { description = 'Prefers to dwell along desert streams, wells or watering holes and feed on Whiptail nectar.', sourceText = '|cFFFFD200Pet Battle: |rUldum', creatureID = 62895, },
	[545] = { description = 'Loves to rest inside footwear of unsuspecting travelers during the night.', sourceText = '|cFFFFD200Pet Battle: |rUldum', creatureID = 62896, },
	[546] = { description = 'Carrion feeders often found in large numbers within ancient unearthed tombs; they can survive long periods of time without feeding.', sourceText = '|cFFFFD200Pet Battle: |rUldum', creatureID = 62899, },
	[547] = { description = 'During the Third War, thousands of these mystical beings annihilated the demon lord Archimonde.', sourceText = '|cFFFFD200Pet Battle: |rMount Hyjal', creatureID = 62888, },
	[548] = { description = 'Too afraid to attempt flying until thrown from a great height.', sourceText = '|cFFFFD200Pet Battle: |rTwilight Highlands', creatureID = 62900, },
	[549] = { description = 'Distinct from their plain dwelling relatives, these critters make their homes amidst the war-torn Twilight Highlands.', sourceText = '|cFFFFD200Pet Battle: |rTwilight Highlands', creatureID = 62904, },
	[550] = { description = 'The mice of the highlands, perhaps twisted by the Twilight\'s Hammer, are aggressive and fight for dominance between each other.', sourceText = '|cFFFFD200Pet Battle: |rStormheim, The Azure Span, The Cape of Stranglethorn, Twilight Highlands', creatureID = 62905, },
	[552] = { description = 'Demonstrating that a little evil goes a long way, this fiendling uses its razor-sharp teeth and gaping maw to devour anything in its path.', sourceText = '|cFFFFD200Pet Battle: |rTwilight Highlands', creatureID = 62914, },
	[553] = { description = 'In the lean times when adrift at sea, the sight of a rat was a welcome one.', sourceText = '|cFFFFD200Pet Battle: |rDeepholm', creatureID = 62921, },
	[554] = { description = 'Through experimentation, the mages of Dalaran have discovered that the powered remains of a crimson shale spider will greatly increase the conflagration of their fire spells.', sourceText = '|cFFFFD200Pet Battle: |rDeepholm', creatureID = 62922, },
	[555] = { description = 'The cockroaches of Deepholm can burrow into stone itself to make their nests.', sourceText = '|cFFFFD200Pet Battle: |rDeepholm', creatureID = 62924, },
	[556] = { description = 'A cousin to the Twilight Beetle, this insect has learned to feed on crystals which resonate with ancient energies.', sourceText = '|cFFFFD200Pet Battle: |rDeepholm', creatureID = 62925, },
	[557] = { description = 'Much like other faerie dragons, these playful and mystical flyers are born with a natural defense against magic.', sourceText = '|cFFFFD200Pet Battle: |rFeralas', creatureID = 62395, },
	[558] = { description = 'During frigid nights in the Storm Peaks, these critters use their fluffy tails as a blanket to stay warm.', sourceText = '|cFFFFD200Pet Battle: |rThe Storm Peaks|n|cFFFFD200Weather: |rSnow', creatureID = 62864, },
	[559] = { description = 'Therazane wore these spunky crimson elementals as jewelry before the azure craze swept through Deepholm.', sourceText = '|cFFFFD200Pet Battle: |rDeepholm', creatureID = 62927, },
	[560] = { description = 'The seagull is a coastal scavenger often found near the docks.', sourceText = '|cFFFFD200Pet Battle: |rFrostwall, Krasarang Wilds, Talador, Tanaris', creatureID = 62953, },
	[562] = { description = 'The coral adder\'s venom is so deadly that one bite has been known to kill a full grown kodo beast.', sourceText = '|cFFFFD200Pet Battle:|r The Jade Forest|n', creatureID = 62991, },
	[564] = { description = 'It is difficult to believe that Shen-zin Su, on whose shell rests all of the Wandering Isle, began life as a tiny emerald turtle.', sourceText = '|cFFFFD200Pet Battle: |rThe Jade Forest', creatureID = 62994, },
	[565] = { description = 'In the jungle, every leaf could conceal a frog. Stop to check and it could be long gone.', sourceText = '|cFFFFD200Pet Battle: |rThe Jade Forest', creatureID = 62997, },
	[566] = { description = 'When preparing to travel to distant locations, Pandaren will traditionally capture a mirror strider and safely relocate it to another body of water.', sourceText = '|cFFFFD200Pet Battle: |rThe Jade Forest', creatureID = 62998, },
	[567] = { description = 'The Jade Forest\'s pandaren consider these reptiles distant relatives of the revered Jade Serpent, Yu\'lon.', sourceText = '|cFFFFD200Pet Battle: |rThe Jade Forest', creatureID = 62999, },
	[568] = { description = 'Hides in shell to escape claws of nearby crabs.', sourceText = '|cFFFFD200Pet Battle: |rGorgrond, Spires of Arak, Talador, The Jade Forest', creatureID = 63001, },
	[569] = { description = 'One of the stranger nuisances to horticulturalists, garden frogs can cause untold damage to gardens.', sourceText = '|cFFFFD200Pet Battle: |rAzsuna, Highmountain, The Jade Forest', creatureID = 63002, },
	[570] = { description = 'There\'s a raging debate over whether tanukis are raccoons or dogs, but the distinction seems trivial when they\'re gnawing on your femur.', sourceText = '|cFFFFD200Pet Battle: |rThe Jade Forest', creatureID = 63003, },
	[898] = { description = '', sourceText = '', creatureID = 66334, },
	[571] = { description = 'The Jade Forest\'s mountain hozen fear these venomous reptiles and often hunt them out of pure spite.', sourceText = '|cFFFFD200Pet Battle: |rThe Jade Forest', creatureID = 63004, },
	[572] = { description = 'These tiny arthropods are Pandaria natives that call the area of the Windless Isle their home.', sourceText = '|cFFFFD200Pet Battle: |rThe Jade Forest', creatureID = 63005, },
	[573] = { description = 'The sandy petrel flies high above the Windless Isle, safe from landbound predators.', sourceText = '|cFFFFD200Pet Battle: |rThe Jade Forest', creatureID = 63006, },
	[626] = { description = 'This foreboding creature stalks the night skies of the Eastern Plaguelands, feeding on carrion.', sourceText = '|cFFFFD200Pet Battle: |rEastern Plaguelands, Mount Hyjal, Tirisfal Glades', creatureID = 61829, },
	[627] = { description = 'More horrifying than its appearance is that the infected squirrels of the Plaguelands no longer hoard nuts, instead collecting rotten, decaying flesh.', sourceText = '|cFFFFD200Pet Battle: |rBloodmyst Isle, Eastern Plaguelands, Silverpine Forest', creatureID = 61828, },
	[628] = { description = 'Only the dimwitted or insane would mount an infected deer head on their wall. The good news is they\'ll fit right in with their new undead friends.', sourceText = '|cFFFFD200Pet Battle: |rBloodmyst Isle, Eastern Plaguelands, Silverpine Forest', creatureID = 61827, },
	[629] = { description = 'Only found on the shores of Kezan, this lovely sea creature can be a bit snarky.', sourceText = '|cFFFFD200Vendor: |rMatty|n|cFFFFD200Zone: |rOrgrimmar', creatureID = 63097, },
	[630] = { description = 'Quoth the raven: the last word you said. It gets old.', sourceText = '|cFFFFD200Vendor:|r Will Larsons|n|cFFFFD200Zone:|r Darkshore', creatureID = 63098, },
	[631] = { description = 'Hunted for its beautifully patterned skin, this majestic species thrives alongside Azeroth\'s lush rivers.', sourceText = '|cFFFFD200Pet Battle: |rNorthern Barrens, Southern Barrens, Uldum, Un\'goro Crater', creatureID = 62127, },
	[632] = { description = 'The ash lizard gets its name from the way its shed skin looks, with an appearance like burnt cinders.', sourceText = '|cFFFFD200Pet Battle: |rMount Hyjal, Un\'goro Crater', creatureID = 62364, },
	[633] = { description = 'The mountain skunk is most famous for its adventurous nature. No really, that is what it is famous for.', sourceText = '|cFFFFD200Pet Battle: |rGrizzly Hills, Stonetalon Mountains, The Storm Peaks, Wetlands, Winterspring', creatureID = 61677, },
	[634] = { description = 'These strange critters glimmer like ice, allowing them to hide among Winterspring\'s snowy expanses.', sourceText = '|cFFFFD200Pet Battle: |rWinterspring', creatureID = 62435, },
	[635] = { description = 'Unlike other snakes that actively hunt, these crafty reptiles hide in thick foliage and ambush their prey.', sourceText = '|cFFFFD200Pet Battle: |rBlasted Lands, Durotar, Hellfire Peninsula, Nagrand, Northern Barrens, Northern Stranglethorn, Southern Barrens, The Waking Shores, The Azure Span|n', creatureID = 61325, },
	[637] = { description = 'These arachnids use their razor-sharp legs to carve tunnels in the solid rock of the Blade\'s Edge Mountains.', sourceText = '|cFFFFD200Pet Battle: |rBlade\'s Edge Mountains', creatureID = 62638, },
	[638] = { description = 'The nether roach has adapted to the cold vacuum of space and requires no air to survive. Inside it bears almost no resemblance to a common roach.', sourceText = '|cFFFFD200Pet Battle: |rNetherstorm', creatureID = 62625, },
	[639] = { description = 'Distinct from their cousins found in warmer climes, these hardy critters make their homes in the icy wastes of Northrend.', sourceText = '|cFFFFD200Pet Battle: |rBorean Tundra', creatureID = 62695, },
	[640] = { description = 'With the extra large surface area of their legs, snowshoe hares are able to effortlessly hop along the snow while their pursuers get bogged down in the drifts.', sourceText = '|cFFFFD200Pet Battle: |rHillsbrad Foothills', creatureID = 61755, },
	[641] = { description = 'The heart of an arctic hare beats twice as fast as a common hare.', sourceText = '|cFFFFD200Pet Battle:|r Borean Tundra, Dragonblight, The Storm Peaks, Zul\'Drak, The Azure Span|n', creatureID = 62693, },
	[644] = { description = 'If not for being extremely accident prone, the fjord rat might take over the world.', sourceText = '|cFFFFD200Pet Battle: |rHowling Fjord', creatureID = 62641, },
	[645] = { description = 'Capable of flying, unlike domesticated types.', sourceText = '|cFFFFD200Pet Battle: |rTwilight Highlands', creatureID = 62906, },
	[646] = { description = 'Tends to avoid crossing roads, regardless of motivations.', sourceText = '|cFFFFD200Pet Battle: |rDuskwood, Dustwallow Marsh, Elwynn Forest, Hillsbrad Foothills, Howling Fjord, Redridge Mountains, Terokkar Forest, Tirisfal Glades, Westfall, Wetlands|n', creatureID = 62664, },
	[647] = { description = 'In the deep forest, even the small creatures must have the ferocity to fend for themselves.', sourceText = '|cFFFFD200Pet Battle: |rGrizzly Hills, Twilight Highlands', creatureID = 62818, },
	[648] = { description = 'This is a huge toad.', sourceText = '|cFFFFD200Pet Battle: |rHillsbrad Foothills, Swamp of Sorrows, Twilight Highlands, Zul\'Drak', creatureID = 61368, },
	[649] = { description = 'The rumors of the medicinal uses of the biletoad were believed to have been started by a particularly mischievous apothecary.', sourceText = '|cFFFFD200Pet Battle: |rSholazar Basin|n', creatureID = 62815, },
	[650] = { description = 'And you thought turnips at the dinner table were bad.', sourceText = '|cFFFFD200Drop: |rWorld Drop|n|cFFFFD200Zone: |rValley of the Four Winds', creatureID = 63365, },
	[652] = { description = 'This bubble is way worse than a tank.', sourceText = '|cFFFFD200Vendor: |rNat Pagle|n|cFFFFD200Zone: |rKrasarang Wilds|n|cFFFFD200Faction: |rThe Anglers - Honored|n|cFFFFD200Cost: |r250|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t', creatureID = 63559, },
	[665] = { description = 'Easily mistaken for a common beetle, the sting of a sand scarab will quickly clear up the misconception should you be unlucky enough to be stung by one.', sourceText = '|cFFFFD200Trading Card Game: |rTomb of the Forgotten|n', creatureID = 54745, },
	[666] = { description = 'Whenever his forces would experience setbacks in Azeroth, Kil\'jaeden would pull out Micronax and terrorize the local cat-like creatures of Argus to vent his frustrations.', sourceText = '|cFFFFD200Achievement:|r Glory of the Tomb Raider|n', creatureID = 63724, },
	[671] = { description = 'Offspring of the Imperial Quilen.', sourceText = '|cFFFFD200Promotion:|r Mists of Pandaria Collector\'s Edition Pet', creatureID = 63832, },
	[675] = { description = 'Unnoticed but to cats and ratcatchers, when Deathwing descended upon Stormwind all the rats beat a hasty retreat from the city. Only of late have they returned.', sourceText = '|cFFFFD200Pet Battle: |rElwynn Forest, Stormwind City|n', creatureID = 62954, },
	[677] = { description = 'A goblin alchemist once claimed that the bite of this raccoon gave him the gift of prophecy. Turns out, it was just rabies.', sourceText = '|cFFFFD200Pet Battle: |rValley of the Four Winds', creatureID = 64246, },
	[678] = { description = 'These little green grubs are native to wilds of Pandaria. Its verdant coloration camouflages it from would-be predators.', sourceText = '|cFFFFD200Pet Battle: |rKrasarang Wilds', creatureID = 63304, },
	[679] = { description = 'You\'ll often find them perched high atop impossibly high and remote stone outcroppings. There are few creatures as sure of foot.', sourceText = '|cFFFFD200Pet Battle:|r Kun-Lai Summit|n', creatureID = 64248, },
	[680] = { description = 'The mongoose is prized for its ability to drive off poisonous snakes.  Rikk-tikk-tikki-tikki-tchk!', sourceText = '|cFFFFD200Pet Battle:|r Townlong Steppes|n', creatureID = 63953, },
	[699] = { description = 'This terrifying and highly venomous species leaps onto its prey from the Jade Forest\'s dense canopy.', sourceText = '|cFFFFD200Pet Battle:|r The Jade Forest|n', creatureID = 63715, },
	[702] = { description = 'The leopard tree frog was named for the way it pounces from branch to branch.', sourceText = '|cFFFFD200Pet Battle: |rGorgrond, The Jade Forest', creatureID = 63919, },
	[703] = { description = 'Many young raccoons rail against their strict curfews, complaining that dawn is way too early to go to bed.', sourceText = '|cFFFFD200Pet Battle: |rThe Jade Forest', creatureID = 63716, },
	[705] = { description = 'There are few things more annoying for a farmer to find underfoot than a rooster looking for love.', sourceText = '|cFFFFD200Pet Battle:|r Valley of Four Winds', creatureID = 65029, },
	[706] = { description = 'The taciturn bandicoon\'s diet consists mostly of apples.', sourceText = '|cFFFFD200Pet Battle:|r Valley of the Four Winds', creatureID = 63062, },
	[707] = { description = 'Some druids say pandaren and raccoons are distant cousins, but never when a pandaren can hear them.', sourceText = '|cFFFFD200Pet Battle:|r Valley of the Four Winds', creatureID = 63064, },
	[708] = { description = 'Many believe that the quillrat is able to shoot its spines at aggressors.', sourceText = '|cFFFFD200Pet Battle:|r Valley of the Four Winds, Krasarang Wilds', creatureID = 63094, },
	[709] = { description = 'Although the wounds caused by the young quillrat are seldom fatal, most predators seek easier prey.', sourceText = '|cFFFFD200Pet Battle:|r Valley of the Four Winds', creatureID = 63095, },
	[710] = { description = 'Known to feast on the tasty plants in Valley of the Four Winds.', sourceText = '|cFFFFD200Pet Battle: |rValley of the Four Winds', creatureID = 63096, },
	[711] = { description = 'The sifang otter lives near rivers and is normally friendly and playful.', sourceText = '|cFFFFD200Pet Battle:|r Valley of the Four Winds', creatureID = 63057, },
	[712] = { description = 'Otters swim by propelling themselves with their powerful tails and flexing their long bodies. Very fast in the water.', sourceText = '|cFFFFD200Pet Battle:|r Valley of the Four Winds', creatureID = 63358, },
	[713] = { description = 'Softshell turtle meat is considered a delicacy and is the key ingredient in soothing turtle bisque.', sourceText = '|cFFFFD200Pet Battle:|r Valley of the Four Winds', creatureID = 63060, },
	[714] = { description = 'The common folk remedy to a feverbite hatchling\'s bite is to dance wildly and try to sweat out the toxins before they can take hold.', sourceText = '|cFFFFD200Pet Battle:|r Krasarang Wilds|n|n', creatureID = 65054, },
	[715] = { description = 'Prized for their ability to spin cocoons of silk, in the wild, to the untrained eye, they can be difficult to discern.', sourceText = '|cFFFFD200Pet Battle:|r Valley of the Four Winds|n', creatureID = 65099, },
	[716] = { description = 'A rarity among arachnids, these non-venomous critters feed solely on plants and fruit.', sourceText = '|cFFFFD200Pet Battle:|r Krasarang Wilds|n', creatureID = 63288, },
	[717] = { description = 'The most delicious of the beetle family.', sourceText = '|cFFFFD200Pet Battle:|r Krasarang Wilds|n|n', creatureID = 63291, },
	[718] = { description = 'A daring, narcissist insect willing to brave any threat in its pursuit of the spotlight.', sourceText = '|cFFFFD200Pet Battle:|r Krasarang Wilds|n', creatureID = 65124, },
	[722] = { description = 'Feeds on small leaves and grass when full grown.', sourceText = '|cFFFFD200Pet Battle:|r Krasarang Wilds|n', creatureID = 65185, },
	[723] = { description = 'Angering a spiny terrapin will cause it to withdraw into its shell and begin to spin wildly towards an attacker.', sourceText = '|cFFFFD200Pet Battle:|r  Krasarang Wilds', creatureID = 63293, },
	[724] = { description = 'Burrows deep into snowbanks to hide from predators.', sourceText = '|cFFFFD200Pet Battle:|r Kun-Lai Summit|n', creatureID = 63550, },
	[725] = { description = 'Occasionally sneaks bites of food from kills made by much larger animals.', sourceText = '|cFFFFD200Pet Battle:|r Kun-Lai Summit|n', creatureID = 63551, },
	[726] = { description = 'While stationary, the plains monitor will continuously flick its tail back and forth. From a distance this mimics the appearance of swaying tallgrass, camouflaging the lizard from aerial predators.', sourceText = '|cFFFFD200Pet Battle:|r Kun-Lai Summit|n', creatureID = 63547, },
	[727] = { description = 'On the prairie there are numerous rodents and other small creatures, but none more common than the mouse.', sourceText = '|cFFFFD200Pet Battle:|r Kun-Lai Summit|n', creatureID = 59702, },
	[728] = { description = 'Will only lay eggs in bright light.', sourceText = '|cFFFFD200Pet Battle:|r Kun-Lai Summit', creatureID = 63585, },
	[729] = { description = 'In the ancient fables, the first Tolai hare strode across the gulf between the stars before it upset them and they tumbled from the heavens to the world below.', sourceText = '|cFFFFD200Pet Battle:|r Kun-Lai Summit|n', creatureID = 63557, },
	[730] = { description = 'Tolai hare pups are sent from their dens at a young age to find their own way.', sourceText = '|cFFFFD200Pet Battle:|r Kun-Lai Summit|n', creatureID = 63558, },
	[899] = { description = '', sourceText = '', creatureID = 66335, },
	[900] = { description = '', sourceText = '', creatureID = 66375, },
	[731] = { description = 'These reptiles possess hooked scales, allowing them to slither up Kun-Lai Summit\'s rocky slopes and steep cliffs.', sourceText = '|cFFFFD200Pet Battle:|r Kun-Lai Summit|n', creatureID = 63555, },
	[732] = { description = 'Known to flourish in the rainy season, this insect is drawn to lightning storms.', sourceText = '|cFFFFD200Pet Battle:|r Dread Wastes|n|cFFFFD200Pet Battle:|r Townlong Steppes', creatureID = 65187, },
	[733] = { description = 'At night, the grassland hoppers fill the night with their maddening song. Those unaccustomed to it have spent many a sleepless night serenaded by their mating calls.', sourceText = '|cFFFFD200Pet Battle:|r  Townlong Steppes', creatureID = 63549, },
	[737] = { description = 'Contrary to its benign appearance, mongoose are dangerous carnivores, well known for their ability to hunt venomous snakes that could kill much larger creatures.', sourceText = '|cFFFFD200Pet Battle:|r Townlong Steppes|n', creatureID = 65190, },
	[739] = { description = 'Mongoose pups prefer long grass where only their mothers can find them.', sourceText = '|cFFFFD200Pet Battle:|r Townlong Steppes|n', creatureID = 63954, },
	[740] = { description = 'The bond between yak and rat is a strange one, and what the rat gains out of the symbiotic relationship is a mystery.', sourceText = '|cFFFFD200Pet Battle:|r Townlong Steppes, Dread Wastes|n', creatureID = 63957, },
	[741] = { description = 'These hedgehogs may be silent, but often are heard the cries of those that foolishly step upon their quills.', sourceText = '|cFFFFD200Pet Battle:|r Dread Wastes|n', creatureID = 64804, },
	[742] = { description = 'Hedgehogs are not at all fond of croquet nor flamingos.', sourceText = '|cFFFFD200Pet Battle:|r Dread Wastes|n', creatureID = 64242, },
	[743] = { description = 'Has been spotted fighting other whelks for prime food sources.', sourceText = '|cFFFFD200Pet Battle:|r Dread Wastes|n|n', creatureID = 64352, },
	[744] = { description = 'The roaches of the Dread Wastes dwell in the shadows. Some people swear the shadows travel with them.', sourceText = '|cFFFFD200Pet Battle:|r Dread Wastes|n', creatureID = 64238, },
	[745] = { description = 'Unique carapace is prized by trinket makers for its texture and strength.', sourceText = '|cFFFFD200Pet Battle:|r Dread Wastes|n', creatureID = 63548, },
	[746] = { description = 'The emperor crab was named for its regal appearance: the ridges of its shell and its raised claws eerily resembling a crown. One that would pinch your eyes out if you tried to wear it.', sourceText = '|cFFFFD200Pet Battle:|r Dread Wastes|n', creatureID = 65203, },
	[747] = { description = 'Luminescent body used to attract mates.', sourceText = '|cFFFFD200Pet Battle:|r Vale of Eternal Blossoms|n', creatureID = 63850, },
	[748] = { description = 'This delicate insect\'s wings are often sewn into clothing and armor.', sourceText = '|cFFFFD200Pet Battle:|r Vale of Eternal Blossoms|n', creatureID = 63838, },
	[749] = { description = 'Civets are landbound cousins of the otter and are prized for their heady musks.', sourceText = '|cFFFFD200Pet Battle:|r Vale of Eternal Blossoms', creatureID = 63841, },
	[750] = { description = 'The civets are known to guard their young ferociously.', sourceText = '|cFFFFD200Pet Battle:|r Vale of Eternal Blossoms', creatureID = 63842, },
	[751] = { description = 'Named for their apparent dancelike behavior when skimming aquatic surfaces, the species uses erratic movements to avoid being devoured by fish and other underwater predators.', sourceText = '|cFFFFD200Pet Battle:|r Vale of Eternal Blossoms|n', creatureID = 63847, },
	[752] = { description = 'Despite its menacing appearance, the yellow-bellied bullfrog was aptly named.', sourceText = '|cFFFFD200Pet Battle:|r Vale of Eternal Blossoms|n', creatureID = 63849, },
	[753] = { description = 'Known to flourish in the rainy season, this insect is drawn to lightning storms.', sourceText = '|cFFFFD200Pet Battle:|r Jade Forest|n', creatureID = 65215, },
	[754] = { description = 'This species of insect is drawn to the latent energy in shrines, their luminescent appearance adding to the mystique of the places of power.', sourceText = '|cFFFFD200Pet Battle:|r The Jade Forest|n', creatureID = 65216, },
	[755] = { description = 'The markings on the carapace of this cockroach closely resemble that of the Death\'s Head.', sourceText = '|cFFFFD200Pet Battle:|r Mount Hyjal|n', creatureID = 62887, },
	[756] = { description = 'Prefers to dwell in dark caves and feed on Heartblossom nectar.', sourceText = '|cFFFFD200Pet Battle:|r Deepholm|n', creatureID = 62916, },
	[757] = { description = 'Legends say that these vibrant emerald beings bring good fortune and safe travels to their owners.', sourceText = '|cFFFFD200Promotion:|r iCoke China', creatureID = 14755, },
	[758] = { description = 'Said to bring good fortune when spotted in autumn.  Known to hide from unfamiliar creatures.', sourceText = '|cFFFFD200Promotion:|r iCoke China', creatureID = 14756, },
	[792] = { description = 'A graceful creature from the isle of Pandaria.', sourceText = '|cFFFFD200Vendor: |rAudrey Burnhep, Varzok|n|cFFFFD200Zone: |rStormwind, Orgrimmar|n|cFFFFD200Cost: |r50|TINTERFACE/MONEYFRAME/UI-SILVERICON.BLP:0|t', creatureID = 65314, },
	[800] = { description = '', sourceText = '', creatureID = 65344, },
	[802] = { description = 'A baby version of the mighty Thundering Cloud Serpent that hails from the far lands of Pandaria.', sourceText = '', creatureID = 65313, },
