	[818] = { description = 'The offspring of Tsulong, hidden away before the corruption of the Sha had fully taken hold of the Terrace of Endless Spring.', sourceText = '|cFFFFD200Pet Battle:|r The Jade Forest|n|cFFFFD200Faction:|r Order of the Cloud Serpent (Exalted)', creatureID = 65324, },
	[819] = { description = 'It\'s red, so it must fly faster.', sourceText = '|cFFFFD200Pet Battle:|r The Jade Forest|n|cFFFFD200Faction:|r Order of the Cloud Serpent (Exalted)|n', creatureID = 65321, },
