	[834] = { description = 'Mogu sorcerers, keepers of the secret to bringing stone to life, prefer these stolid companions over more beastly familiars.', sourceText = '|cFFFFD200Drop:|r Karr the Darkener|n|cFFFFD200Zone:|r Dread Wastes|n|n', creatureID = 64634, },
	[835] = { description = 'The great big world can be a dangerous place for such a tiny, adorable creature.', sourceText = '|cFFFFD200Achievement: |rLing-Ting\'s Herbal Journey|n|cFFFFD200Category: |rDungeons and Raids', creatureID = 64632, },
	[836] = { description = 'Water striders serve an important role in Jinyu culture, acting as a mount, means of sending communications, and even a family pet.', sourceText = '|cFFFFD200Drop:|r Nalash Verdantis|n|cFFFFD200Zone:|r Dread Wastes|n|n', creatureID = 64633, },
	[837] = { description = 'Adventurers have reported rare sightings of a monstrous sized emerald shale spider, which the denizens of Deepholm have come to call the Jadefang.', sourceText = '|cFFFFD200Pet Battle: |rDeepholm', creatureID = 62915, },
	[838] = { description = 'Adult amethyst shale spiders prefer to create their nests against the outer walls of Deepholm.', sourceText = '|cFFFFD200Pet Battle: |rDeepholm, Desolace', creatureID = 62182, },
	[844] = { description = 'Constructed from a seemingly impossible mass of gears, springs, wires, and a little something special, this dragonling embodies the spirit of Pandaria as seen through an engineer\'s goggles.', sourceText = '|cFFFFD200Profession: |rMists of Pandaria Engineering', creatureID = 64899, },
	[845] = { description = 'Jade is naturally rich with life and color, making it the perfect raw material for this gentle companion.  It is said the wind created by their wings can soothe the troubled mind.', sourceText = '|cFFFFD200Profession:|r Mists of Pandaria Jewelcrafting|n', creatureID = 61877, },
	[846] = { description = 'While each cub is crafted in the same fashion, tiny imperfections in the gemstones create a unique personality when imbued with life.  They are, however, always playful and friendly.', sourceText = '|cFFFFD200Profession:|r Mists of Pandaria Jewelcrafting|n', creatureID = 61883, },
	[847] = { description = 'Many believe (incorrectly) that goldfish have no memories. Trainers who have worked with them know this is clearly not the case and have the bruises to prove it.', sourceText = '|cFFFFD200Quest: |rLet Them Burn|n|cFFFFD200Zone: |rThe Jade Forest', creatureID = 62829, },
	[848] = { description = 'This rabbit is much stronger than it looks.', sourceText = '|cFFFFD200Drop: |rDarkmoon Rabbit|n|cFFFFD200Zone: |rDarkmoon Island', creatureID = 59358, },
	[849] = { description = 'A beautiful kite brought to life by the scribes of Pandaria.', sourceText = '|cFFFFD200Profession: |rMists of Pandaria Inscription', creatureID = 66104, },
	[850] = { description = 'Who knew scribes could make kites?', sourceText = '|cFFFFD200Profession: |rMists of Pandaria Inscription', creatureID = 66105, },
	[851] = { description = 'Not only is it quick, it\'s loud too!', sourceText = '|cFFFFD200Pet Battle: |rUldum', creatureID = 62894, },
