	[1126] = { description = 'The elements of Pandaria can be cute and playful, until their homeland is threatened.', sourceText = '|cFFFFD200Quest: |rPandaren Spirit Tamer|n|cFFFFD200Zone: |rVale of Eternal Blossoms|n', creatureID = 68468, },
