	[1142] = { description = 'Created for one purpose and one purpose alone: to punch little critters, as hard as it possibly can.', sourceText = '|cFFFFD200Vendor: |rQuackenbush|n|cFFFFD200Zone: |rDeeprun Tram|n|cFFFFD200Cost: |r500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|cFFFFD200Reputation: |rBizmo\'s Brawlpub, Rank 3|n|n|cFFFFD200Vendor: |rPaul North|n|cFFFFD200Zone: |rBrawl\'gar Arena|n|cFFFFD200Cost: |r500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|cFFFFD200Reputation: |rBrawl\'gar Arena, Rank 3', creatureID = 68601, },
	[1143] = { description = 'Found in a dusty egg clutch at the back of Maexxna\'s lair, these creatures are already undead when they hatch.', sourceText = '|cFFFFD200Drop: |rMaexxna|n|cFFFFD200Zone: |rNaxxramas', creatureID = 68656, },
	[1144] = { description = 'Loatheb\'s body is covered in small pods capable of spreading blighted fungal spores up to 1000 yards given proper wind conditions.', sourceText = '|cFFFFD200Drop: |rLoatheb|n|cFFFFD200Zone: |rNaxxramas', creatureID = 68657, },
	[1145] = { description = 'Upon exiting the freezing necropolis of Naxxramas, Mr. Bigglesworth promptly found a warm spot in the sun to take a long nap in.', sourceText = '|cFFFFD200Achievement: |rRaiding with Leashes|n|cFFFFD200Category: |rCollect', creatureID = 68655, },
	[1146] = { description = 'When Gluth died to a band of brave adventurers, these Lil\' guys were found in the corpse, gnawing on giant, undead bones.', sourceText = '|cFFFFD200Drop: |rGluth|n|cFFFFD200Zone: |rNaxxramas', creatureID = 68654, },
	[1147] = { description = 'Few of these fiery harbingers remain following Ragnaros\' defeat in the Firelands.', sourceText = '|cFFFFD200Drop: |rSulfuron Harbinger|n|cFFFFD200Zone: |rMolten Core', creatureID = 68665, },
	[1149] = { description = 'These tiny flame imps roam the Molten Core in large swarms and can replenish their numbers rapidly.', sourceText = '|cFFFFD200Drop: |rMagmadar|n|cFFFFD200Zone: |rMolten Core', creatureID = 68664, },
	[1150] = { description = 'The cooled core of Golemagg, it appears to retain some semi-sentient properties.', sourceText = '|cFFFFD200Drop: |rGolemagg the Incinerator|n|cFFFFD200Zone: |rMolten Core|n', creatureID = 68666, },
	[1151] = { description = 'One of Razorgore\'s eggs that miraculously survived in Blackwing Lair.', sourceText = '|cFFFFD200Drop: |rRazorgore the Untamed|n|cFFFFD200Zone: |rBlackwing Lair', creatureID = 68661, },
	[1152] = { description = 'Opponents that face this fierce chromatic monstrosity just hope it doesn\'t have Time Stop!', sourceText = '|cFFFFD200Drop: |rChromaggus|n|cFFFFD200Zone: |rBlackwing Lair', creatureID = 68662, },
	[1153] = { description = 'A runt of the Black Talon, left to guard the whelps.', sourceText = '|cFFFFD200Drop: |rBroodlord Lashlayer|n|cFFFFD200Zone: |rBlackwing Lair', creatureID = 68663, },
	[1154] = { description = 'This small globule didn\'t make it back to Viscidus in time to re-form.', sourceText = '|cFFFFD200Drop: |rViscidus|n|cFFFFD200Zone: |rAhn\'Qiraj', creatureID = 68660, },
	[1155] = { description = 'An anubisath idol from the Temple of Ahn\'Qiraj, infused with ancient qiraji magic.', sourceText = '|cFFFFD200Drop: |rEmperor Vek\'lor|n|cFFFFD200Zone: |rAhn\'Qiraj', creatureID = 68659, },
	[1156] = { description = 'The bane of many an adventurer, to look into its eyes is to truly know madness.', sourceText = '|cFFFFD200Drop: |rThe Prophet Skeram|n|cFFFFD200Zone: |rAhn\'Qiraj', creatureID = 68658, },
	[1157] = { description = 'They only get more beautiful as they age.', sourceText = '|cFFFFD200Pet Battle:|r Northern Barrens', creatureID = 68804, },
	[1158] = { description = 'Hunted by locals for years, this is an extremely rare specimen.', sourceText = '|cFFFFD200Pet Battle:|r Feralas', creatureID = 68805, },
	[1159] = { description = 'Most people have never read this book.', sourceText = '|cFFFFD200Pet Battle:|r Hillsbrad Foothills|n', creatureID = 68806, },
	[1160] = { description = 'Never take your eye off of it.', sourceText = '|cFFFFD200Pet Battle:|r Deadwind Pass|n', creatureID = 68819, },
	[1161] = { description = 'You better believe he knows what time it is.', sourceText = '|cFFFFD200Pet Battle:|r Caverns of Time|n', creatureID = 68820, },
	[1162] = { description = 'Originally created by the engineers in the bowels of Gnomeregan, the Fluxfire Feline is a marvel of modern engineering.', sourceText = '|cFFFFD200Pet Battle:|r Gnomeregan|n', creatureID = 68838, },
	[1163] = { description = 'The engineering wizards in Everlook created this unique cub as a test model.  Unfortunately a few went rogue and were never recovered.', sourceText = '|cFFFFD200Pet Battle:|r Winterspring|n', creatureID = 68839, },
	[1164] = { description = 'This model of raptors is produced in the Blade\'s Edge Mountains.  Their robotic roars are a common sound near Toshley Station.', sourceText = '|cFFFFD200Pet Battle:|r Blade\'s Edge Mountains|n', creatureID = 68841, },
	[1165] = { description = 'The runts of Malygos brood, these whelplings are prized for their boundless arcane magics.', sourceText = '|cFFFFD200Pet Battle:|r Coldarra|n', creatureID = 68845, },
	[1166] = { description = 'Indigenous to the peaks of Kun-Lai Summit, this rare yeti is much more dangerous than it looks.', sourceText = '|cFFFFD200Pet Battle:|r Kun-Lai Summit|n', creatureID = 68846, },
	[1167] = { description = 'These beautiful drakes can be found across the shores of Northrend.', sourceText = '|cFFFFD200Pet Battle:|r Sholazar Basin|n', creatureID = 68850, },
	[1168] = { description = 'Murki is one of the world\'s rarest species of murloc.', sourceText = '|cFFFFD200Promotion:|r Korean Promotional Event', creatureID = 15361, },
	[1174] = { description = 'A fel-tainted tome whose secrets are so terrible and bizarre that its pages have taken on an eerie semblance of life. Found on many a warlock\'s nightstand.', sourceText = '|cFFFFD200Trading Card Game:|r Betrayal of the Guardian', creatureID = 69208, },
	[1175] = { description = 'Touched by lightning, these dangerous rodents should be kept at a safe distance.', sourceText = '|cFFFFD200Pet Battle: |rIsle of Thunder', creatureID = 69648, },
	[1176] = { description = 'Can you believe how cute I am?  Me neither.', sourceText = '|cFFFFD200Quest:|r Beasts of Fable|n', creatureID = 69649, },
	[1177] = { description = 'Born of sand, these dangerous creatures can form and disperse in the blink of an eye.', sourceText = '|cFFFFD200Drop:|r Throne of Thunder|n|n', creatureID = 69748, },
	[1178] = { description = 'Loyal to the Sunreavers from creation, these pint-sized constructs can still pack a punch.', sourceText = '|cFFFFD200Drop: |rHaywire Sunreaver Construct|n|cFFFFD200Zone: |rIsle of Thunder', creatureID = 69778, },
	[1179] = { description = 'Lightning can be seen flowing across its body, teeth to tail.', sourceText = '|cFFFFD200Pet Battle: |rIsle of Thunder', creatureID = 69794, },
	[1180] = { description = 'This unique raptor was born on the island of Zandalar and transported here along with the rest of the Zandalari army.', sourceText = '|cFFFFD200Drop: |rZandalari Dinomancer|n|cFFFFD200Zone: |rIsle Of Giants', creatureID = 69796, },
	[1181] = { description = 'These snakes have been known to swallow gnomes in one gulp.', sourceText = '|cFFFFD200Pet Battle: |rIsle of Thunder', creatureID = 69818, },
	[1182] = { description = 'Probably best not to touch it.', sourceText = '|cFFFFD200Pet Battle: |rIsle of Thunder', creatureID = 69819, },
	[1183] = { description = 'Forged in the Throne of Thunder, these tiny creations are servants of the great Dark Animus.', sourceText = '|cFFFFD200Drop:|r Animus|n|cFFFFD200Zone:|r Throne of Thunder', creatureID = 69820, },
	[1184] = { description = 'Even at this tiny size, Direhorns are known to wreak havoc on unsuspecting victims.', sourceText = '|cFFFFD200Achievement: |rBrutal Pet Brawler|n|cFFFFD200Category: |rBattle', creatureID = 69849, },
	[1185] = { description = 'Something about this creature looks familiar...', sourceText = '|cFFFFD200Quest: |rA Large Pile of Giant Dinosaur Bones|n|cFFFFD200Zone: |rIsle Of Giants', creatureID = 69848, },
	[1187] = { description = '', sourceText = '', creatureID = 68558, },
	[1188] = { description = '', sourceText = '', creatureID = 68559, },
	[1189] = { description = '', sourceText = '', creatureID = 68560, },
