	[1195] = { description = '', sourceText = '', creatureID = 68566, },
	[1196] = { description = 'These cuddly rascals enjoy basking in the sun, sleeping in the sun, and napping in the sun.', sourceText = '|cFFFFD200Quest:|r Beasts of Fable|n', creatureID = 69891, },
	[1197] = { description = 'The white fur of the Snowy Panda allows it to hide in the snowbanks of Kun-Lai Summit.', sourceText = '|cFFFFD200Quest:|r Beasts of Fable|n', creatureID = 69893, },
	[1198] = { description = 'Cousin to the Snowy Panda, Mountain Pandas reside in the lower regions of Kun-Lai Summit.', sourceText = '|cFFFFD200Quest:|r Beasts of Fable|n', creatureID = 69892, },
	[1200] = { description = 'Don\'t pet the pointy end. Note: all of the ends are pointy.', sourceText = '|cFFFFD200Drop: |rHorridon|n|cFFFFD200Zone: |rThrone of Thunder', creatureID = 70083, },
	[1201] = { description = 'Some Pandaren believe G\'nathus to be an ancient Zandalari Loa left behind to guard the waters south of Lei Shen\'s island citadel.', sourceText = '|cFFFFD200Drop: |rG\'nathus|n|cFFFFD200Zone: |rTownlong Steppes', creatureID = 70098, },
	[1202] = { description = 'Deadly offspring of the Ancient Mother, Ji-Kun Hatchlings are considered sacred treasures in many cultures.', sourceText = '|cFFFFD200Drop:|r Ji-Kun|n|cFFFFD200Zone:|r Throne of Thunder', creatureID = 70144, },
	[1204] = { description = 'Every cook\'s dream wrapped up in a perfect bundle of metal and bolts. Too bad what he cooks up isn\'t suitable for humanoid consumption. According to Jard\'s notes, he intends to make this little guy for his friend, Emily Cole.', sourceText = '|cFFFFD200Profession:|r Mists of Pandaria Engineering|n', creatureID = 70082, },
	[1205] = { description = 'The Zandalari breed specific Direhorn species for their small size for use as mounts.', sourceText = '|cFFFFD200Drop: |rDirehorns|n|cFFFFD200Zone:|r Isle of Giants|n', creatureID = 70154, },
	[1206] = { description = 'The red scales on these fish are believed to be a warning to predators of the Townlong Steppes and the Dread Wastes that their flesh is toxic. Dedicated fishermen can sometimes befriend one.', sourceText = '|cFFFFD200Profession:|r Mists of Pandaria Fishing', creatureID = 70257, },
	[1207] = { description = 'These little guys can be found in the waters of the Timeless Isle, or swimming alongside Jewel Danio or Redbelly Mandarin. Dedicated fishermen can sometimes befriend one.', sourceText = '|cFFFFD200Profession:|r Mists of Pandaria Fishing', creatureID = 70258, },
	[1257] = { description = 'Likely to flip out and kill someone.', sourceText = '|cFFFFD200Drop:|r Developers|n', creatureID = 71700, },
	[1258] = { description = '', sourceText = '', creatureID = 71816, },
	[1208] = { description = 'It is believed that the green coloration and stripes on the tiny green carp serve as camouflage in the waters of the Jade Forest, Valley of the Four Winds and the Krasarang Wilds. Dedicated fishermen can sometimes befriend one.', sourceText = '|cFFFFD200Profession:|r Mists of Pandaria Fishing', creatureID = 70259, },
	[1209] = { description = 'These hardy fish can survive in a wide range of climates, from the snow capped mountains of Kun-Lai Summit to the temperate, salty waters of the oceans around Pandaria. Dedicated fishermen can sometimes befriend one.', sourceText = '|cFFFFD200Profession:|r Mists of Pandaria Fishing|n', creatureID = 70260, },
	[1211] = { description = 'These tiny minions of Zandalar are adept at stowing away within the cargo holds of the enormous Zandalari warships.', sourceText = '|cFFFFD200Drop: |rZandalari Dinomancer|n|cFFFFD200Zone: |rIsle Of Giants', creatureID = 70451, },
	[1212] = { description = 'Even at this size, these raptors can eviscerate an entire cow in less than 10 seconds.', sourceText = '|cFFFFD200Drop: |rZandalari Dinomancer|n|cFFFFD200Zone: |rIsle Of Giants', creatureID = 70452, },
	[1213] = { description = 'These adorable beasts are both cuddly and huggable, provided you do not enjoy having a face.', sourceText = '|cFFFFD200Drop: |rZandalari Dinomancer|n|cFFFFD200Zone: |rIsle Of Giants', creatureID = 70453, },
	[1226] = { description = 'The oft-forgotten offspring of the Big Bad Wolf, found gorged and asleep in a basket of sweets.', sourceText = '|cFFFFD200Drop: |rThe Big Bad Wolf|n|cFFFFD200Zone: |rKarazhan', creatureID = 71014, },
	[1227] = { description = 'Primary responsibilities include dusting Medivh\'s many magical artifacts, an activity which has claimed the life, sanity, or physical composition of the many caretakers preceding Moroes.', sourceText = '|cFFFFD200Drop: |rThe Curator|n|cFFFFD200Zone: |rKarazhan', creatureID = 71015, },
	[1228] = { description = 'Upon leaving Netherspace, Malchezaar\'s Abyssals become strangely distorted by Karazhan\'s magical protections, rendering them tiny, and much less likely to spontaneously erupt in flames.', sourceText = '|cFFFFD200Drop: |rPrince Malchezaar|n|cFFFFD200Zone: |rKarazhan', creatureID = 71016, },
	[1229] = { description = 'A minion of Terestian Illhoof, this tricky imp was left in Karazhan when the portal to the Twisting Nether closed behind it.', sourceText = '|cFFFFD200Drop: |rTerestian Illhoof|n|cFFFFD200Zone: |rKarazhan', creatureID = 71033, },
	[1230] = { description = 'An astounding find for the scientific community of Azeroth, who have long pondered, often in taverns, what a baby Sea Giant might look like.', sourceText = '|cFFFFD200Drop: |rMorogrim Tidewalker|n|cFFFFD200Zone: |rSerpentshrine Cavern', creatureID = 71017, },
	[1231] = { description = 'The spawn of Hydross, considered by the elemental community to be the "Earl of Puddles."', sourceText = '|cFFFFD200Drop: |rHydross the Unstable|n|cFFFFD200Zone: |rSerpentshrine Cavern', creatureID = 71018, },
	[1232] = { description = 'It is unknown how Lady Vashj empowers these Fen Striders with their mind-bending powers; even at an early age this creature shows significant psychic ability.', sourceText = '|cFFFFD200Drop: |rLady Vashj|n|cFFFFD200Zone: |rSerpentshrine Cavern', creatureID = 71019, },
	[1233] = { description = 'Sent through the Twisting Nether by the great Archimonde the Defiler to terrorize tiny civilizations with their medium-sized stomping noises.', sourceText = '|cFFFFD200Drop: |rVoid Reaver|n|cFFFFD200Zone: |rTempest Keep|n|n', creatureID = 71020, },
	[1234] = { description = 'The lanterns carried by Voidcallers bridge the realms of the living and the dead, and often attract wayward ghosts and spirits.', sourceText = '|cFFFFD200Drop: |rHigh Astromancer Solarian|n|cFFFFD200Zone: |rTempest Keep|n', creatureID = 71021, },
	[1235] = { description = 'These brightly-colored dragonhawks carry Kael\'thas\' forces swiftly and unerringly through the nether storm surrounding the Eye.', sourceText = '|cFFFFD200Drop: |rAl\'ar|n|cFFFFD200Zone: |rTempest Keep|n', creatureID = 71022, },
	[1236] = { description = 'Though generally an expert at taking stage direction, the show had to be stopped on more than one occasion when Tito and Roar would lock eyes, resulting in an extended chase around the Karazhan Opera House.', sourceText = '|cFFFFD200Achievement: |rRaiding with Leashes II: Attunement Edition|n|cFFFFD200Category: |rCollect', creatureID = 71023, },
	[1237] = { description = 'The trolls have a long and storied history of summoning hydras. While he might not be the biggest or strongest hydra, Gahz\'rooki will bite your fingers quite hard if you get too close.', sourceText = '|cFFFFD200Vendor: |rRavika|n|cFFFFD200Zone: |rDurotar|n|cFFFFD200Cost: |r1|TINTERFACE/ICONS/INV_drink_31_embalmingfluid:0|t|n|n|cFFFFD200Vendor: |rTenuki|n|cFFFFD200Zone: |rDurotar|n|cFFFFD200Cost: |r1|TINTERFACE/ICONS/INV_drink_31_embalmingfluid:0|t', creatureID = 71159, },
	[1238] = { description = 'Val\'kyr are considered "unborn" until they vanquish a creature of high nobility.', sourceText = '|cFFFFD200Pet Battle: |rNorthrend', creatureID = 71163, },
	[1243] = { description = 'The dark pools in Primordius\' chamber laid stagnant after the fall of the mogu, and murky thoughts began to coalesce.', sourceText = '|cFFFFD200Drop:|r Primordius|n|cFFFFD200Zone: |rThrone of Thunder|n|cFFFFD200Difficulty: |rLooking For Raid|n', creatureID = 71199, },
	[1244] = { description = 'These malicious oozes are highly sensitive to Primordius\' pheromone signature.', sourceText = '|cFFFFD200Drop:|r Primordius|n|cFFFFD200Zone: |rThrone of Thunder|n|cFFFFD200Difficulty: |rNormal or Heroic|n', creatureID = 71200, },
	[1245] = { description = 'Battle pet trainers are strongly encouraged to wash their hands for at least 30 minutes after playing with the filthling.', sourceText = '|cFFFFD200Drop: |rQuivering Filth', creatureID = 71201, },
	[1247] = { description = '', sourceText = '', creatureID = 71438, },
	[1248] = { description = 'This budding ancient is a unique variety in Azeroth, created from the ever-living branches of the Great Tree, and can bloom in any season.', sourceText = '|cFFFFD200In-Game Shop|r|n', creatureID = 71488, },
	[1255] = { description = 'Infamous psionic battering ram of the Protoss, Zeradar is also the only archon that can utilize psionic storm. Power overwhelming!', sourceText = '|cFFFFD200Promotion:|r StarCraft II: Legacy of the Void Collector\'s Edition|n', creatureID = 71655, },
	[1256] = { description = 'This little guy seems to like to hang out at the fringes of cataclysmic events. There is no evidence to suggest he has anything to do with the cause of these events, though - he\'s probably just an observer. Or maybe he just gets lost on his trek to other places.', sourceText = '|cFFFFD200Profession:|r Mists of Pandaria Engineering|n', creatureID = 71693, },
	[1259] = { description = '', sourceText = '', creatureID = 71844, },
	[1266] = { description = 'Fierce and loyal, Xu-Fu loves to hunt by moonlight.  Once during a full moon, he killed and ate an entire Ironfur Great Bull, prompting a two day nap.', sourceText = '|cFFFFD200Vendor:|r Tournament of Celestials', creatureID = 71942, },
