	[1303] = { description = 'Chi-Chi tries to inspire hope with her loud squawk and by chasing, and eventually eating, butterflies.', sourceText = '|cFFFFD200Vendor: |rMaster Li|n|cFFFFD200Zone: |rTimeless Isle|n|cFFFFD200Cost: |r3|TINTERFACE/ICONS/INV_MISC_TRINKETPANDA_07:0|t', creatureID = 72462, },
	[1304] = { description = 'A master sculptor devoted her life to creating an incomparably beautiful statue, inspired by her love and reverence for Yu\'lon.  When the sculptor passed away, Yu\'lon breathed a fraction of her essence into the statue, bringing Yu\'la into the world.', sourceText = '|cFFFFD200Vendor: |rMaster Li|n|cFFFFD200Zone: |rTimeless Isle|n|cFFFFD200Cost: |r3|TINTERFACE/ICONS/INV_MISC_TRINKETPANDA_07:0|t', creatureID = 72463, },
	[1305] = { description = 'Always on the lookout for a fight or some tasty grass, Zao keeps his head down and eyes forward.', sourceText = '|cFFFFD200Vendor:|r Tournament of Celestials', creatureID = 72464, },
	[1311] = { description = '', sourceText = '', creatureID = 72285, },
	[1317] = { description = '', sourceText = '', creatureID = 72291, },
	[1319] = { description = '', sourceText = '', creatureID = 72290, },
	[1320] = { description = 'This handsome little robot is the spitting image of his "Dad", Blingtron 4000.  As a Blingtron\'s generosity circuits don\'t mature until they have been built to maximum specification, you shouldn\'t expect any free handouts from him.', sourceText = '|cFFFFD200Drop:|r Blingtron Gift Package|n', creatureID = 73011, },
	[1321] = { description = 'Crane chicks of the Timeless Isle sometimes sing a curious song that calms the nerves and soothes the soul.', sourceText = '|cFFFFD200Drop:|r Crane Nest|n|cFFFFD200Zone: |rTimeless Isle', creatureID = 73534, },
	[1322] = { description = 'The first bombling created in the Underhold, Siegecrafter Blackfuse couldn\'t bear to see it destroyed, and kept it as a friendly, if explosive, pet.', sourceText = '|cFFFFD200Drop:|r Siegecrafter Blackfuse|n|cFFFFD200Raid:|r Siege of Orgrimmar', creatureID = 73352, },
	[1323] = { description = 'The Ashleaf Sprites of the Timeless Isle tirelessly protect the vegetation of the island from the burning fury of Ordos and his minions.', sourceText = '|cFFFFD200Drop:|r Leafmender|n|cFFFFD200Zone:|r Timeless Isle|n', creatureID = 73533, },
	[1324] = { description = 'Jinyu assassins employ this moth as both a portable decoy and a light source to reveal weak spots in their enemy\'s armor.', sourceText = '|cFFFFD200Pet Battle:|r Timeless Isle|n', creatureID = 73542, },
	[1325] = { description = 'Cats and other curious critters often have short-lived play sessions with this moth that end with a face full of singed hair.', sourceText = '|cFFFFD200Pet Battle:|r Timeless Isle|n', creatureID = 73543, },
	[1326] = { description = 'This curious moth is sometimes found interrupting the spells of a wandering mage or warlock.', sourceText = '|cFFFFD200Pet Battle:|r Timeless Isle|n', creatureID = 73368, },
	[1328] = { description = 'The waters of Ruby Lake are colored a strange reddish hue, possibly due to the ruby crystals found deep within the mountains of the Timeless Isle.', sourceText = '|cFFFFD200Drop:|r Garnia|n|cFFFFD200Zone:|r Timeless Isle', creatureID = 73356, },
	[1329] = { description = 'While fascinated by flowers, the frolicker has also been known to dance with blades of grass and the occasional weed.', sourceText = '|cFFFFD200Drop:|r Scary Sprite|n|cFFFFD200Zone:|r Timeless Isle|n', creatureID = 73532, },
	[1330] = { description = 'Late in life, if the environmental conditions are correct, a Death Adder may shed its skin and undergo a transformation into an Imperial Python.', sourceText = '|cFFFFD200Drop:|r Imperial Python|n|cFFFFD200Zone:|r Timeless Isle', creatureID = 73364, },
	[1331] = { description = 'A small remnant of the power of Y\'shaarj that spawned the Sha, a single Drop of Y\'shaarj can magnify emotional states for thousands of yards, causing entire towns to shut down in fear or doubt.', sourceText = '|cFFFFD200Drop:|r Sha of Pride|n|cFFFFD200Raid:|r Siege of Orgrimmar|n|cFFFFD200Difficulty:|r Flexible, Normal or Heroic', creatureID = 73350, },
	[1332] = { description = 'Sha-lings reflect the emotional state of their handler, becoming agitated, depressed, fearful, or doubtful depending on the magnitude of negative thoughts around them.', sourceText = '|cFFFFD200Drop:|r Sha of Pride|n|cFFFFD200Raid:|r Siege of Orgrimmar|n|cFFFFD200Difficulty:|r LFR and Flexible', creatureID = 73351, },
	[1333] = { description = 'The Jademist Dancers of the Timeless Isle have a curious attraction to the steam vents of the northwestern shore.', sourceText = '|cFFFFD200Drop:|r Jademist Dancer|n|cFFFFD200Zone:|r Timeless Isle', creatureID = 73355, },
	[1334] = { description = 'The tiny terror of the Dread Wastes, Kovok prefers a steady diet of "brain food:" mushan brains, human brains, any brains really. This strange nutritional requirement may be tangentally related to his enormous head.', sourceText = '|cFFFFD200Drop:|r Paragons of the Klaxxi|n|cFFFFD200Raid:|r Siege of Orgrimmar|n', creatureID = 73354, },
	[1335] = { description = 'The odd blue flames found within the Cavern of Lost Spirits have led many unwary adventurers to their doom.', sourceText = '|cFFFFD200Drop:|r Foreboding Flame|n|cFFFFD200Zone:|r Timeless Isle|n|n', creatureID = 73357, },
	[1336] = { description = 'When the village of Pi\'jiu was attacked by the Ordon Yaungol generations ago, the villagers bravely erected a keg in the center of the village. The following morning, after the celebration had died down, the still-drunken Yaungol blearily agreed to a peace treaty.', sourceText = '|cFFFFD200Drop:|r Zhu-Gon the Sour|n|cFFFFD200Zone:|r Timeless Isle|n|n', creatureID = 73367, },
	[1337] = { description = 'Spineclaw crabs of Pandaria have existed for thousands of years and remain one of the continent\'s oldest creatures.', sourceText = '|cFFFFD200Drop:|r Monstrous Spineclaw|n|cFFFFD200Zone:|r Timeless Isle|n', creatureID = 73366, },
	[1338] = { description = 'Gulp frogs of the Timeless Isle can feast upon prey several times larger than themselves by slowly digesting the victim externally with a toxic slime.', sourceText = '|cFFFFD200Drop: |rBufo|n|cFFFFD200Zone: |rTimeless Isle', creatureID = 73359, },
	[1339] = { description = '', sourceText = '', creatureID = 73627, },
	[1343] = { description = 'Some young and rambunctious hozen are trained from an early age to use boxing gloves so they don\'t accidentally kill their tribe members, and more importantly, to keep them from picking their noses.', sourceText = '|cFFFFD200Drop:|r Kukuru Chest|n|cFFFFD200Zone:|r Timeless Isle', creatureID = 73668, },
	[1344] = { description = 'One of the beasts left on the Timeless Isle when it shifted through time, most likely sacrificed by the Ordon to their demigod, Ordos.', sourceText = '|cFFFFD200Vendor: |rSpeaker Gulan|n|cFFFFD200Zone: |rTimeless Isle', creatureID = 73688, },
	[1345] = { description = 'Swarmlings of Gu\'chi spin an ancient, and very powerful, form of silk that has not been seen in Pandaria in ages.', sourceText = '|cFFFFD200Drop:|r Gu\'chi the Swarmbringer|n|cFFFFD200Zone:|r Timeless Isle', creatureID = 73730, },
	[1346] = { description = 'Prolonged exposure to Shaohao\'s tranquil mists has calmed these spirits, which act as peaceful companions to the Mistweavers.', sourceText = '|cFFFFD200Vendor: |rMistweaver Ku|n|cFFFFD200Zone: |rTimeless Isle|n|cFFFFD200Faction: |rShaohao - Pathwalker', creatureID = 73732, },
	[1348] = { description = 'The Jadefire Spirit is thought to be a living embodiment of Yu\'lon\'s own jade breath.', sourceText = '|cFFFFD200Drop:|r Spirit of Jadefire|n|cFFFFD200Zone:|r Timeless Isle|n|n', creatureID = 73738, },
	[1349] = { description = 'Fed up with long hours, low pay, and entitled adventurers whining about sub-standard Winter Veil gifts, these nasty little creatures fled their magical workshops to join up with the Abominable Greench.', sourceText = '|cFFFFD200World Event:|r Feast of Winter Veil|n', creatureID = 73741, },
	[1350] = { description = 'A candle inside of the kite heats the air, causing it to rise into the air without the need for wind.', sourceText = '|cFFFFD200Vendor:|r Ku-Mo|n|cFFFFD200Zone:|r Timeless Isle', creatureID = 73809, },
	[1351] = { description = 'These possessed puppets are given a semblance of life by whimsical spirits of the departed.', sourceText = '|cFFFFD200World Event:|r Day of the Dead', creatureID = 34770, },
