	[1364] = { description = 'We\'re Crusaders from a fable. We dance whene\'er we\'re able. We do routines and chorus scenes, with footwork impeccable!', sourceText = '|cFFFFD200Promotion:|r BlizzCon 2013', creatureID = 74405, },
