	[1441] = { description = 'Orcish children have a tradition where they sneak up on a mud jumper and whisper a secret they wish to come true.  If the mud jumper doesn\'t jump, the wish will come true.', sourceText = '|cFFFFD200Pet Battle: |rHighmountain, Nagrand, Spires of Arak, Talador|n', creatureID = 83642, },
