	[1451] = { description = 'Loves to herd imps, roll around in lava, and snuggle with elemental lords.', sourceText = '|cFFFFD200Achievement:|r WoW\'s 10th Anniversary|n|cFFFFD200Category:|r Feats of Strength', creatureID = 84915, },
	[1505] = { description = '', sourceText = '', creatureID = 85683, },
