	[1457] = { description = 'Equipped with extremely sharp claws, these frigid hatchlings dig through thick layers of ice and rock for shelter.', sourceText = '|cFFFFD200Pet Battle: |rFrostfire Ridge', creatureID = 85003, },
	[1458] = { description = 'One ogre ritual involves cupping a bone wasp in their hands and shaking vigorously.  The venom increases strength and invokes vivid imagery.', sourceText = '|cFFFFD200Vendor:|r Faction Vendor|n|cFFFFD200Reputation:|r Laughing Skull', creatureID = 85014, },
	[1462] = { description = 'Contrary to popular belief, bloodsting wasps do not feed on blood, but love.', sourceText = '|cFFFFD200Pet Battle: |rSpires of Arak', creatureID = 85255, },
