	[1530] = { description = 'The little wolf told to stay. Watching, waiting, for vacant prey. Too proud to leave, she slipped away. Unburdened now, forever free to play.', sourceText = '|cFFFFD200Profession:|r Archaeology', creatureID = 86422, },
	[1531] = { description = 'The ancient nest guardian was designed with the unthinkable in mind: to hate cats.', sourceText = '|cFFFFD200Profession:|r Archaeology', creatureID = 86420, },
