	[1533] = { description = 'No one knows what deep part of Blackwing Descent the Lanticore was kept in, but it probably smelled pretty awful.', sourceText = '|cFFFFD200Drop:|r The Lanticore|n|cFFFFD200Zone:|r Upper Black Rock Spire', creatureID = 86532, },
	[1536] = { description = 'In Draenor, the Zangar Sea seeks to overtake the land by releasing thousands of these spores and slowly growing inland over time.', sourceText = '|cFFFFD200Drop:|r Fel-Touched Pet Supplies|n|cFFFFD200Zone:|r Tanaan Jungle|n', creatureID = 86715, },
	[1537] = { description = 'A spore plucked from the Crimson Fen, an area of Gorgrond that was swallowed when Draenor was torn apart by nether magics.', sourceText = '|cFFFFD200Drop:|r Strange Spore|n|cFFFFD200Zone:|r Gorgrond|n', creatureID = 86716, },
	[1538] = { description = 'The deep blue Umbrafen would later become home to a tribe of Broken.', sourceText = '|cFFFFD200Drop:|r Strange Spore|n|cFFFFD200Zone:|r Shadowmoon Valley|n', creatureID = 86717, },
	[1539] = { description = 'A spore from Zangarra, a central area of the Zangar encroachment that would later bloom into the Zangarmarsh.', sourceText = '|cFFFFD200Drop:|r Fel-Touched Pet Supplies|n|cFFFFD200Zone:|r Tanaan Jungle|n', creatureID = 86718, },
	[1540] = { description = 'Brilliant spores are very rarely seen, found deep under the waves of Draenor within the darkest corners of the Zangar fungal encroachment.', sourceText = '|cFFFFD200Drop:|r Rotcap|n|cFFFFD200Zone:|r Spires of Arak', creatureID = 86719, },
	[1541] = { description = 'The hydraling can be an expensive pet, as it is capable of eating from three food bowls at the same time.', sourceText = '|cFFFFD200Drop:|r Vasha\'s Egg|n|cFFFFD200Zone:|r Spires of Arak', creatureID = 86879, },
	[1542] = { description = 'These pups are bundles of fur and teeth until they grow up, at which point they are really just teeth.', sourceText = '|cFFFFD200Vendor:|r Beska Redtusk|n|cFFFFD200Faction:|r Frostwolf|n|n', creatureID = 87111, },
