	[1569] = { description = 'When forging enchanted steel, bits of magic seep into the coals.  Over a period of years, decades even, the unbound energy slowly takes form, creating a tiny elemental being of immense fortitude.', sourceText = '|cFFFFD200Profession:|r Warlords of Draenor Blacksmithing', creatureID = 84853, },
	[1570] = { description = 'Legend says the Sunfire Kaliri flies into the sun itself when it is ready to die.', sourceText = '|cFFFFD200Quest:|r Mastering the Menagerie', creatureID = 88367, },
	[1571] = { description = 'Their white hide would make them vulnerable to sunburn, if it wasn\'t as thick as plate armor.', sourceText = '|cFFFFD200Vendor: |rKwilax Fuseshiv|n|cFFFFD200Faction: |rSteamwheedle Preservation Society - Revered|n|cFFFFD200Cost: |r1000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t', creatureID = 85281, },
	[1572] = { description = 'The bloodfeather is completely fearless, fanning out its feathers and charging any threat.', sourceText = '|cFFFFD200Pet Battle: |rTalador', creatureID = 88385, },
	[1573] = { description = 'It\'s said the feathers glitter like gold when the rising sun shines upon them.', sourceText = '|cFFFFD200Pet Battle: |rSpires of Arak', creatureID = 85798, },
	[1574] = { description = 'Godlike being now in travel size.', sourceText = '|cFFFFD200Vendor: |rRavenspeaker Skeega|n|cFFFFD200Zone: |rWarspear|n|cFFFFD200Faction: |rArakkoa Outcasts - Revered|n|cFFFFD200Cost: |r1000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|n|cFFFFD200Vendor: |rShadow-Sage Brakoss|n|cFFFFD200Zone: |rStormshield|n|cFFFFD200Faction: |rArakkoa Outcasts - Revered|n|cFFFFD200Cost: |r1000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t', creatureID = 88401, },
	[1575] = { description = 'Playing fetch with a ray is always fun.  You don\'t even have to aim.', sourceText = '|cFFFFD200Vendor:|r Reputation Vendor|n|cFFFFD200Reputation:|r Sha\'tari Defense', creatureID = 88452, },
	[1576] = { description = 'You\'ll always feel like... something is watching you.', sourceText = '|cFFFFD200Drop:|r Oromo the Observer|n|cFFFFD200Zone:|r Talador', creatureID = 88490, },
	[1577] = { description = 'Ravagers are favored ogre snacks when they are small and less deadly.', sourceText = '|cFFFFD200Vendor: |rTiffy Trapspring, Giada Goldleash|n|cFFFFD200Zone: |rGarrison', creatureID = 88514, },
	[1578] = { description = 'The frostshell pinchers build up layers of ice to give them the extra protection from larger predators.', sourceText = '|cFFFFD200Pet Battle: |rFrostfire Ridge', creatureID = 88480, },
	[1579] = { description = 'With claws that can crush a platemail gauntlet, it\'s best to handle with care.', sourceText = '|cFFFFD200Pet Battle:|r Frostfire Ridge', creatureID = 88474, },
	[1581] = { description = 'These crafty crustaceans burrow under the mud to avoid being scooped up by larger predators.', sourceText = '|cFFFFD200Pet Battle:|r Tanaan Jungle|n', creatureID = 88473, },
	[1582] = { description = 'They feed off of the nutrient-rich fungal spores which gives these crabs the district coloring of their shells.', sourceText = '|cFFFFD200Pet Battle: |rShadowmoon Valley', creatureID = 88466, },
	[1583] = { description = 'They will cover themselves in seaweed and other ocean foliage to get the upper claw on their prey.', sourceText = '|cFFFFD200Pet Battle: |rTalador', creatureID = 88465, },
	[1586] = { description = 'Delicate and dangerous, brilliant and bold, a beauty worth more than gold.', sourceText = '|cFFFFD200Pet Battle:|r Tanaan Jungle|n|n', creatureID = 88422, },
	[1587] = { description = 'Their deep purple wings are highly sought after by ogre magi believing that they can bring prolonged life when added to potions.', sourceText = '|cFFFFD200Pet Battle: |rShadowmoon Valley, Talador', creatureID = 88417, },
	[1588] = { description = 'Their wings emit a fine powder as a defensive mechanism when it is in danger.', sourceText = '|cFFFFD200Vendor: |rTiffy Trapspring, Giada Goldleash|n|cFFFFD200Zone: |rGarrison', creatureID = 88415, },
	[1729] = { description = 'Olivetail is a bit of misnomer, as its whole body is just brown...', sourceText = '|cFFFFD200Pet Battle: |rAzsuna, Eye of Azshara', creatureID = 97294, },
	[1589] = { description = 'Delicate and dangerous, brilliant and bold, a beauty worth more than its weight in gold.  Too bad they are so light.', sourceText = '|cFFFFD200Pet Battle: |rTalador', creatureID = 88413, },
	[1590] = { description = 'Scholars have debated at length about how fireflies glow, but the leading hypothesis is "magic".', sourceText = '|cFFFFD200Pet Battle: |rHighmountain, Spires of Arak', creatureID = 88359, },
	[1591] = { description = 'These fireflies retain their purple glow by eating only purple flowers and nectar.', sourceText = '|cFFFFD200Pet Battle:|r Tanaan Jungle|n', creatureID = 88357, },
	[1592] = { description = 'It has been referred to as "the flying gem" due to its similar luster to the finest of sapphires.', sourceText = '|cFFFFD200Pet Battle: |rSpires of Arak', creatureID = 88356, },
	[1593] = { description = 'The watefly uses the surface of the water to reflect the light from their abdomen to attract mates.', sourceText = '|cFFFFD200Pet Battle: |r Shadowmoon Valley, Talador, Tanaan Jungle', creatureID = 88355, },
	[1594] = { description = 'Surprisingly quick when provoked, but easily calmed with a meal of Nagrand arrowbloom.', sourceText = '|cFFFFD200Pet Battle: |rGorgrond', creatureID = 88571, },
	[1595] = { description = 'Surprisingly quick when provoked, but easily calmed with a meal of Nagrand arrowbloom.', sourceText = '|cFFFFD200Pet Battle: |rTalador', creatureID = 88572, },
	[1596] = { description = 'Hunted to near extinction for its feathers, thie kaliri has developed keen senses.', sourceText = '|cFFFFD200Vendor:|r Vesharr|n|cFFFFD200Zone:|r Spires of Arak|n|cFFFFD200Cost:|r 1000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t', creatureID = 88573, },
	[1597] = { description = 'Hunted to near extinction for its feathers, the kaliri has developed keen senses.', sourceText = '|cFFFFD200Vendor:|r Vesharr|n|cFFFFD200Zone:|r Spires of Arak|n|cFFFFD200Cost:|r 10|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t', creatureID = 88574, },
	[1598] = { description = 'Wild sporebats are vicious creatures, but if tamed at a young age, they can be loyal companions.', sourceText = '|cFFFFD200Vendor: |rTiffy Trapspring, Giada Goldleash|n|cFFFFD200Zone: |rGarrison', creatureID = 88575, },
	[1599] = { description = 'Wild sporebats are vicious creatures, but if tamed at a young age, they can be loyal companions.', sourceText = '|cFFFFD200Pet Battle: |rTalador', creatureID = 88576, },
	[1600] = { description = 'Young bone serpents will try to burrow into a living creature\'s skull and devour their brain.  So you may want to wear a night cap.', sourceText = '|cFFFFD200Pet Battle:|r Erris the Collector, Kura Thunderhoof', creatureID = 88577, },
	[1601] = { description = 'Demidos created a great many servants when he was hosting parties for other void lords.', sourceText = '|cFFFFD200Drop:|r Demidos|n|cFFFFD200Zone:|r Shadowmoon Valley', creatureID = 88692, },
	[1602] = { description = 'No matter how much he screams, never give him demon blood.', sourceText = '|cFFFFD200Promotion:|r BlizzCon 2014', creatureID = 88805, },
