	[1604] = { description = 'The lost magical light of a Dalaran mage, or sentient candle with a positive attitude? You decide.', sourceText = '|cFFFFD200Zone:|r Dalaran|n', creatureID = 88814, },
	[1605] = { description = 'A magical friend created with equal parts adventure and spirit.', sourceText = '|cFFFFD200Achievement:|r An Awfully Big Adventure', creatureID = 88830, },
