	[1624] = { description = 'A fragment of the great abyssal Supremus, Abyssius contains enough fiery essence to burn a hole right through your pocket.', sourceText = '|cFFFFD200Drop: |rSupremus|n|cFFFFD200Zone: |rBlack Temple', creatureID = 90202, },
