	[1632] = { description = 'Because you don\'t want to know what happens when a rat wanders into the Sunwell.', sourceText = '|cFFFFD200Drop: |rBrutallus|n|cFFFFD200Zone: |rSunwell Plateau', creatureID = 90212, },
	[1633] = { description = 'The Twisting Nether is an infinite abyss of terrors and one small corner of extreme cuteness.', sourceText = '|cFFFFD200Drop: |rM\'uru|n|cFFFFD200Zone: |rSunwell Plateau', creatureID = 90213, },
	[1634] = { description = 'Magic-addicted elves too small and frail to serve in Kil\'Jaeden\'s army were relegated to servant roles for the higher-class demons.', sourceText = '|cFFFFD200Drop: |rEredar Twins|n|cFFFFD200Zone: |rSunwell Plateau', creatureID = 90214, },
	[1635] = { description = 'When this pet is near, calming chimes fill your mind and remind you of a time long passed.', sourceText = '|cFFFFD200Achievement: |rRaiding with Leashes III: Drinkin\' From the Sunwell|n|cFFFFD200Category: |rCollect', creatureID = 90215, },
