	[1655] = { description = 'The rivers of Draenor are filled with moray.  They are vicious predators, attacking anything that enters the water.', sourceText = '|cFFFFD200Pet Battle:|r Erris the Collector, Kura Thunderhoof|n', creatureID = 91407, },
