	[1660] = { description = 'These pups are bundles of fur and teeth until they grow up, at which point they are really just teeth.', sourceText = '|cFFFFD200Quest:|r A Fel Puppy Of My Own|n|cFFFFD200Zone:|r Tanaan Jungle|n|n', creatureID = 91823, },
	[1661] = { description = 'Rumor has it that some beasts that were touched by the void exist in a space between this world and the next.', sourceText = '|cFFFFD200Vendor: |rTiffy Trapspring, Giada Goldleash|n|cFFFFD200Zone: |rGarrison', creatureID = 93142, },
	[1662] = { description = 'Found munching on a blackened bone in the Black Forge of Blackrock Foundry, this pup really seems to warm up to you quickly.', sourceText = '|cFFFFD200Garrison Mission:|r Fiery Friends|n', creatureID = 93143, },
	[1663] = { description = 'These playful little riverbeasts enjoy scooping up a large mouthful of water and then spitting it at one another.', sourceText = '|cFFFFD200Drop:|r Fel-Touched Pet Supplies|n|cFFFFD200Zone:|r Tanaan Jungle|n', creatureID = 93352, },
	[1664] = { description = 'Once a holy artifact used by draenei priests to calm spirits, it has become animated by anguished souls and drawn to locations of death and corruption.', sourceText = '|cFFFFD200Drop:|r Fel-Touched Pet Supplies|n|cFFFFD200Zone:|r Tanaan Jungle|n', creatureID = 93483, },
	[1665] = { description = 'These crabs have developed an advanced form of camouflage that causes their skin to appear translucent.', sourceText = '|cFFFFD200Vendor: |rGalissa Sundew|n|cFFFFD200Zone: |rDarkmoon Island', creatureID = 93808, },
