	[1688] = { description = 'Found wandering in a small blizzard, this lost goren carries a mining pick to show that he is a jack of all trades.', sourceText = '|cFFFFD200Vendor: |rTrader Araanda|n|cFFFFD200Zone: |rLunarfall|n|cFFFFD200Cost: |r1000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|n|cFFFFD200Vendor: |rTrader Darakk|n|cFFFFD200Zone: |rFrostwall|n|cFFFFD200Cost: |r1000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t', creatureID = 94927, },
	[1690] = { description = 'A small amount of Cyrukh burns everlasting within this portion of the Cipher. Best keep it close to you.', sourceText = '|cFFFFD200Treasure: |rForgotten Shard of the Cipher|n|cFFFFD200Zone: |rTanaan Jungle', creatureID = 95572, },
	[1691] = { description = 'This young stalker was easily swayed from Sargeras\' grasp by a handful of Magical Mini-Treats.', sourceText = '|cFFFFD200Promotion:|r Legion Collector\'s Edition', creatureID = 95841, },
	[1692] = { description = 'Pets often look a lot like their owners, which is especially true if the owner was a saberon.', sourceText = '|cFFFFD200Vendor: |rZ\'tenga the Walker|n|cFFFFD200Zone: |rTanaan Jungle|n|cFFFFD200Faction: |rThe Saberstalkers - Revered|n|cFFFFD200Cost: |r1500|TINTERFACE/ICONS/INV_misc_blacksaberonfang.blp:0|t', creatureID = 96126, },
	[1693] = { description = 'Arakkoa sun worship has resulted in the creation of some strange and monstrous minions.', sourceText = '|cFFFFD200Vendor: |rDawn-Seeker Krisek|n|cFFFFD200Zone: |rTanaan Jungle|n|cFFFFD200Faction: |rOrder of the Awakened - Exalted', creatureID = 96123, },
	[1699] = { description = 'Strega the Hag enchanted her cauldron to prevent it from tipping over and spilling hot soup.  She never expected it to achieve sentience and attempt to murder her.', sourceText = '|cFFFFD200Profession:|r Legion Enchanting|n|cFFFFD200Formula:|r Enchanted Cauldron', creatureID = 96403, },
	[1700] = { description = 'Strega the Hag didn\'t like carrying her own torches.  One quick enchantment, and the thing would dutifully follow her into the night.', sourceText = '|cFFFFD200Profession:|r Legion Enchanting|n|cFFFFD200Formula:|r Enchanted Torch', creatureID = 96404, },
	[1701] = { description = 'Strega the Hag, tired of writing thank you notes, used her magic to automate the task.  Unfortunately, the pen had a dark side and all the recipients received strongly worded letters...', sourceText = '|cFFFFD200Profession:|r Legion Enchanting|n|cFFFFD200Formula:|r Enchanted Pen', creatureID = 96405, },
	[1705] = { description = 'You saved him, but maybe he saved you.', sourceText = '|cFFFFD200Quest: |rGrumpy|n|cFFFFD200Zone: |rVal\'sharah', creatureID = 96622, },
	[1706] = { description = 'This little guy has had a rough start at life, be sure to take good care of him.', sourceText = '|cFFFFD200Quest:|r Adopting the Adorable|n|cFFFFD200Zone:|r Val\'sharah', creatureID = 96649, },
	[1708] = { description = 'Many sailors believe that harming an albatross will bring terrible luck upon their voyage.', sourceText = '|cFFFFD200Pet Battle: |rAzsuna, Stormheim', creatureID = 97018, },
	[1709] = { description = 'While other species of birds take turns leading the flock during migration, the kingfeather always remains in the lead.  Whether this is an effort to help the rest of the flock conserve energy or out of dominance remains unknown.', sourceText = '|cFFFFD200Pet Battle:|r Azsuna|n', creatureID = 97076, },
	[1710] = { description = 'Always in its brother\'s shadow.', sourceText = '|cFFFFD200Pet Battle:|r Azsuna|n', creatureID = 97078, },
	[1711] = { description = 'A very rare bird, but this one was intrusted to you.', sourceText = '|cFFFFD200Quest: |rEmpty Nest|n|cFFFFD200Zone: |rHighmountain', creatureID = 97079, },
	[1712] = { description = 'These birds often prey on turtles by dropping them from great heights to break open their shell.', sourceText = '|cFFFFD200Pet Battle: |rStormheim', creatureID = 97080, },
	[1713] = { description = 'Its eyesight is better than its hearing.', sourceText = '|cFFFFD200Pet Battle: |rHighmountain, Stormheim', creatureID = 97118, },
	[1714] = { description = 'Its sharp eyes can spot a snowshoe rabbit in a snowstorm.', sourceText = '|cFFFFD200Pet Battle: |rHighmountain', creatureID = 97126, },
	[1715] = { description = 'These creatures can hunt in almost pure darkness.', sourceText = '|cFFFFD200Vendor:|r Draemus|n|cFFFFD200Zone:|r Dalaran|n|cFFFFD200Cost:|r 100|TINTERFACE/ICONS/ACHIEVEMENT_GUILDPERK_HONORABLEMENTION.BLP:0|t', creatureID = 97127, },
	[1716] = { description = 'A young companion in need of training.', sourceText = '|cFFFFD200Vendor: |rMarin Bladewing|n|cFFFFD200Zone: |rAzsuna|n|cFFFFD200Faction: |rThe Wardens - Revered|n|cFFFFD200Cost: |r500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t', creatureID = 97128, },
	[1717] = { description = 'What was once a great enemy is now an adorable friend!', sourceText = '|cFFFFD200Vendor: |rFirst Arcanist Thalyssra|n|cFFFFD200Zone: |rSuramar|n|cFFFFD200Faction: |rThe Nightfallen - Revered|n|cFFFFD200Cost: |r500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t', creatureID = 97174, },
	[1718] = { description = 'What are YOU looking at?!', sourceText = '|cFFFFD200Order Hall:|r Dreadscar Rift, The Fel Hammer', creatureID = 97178, },
	[1719] = { description = 'Hello. Is it me you\'re looking for?', sourceText = '|cFFFFD200Drop: |rFelsoul Inquisitor|n|cFFFFD200Zone: |rSuramar', creatureID = 97179, },
	[1720] = { description = 'The great-great-great-great-great granddaughter of Senegos.', sourceText = '|cFFFFD200Quest: |rOn the Brink|n|cFFFFD200Zone: |rAzsuna', creatureID = 97207, },
	[1721] = { description = 'Its scales are electrified, making it exceedingly tricky to cuddle.', sourceText = '|cFFFFD200Drop: |rStormwing Matriarch|n|cFFFFD200Zone: |rStormheim', creatureID = 97205, },
	[1722] = { description = 'Beautiful dreamer, wake unto me, Starlight and dewdrops are waiting for thee.', sourceText = '|cFFFFD200Pet Battle: |rThe Emerald Nightmare', creatureID = 97206, },
	[1723] = { description = 'What\'s a little corruption between friends?', sourceText = '|cFFFFD200Drop: |rYsondre|n|cFFFFD200Zone: |rThe Emerald Nightmare', creatureID = 112015, },
	[1725] = { description = 'Grumplings are created when the tears of a kidnapped child fall upon a mound of new snow.', sourceText = '|cFFFFD200World Event:|r Feast of Winter Veil', creatureID = 97229, },
	[1726] = { description = 'The front legs of this spider are almost indestructable, allowing it to burrow into almost any surface.', sourceText = '|cFFFFD200Pet Battle: |rHighmountain', creatureID = 97236, },
	[1727] = { description = 'Why is it called a nursery spider? Does it prefer to lurk in nurseries? Why would it do that? What does it do there? Oh, dear! Someone check on the kids!', sourceText = '|cFFFFD200Vendor: |rBreanni|n|cFFFFD200Zone: |rDalaran|n|cFFFFD200Cost: |r200|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|n|cFFFFD200Vendor: |rBreanni|n|cFFFFD200Zone: |rCrystalsong Forest|n|cFFFFD200Cost: |r200|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t', creatureID = 97238, },
	[1728] = { description = 'Juvenile describes this scuttleback\'s age, not its maturity.', sourceText = '|cFFFFD200Pet Battle: |rAzsuna, Eye of Azshara', creatureID = 97283, },
	[1730] = { description = 'As if a mine full of non-ghost spiders wasn\'t scary enough.', sourceText = '|cFFFFD200World Event:|r Hallow\'s End|n', creatureID = 97324, },
	[1731] = { description = 'It\'s just like a regular spider, but fel.', sourceText = '|cFFFFD200Pet Battle: |rAzsuna, Highmountain', creatureID = 97323, },
