	[2122] = { description = 'Proximity to Antorus makes these larva hyper agressive.', sourceText = '|cFFFFD200Pet Battle:|r Antoran Wastes|n', creatureID = 128163, },
	[2123] = { description = 'An early stage of the bilescourge life cycle.', sourceText = '|cFFFFD200Pet Battle:|r Krokuun|n', creatureID = 128162, },
	[2124] = { description = 'The final phase of the bilescourge life cycle.', sourceText = '|cFFFFD200Pet Battle:|r Krokuun|n', creatureID = 128164, },
	[2126] = { description = 'Bilescourge mostly feed on the corpses of victims of the Legion. However, since your arrival, it has developed a taste for demon corpses as well.', sourceText = '|cFFFFD200Pet Battle:|r Antoran Wastes|n|n', creatureID = 128175, },
	[2127] = { description = 'Born from the crash site of the Xenedar on Argus.', sourceText = '|cFFFFD200Pet Battle:|r Krokuun|n', creatureID = 128166, },
	[2128] = { description = 'While there are many similarities to the warpstalkers from Azeroth, evidence suggests they evolved completely independently from one another. Fascinating!', sourceText = '|cFFFFD200Pet Battle:|r Eredath|n', creatureID = 128170, },
	[2129] = { description = 'The void can consume life or meld with it. The latter often has unpercieved consequences.', sourceText = '|cFFFFD200Pet Battle:|r Eredath|n', creatureID = 128168, },
	[2130] = { description = 'You have the odd feeling that it wants to kill you.', sourceText = '|cFFFFD200Pet Battle:|r Eredath|n', creatureID = 128171, },
	[2131] = { description = 'Mana wyrm used to thrive on Argus many millennia ago, but lots has changed since those halcyon days.', sourceText = '|cFFFFD200Pet Battle:|r Eredath|n', creatureID = 128172, },
	[2132] = { description = 'If mana becomes scarce these wyrm look for other sources of energy to feed on.', sourceText = '|cFFFFD200Pet Battle:|r Eredath|n', creatureID = 128174, },
	[2133] = { description = 'It likes to be petted behind the fins.', sourceText = '|cFFFFD200Pet Battle:|r Eredath |n', creatureID = 128173, },
	[2134] = { description = 'Skyfin can fly from birth and there are some observed cases of mothers giving birth midflight!', sourceText = '|cFFFFD200Pet Battle:|r Eredath|n', creatureID = 128167, },
	[2135] = { description = 'What are they rebelling against? The real question is what are they NOT rebelling against.', sourceText = '|cFFFFD200Drop: |rMother Rosula', creatureID = 128388, },
	[2136] = { description = 'Uuna was found bound to an Ur\'zul, and was released into the Shadowlands, where she wandered in the utter darkness.', sourceText = '|cFFFFD200Drop:|r The Many-Faced Devourer', creatureID = 128396, },
	[2143] = { description = 'Tottle\'s one wish is to explore the world at your side!', sourceText = '|cFFFFD200Promotion:|r Battle for Azeroth Collector\'s Edition|n', creatureID = 129049, },
	[2144] = { description = '', sourceText = ' ', creatureID = 129253, },
	[2145] = { description = '', sourceText = '', creatureID = 129256, },
	[2146] = { description = 'Unfortunately, it\'s not a duck.', sourceText = '|cFFFFD200Drop:|r Finely-Embroidered Handbag', creatureID = 129920, },
	[2157] = { description = 'This young crawgling was rescued from a vicious blood troll war camp. With care, and lots of feeding, you earned his trust and Krag\'wa asked you to take care of him always.', sourceText = '|cFFFFD200Quest: |rIt Seems You\'ve Made a Friend|n|cFFFFD200Zone: |rNazmir', creatureID = 131644, },
	[2158] = { description = 'The Postmaster is often described as esoteric, mystical, even inexplicable. For the precious little known about the enigmatic mail-master, perhaps none outside of the wax seal can confirm the existence of the fantastical letter-loving Mailemental.', sourceText = '|cFFFFD200Achievement:|r Priority Mail|n', creatureID = 132366, },
	[2163] = { description = 'This silithid battle tank evolved prematurely. It will never reach full size, but it is just as ferocious as its larger cousins.', sourceText = '|cFFFFD200Drop:|r Silithus: The Wound', creatureID = 133064, },
	[2164] = { description = '', sourceText = '', creatureID = 133825, },
	[2165] = { description = 'Francois, a pompous member of the bockgeoisie, whose wealth and influence was born from the exploitation and labor of the peckletariat.', sourceText = '|cFFFFD200Zone:|r Tiragarde Sound', creatureID = 134406, },
	[2184] = { description = 'When Whomper wants to play, he\'ll let you know with a playful headbutt.', sourceText = '|cFFFFD200In-Game Shop|r|n', creatureID = 138742, },
	[2185] = { description = 'Don\'t say anything to Cap\'n Crackers that you don\'t want repeated to you every day for the next 20 years.', sourceText = '|cFFFFD200In-Game Shop|r|n', creatureID = 138741, },
	[2186] = { description = 'Not much is known about the great snake Merektha and the many eggs in the Temple of Sethraliss, but the residents of Zandalar all seem to have a distinct amount of both respect and fear for these serpents.', sourceText = '|cFFFFD200Drop: |rMerektha|n|cFFFFD200Zone: |rTemple of Sethraliss (Mythic)', creatureID = 138964, },
	[2187] = { description = 'you\'re not entirely sure where this came from, nor do you really want to know, but you are sure that it does eat a tremendous amount of garbage.', sourceText = '|cFFFFD200Drop: |rGol\'than the Malodorous|n|cFFFFD200Zone: |rTol Dagor (Mythic)', creatureID = 139049, },
	[2188] = { description = 'Poda was just a small part of the larger business, always trying to live up to the standards of his older brother Coda.', sourceText = '|cFFFFD200Quest: |rMissing Business|n|cFFFFD200Zone: |rVol\'dun', creatureID = 139073, },
	[2189] = { description = 'Smoochums loved life in Glenbrook, it was full of people to play with and places to sleep, and also where unspeakable acts happen at the most innocent of tea parties.', sourceText = '|cFFFFD200Vendor: |rNigel Rifthold|n|cFFFFD200Zone: |rDrustvar|n|cFFFFD200Cost: |r200|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 139081, },
	[2190] = { description = 'A newly hatched Cobra can be just as deadly as a fully grown one, if not more.', sourceText = '|cFFFFD200Quest: |rMy Hungry Neighbor|n|cFFFFD200Zone: |rVol\'dun', creatureID = 139252, },
	[2192] = { description = 'The wisest engineers of Azeroth all know that the only thing more dangerous than a living evil chicken, is the mind of an evil chicken transplanted into the body of a mechanical chicken.', sourceText = '|cFFFFD200Quest: |rCutting Edge Poultry Science', creatureID = 139372, },
