	[2195] = { description = '', sourceText = '', creatureID = 139455, },
	[2196] = { description = 'Most predators favored the soft parts of their prey to attack first.  Lil\' Tika prefers to go for the face.', sourceText = '|cFFFFD200Vendor: |rHappy Holaua|n|cFFFFD200Zone: |rZuldazar|n|cFFFFD200Cost: |r200|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h|n', creatureID = 139622, },
	[2197] = { description = 'The Direhorn species is among the toughest and strongest of all things that walk on Azeroth. They can seem like gentle giants, but be cautious that you don\'t find yourself in front of one when they are on a warpath.', sourceText = '|cFFFFD200Profession: |rArchaeology', creatureID = 139744, },
	[2198] = { description = 'Taptaf is loyal beyond a doubt, as long as food is provided.', sourceText = '|cFFFFD200Quest:|r Taptaf the Pig!|n|cFFFFD200Zone: |r Drustvar', creatureID = 139770, },
	[2199] = { description = 'The Drustvar people learned over time that when burying someone or some thing that there are sadly a number of shady people looking to exhume and reanimate whatever they can find.', sourceText = '|cFFFFD200Profession: |rBattle for Azeroth Archaeology', creatureID = 139782, },
	[2200] = { description = '', sourceText = 'Vol\'dun Quest', creatureID = 139987, },
	[2201] = { description = 'The Zandalari are masters of magic and communications with the spirits, and they often imbued items with powers to strengthen their bonds with the spirit world.', sourceText = '|cFFFFD200Profession: |rBattle for Azeroth Enchanting', creatureID = 139743, },
	[2202] = { description = 'The mummy\'s wraps serves two purposes, one being to keep everything inside intact, the other is to lessen the fright of people seeing what\'s really below the wrappings.', sourceText = '|cFFFFD200Achievement: |rHow to Keep a Mummy|n|cFFFFD200Category: |rBattle Dungeon', creatureID = 140125, },
