	[2372] = { description = 'Even the toughest of crabs with the toughest of shells can succumb to the dark mysteries of the shadows.', sourceText = '|cFFFFD200Pet Battle:|r Stormsong Valley|n', creatureID = 143031, },
	[2373] = { description = 'The river frogs of Stormsong Valley have been known to try to hypnotize people by staring directly into their eyes and making a low rumbling noise.', sourceText = '|cFFFFD200Pet Battle: |rStormsong Valley', creatureID = 143032, },
	[2374] = { description = 'The hermit crabs of Stormsong Valley will use whatever they can find that fits them for their shell.  If it fits, they sits.', sourceText = '|cFFFFD200Pet Battle: |rStormsong Valley', creatureID = 143033, },
	[2375] = { description = 'Vale Marmots are very quick on their feet and are masters of digging holes for their burrows.  Children like to chase them around and try to bop them on the head when they poke their head out to catch some sunlight.', sourceText = '|cFFFFD200Pet Battle: |rStormsong Valley', creatureID = 143034, },
	[2376] = { description = 'Long ago, the population of chickens in Stormsong Valley was so high, there was fear that the chickens would eat all of the corn from the fields and leave the other residents hungry.', sourceText = '|cFFFFD200Pet Battle: |rStormsong Valley', creatureID = 143035, },
	[2377] = { description = 'The sandyback crawlers get their name from having a slightly sticky outer shell that sand sticks to as they walk around.', sourceText = '|cFFFFD200Pet Battle: |rStormsong Valley, Tiragarde Sound', creatureID = 143036, },
	[2378] = { description = 'River otters start growing whiskers at a very young age and never stop.  By the time they are fully grown and reach old age, they have more whiskers on their face than any other known creature.', sourceText = '|cFFFFD200Pet Battle: |rDrustvar, Stormsong Valley', creatureID = 143037, },
	[2613] = { description = '', sourceText = '', creatureID = 150929, },
	[2379] = { description = 'It is rumored that these honey bees can detect the pollen of a newly blossomed plant in northern Stormsong Valley when they are all the way down in Drustvar.', sourceText = '|cFFFFD200Pet Battle: |rStormsong Valley', creatureID = 143038, },
	[2380] = { description = 'The stinger of a Ravager Wasp can cause intense pain for up to three hours.  Consult a physician immediately if stung.', sourceText = '|cFFFFD200Pet Battle: |rTiragarde Sound', creatureID = 143039, },
	[2381] = { description = 'Shack crabs are the only species of hermit crabs known to regenerate limbs if torn off in battle.', sourceText = '|cFFFFD200Pet Battle: |rTiragarde Sound', creatureID = 143040, },
	[2382] = { description = 'The mucus secreted from inland croakers is so effective at repelling water, the ship builders of Kul Tiras have been using it in the wood sealant for many years.', sourceText = '|cFFFFD200Pet Battle: |rTiragarde Sound', creatureID = 143041, },
	[2383] = { description = 'The giant woodworm can burrow into almost any surface they crawl on, except solid stone.', sourceText = '|cFFFFD200Pet Battle: |rTiragarde Sound', creatureID = 143042, },
	[2384] = { description = 'Butterflies taste with their feet! I tried it once, but I only made a mess.', sourceText = '|cFFFFD200Pet Battle: |rZuldazar', creatureID = 143043, },
	[2385] = { description = 'The \'door ditch\' prank is not as effective when the house can chase you.', sourceText = '|cFFFFD200Pet Battle: |rZuldazar', creatureID = 143044, },
	[2386] = { description = 'The purple coloration helps this species blend into the rocks and shadows, but the crabs become easy prey for seagulls when they linger too long on the open sand.', sourceText = '|cFFFFD200Pet Battle: |rDrustvar', creatureID = 143045, },
	[2387] = { description = 'Many ancient tribes revered these enormous beetles.', sourceText = '|cFFFFD200Pet Battle: |rZuldazar', creatureID = 143046, },
	[2388] = { description = 'While completely harmless to trolls, the animal life within Nazmir fear these creatures.', sourceText = '|cFFFFD200Pet Battle: |rNazmir', creatureID = 143047, },
	[2389] = { description = 'Water Skimmers use the tension of water and long, hydrophobic legs to help them walk along the water\'s surface.', sourceText = '|cFFFFD200Pet Battle: |rNazmir', creatureID = 143048, },
	[2390] = { description = 'When perched on a tree, this species can be almost invisible to any prey.', sourceText = '|cFFFFD200Pet Battle: |rZuldazar', creatureID = 143049, },
	[2392] = { description = 'This species looks through the sand for the buried remains of rotting creatures. It keeps our beaches clean!', sourceText = '|cFFFFD200Pet Battle: |rNazmir', creatureID = 143050, },
	[2393] = { description = 'It\'s a repulsive creature. No one should want this.', sourceText = '|cFFFFD200Pet Battle: |rNazmir', creatureID = 143051, },
	[2394] = { description = 'A terror in life. A terror in death.', sourceText = '|cFFFFD200Pet Battle: |rNazmir', creatureID = 143052, },
	[2395] = { description = 'Apply directly to the forehead.', sourceText = '|cFFFFD200Pet Battle: |rNazmir', creatureID = 143053, },
	[2397] = { description = 'Suddenly there came a tapping...', sourceText = '|cFFFFD200Pet Battle: |rNazmir', creatureID = 143054, },
	[2398] = { description = 'How many bogs has it hopped? No one knows.', sourceText = '|cFFFFD200Pet Battle: |rNazmir', creatureID = 143055, },
	[2399] = { description = 'When you\'re a hoarder, but want to stay on the move!', sourceText = '|cFFFFD200Pet Battle: |rVol\'dun', creatureID = 143056, },
	[2400] = { description = 'A quite annoying species. No matter how high you build your walls, these creatures still manage to hop over.', sourceText = '|cFFFFD200Pet Battle: |rNazmir', creatureID = 143057, },
	[2401] = { description = 'Built to dig holes by a gnomish farmer, Rooter quickly and predictably went haywire, with disastrous results.', sourceText = '|cFFFFD200Achievement: |rPet Emporium|n|cFFFFD200Category: |rCollect', creatureID = 143142, },
	[2402] = { description = 'Built with ever-wagging tail technology, Brutus is a mechanically-certified good boy.', sourceText = '|cFFFFD200Achievement: |rToybox Tycoon|n|cFFFFD200Category: |rToy Box', creatureID = 143160, },
	[2403] = { description = 'When the Naga invaded Stormsong, they brought their favored pets: Abyssal Eels.', sourceText = '|cFFFFD200Vendor: |rMadeline Netley|n|cFFFFD200Zone: |rStormsong Valley|n|cFFFFD200Cost: |r75|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h|n|n|cFFFFD200Vendor: |rOlly|n|cFFFFD200Zone: |rZuldazar|n|cFFFFD200Cost: |r75|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 143175, },
	[2404] = { description = 'These hardy bees form the backbone of a great honey and mead industry. For decades, the Tidesages forbade anyone from taking them outside of Stormsong.', sourceText = '|cFFFFD200Vendor: |rSister Lilyana|n|cFFFFD200Zone: |rStormsong Valley|n|cFFFFD200Faction: |rStorm\'s Wake - Revered|n|cFFFFD200Cost: |r250|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 143176, },
	[2405] = { description = 'Corlain Falcons are trained from birth to be scouts and fighting companions. The few spared from the Heartsbane curse serve the Order of Embers in the same capacity.', sourceText = '|cFFFFD200Vendor: |rQuartermaster Alcorn|n|cFFFFD200Zone: |rDrustvar|n|cFFFFD200Faction: |rOrder of Embers - Revered|n|cFFFFD200Cost: |r200|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 143177, },
	[2406] = { description = 'This piglet avoided being turned into sausage at the hands of Roland with the help of a cunning spider.', sourceText = '|cFFFFD200Vendor: |rNigel Rifthold|n|cFFFFD200Zone: |rDrustvar|n|cFFFFD200Cost: |r75|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h|n|n|cFFFFD200Vendor: |rOlly|n|cFFFFD200Zone: |rZuldazar|n|cFFFFD200Cost: |r75|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 143178, },
	[2407] = { description = 'This red-nosed bunny is incredibly soft to the touch; just watch your fingers.', sourceText = '|cFFFFD200Vendor: |rNigel Rifthold|n|cFFFFD200Zone: |rDrustvar|n|cFFFFD200Cost: |r300|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h|n|n|cFFFFD200Vendor: |rOlly|n|cFFFFD200Zone: |rZuldazar|n|cFFFFD200Cost: |r300|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 143179, },
	[2445] = { description = 'Scuttle was named after the ships he destroys, not his movement patterns, and don\'t you forget it.', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n', creatureID = 143794, },
	[2446] = { description = 'The shells of this particular group of crabs shimmer like gems on the sand.', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n', creatureID = 143801, },
	[2408] = { description = 'Silver dust sparkling on each of this spider\'s legs attracts prey; unwittingly drawn into an inescapable web.', sourceText = '|cFFFFD200Vendor: |rNigel Rifthold|n|cFFFFD200Zone: |rDrustvar|n|cFFFFD200Cost: |r100|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h|n|n|cFFFFD200Vendor: |rOlly|n|cFFFFD200Zone: |rZuldazar|n|cFFFFD200Cost: |r100|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 143181, },
	[2409] = { description = 'Best not to park your mount beneath this one.', sourceText = '|cFFFFD200Vendor: |rDana Pull|n|cFFFFD200Zone: |rTiragarde Sound|n|cFFFFD200Cost: |r50|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h|n|n|cFFFFD200Vendor: |rOlly|n|cFFFFD200Zone: |rZuldazar|n|cFFFFD200Cost: |r50|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 143184, },
	[2410] = { description = 'This adorable mass of whirlers, cogs, and sprockets wasn\'t assembled in Gnomeregan. Where then?', sourceText = '|cFFFFD200Vendor: |rDana Pull|n|cFFFFD200Zone: |rTiragarde Sound|n|cFFFFD200Cost: |r75|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h|n|n|cFFFFD200Vendor: |rOlly|n|cFFFFD200Zone: |rZuldazar|n|cFFFFD200Cost: |r75|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 143188, },
	[2411] = { description = 'She loves tummy rubs. Just watch out for splinters.', sourceText = '|cFFFFD200Zone: |rDrustvar', creatureID = 143189, },
	[2412] = { description = 'A popular pet among children, despite frequent accidental gorings.', sourceText = '|cFFFFD200Vendor:|r Happy Holaua|n|cFFFFD200Zone:|r Zuldazar|n|cFFFFD200Cost:|r 100|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h|n|n|cFFFFD200Vendor: |rDodger|n|cFFFFD200Zone: |rBoralus Harbor|n|cFFFFD200Cost: |r100|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 143191, },
	[2413] = { description = 'Known to chomp on the shins of everything from saurids to brutosaurs.', sourceText = '|cFFFFD200Vendor:|r Happy Holaua|n|cFFFFD200Zone:|r Zuldazar|n|cFFFFD200Cost:|r 50|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h|n|n|cFFFFD200Vendor: |rDodger|n|cFFFFD200Zone: |rBoralus Harbor|n|cFFFFD200Cost: |r50|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h|n', creatureID = 143193, },
	[2414] = { description = 'Exceptionally squishy, especially after a big meal.', sourceText = '|cFFFFD200Vendor: |rShoak|n|cFFFFD200Zone: |rNazmir|n|cFFFFD200Cost: |r200|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h|n|n|cFFFFD200Vendor: |rBlind Wunja|n|cFFFFD200Zone: |rNazmir|n|cFFFFD200Cost: |r200|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h|n|n|cFFFFD200Vendor: |rDodger|n|cFFFFD200Zone: |rBoralus Harbor|n|cFFFFD200Cost: |r200|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 143194, },
	[2415] = { description = 'Krag\'wa reluctantly sent Tragg to the Talanji Expedition with the hope that they could find someone suitable to escort him on a tour of Azeroth.', sourceText = '|cFFFFD200Vendor: |rProvisioner Lija|n|cFFFFD200Zone: |rNazmir|n|cFFFFD200Faction: |rTalanji\'s Expedition - Revered|n|cFFFFD200Cost: |r75|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 143195, },
	[2416] = { description = 'White Brutosaurs were on the brink of extinction until some enterprising adventurers took action. Lil\' Ben\'fon may have inherited her father\'s size, but definitely has her mother\'s temper!', sourceText = '|cFFFFD200Vendor: |rHappy Holaua|n|cFFFFD200Zone: |rZuldazar|n|cFFFFD200Cost: |r300|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h|n', creatureID = 143196, },
	[2417] = { description = 'Always hungry, and has a shockingly good personality.', sourceText = '|cFFFFD200Vendor: |rJenoh|n|cFFFFD200Zone: |rVol\'dun|n|cFFFFD200Cost: |r250|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h|n|n|cFFFFD200Vendor: |rDodger|n|cFFFFD200Zone: |rBoralus Harbor|n|cFFFFD200Cost: |r250|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 143197, },
	[2418] = { description = 'The scavenger Loa thinks you can teach this saurid a thing or two. Just don\'t lose sight of your coin purse.', sourceText = '|cFFFFD200Vendor: |rHappy Holaua|n|cFFFFD200Zone: |rZuldazar|n|cFFFFD200Cost: |r100|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h|n|n|cFFFFD200Vendor: |rDodger|n|cFFFFD200Zone: |rBoralus Harbor|n|cFFFFD200Cost: |r100|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 143198, },
	[2419] = { description = 'These toads do not appreciate having their legs pulled off.', sourceText = '|cFFFFD200Vendor: |rShoak|n|cFFFFD200Zone: |rNazmir|n|cFFFFD200Cost: |r200|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h|n|n|cFFFFD200Vendor: |rDodger|n|cFFFFD200Zone: |rBoralus Harbor|n|cFFFFD200Cost: |r200|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 143199, },
	[2420] = { description = 'No one knows where this haunted tiki mask came from, or why its so angry all the time. It seems to like you though!', sourceText = '|cFFFFD200Vendor: |rShoak|n|cFFFFD200Zone: |rNazmir|n|cFFFFD200Cost: |r500|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h|n|n|cFFFFD200Vendor: |rBlind Wunja|n|cFFFFD200Zone: |rNazmir|n|cFFFFD200Cost: |r500|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h|n|n|cFFFFD200Vendor: |rDodger|n|cFFFFD200Zone: |rBoralus Harbor|n|cFFFFD200Cost: |r500|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 143200, },
	[2421] = { description = 'They are so cute before they develop razor-sharp teeth and venom.', sourceText = '|cFFFFD200Vendor:|r Madeline Netley|n|cFFFFD200Zone:|r Stormsong Valley|n|cFFFFD200Cost:|r 200|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h|n|n|cFFFFD200Vendor:|r Happy Holaua|n|cFFFFD200Zone:|r Zuldazar|n|cFFFFD200Cost:|r 200|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 143202, },
	[2422] = { description = 'So pretty you could eat it. But don\'t. It\'s poisonous.', sourceText = '|cFFFFD200Vendor:|r Madeline Netley|n|cFFFFD200Zone:|r Stormsong Valley|n|cFFFFD200Cost:|r 100|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h|n|n|cFFFFD200Vendor:|r Happy Holaua|n|cFFFFD200Zone:|r Zuldazar|n|cFFFFD200Cost:|r 100|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 143203, },
	[2423] = { description = 'Finding freshwater pearls with this crab by your side will be a pinch.', sourceText = '|cFFFFD200Vendor:|r Madeline Netley|n|cFFFFD200Zone:|r Stormsong Valley|n|cFFFFD200Cost:|r 250|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h|n|n|cFFFFD200Vendor:|r Jenoh|n|cFFFFD200Zone:|r Vol\'dun|n|cFFFFD200Cost:|r 250|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 143204, },
	[2447] = { description = 'The kindleweb spider species is known amongst the island tribes for its warm, threadable webbing.', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n|n', creatureID = 143802, },
	[2805] = { description = '', sourceText = '', creatureID = 161429, },
	[2424] = { description = 'This frog\'s peculiar red color appears to have evolved specifically for camouflage in blood. Spooky.', sourceText = '|cFFFFD200Vendor: |rNigel Rifthold|n|cFFFFD200Zone: |rDrustvar|n|cFFFFD200Cost: |r100|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h|n|n|cFFFFD200Vendor: |rShoak|n|cFFFFD200Zone: |rNazmir|n|cFFFFD200Cost: |r100|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 143205, },
	[2425] = { description = 'She just wanted to get away. Find a nice shell. Sit on a beach. Now she\'s your pet.', sourceText = '|cFFFFD200Vendor:|r Madeline Netley|n|cFFFFD200Zone:|r Stormsong Valley|n|cFFFFD200Cost:|r 50|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h|n|n|cFFFFD200Vendor:|r Jenoh|n|cFFFFD200Zone:|r Vol\'dun|n|cFFFFD200Cost:|r 50|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 143206, },
	[2426] = { description = 'It is a well-established fact that wasps produce far higher quality honey than bees. Well-established in very, very small circles of wasp farmers.', sourceText = '|cFFFFD200Vendor:|r Madeline Netley|n|cFFFFD200Zone:|r Stormsong Valley|n|cFFFFD200Cost:|r 50|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h|n|n|cFFFFD200Vendor:|r Jenoh|n|cFFFFD200Zone:|r Vol\'dun|n|cFFFFD200Cost:|r 50|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 143207, },
	[2427] = { description = 'He is a real trooper, faithfully tagging along with Kojo for years. He enjoys hide and seek as well as some serious shell sliding.', sourceText = '|cFFFFD200Vendor: |rCollector Kojo|n|cFFFFD200Zone: |rStormsong Valley|n|cFFFFD200Faction: |rTortollan Seekers - Revered|n|cFFFFD200Cost: |r200|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h|n|n|cFFFFD200Vendor: |rCollector Kojo|n|cFFFFD200Zone: |rZuldazar|n|cFFFFD200Faction: |rTortollan Seekers - Revered|n|cFFFFD200Cost: |r200|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 143209, },
	[2428] = { description = 'This horrific, flesh-eating plant promises it can make all of your wildest wishes come true. If you feed it.', sourceText = '|cFFFFD200Vendor: |rJenoh|n|cFFFFD200Zone: |rVol\'dun|n|cFFFFD200Cost: |r100|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h|n|n|cFFFFD200Vendor: |rMadeline Netley|n|cFFFFD200Zone: |rStormsong Valley|n|cFFFFD200Cost: |r100|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 143211, },
	[2429] = { description = 'A chip off the ol\' girl\'s block. Azeriti is fiercely protective and will strike out against anyone wishing to do her mother harm.', sourceText = '|cFFFFD200Vendor: |rMagni Bronzebeard|n|cFFFFD200Zone: |rSilithus|n|cFFFFD200Faction: |rChampions of Azeroth - Revered|n|cFFFFD200Cost: |r100|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 143214, },
	[2430] = { description = 'The platysaur species has been roaming around the lands since before time was even measured. An ancient species that has adapted to walking on the land, and also for some reason has adapted to repeating some short sounds three times in a row.', sourceText = '|cFFFFD200Vendor: |rTalutu|n|cFFFFD200Zone: |rZuldazar|n|cFFFFD200Cost: |r100000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|n|cFFFFD200Vendor: |rTricky Nick|n|cFFFFD200Zone: |rTiragarde Sound|n|cFFFFD200Cost: |r100000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t', creatureID = 143360, },
	[2431] = { description = 'The Dreadticks have grown to be a very invasive and dominant species wherever they wind up, they are incredibly quick to assault any new species until they find a weakness to exploit.', sourceText = '|cFFFFD200Achievement: |rBattle Safari|n|cFFFFD200Category: |rCollect', creatureID = 143464, },
	[2432] = { description = 'The young of Arathi Highlands\' plains spiders may kick clouds of barbed abdomen hairs into predators\' eyes. All this, and poisonous web-spitters, too.', sourceText = '|cFFFFD200Drop: |rVenomarus|n|cFFFFD200Zone: |rArathi Highlands', creatureID = 143499, },
	[2433] = { description = 'So long as one of his Sproutlings remains, Branchlord Aldrus will never truly die.', sourceText = '|cFFFFD200Drop: |rBranchlord Aldrus|n|cFFFFD200Zone: |rArathi Highlands', creatureID = 143503, },
	[2434] = { description = 'Either Darbel Montrose was a terrible warlock, or she simply adored cute things. Perhaps both.', sourceText = '|cFFFFD200Drop: |rDarbel Montrose|n|cFFFFD200Zone: |rArathi Highlands', creatureID = 143507, },
	[2435] = { description = 'Constructs designed to assist the titans with sundry small tasks, this example is oddly small. Teeny Titan Orb! Let\'s Go!', sourceText = '|cFFFFD200Drop: |rEcho of Myzrael|n|cFFFFD200Zone: |rArathi Highlands', creatureID = 143515, },
	[2436] = { description = 'While Yogursa managed to survive the blight infesting many of Hillsbrad\'s ursines, she was driven mad, and her cubs forever tainted.', sourceText = '|cFFFFD200Drop: |rYogursa|n|cFFFFD200Zone: |rArathi Highlands', creatureID = 143533, },
	[2437] = { description = 'Part owl, part bear... and part stag, which handily avoids potential lawsuits.', sourceText = '|cFFFFD200Drop: |rRagebeak|n|cFFFFD200Zone: |rArathi Highlands', creatureID = 143563, },
	[2438] = { description = 'Plaguefeather and her brood feed well on the carrion the battle for Stromgarde has left scattered across the Highlands.', sourceText = '|cFFFFD200Drop: |rPlaguefeather|n|cFFFFD200Zone: |rArathi Highlands', creatureID = 143564, },
	[2439] = { description = 'These drops of Azerite are volatile and prone to explosive reactions when prodded.', sourceText = '|cFFFFD200Vendor: |rMagni Bronzebeard|n|cFFFFD200Zone: |rSilithus|n|cFFFFD200Faction: |rChampions of Azeroth - Revered|n|cFFFFD200Cost: |r200|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 143611, },
	[2440] = { description = 'A living piece of Arathi\'s most infamous stone-faced behemoth, Fozruk.', sourceText = '|cFFFFD200Drop: |rFozruk|n|cFFFFD200Zone: |rArathi Highlands', creatureID = 143627, },
	[2441] = { description = 'Boulderfist legend recounts a cloud of carrion birds following ogre warriors through the Dark Portal during the Second War; it may be that all two-headed scavengers in Azeroth are their descendants.', sourceText = '|cFFFFD200Drop: |rMan-Hunter Rog|n|cFFFFD200Zone: |rArathi Highlands', creatureID = 143628, },
	[2442] = { description = 'Bumbles may not have been the largest bee of the millions within the hive, but that doesn\'t mean he can\'t grow into a champion.', sourceText = '|cFFFFD200Achievement: |rLet\'s Bee Friends|n|cFFFFD200Category: |rBattle for Azeroth', creatureID = 143730, },
	[2443] = { description = 'A mighty war machine of adorable proportions.', sourceText = '|cFFFFD200Vendor: |rVindicator Jaelaana|n|cFFFFD200Location: |rBoralus|n|cFFFFD200Faction: |r7th Legion', creatureID = 143738, },
	[2444] = { description = 'A mighty war machine of adorable proportions.', sourceText = '|cFFFFD200Vendor: |rRansa Greyfeather|n|cFFFFD200Location: |rZuldazar|n|cFFFFD200Faction: |rThe Honorbound', creatureID = 143739, },
	[2448] = { description = 'The hooves of this subgroup of goats have evolved to be equally surefooted on sand or stone.', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n', creatureID = 143803, },
	[2449] = { description = 'Someone put this mechanical gorilla on the island to monitor the species, but who, and why?', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n', creatureID = 143804, },
	[2450] = { description = 'The only balls this hound chases are diamond-hard, fireproof rocks. Very heavy, not fun to throw.', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n|n', creatureID = 143805, },
	[2451] = { description = 'Is that a venomous worm in your pocket or... why is that in your pocket?', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n|n', creatureID = 143816, },
	[2452] = { description = 'Poro cannot fly, but enjoys a long lifespan hopping along the ground and shaking the fruits from trees.', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n', creatureID = 143797, },
	[2453] = { description = 'Even young brineshell are drawn to the line in the sky that meets the waves, it calls them.', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n', creatureID = 143819, },
	[2454] = { description = 'Based on the size, the mantid appear to be re-growing their swarm, likely due to the presence of a new empress.', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n', creatureID = 143820, },
	[2455] = { description = 'Captain Nibs was once the leader of an ill-fated sea voyage of slightly-too-smart-for-their-own-good monkeys that quickly capsized.', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n', creatureID = 143795, },
	[2456] = { description = 'A wealthy pirate captain met with a strange man on the docks of an unknown city and bartered for everlasting life for his faithful companion. As with most monkey-related wishes, something went wrong.', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n', creatureID = 143796, },
	[2457] = { description = 'The coldlight once were quite common across the oceans, to the grumbles of many hunters, but no longer.', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n', creatureID = 143821, },
	[2458] = { description = 'Getting just one of these fragrant beasts damp is the olfactory equivalent to rolling around in a pile of wet dogs.', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n', creatureID = 143818, },
	[2459] = { description = 'Particularly partial to coastal scrub grasses, this hardy species has survived many years in uncharted lands.', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n', creatureID = 143817, },
	[2460] = { description = 'The voru\'kar are fiercely loyal to their leader, Vorus\'arak, who led them to safety from the Lich King\'s grasp.', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n', creatureID = 143822, },
	[2461] = { description = 'Whatever the story of his mysterious eyepatch is, he\'s not talking.', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n', creatureID = 143799, },
	[2462] = { description = 'A baby octopode is technically referred to as a "squish."', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n', creatureID = 143798, },
	[2463] = { description = 'During the warm summer months, the noise that these pests make is enough to drive a person mad.', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n', creatureID = 143806, },
	[2464] = { description = 'It\'s sting is worse than... well nothing, really. Are you sure you want to keep this as a pet?', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n', creatureID = 143807, },
	[2465] = { description = 'You know how disappointing it is when you return to your beverage and the ice has all melted? This guy\'s fault.', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n', creatureID = 143811, },
	[2466] = { description = 'The most nefarious of the trickster spirits, and the reason why the pandaren invented fireproof pants.', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n', creatureID = 143809, },
	[2467] = { description = '"Beware the cool breeze, for it brings trickery." - pandaren proverb', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n', creatureID = 143812, },
	[2468] = { description = 'The earth That\'s rumbling beneath your feet... is it quaking or laughing, or both?', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n', creatureID = 143810, },
	[2469] = { description = 'The sprites that inhabit the islands of the Great Sea have thought of many traps to lure in unwary visitors.', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n', creatureID = 143808, },
	[2471] = { description = 'Littlehoof likes: bounding, bouncing, frolicking, and headbutts, in that order.', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n', creatureID = 143813, },
	[2472] = { description = 'Wear gloves with this little guy, he loves to bite!', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n', creatureID = 143814, },
	[2473] = { description = 'The bright scales of this rare wind serpent soak up the sunlight to keep it warm, even in shade.', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n', creatureID = 143815, },
	[2474] = { description = 'Mechanical Toads can hop over seven million times before their gears begin to wear out.', sourceText = '|cFFFFD200Profession: |rBattle for Azeroth Alchemy', creatureID = 143957, },
	[2475] = { description = 'These creatures are able to control each arm individually, which allows for great multi-tasking, and the ability to give the best hugs.', sourceText = '|cFFFFD200Profession: |rBattle for Azeroth Alchemy', creatureID = 143959, },
	[2476] = { description = 'The smallest of cuts can be devastating to this species, they start to leak all over the place.', sourceText = '|cFFFFD200Profession: |rBattle for Azeroth Alchemy', creatureID = 143958, },
	[2477] = { description = 'The horns of a crawg grow at a tremendous rate starting almost immediately after they are born, and is among the hardest of natural materials found on a living species.', sourceText = '|cFFFFD200Profession: |rBattle for Azeroth Alchemy', creatureID = 143374, },
	[2478] = { description = 'No one knows who first started dressing up crabs in Azeroth, and it won\'t be stopping any time soon.', sourceText = '|cFFFFD200PvP:|r Honor Level 400 Reward|n|n|cFFFFD200Vendor: |rCaptain Roberts|n|cFFFFD200Zone: |rDalaran|n|cFFFFD200Cost: |r500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|n|cFFFFD200Vendor: |rMarshal Frazer|n|cFFFFD200Zone: |rTiragarde Sound|n|cFFFFD200Cost: |r500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|n|cFFFFD200Vendor: |rKnight-Captain Jessica|n|cFFFFD200Zone: |rStormwind City|n|cFFFFD200Cost: |r500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t', creatureID = 144005, },
