	[2504] = { description = '', sourceText = '', creatureID = 146181, },
	[2525] = { description = 'The tarantulas that inhabit southern Redridge are smaller than their cousins elsewhere in Azeroth, but make up for their size with speed and ferocity.', sourceText = '|cFFFFD200Drop: |rGnollfeaster|n|cFFFFD200Zone: |rRedridge Mountains', creatureID = 147221, },
	[2526] = { description = 'Thought to be lost in a massive explosion, the smartest of engineers were able to rebuild Feathers into a magnificent mechanical creation.', sourceText = '|cFFFFD200Quest: |rShe\'s in a Happier Place|cFFFFD200Zone: |rTanaris|cFFFFD200Quest: |rhe\'s Dead, Jastor|cFFFFD200Zone: |rTanaris', creatureID = 147583, },
	[2527] = { description = 'There is a story told to some children that all winged creatures were unable to fly until Pa\'ku provided enough wind to lift them off of the ground.', sourceText = '|cFFFFD200Drop: |rPa\'ku\'s Aspect|n|cFFFFD200Zone: |rBattle of Dazar\'alor', creatureID = 147586, },
	[2528] = { description = 'A toad with Krag\'wa\'s bloodline has an average expectancy to leap over seven million times during its lifetime.', sourceText = '|cFFFFD200Drop: |rPa\'ku\'s Aspect|n|cFFFFD200Zone: |rBattle of Dazar\'alor (Mythic)', creatureID = 147585, },
	[2529] = { description = 'The large plates on the back of this lizard are made of a mysterious but highly conductive material, making it ideal to charge up and expel massive amounts of electricity.', sourceText = '|cFFFFD200Drop: |rPa\'ku\'s Aspect|n|cFFFFD200Zone: |rBattle of Dazar\'alor', creatureID = 147587, },
	[2530] = { description = 'The Mechantula was created by an unknown gnomish Engineer. Rumor has it when the original schematic was found, it was in a room covered with dusty old webbing, but neither the original Mechantula or the engineer who drafted the schematic could be found.', sourceText = '|cFFFFD200Profession: |rBattle for Azeroth Engineering', creatureID = 147619, },
	[2531] = { description = 'Mechanical Cockroaches are less likely to survive an apocalypse than an actual living cockroach, making this one of the more questionable feats of engineering.', sourceText = '|cFFFFD200Vendor: |rMicro Zoox|n|cFFFFD200Zone: |rDun Morogh|n|cFFFFD200Cost: |r1|Hitem:165835|h|TINTERFACE/ICONS/INV_Misc_EngGizmos_35.blp:0|t|h', creatureID = 147671, },
	[2532] = { description = 'The radiation from all of the machinery in Gnomeregan gives these rats a soft green glow when looked at in absolute darkness.', sourceText = '|cFFFFD200Vendor: |rMicro Zoox|n|cFFFFD200Zone: |rDun Morogh|n|cFFFFD200Cost: |r2|Hitem:165835|h|TINTERFACE/ICONS/INV_Misc_EngGizmos_35.blp:0|t|h', creatureID = 147679, },
	[2533] = { description = 'The Alarm-O-Dog was built for security purposes, but people really wanted one for their home because they are so darn cute.', sourceText = '|cFFFFD200Vendor: |rMicro Zoox|n|cFFFFD200Zone: |rDun Morogh|n|cFFFFD200Cost: |r3|Hitem:165835|h|TINTERFACE/ICONS/INV_Misc_EngGizmos_35.blp:0|t|h', creatureID = 147692, },
	[2534] = { description = 'Nobody is sure why someone thought making a miniature version of the machines in Gnomeregan was a good idea, but they sure are fun to play with.', sourceText = '|cFFFFD200Achievement: |rPet Battle Challenge: Gnomeregan|n|cFFFFD200Category: |rPet Battles', creatureID = 147838, },
	[2535] = { description = 'The eerie magic in Drustvar has caused some unimaginable creatures to show up. Some still lurk in the shadows, others seem to be made out of shadows.', sourceText = '|cFFFFD200Achievement: |rFamily Battler|n|cFFFFD200Category: |rBattle', creatureID = 147884, },
	[2537] = { description = 'The raptors of Zandalar have their feathers at a very young age, making them seem like easy prey to an unsuspecting predator. In reality, these little terrors eat everything they want to and are prey to almost no other species.', sourceText = '|cFFFFD200Pet Battle: |rZuldazar', creatureID = 148517, },
	[2538] = { description = 'Like many other species, you can get a rough guess of how large the raptor will be as a fully grown adult by the size of its largest claw.', sourceText = '|cFFFFD200Drop: |rN\'chala the Egg Thief|n|cFFFFD200Zone: |rZuldazar', creatureID = 148520, },
	[2539] = { description = 'One of the earliest recorded victories in a large battle on Zandalar depicted mighty warriors riding into battle on armored raptors. The legend has it that the warrior did not choose their raptor, but that the raptor would choose the warrior.', sourceText = '|cFFFFD200Vendor: |rProvisioner Stoutforge|n|cFFFFD200Zone: |rTiragarde Sound|n|cFFFFD200Cost: |r100|Hcurrency:1717|h|TINTERFACE/ICONS/INV_AllianceWarEffort.BLP:0|t|h', creatureID = 148524, },
	[2540] = { description = 'During certain times of the day, the sunlight can cause a reflection on the feathers of the red tinted raptors causing them to look more like flames than feathers.', sourceText = '|cFFFFD200Vendor: |rProvisioner Mukra|n|cFFFFD200Zone: |rZuldazar|n|cFFFFD200Cost: |r100|Hcurrency:1716|h|TINTERFACE/ICONS/INV_HordeWarEffort.BLP:0|t|h', creatureID = 148525, },
	[2541] = { description = 'This upper class parrot is only for the most distinguished of sailors.', sourceText = '', creatureID = 148542, },
	[2544] = { description = 'The Sentinel Owls of Darkshore are forever protective of their homeland.', sourceText = '|cFFFFD200Drop:|r Burninator Mark V|n|cFFFFD200Zone:|r Darkshore|n|n|cFFFFD200Drop:|r Athil Dewfire|n|cFFFFD200Zone:|r Darkshore', creatureID = 148781, },
	[2545] = { description = 'When Cyclarus came to Darkshore, small cyclones appeared all over the place. Some of them dissipated over time, others found their way back to Cyclarus and were absorbed into the surrounding winds.', sourceText = '|cFFFFD200Drop: |rCyclarus|n|cFFFFD200Zone: |rDarkshore', creatureID = 148784, },
	[2546] = { description = 'This is what happens when you try to one-up Pip Quickwit\'s tall tales of being stuck inside of dangerous creatures.', sourceText = '|cFFFFD200Drop: |rConflagros|cFFFFD200Zone: |rDarkshore', creatureID = 148825, },
	[2547] = { description = 'Hydrath Water Droplets are also an excellent skin moisturizer.', sourceText = '|cFFFFD200Drop: |rHydrath|n|cFFFFD200Zone: |rDarkshore', creatureID = 148841, },
	[2548] = { description = 'A lone Ancient of Lore in Darnassus managed to save this Treant in its final actions with a magical aura to prevent the flames from burning it any further.', sourceText = '|cFFFFD200Drop:|r Zim\'kaga|n|cFFFFD200Zone:|r Darkshore|n|n|cFFFFD200Drop:|r Onu|n|cFFFFD200Zone:|r Darkshore', creatureID = 148843, },
	[2549] = { description = 'This Jelly has seen better days. Now it sees through fifty different eyes.', sourceText = '|cFFFFD200Drop: |rSoggoth the Slitherer|n|cFFFFD200Zone: |rDarkshore', creatureID = 148844, },
	[2550] = { description = 'Once a mighty warrior of the n\'raqi, Zur\'aj\'s power was stolen and absorbed by an unknown rival.', sourceText = '|cFFFFD200Drop: |rTwilight Prophet Graeme|n|cFFFFD200Zone: |rDarkshore', creatureID = 148846, },
	[2551] = { description = 'The playful kicks of these baby moose are enough to knock an orc flat on his back.', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n', creatureID = 148976, },
	[2552] = { description = 'We don\'t know much about this species, but we do know this: bats aren\'t bugs.', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n', creatureID = 148979, },
	[2553] = { description = 'Get it while it lasts.', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n', creatureID = 148981, },
	[2554] = { description = 'The needleback porcupines have survived life in island climates by evolving to be extra lucky.', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n', creatureID = 148982, },
	[2555] = { description = 'Albino ravens are incredibly rare and highly prized amongst the traders of the Great Sea.', sourceText = '|cFFFFD200Vendor: |rCaptain Klarisa|n|cFFFFD200Zone: |rTiragarde Sound|n|cFFFFD200Cost: |r200|Hcurrency:1710|h|TINTERFACE/ICONS/INV_Misc_AzsharaCoin.BLP:0|t|h|n|n|cFFFFD200Vendor: |rCaptain Zen\'taga|n|cFFFFD200Zone: |rZuldazar|n|cFFFFD200Cost: |r200|Hcurrency:1710|h|TINTERFACE/ICONS/INV_Misc_AzsharaCoin.BLP:0|t|h', creatureID = 148985, },
	[2556] = { description = 'Though barely a hatchling, its claws are already sharp enough to pierce the flesh of many smaller critters.', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n', creatureID = 148984, },
	[2557] = { description = 'Firesting wasps are said to have one of the worst bites of any wasp species, causing a pain like a burning blaze.', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n', creatureID = 148988, },
	[2558] = { description = 'Lord Woofington was given his title after the rest of the denizens of Havenswood succumbed to madness.', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n|n', creatureID = 148989, },
	[2559] = { description = 'It has one solution to all of its problems: give em\' a lashing.', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n', creatureID = 148990, },
	[2560] = { description = 'Key activities include: rolling over, sunning self, whimpering for belly scratches, and enjoying belly scratches.', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n|n', creatureID = 148991, },
	[2561] = { description = 'These whelps are frequently captured by the denizens of Jorundall when still young and trainable.', sourceText = '|cFFFFD200Drop:|r Island Expeditions|n', creatureID = 148995, },
	[2562] = { description = 'The crimson species of octopode grows larger and hardier than others, and is capable of sustaining crushing pressure in the depths.', sourceText = '|cFFFFD200Vendor: |rCaptain Klarisa|n|cFFFFD200Zone: |rTiragarde Sound|n|cFFFFD200Cost: |r100|Hcurrency:1710|h|TINTERFACE/ICONS/INV_Misc_AzsharaCoin.BLP:0|t|h|n|n|cFFFFD200Vendor: |rCaptain Zen\'taga|n|cFFFFD200Zone: |rZuldazar|n|cFFFFD200Cost: |r100|Hcurrency:1710|h|TINTERFACE/ICONS/INV_Misc_AzsharaCoin.BLP:0|t|h', creatureID = 148997, },
	[2563] = { description = 'It is an honor in the Sentinel Army to be entrusted with the care of an owl born from the boughs of the great tree Teldrassil.', sourceText = '|cFFFFD200Quest: |rA New Hope|n|cFFFFD200Zone: |rDarkshore|n|n|cFFFFD200Drop:|r Orwell Stevenson|n|cFFFFD200Zone:|r Darkshore|n|n|cFFFFD200Drop:|r Grimhorn|n|cFFFFD200Zone:|r Darkshore', creatureID = 149205, },
	[2611] = { description = '', sourceText = '', creatureID = 150924, },
	[2612] = { description = '', sourceText = '', creatureID = 150925, },
	[2564] = { description = 'The convenient part of robots that fight each other is that you can just put them back together. Step two is to attach a giant laser cannon to ensure you win the next battle.', sourceText = '|cFFFFD200Drop:|r Dookthar the Destroyer|n|cFFFFD200Zone:|r Vol\'dun|n', creatureID = 149348, },
	[2565] = { description = 'Anyone serious about using a mechanical spider in battle knows that you need to buy replacement legs in bulk. Those things are fragile.', sourceText = '|cFFFFD200Drop:|r Arachni|n|cFFFFD200Zone:|r Vol\'dun|n', creatureID = 149361, },
	[2566] = { description = 'The Albatross is capable of flying further than any other species of bird on all of Azeroth.', sourceText = '|cFFFFD200Drop:|r Proudmoore Admiralty Supplies', creatureID = 149363, },
	[2567] = { description = 'Unlike most other bat species, the crimson bats are actually blind and rely entirely on echolocation to get around.', sourceText = '|cFFFFD200Drop:|r Talanji\'s Expedition Supplies', creatureID = 149372, },
	[2568] = { description = 'The feathers of the cobalt raven glow eerily in the moonlight, making them easy to spot during the night.', sourceText = '|cFFFFD200Drop:|r Order of Embers Supplies|n', creatureID = 149375, },
	[2569] = { description = 'The Abyssal Eels are able to swallow and digest something up to twice the width of their body.', sourceText = '|cFFFFD200Drop:|r Storm\'s Wake Supplies|n', creatureID = 149376, },
	[2575] = { description = 'Mr. Crabs was victorious in a lengthy brawl amongst nearly a dozen other hermit crabs to claim his current shell.', sourceText = '|cFFFFD200World Event:|r Children\'s Week', creatureID = 150098, },
	[2576] = { description = 'Beakbert\'s favorite food is a ravenberry.', sourceText = '|cFFFFD200World Event:|r Children\'s Week', creatureID = 150119, },
	[2577] = { description = 'The skin of a teal frog sometimes shimmers just right in the sunlight, providing excellent camouflage when on the surface of the water.', sourceText = '|cFFFFD200World Event:|r Children\'s Week', creatureID = 150120, },
	[2578] = { description = 'Saurolisk scales are a surprisingly flexible but durable material that are often used to reinforce or mend things that are broken or worn through.', sourceText = '|cFFFFD200World Event:|r Children\'s Week', creatureID = 150126, },
	[2579] = { description = 'Quilen were the favored companions of the ancient Mogu race due to their loyal nature.', sourceText = '|cFFFFD200Drop: |rThe Stone Guard|n|cFFFFD200Zone: |rMogu\'shan Vaults', creatureID = 150354, },
	[2580] = { description = 'Crossing into the spirit world too often can be dangerous for your health.', sourceText = '|cFFFFD200Drop: |rGara\'jal the Spiritbinder|n|cFFFFD200Zone: |rMogu\'shan Vaults', creatureID = 150356, },
	[2581] = { description = 'The Engine of Nalak\'sha is defended by many mighty beings, and many tiny beings.', sourceText = '|cFFFFD200Drop: |rElegon|n|cFFFFD200Zone: |rMogu\'shan Vaults', creatureID = 150357, },
	[2582] = { description = 'For being an inanimate object for many centuries, this statue sure moves around really well.', sourceText = '|cFFFFD200Drop: |rQin-xi|n|cFFFFD200Zone: |rMogu\'shan Vaults', creatureID = 150360, },
	[2583] = { description = 'This little cloud serpent\'s favorite activity is chasing its own tail.', sourceText = '|cFFFFD200Drop: |rTsulong|n|cFFFFD200Zone: |rTerrace of Endless Spring', creatureID = 150365, },
	[2584] = { description = 'The elemental spirits of Pandaria have been known to plan tricks to play on unsuspecting visitors years in advance.', sourceText = '|cFFFFD200Drop: |rLei Shi|n|cFFFFD200Zone: |rTerrace of Endless Spring', creatureID = 150372, },
	[2585] = { description = 'Don\'t let these eat after midnight, legend has it that it\'s very dangerous.', sourceText = '|cFFFFD200Drop: |rImperial Vizier Zor\'lok|n|cFFFFD200Zone: |rHeart of Fear', creatureID = 150374, },
	[2586] = { description = 'The glow eminating from these is both calming and eerie at the same time when in total darkness.', sourceText = '|cFFFFD200Drop: |rBlade Lord Ta\'yak|n|cFFFFD200Zone: |rHeart of Fear', creatureID = 150375, },
	[2587] = { description = 'The kunchong are extremely aggressive and will eat just about anything they can as soon as they are able to walk. If they can\'t eat it, they will smash it instead.', sourceText = '|cFFFFD200Drop: |rGaralon|n|cFFFFD200Zone: |rHeart of Fear', creatureID = 150377, },
	[2589] = { description = 'The mantid are extremely reliant upon amber for many things, and even use it in some of their magic.', sourceText = '|cFFFFD200Drop: |rAmber-Shaper Un\'sok|n|cFFFFD200Zone: |rHeart of Fear', creatureID = 150380, },
	[2590] = { description = 'As a spawn of the most dangerous sha, this little monster has some big plans, but needs to wait a few thousand years first.', sourceText = '|cFFFFD200Drop: |rGrand Empress Shek\'zeer|n|cFFFFD200Zone: |rHeart of Fear', creatureID = 150381, },
	[2591] = { description = 'This thing is equally as evil as it is adorable.', sourceText = '|cFFFFD200Achievement: |rRaiding with Leashes VI: Pets of Pandaria|n|cFFFFD200Category: |rCollect', creatureID = 150385, },
