	[2621] = { description = 'This pocket-sized Lord of Blackrock is too small to call out any classes, but he can still dish up a good insult.', sourceText = '|cFFFFD200WoW\'s 15th Anniversary|r|n', creatureID = 151779, },
	[2622] = { description = 'These dazzling hatchlings have a distinctive coat color that has been highly sought after by leatherworkers for generations.', sourceText = '|cFFFFD200World Event:|r Feast of Winter Veil |n', creatureID = 151780, },
	[2623] = { description = 'Don\'t be fooled by Dottie\'s soft coat and big eyes, her tiny, sharp teeth are for more than just munching on grass.', sourceText = '|cFFFFD200In-Game Shop|r', creatureID = 151788, },
	[2638] = { description = 'Some argue that the beard is what grants a spell caster their power, but they\'re wrong. The hat is where the magic is really from.', sourceText = '|cFFFFD200Achievement: |rPet Battle Challenge: Stratholme|n|cFFFFD200Category: |rPet Battles', creatureID = 154445, },
	[2645] = { description = 'The crabs of Nazjatar seem relatively unfazed by the sudden absence of the water.', sourceText = '|cFFFFD200Pet Battle: |rNazjatar', creatureID = 154697, },
	[2646] = { description = 'The bright shells on these crabs makes them easy prey for predators, so they mostly stay to the shadows.', sourceText = '|cFFFFD200Pet Battle: |rNazjatar', creatureID = 154702, },
	[2647] = { description = 'The bright shells of the hermit crabs of Nazjatar have the jewelry crafters of Azeroth buzzing with excitement.', sourceText = '|cFFFFD200Pet Battle: |rNazjatar', creatureID = 154704, },
	[2648] = { description = 'The chitterspine make a distinctive clicking noise when they see prey in the open.', sourceText = '|cFFFFD200Pet Battle: |rNazjatar', creatureID = 154706, },
	[2649] = { description = 'The "hissing" of this crab can be heard underwater, and is actually it rapidly clicking its smaller claws.', sourceText = '|cFFFFD200Pet Battle: |rNazjatar', creatureID = 154708, },
	[2650] = { description = 'These albatrosses are slightly smaller than those found inland, and they can travel great distances over the sea.', sourceText = '|cFFFFD200Pet Battle: |rNazjatar', creatureID = 154710, },
	[2651] = { description = 'The deep sea frenzies of Nazjatar have long been a staple food of the naga.', sourceText = '|cFFFFD200Pet Battle: |rNazjatar', creatureID = 154712, },
	[2652] = { description = 'Some frenzies have been touched by whispers in the deep, and seek to turn the tides red with blood.', sourceText = '|cFFFFD200Pet Battle: |rNazjatar', creatureID = 154714, },
	[2653] = { description = 'The pressure of the crushing depths at which these snails exist help to form its distinctive shell.', sourceText = '|cFFFFD200Pet Battle: |rNazjatar', creatureID = 154716, },
	[2657] = { description = 'In the lightless trenches and chasms of the Great Sea, life still exists.', sourceText = '|cFFFFD200Pet Battle: |rThe Eternal Palace', creatureID = 154718, },
	[2658] = { description = 'Of the many frightening things that lurk in the deeps, a chitterspine nest is really up there.', sourceText = '|cFFFFD200Pet Battle: |rThe Eternal Palace|n', creatureID = 154720, },
	[2659] = { description = 'If you are diving into the deeps and one of these attaches itself to your leg, don\'t panic -- they\'re relatively harmless.', sourceText = '|cFFFFD200Pet Battle: |rThe Eternal Palace', creatureID = 154722, },
	[2660] = { description = 'What else is there to say? Slugs love muck.', sourceText = '|cFFFFD200Pet Battle: |rNazjatar', creatureID = 154724, },
	[2661] = { description = 'Snooters have a very good sense of smell and can easily find grass and seeds buried under snow or scrap.', sourceText = '|cFFFFD200Pet Battle: |rMechagon', creatureID = 154767, },
	[2662] = { description = 'The dark fur of the duskytooth allows them to more easily hide in the junkheaps of Mechagon Island.', sourceText = '|cFFFFD200Pet Battle: |rMechagon', creatureID = 154769, },
	[2663] = { description = 'Of all of the creatures in Mechagon, the roaches seem the most unaffected by all of the changes.', sourceText = '|cFFFFD200Pet Battle: |rMechagon', creatureID = 154771, },
	[2664] = { description = 'Experiments on this roach have grown it to an enormous size. Who would want that?', sourceText = '|cFFFFD200Pet Battle: |rMechagon', creatureID = 154773, },
	[2665] = { description = 'The frogs of Mechagon had two choices when faced with mechanization: hide or breed. This species chose the latter.', sourceText = '|cFFFFD200Pet Battle: |rMechagon', creatureID = 154775, },
	[2666] = { description = 'The frogs of Mechagon had two choices when faced with mechanization: hide or breed. This species chose the former.', sourceText = '|cFFFFD200Pet Battle: |rMechagon', creatureID = 154777, },
	[2667] = { description = 'The mechanical frogs of Mechagon Island have evolved to eat various mechanical insects, somehow.', sourceText = '|cFFFFD200Pet Battle: |rMechagon', creatureID = 154779, },
	[2668] = { description = 'The results of King Mechagon\'s experiments can be seen throughout the city and island.', sourceText = '|cFFFFD200Pet Battle: |rOperation: Mechagon', creatureID = 154781, },
	[2669] = { description = 'Some of the few remaining fleshy creatures on Mechagon, they provide food and even companionship for the citizens of Rustbolt.', sourceText = '|cFFFFD200Pet Battle: |rMechagon', creatureID = 154783, },
	[2670] = { description = 'Experiments in mechanization have driven the few remaining critters in Mechagon to hide or flee.', sourceText = '|cFFFFD200Pet Battle: |rMechagon', creatureID = 154785, },
	[2671] = { description = 'Specimen 97 escaped from his cage before being fully mechanized. Now, he seeks revenge.', sourceText = '|cFFFFD200Pet Battle: |rMechagon', creatureID = 154787, },
	[2672] = { description = 'Though not real rats, they have taken over the back alleys of Mechagon City and carry diseased programs, earning them the name.', sourceText = '|cFFFFD200Pet Battle: |rOperation: Mechagon', creatureID = 154789, },
	[2673] = { description = 'Natural burrowing tendencies were programmed into these critters, and now they perform vital functions breaking down the great piles of scrap.', sourceText = '|cFFFFD200Pet Battle: |rMechagon', creatureID = 154791, },
	[2674] = { description = 'When you need a hand, you know who to call.', sourceText = '|cFFFFD200Pet Battle: |rOperation: Mechagon', creatureID = 154794, },
	[2675] = { description = 'The machinists of Mechagon may automate many of the tasks of the city, but they also spend a lot of time repairing and maintaining their inventions.', sourceText = '|cFFFFD200Pet Battle: |rOperation: Mechagon', creatureID = 154796, },
	[2676] = { description = 'doesn\'t remember what it was originally programmed to do, just wants to battle other pets.', sourceText = '|cFFFFD200Pet Battle: |rMechagon', creatureID = 154798, },
	[2677] = { description = 'A re-emergence of whelps outside of Grim Batol has the red dragonflight worried -- something is happening in the lost city.', sourceText = '|cFFFFD200Pet Battle: |rTwilight Highlands', creatureID = 154803, },
	[2678] = { description = 'The eels that inhabit the trenches and chasms of Nazjatar are a favored food of many creatures that inhabit the area.', sourceText = '|cFFFFD200Pet Battle: |rNazjatar', creatureID = 154814, },
	[2680] = { description = 'Master of poking, stabbing, thrusting, jabbing, and gouging.', sourceText = '|cFFFFD200Treasure: |rAzshara Chest|n|cFFFFD200Zone: |rThe Eternal Palace', creatureID = 154819, },
	[2681] = { description = 'The murloc tribe that Murgle belongs to is one of few that have resisted the naga\'s enslavement.', sourceText = '|cFFFFD200Drop: |rKing Gakula|n|cFFFFD200Zone: |rNazjatar', creatureID = 154820, },
	[2682] = { description = 'Legend has it that the Necrofin tribe found necromancy on a series of runed tablets at the bottom of the sea.', sourceText = '|cFFFFD200Drop: |rBlindlight|n|cFFFFD200Zone: |rNazjatar', creatureID = 154821, },
	[2683] = { description = 'Don\'t let the cuteness fool you, this little monster will jab your ankles the second you\'re not looking.', sourceText = '|cFFFFD200Achievement: |rTeam Aquashock|n|cFFFFD200Category: |rPet Battles', creatureID = 154822, },
	[2684] = { description = 'A rare species of crab found mostly in Nazjatar, it hides easily amongst the ample kelp and coral of the area.', sourceText = '|cFFFFD200Drop: |rSandclaw Stoneshell|n|cFFFFD200Zone: |rNazjatar', creatureID = 154823, },
	[2685] = { description = 'Anything can be a nest, if you try hard enough.', sourceText = '|cFFFFD200Treasure:|r Glimmering Chest|n|cFFFFD200Zone:|r Nazjatar|n', creatureID = 154824, },
	[2686] = { description = 'When the sun shines on its shell just right, the light scatters in a thousand different directions.', sourceText = '|cFFFFD200Vendor: |rArtisan Okata|n|cFFFFD200Zone: |rNazjatar|n|cFFFFD200Cost: |r150|Hcurrency:1721|h|TINTERFACE/ICONS/INV_misc_enchantedpearlF.blp:0|t|h|n|n|cFFFFD200Vendor: |rFinder Pruc|n|cFFFFD200Zone: |rNazjatar|n|cFFFFD200Cost: |r150|Hcurrency:1721|h|TINTERFACE/ICONS/INV_misc_enchantedpearlF.blp:0|t|h|n|n|cFFFFD200Drop: |rIridescent Glimmershell|n|cFFFFD200Zone: |rNazjatar', creatureID = 154825, },
	[2687] = { description = 'A gift, found one day and saved for a meaningful occasion, should things ever turn around.', sourceText = '|cFFFFD200Quest:|r Glittering Shell|n', creatureID = 154826, },
	[2688] = { description = 'A gift, found one day and saved for a meaningful occasion, should things ever turn around.', sourceText = '|cFFFFD200Quest:|r Beauty in the Deeps|n', creatureID = 154827, },
	[2689] = { description = 'A rare evolution of chitterspine, with elongated spines to pierce soft flesh and eyes.', sourceText = '|cFFFFD200Drop: |rNeedlespine|n|cFFFFD200Zone: |rNazjatar', creatureID = 154828, },
	[2690] = { description = 'As if a dark, underwater cave wasn\'t nightmarish enough, these things exist -- and they love it there.', sourceText = '|cFFFFD200Drop: |rCaverndark Terror|n|cFFFFD200Zone: |rNazjatar', creatureID = 154829, },
	[2691] = { description = 'These gruesome crabs are attracted to the scent of blood and seem to revel in combat.', sourceText = '|cFFFFD200Vendor: |rCrafticus Mindbender|n|cFFFFD200Zone: |rNazjatar|n|cFFFFD200Cost: |r30|Hitem:168802|h|TINTERFACE/ICONS/INV_misc_token_pvp01.BLP:0|t|h|n|n|cFFFFD200Vendor: |rDazzerian|n|cFFFFD200Zone: |rNazjatar|n|cFFFFD200Cost: |r30|Hitem:168802|h|TINTERFACE/ICONS/INV_misc_token_pvp01.BLP:0|t|h', creatureID = 154830, },
	[2692] = { description = 'In the lightless caverns, a bright shell color doesn\'t make you any worse at ambushing.', sourceText = '|cFFFFD200Drop: |rLady Ashvane|n|cFFFFD200Zone: |rThe Eternal Palace', creatureID = 154831, },
	[2693] = { description = 'In a dark place unknown to all, the seabed writhes with the children of Nalaada, a carpet of life and death.', sourceText = '|cFFFFD200Drop: |rElderspawn Nalaada|n|cFFFFD200Zone: |rNazjatar', creatureID = 154832, },
	[2694] = { description = 'In the fathomless depths beyond the Eternal Palace, minds are lost in the blackness.', sourceText = '|cFFFFD200Drop: |rBlackwater Behemoth|n|cFFFFD200Zone: |rThe Eternal Palace', creatureID = 154833, },
	[2695] = { description = 'Frenzies in Nazjatar have learned to operate in large schools or face consumption by the many predators of the deeps.', sourceText = '|cFFFFD200Drop: |rDaggertooth Terror|n|cFFFFD200Zone: |rNazjatar', creatureID = 154834, },
	[2696] = { description = 'The influence of the old gods on the aquatic life of Nazjatar is unmistakable, as many simply wander as mindless eyes of N\'Zoth.', sourceText = '|cFFFFD200Drop: |rZa\'qul|n|cFFFFD200Zone: |rThe Eternal Palace', creatureID = 154835, },
	[2697] = { description = 'Spireshells produce a softer shell when young which will be grown out of quickly.', sourceText = '|cFFFFD200Drop: |rAmethyst Spireshell|n|cFFFFD200Zone: |rNazjatar', creatureID = 154836, },
	[2698] = { description = 'Even with its softer juvenile shell, a young prismatic spireshell still glitters like a diamond.', sourceText = '|cFFFFD200Vendor: |rSpeaker Utia|n|cFFFFD200Zone: |rNazjatar|n|cFFFFD200Faction: |rAnkoan - Revered|n|cFFFFD200Cost: |r500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t', creatureID = 154837, },
	[2699] = { description = 'The bright coloration of this deep water slug seems to give off a soft, warm glow.', sourceText = '|cFFFFD200Vendor: |rAtolia Seapearl|n|cFFFFD200Zone: |rNazjatar|n|cFFFFD200Faction: |rUnshackled - Revered|n|cFFFFD200Cost: |r500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t', creatureID = 154838, },
	[2700] = { description = 'Watch your ears.', sourceText = '|cFFFFD200Drop: |rMirecrawler|n|cFFFFD200Zone: |rNazjatar', creatureID = 154839, },
	[2701] = { description = 'The elements clash and battle for relics constantly, but few moreso than the Tidestone.', sourceText = '|cFFFFD200Drop: |rPrince Typhonus|n|cFFFFD200Zone: |rNazjatar', creatureID = 154840, },
	[2702] = { description = 'The Tidestone would empower minions of both the storm and the sea, and is sought after by both parties.', sourceText = '|cFFFFD200Drop: |rPrince Vortran|n|cFFFFD200Zone: |rNazjatar', creatureID = 154841, },
	[2703] = { description = 'This pile of animated sand is fun to squish between your fingers, but doesn\'t really like it when you do that.', sourceText = '|cFFFFD200Drop:|r Sandcastle|n|cFFFFD200Zone:|r Nazjatar', creatureID = 154842, },
	[2806] = { description = '', sourceText = '', creatureID = 161430, },
	[2807] = { description = '', sourceText = '', creatureID = 161432, },
	[2704] = { description = 'The hydra matriarchs of Nazjatar steal and destroy one another\'s eggs constantly, so you can never really know what will hatch out of one.', sourceText = '|cFFFFD200Drop: |rNazjatar', creatureID = 154843, },
	[2706] = { description = 'Standing next to a brinestone algan will remind you of the smell of the sea, mixed with rotting kelp.', sourceText = '|cFFFFD200Drop: |rAvarius|n|cFFFFD200Zone: |rNazjatar', creatureID = 154845, },
	[2707] = { description = 'There\'s no mistaking the evidence -- algan breed by budding. We can all put this argument to rest now.', sourceText = '|cFFFFD200Drop: |rRockweed Shambler|n|cFFFFD200Zone: |rNazjatar', creatureID = 154846, },
	[2708] = { description = 'The predatory coral of Nazjatar can grow incredibly quickly given the right conditions.', sourceText = '|cFFFFD200Drop: |rCarnivorous Lasher|n|cFFFFD200Zone: |rNazjatar', creatureID = 154847, },
	[2709] = { description = 'The eels of Nazjatar are experts at hiding in the many cracks and rocks of the sea floor.', sourceText = '|cFFFFD200Drop: |rVor\'koth|n|cFFFFD200Zone: |rNazjatar', creatureID = 154848, },
	[2710] = { description = 'When the waves crashed down around the city, not all were fortunate enough to receive N\'Zoth\'s gift.', sourceText = '|cFFFFD200Vendor: |rFeylana the Handler|n|cFFFFD200Zone: |rNazjatar|n|cFFFFD200Cost: |r40|Hcurrency:1721|h|TINTERFACE/ICONS/INV_misc_enchantedpearlF.blp:0|t|h', creatureID = 154849, },
	[2711] = { description = 'The birth of an exceptionally rare Golden Snorf was such an abnormality that not even King Mechagon could force himself to mechanize his beloved, fuzzy friend.', sourceText = '|cFFFFD200Drop:|r King Mechagon|n|cFFFFD200Zone:|r Operation: Mechagon (Mythic)|n', creatureID = 154850, },
	[2712] = { description = 'Of all snooters, this one has the softest fur. Ironically, it does not like to be pet very much.', sourceText = '|cFFFFD200Drop: |rFungarian Furor|n|cFFFFD200Zone: |rMechagon', creatureID = 154851, },
	[2713] = { description = 'A rare snooter, and hard to find, it\'s known for burrowing itself deep under wet foliage to nest.', sourceText = '|cFFFFD200Vendor: |rStolen Royal Vendorbot|n|cFFFFD200Zone: |rMechagon|n|cFFFFD200Faction: |rRustbolt Resistance - Honored|n|cFFFFD200Cost: |r500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t', creatureID = 154852, },
	[2714] = { description = 'Many advancements have been made to the initial prototype homing beacon over the years.', sourceText = '|cFFFFD200Zone: |rMechagon', creatureID = 154853, },
	[2715] = { description = 'It\'s not clear who the original owner of this arm was, but they\'re not getting it back now.', sourceText = '|cFFFFD200Drop: |rMalfunctioning Beastbot|n|cFFFFD200Zone: |rMechagon', creatureID = 154854, },
	[2716] = { description = 'As handy as this invention is, it was also programmed to absolutely despise hand puns.', sourceText = '|cFFFFD200Profession: |rJunkyard Tinkering', creatureID = 154855, },
	[2717] = { description = 'One of the microbots used to guard the city of Rustbolt, now purchasable for a reasonable price.', sourceText = '|cFFFFD200Profession: |rJunkyard Tinkering', creatureID = 154856, },
	[2718] = { description = 'he\'s just so happy to be programmed to exist.', sourceText = '|cFFFFD200Drop: |rHK-8 Aerial Oppression Unit|n|cFFFFD200Zone: |rOperation: Mechagon', creatureID = 154857, },
	[2719] = { description = 'Absolutely do not let him bite your bones.', sourceText = '|cFFFFD200Drop: |rBonepicker|n|cFFFFD200Zone: |rMechagon', creatureID = 154893, },
