	[2833] = { description = 'N\'Zoth\'s corruption spread across many beings, including this little Anubisath.', sourceText = '|cFFFFD200Drop: |rRa-den|n|cFFFFD200Zone: |rNy\'alotha, the Waking City', creatureID = 161921, },
	[2834] = { description = 'The aqir hive has countless numbers of beings in it, all of which are supportive of the ever-present Hivemind.', sourceText = '|cFFFFD200Drop: |rTek\'ris|n|cFFFFD200Zone: |rNy\'alotha, the Waking City', creatureID = 161923, },
	[2835] = { description = 'The winged aqir are very fast and agile, and their wings provide a nice breeze on a warm afternoon.', sourceText = '|cFFFFD200Drop: |rTek\'ris|n|cFFFFD200Zone: |rNy\'alotha, the Waking City', creatureID = 161924, },
	[2836] = { description = 'This creepy eye is looking at everyone, always, and only has to blink once every 10,000 years.', sourceText = '|cFFFFD200Treasure: |rVault of the Black Empire|n|cFFFFD200Zone: |rNy\'alotha, the Waking City', creatureID = 161946, },
	[2837] = { description = 'It is very unclear whether this slime started with all of those eyes inside of it, or if those are "leftovers".', sourceText = '|cFFFFD200Profession: |rFishing|n|cFFFFD200Zone: |rCorrupted Uldum or Vale of Eternal Blossoms', creatureID = 161951, },
	[2838] = { description = 'It is always C\'Thuffer. Always.', sourceText = '|cFFFFD200Treasure: |rRexxar\'s Corrupted Chest|n|cFFFFD200Zone: |rVision of Orgrimmar', creatureID = 161959, },
	[2839] = { description = 'Only a being of immense power could scar such an adorable creature, unless this hare was already approaching the darkness...', sourceText = '|cFFFFD200Treasure: |rUmbric\'s Corrupted Chest|n|cFFFFD200Zone: |rVision of Stormwind', creatureID = 161961, },
	[2840] = { description = 'This cat eats anything from random scraps of food to strange glowing cubes of immense power. Be careful in front of its mouth.', sourceText = '|cFFFFD200Treasure: |rAlleria\'s Corrupted Chest|n|cFFFFD200Zone: |rVision of Stormwind', creatureID = 161962, },
	[2841] = { description = 'A recent debate has risen up about whether Void-Scarred rats are better or worse than plagued rats.', sourceText = '|cFFFFD200Treasure: |rKelsey\'s Corrupted Chest|n|cFFFFD200Zone: |rVision of Stormwind', creatureID = 161963, },
	[2842] = { description = 'This ooze leaves behind a slimy trail as it moves that has a stench that would rival a beast rotting in the dunes of Uldum for a thousand years.', sourceText = '|cFFFFD200Treasure: |rAlleria or Thrall\'s Corrupted Chest|n|cFFFFD200Zone: |rVision of Stormwind or Ogrimmar', creatureID = 161964, },
	[2843] = { description = 'The shell on these beetles is quite brittle from the void interference, but it also has a strangely fast regeneration rate.', sourceText = '|cFFFFD200Drop: |rVision of the Twisting Sands Creatures', creatureID = 161966, },
	[2844] = { description = 'This little guy grows an additional eye somewhere on its body every year. It rhymes with...', sourceText = '|cFFFFD200Achievement:|r Reeking of Visions|n', creatureID = 161967, },
	[2845] = { description = 'His tentacles give you the best hug your brain has ever felt.', sourceText = '|cFFFFD200Drop: |rThe Forgotten|n|cFFFFD200Zone: |rVale of Eternal Blossoms', creatureID = 161954, },
	[2846] = { description = 'Where did it come from, where does it go? Where did you come from, creepy tentacle?', sourceText = '|cFFFFD200Drop: |rWill of N\'Zoth|n|cFFFFD200Zone: |rVale of Eternal Blossoms', creatureID = 161992, },
	[2847] = { description = 'Dragonbreath Chili is nothing compared to the stench coming from the mouth of this thing.', sourceText = '|cFFFFD200Drop: |rLord Aj\'qirai|n|cFFFFD200Zone: |rUldum', creatureID = 161997, },
	[2848] = { description = 'These Aqir Scarabs are masters at tunneling through any terrain.', sourceText = '|cFFFFD200Drop:|r Skikx\'traz|n|cFFFFD200Zone: |rUldum', creatureID = 162004, },
	[2849] = { description = 'These Pygmy Camels are often mistaken for Mysterious Camel Figurines.', sourceText = '|cFFFFD200Vendor: |rProvisioner Qorra|n|cFFFFD200Zone: |rUldum', creatureID = 162006, },
	[2850] = { description = 'This little guy was exiled and cursed by other watchers of Uldum to spend the rest of its time as a tiny insignificant being.', sourceText = '|cFFFFD200Drop: |rUldum Accord Supplies', creatureID = 162007, },
	[2851] = { description = 'Three legs, two arms, three eyeballs, the extras are for emergency situations only.', sourceText = '|cFFFFD200Drop: |rAmalgamation of Flesh|n|cFFFFD200Zone: |rUldum', creatureID = 162012, },
	[2852] = { description = 'Several adventurous miners have gone after the jade quilen in search of riches, but none have ever been heard from again.', sourceText = '|cFFFFD200Drop: |rRajani Supplies', creatureID = 162013, },
	[2853] = { description = 'Despite what the young pandaren may tell you, the windfeather cranes do not actually have feathers made out of wind.', sourceText = '|cFFFFD200Vendor: |rZhang Ku|n|cFFFFD200Zone: |rVale of Eternal Blossoms', creatureID = 162014, },
	[2854] = { description = '', sourceText = '', creatureID = 162458, },
	[2855] = { description = '', sourceText = '', creatureID = 162461, },
	[2856] = { description = '', sourceText = '', creatureID = 162465, },
	[2857] = { description = '', sourceText = '', creatureID = 162466, },
	[2858] = { description = '', sourceText = '', creatureID = 162468, },
	[2859] = { description = '', sourceText = '', creatureID = 162469, },
	[2860] = { description = '', sourceText = '', creatureID = 162470, },
	[2861] = { description = '', sourceText = '', creatureID = 162471, },
	[2863] = { description = 'Nobody is sure what sort of powder is coming off of these moths, and everyone is too terrified to find out what it actually is.', sourceText = '|cFFFFD200Pet Battle: |rUldum', creatureID = 162499, },
	[2864] = { description = 'Even some of the insects have succumbed to the void. They really do adapt and survive through everything.', sourceText = '|cFFFFD200Pet Battle: |rUldum', creatureID = 162502, },
	[2865] = { description = 'These look especially magnificent when gliding over the water under a full moon.', sourceText = '|cFFFFD200Pet Battle: |rVale of Eternal Blossoms', creatureID = 162518, },
	[2866] = { description = 'Is the void glow in addition to their natural glow, or does it replace it? The world may never know.', sourceText = '|cFFFFD200Pet Battle: |rVale of Eternal Blossoms', creatureID = 162506, },
	[2867] = { description = 'The golden chirper has managed to stay its pristine self, despite corruption attempts from both the Sha and from the forces of N\'Zoth.', sourceText = '|cFFFFD200Pet Battle: |rVale of Eternal Blossoms', creatureID = 162525, },
	[2868] = { description = 'The name implies that there were 12 experiments done before this one that were less effective than sticking some guns onto a mechanical tiger. What could those have possibly been?', sourceText = '|cFFFFD200Vendor: |rBurt Macklyn|n|cFFFFD200Zone: |rBurning Steppes|n|cFFFFD200Cost: |r3|Hitem:174360|h|TINTERFACE/ICONS/INV_Jewelcrafting_ShadowsongAmethyst_02.blp:0|t|h', creatureID = 162663, },
	[2869] = { description = 'This bear\'s necessities are fresh batteries and an occasional oil change.', sourceText = '|cFFFFD200Vendor: |rBurt Macklyn|n|cFFFFD200Zone: |rBurning Steppes|n|cFFFFD200Cost: |r2|Hitem:174360|h|TINTERFACE/ICONS/INV_Jewelcrafting_ShadowsongAmethyst_02.blp:0|t|h', creatureID = 162670, },
	[2870] = { description = 'This lasher does not come from a black lagoon, or from the stars, or from beyond the moon. It just wants you to feed it.', sourceText = '|cFFFFD200Vendor: |rBurt Macklyn|n|cFFFFD200Zone: |rBurning Steppes|n|cFFFFD200Cost: |r1|Hitem:174360|h|TINTERFACE/ICONS/INV_Jewelcrafting_ShadowsongAmethyst_02.blp:0|t|h', creatureID = 162677, },
	[2871] = { description = '', sourceText = '', creatureID = 163534, },
	[2872] = { description = 'Void magic and giant flying insects, a combination nobody ever asked for. Surprisingly effective!', sourceText = '|cFFFFD200Quest: |rThey Grow So Fast|n|cFFFFD200Zone: |rUldum', creatureID = 163646, },
	[2878] = { description = 'If you received points for causing mischief while teleporting around as a tiny adorable vulpin, Renny would be on top of the leaderboard.', sourceText = '|cFFFFD200Quest: |rRenny the Vulpin', creatureID = 163897, },
	[2885] = { description = '', sourceText = '', creatureID = 169477, },
	[2886] = { description = '', sourceText = '', creatureID = 169478, },
	[2887] = { description = '', sourceText = '', creatureID = 169479, },
	[2888] = { description = 'A wandering adventurer saved a small band of murlocs that had lost their homes, and a glimmer of hope was born.', sourceText = '|cFFFFD200Quest:|r Guardian of the Smallest|n|cFFFFD200Zone:|r Grizzly Hills|n', creatureID = 169514, },
	[2889] = { description = 'Inspired by the phalynx of Bastion, Engineers have modified the design to be approximately 300% more adorable.', sourceText = '|cFFFFD200Profession:|r Engineering|n', creatureID = 170421, },
	[2890] = { description = 'All dogs go to Bastion. Cats go to Revendreth.', sourceText = '|cFFFFD200In-Game Shop|r ', creatureID = 171025, },
	[2891] = { description = 'The crypts and tombs of Revendreth offer the perfect spider breeding ground, and Tomb Burster\'s brood has become fiercely territorial of their foothold.', sourceText = '|cFFFFD200Drop: |rTomb Burster|n|cFFFFD200Zone: |rRevendreth', creatureID = 171117, },
	[2892] = { description = 'Lightspawn can always be found on the battlefields where light touches, no matter what form it takes.', sourceText = '|cFFFFD200Drop: |rRevendreth', creatureID = 171119, },
	[2893] = { description = 'Beware awakening the power of knowledge.', sourceText = '|cFFFFD200Drop: |rScrivener Lenua|n|cFFFFD200Zone: |rRevendreth', creatureID = 171120, },
	[2894] = { description = 'A soul burdened with the sins of its past.', sourceText = '|cFFFFD200Treasure: |rSecret Treasure|n|cFFFFD200Zone: |rRevendreth', creatureID = 171122, },
	[2895] = { description = 'A soul, having faced the sins of the past, wanders lost in the recent turmoil of the Shadowlands.', sourceText = '|cFFFFD200Pet Battle:|r Revendreth|n', creatureID = 171123, },
	[2896] = { description = 'The sludge that can give rise to the dredgers has been known to coalesce and run a muck.', sourceText = '|cFFFFD200Drop:|r Bog Beast|n|cFFFFD200Zone:|r Revendreth', creatureID = 171121, },
	[2897] = { description = 'Drawn from an overabundance of pride and wrath these manifestations seek even more to sustain themselves.', sourceText = '|cFFFFD200Drop: |rManifestation of Wrath|n|cFFFFD200Drop: |rLeeched Soul|n|cFFFFD200Zone: |rRevendreth|n', creatureID = 171118, },
	[2898] = { description = 'These creatures manifest in the catacombs beneath Revendreth due to vile rituals practiced on souls there.', sourceText = '|cFFFFD200Drop: |rGrand Proctor Beryllia|n|cFFFFD200Zone: |rSanguine Depths|n|cFFFFD200Difficulty: |rMythic', creatureID = 171124, },
	[2899] = { description = 'The stone fiends were created to serve as messengers and spies for the realm of Revendreth, and those of the Court of Harvesters serve that role extremely well.', sourceText = '|cFFFFD200Vendor: |rMistress Mihaela|n|cFFFFD200Zone: |rRevendreth|n|cFFFFD200Faction: |rCourt of Harvesters - Revered|n|cFFFFD200Cost: |r250|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 171127, },
	[2900] = { description = 'A fellow message courier to Chip, Dal was always on time until one day when he did not return from his appointed round.', sourceText = '|cFFFFD200Quest:|r A Stolen Stonefiend|n|cFFFFD200Zone:|r Revendreth|n', creatureID = 171136, },
	[2998] = { description = '', sourceText = '', creatureID = 173376, },
	[2901] = { description = 'Trapped in a chest and forgotten for years, this stone fiend is mad and looking for a fight.', sourceText = '|cFFFFD200Treasure: |rBleakwood Chest|n|cFFFFD200Zone: |rRevendreth', creatureID = 171125, },
	[2902] = { description = 'This dredwing is the most common of the dredbats, seen throughout the entire realm.', sourceText = '|cFFFFD200Pet Battle:|r Revendreth|n', creatureID = 171149, },
	[2903] = { description = 'A wild dredbat, the crimson pups are tough to tame. Their elusiveness and hardiness set them apart from their kin.', sourceText = '|cFFFFD200Drop: |rRevendreth', creatureID = 171150, },
	[2904] = { description = 'A rare mutation gives this dredbat its violet underbelly, making it a sought after gift for pet collectors with eclectic tastes.', sourceText = '|cFFFFD200Covenant Feature: |rThe Ember Court|n|cFFFFD200Covenant: |rVenthyr|n|n|cFFFFD200Covenant Feature: |rQueen\'s Conservatory|n|cFFFFD200Covenant: |rNight Fae|n|n|cFFFFD200Covenant Feature: |rPath of Ascension|n|cFFFFD200Covenant: |rKyrian|n|n|cFFFFD200Covenant Feature: |rAbomination Factory|n|cFFFFD200Covenant: |rNecrolord', creatureID = 171151, },
	[2905] = { description = 'Dodger is a bit small relative to other vulpin, but is excellent at playing hide and seek.', sourceText = '|cFFFFD200Drop:|r Playful Vulpin|n|cFFFFD200Zone:|r Ardenweald', creatureID = 171247, },
	[2907] = { description = 'The claws of a vulpin sharpen themselves naturally when walking around on any sort of cobblestone or rocky surface.', sourceText = '|cFFFFD200Drop:|r Macabre|n|cFFFFD200Zone:|r Ardenweald|n', creatureID = 171246, },
	[2908] = { description = 'Despite the adorable name, the chirp is more of a horrible, spine-tingling shriek.', sourceText = '|cFFFFD200Drop: |rOld Ardeite|n|cFFFFD200Zone: |rArdenweald', creatureID = 171239, },
	[2909] = { description = 'These ferocious flying creatures scrape their beaks against hard surfaces to sharpen them and intimidate foes.', sourceText = '|cFFFFD200Treasure: |rAncient Cloudfeather Egg|n|cFFFFD200Zone: |rArdenweald', creatureID = 171238, },
	[2910] = { description = 'Floofa smells like citrus fruits when its fur is wet.', sourceText = '|cFFFFD200Vendor: |rAithlyn|n|cFFFFD200Zone: |rArdenweald|n|cFFFFD200Faction: |rThe Wild Hunt - Friendly|n|cFFFFD200Cost: |r250|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 171248, },
	[2911] = { description = 'Much like how a squid can squirt ink to escape in the water, a glitterwing can shake the dust off of its wings and similarly vanish.', sourceText = '|cFFFFD200Treasure:|r Desiccated Moth|n|cFFFFD200Zone:|r Ardenweald|n|n', creatureID = 171234, },
	[2912] = { description = 'The moths of Ardenweald can carry seeds and pods many times their own weight, aiding the Night Fae in their eternal tasks.', sourceText = '|cFFFFD200Covenant Feature: |rThe Ember Court|n|cFFFFD200Covenant: |rVenthyr|n|n|cFFFFD200Covenant Feature: |rQueen\'s Conservatory|n|cFFFFD200Covenant: |rNight Fae|n|n|cFFFFD200Covenant Feature: |rPath of Ascension|n|cFFFFD200Covenant: |rKyrian|n|n|cFFFFD200Covenant Feature: |rAbomination Factory|n|cFFFFD200Covenant: |rNecrolord', creatureID = 171233, },
	[2913] = { description = 'Shimmermoths don\'t actually shimmer in the sunlight. Nobody knows who named these, but they have a lot to answer for.', sourceText = '|cFFFFD200Vendor: |rCortinarius|n|cFFFFD200Zone: |rArdenweald|n|cFFFFD200Faction: |rMarasmius - Friendly|n|cFFFFD200Covenant: |rNight Fae|n|cFFFFD200Cost: |r250|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 171235, },
	[2914] = { description = 'When a full moon is visible, streaks of hair on the dreamfoal\'s mane start to shimmer and glow like shooting stars.', sourceText = '|cFFFFD200Treasure: |rCache of the Night|n|cFFFFD200Zone: |rArdenweald', creatureID = 171225, },
	[2915] = { description = 'When the winds blow just right, howling through the canyons of Ardenweald, you can find these foals running with the wind in a spectacular show of prowess and color.', sourceText = '|cFFFFD200Vendor: |rSpindlenose|n|cFFFFD200Zone: |rArdenweald|n|cFFFFD200Faction: |rCourt of Night - Friendly|n|cFFFFD200Covenant: |rNight Fae|n|cFFFFD200Cost: |r250|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 171224, },
	[2916] = { description = 'While these gormlings may not be the fastest diggers, they are very efficient and can move long distances underground before tiring.', sourceText = '|cFFFFD200Drop:|r Wild Hunt Supplies|n', creatureID = 171242, },
	[2917] = { description = 'Even the terrifying G\'huun was, perhaps, once this cute.', sourceText = '|cFFFFD200Vendor: |rMaster Clerk Salorn|n|cFFFFD200Zone: |rArdenweald|n|cFFFFD200Cost: |r250|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 171243, },
	[2918] = { description = 'These little slugs will try to eat literally anything they scoot over.', sourceText = '|cFFFFD200Quest: |rA New Age|n|cFFFFD200Covenant: |rKyrian|n|cFFFFD200Zone: |rBastion', creatureID = 171241, },
	[2919] = { description = 'The sting from these gorm is incredibly painful and often inflicts temporary paralysis, even on large targets.', sourceText = '|cFFFFD200Pet Battle:|r Ardenweald|n', creatureID = 171229, },
	[2920] = { description = 'There are more sharp pointy things on this species of gorm than there are on a Horde capital city.', sourceText = '|cFFFFD200Drop: |rDeifir the Untamed|n|cFFFFD200Zone: |rArdenweald', creatureID = 171231, },
	[2921] = { description = 'If you count the number of times its wings beat per minute, you can calculate how long it will be until this little terror will sting you.', sourceText = '|cFFFFD200Treasure:|r Aerto|n|cFFFFD200Zone:|r Ardenweald', creatureID = 171230, },
	[2922] = { description = 'No one knows where the devourers came from, but it is theorized that they flourish on several lost land masses in the In-Between.', sourceText = '|cFFFFD200Adventures:|r Shadowlands', creatureID = 171240, },
	[2923] = { description = 'The whooping noise these pets make when hungry will rival any tormented cry you can find in Torghast.', sourceText = '|cFFFFD200Covenant Feature: |rThe Ember Court|n|cFFFFD200Covenant: |rVenthyr|n|n|cFFFFD200Covenant Feature: |rQueen\'s Conservatory|n|cFFFFD200Covenant: |rNight Fae|n|n|cFFFFD200Covenant Feature: |rPath of Ascension|n|cFFFFD200Covenant: |rKyrian|n|n|cFFFFD200Covenant Feature: |rAbomination Factory|n|cFFFFD200Covenant: |rNecrolord', creatureID = 171227, },
	[2924] = { description = 'Part of the wildlife of Ardenweald, they sometimes sleep while standing straight up in the water.', sourceText = '|cFFFFD200Pet Battle:|r Ardenweald|n', creatureID = 171228, },
	[2925] = { description = 'A group of teroclaws is known as a fleece.', sourceText = '|cFFFFD200Drop: |rCloudfeather Guardian|n|cFFFFD200Zone: |rBastion', creatureID = 171565, },
	[2999] = { description = '', sourceText = '', creatureID = 173381, },
	[2926] = { description = 'Young teroclaws are surprisingly effective hunters and will attempt to bring down prey up to twice their size.', sourceText = '|cFFFFD200Pet Battle:|r Bastion|n', creatureID = 171567, },
	[2927] = { description = 'For some reason, these blue glimmerflies are often afraid of water.', sourceText = '|cFFFFD200Pet Battle:|r Bastion|n', creatureID = 171664, },
	[2928] = { description = 'A glimmerfly can load up nearly fifty percent of its weight as pollen and still retain full agility while flying.', sourceText = '|cFFFFD200Quest:|r How to Catch a Glimmerfly|n|cFFFFD200Zone:|r Bastion', creatureID = 171667, },
	[2929] = { description = 'The wings of glimmerflies are almost always saturated with some sort of pollen, causing a very steady flow of glittery dust to fall off of them as they fly.', sourceText = '|cFFFFD200Pet Battle:|r Bastion|n', creatureID = 171668, },
	[2930] = { description = 'Contrary to popular belief, a terrapin\'s diet has nothing to do with its shell color.', sourceText = '|cFFFFD200Pet Battle:|r Bastion|n', creatureID = 171670, },
	[2931] = { description = 'These brightly-colored terrapins are the least commonly seen in the wild. It is considered a sign of luck if you rub the top of its shell.', sourceText = '|cFFFFD200Covenant Feature: |rThe Ember Court|n|cFFFFD200Covenant: |rVenthyr|n|n|cFFFFD200Covenant Feature: |rQueen\'s Conservatory|n|cFFFFD200Covenant: |rNight Fae|n|n|cFFFFD200Covenant Feature: |rPath of Ascension|n|cFFFFD200Covenant: |rKyrian|n|n|cFFFFD200Covenant Feature: |rAbomination Factory|n|cFFFFD200Covenant: |rNecrolord', creatureID = 171569, },
	[2932] = { description = 'Terrapins prefer any sort of berry as their primary source of food, though they will eat almost anything.', sourceText = '|cFFFFD200Treasure: |rStewart\'s Stewpendous Stew|n|cFFFFD200Zone: |rBastion', creatureID = 171693, },
	[2933] = { description = 'Vulpin actually have very light-colored skin, but their incredibly thick coats hide any color from showing through.', sourceText = '|cFFFFD200Covenant Feature: |rThe Ember Court|n|cFFFFD200Covenant: |rVenthyr|n|n|cFFFFD200Covenant Feature: |rQueen\'s Conservatory|n|cFFFFD200Covenant: |rNight Fae|n|n|cFFFFD200Covenant Feature: |rPath of Ascension|n|cFFFFD200Covenant: |rKyrian|n|n|cFFFFD200Covenant Feature: |rAbomination Factory|n|cFFFFD200Covenant: |rNecrolord', creatureID = 171568, },
	[2934] = { description = 'The golden vulpin are notorious for loud, barking yowls that echo across the plains of Bastion.', sourceText = '|cFFFFD200Vendor: |rAdjutant Nikos|n|cFFFFD200Zone: |rBastion|n|cFFFFD200Faction: |rThe Ascended - Revered|n|cFFFFD200Cost: |r1765|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t', creatureID = 171694, },
	[2935] = { description = 'Despite having a very fine coat of fur, young vulpin do not shed at all.', sourceText = '|cFFFFD200Treasure: |rGift of Vesiphone|n|cFFFFD200Zone: |rBastion', creatureID = 171697, },
	[2936] = { description = 'How is a vulpin different than a fox? That is a good question.', sourceText = '|cFFFFD200Pet Battle:|r Bastion|n', creatureID = 171702, },
	[2937] = { description = 'Rarely, one of these kits is born to a golden-furred vulpin. It is considered a sign of distress in the pack.', sourceText = '|cFFFFD200Pet Battle:|r Bastion|n', creatureID = 171703, },
	[2938] = { description = 'These golden-feathered creatures do not lay golden eggs, for that you would need a golden goose.', sourceText = '|cFFFFD200Treasure: |rBastion', creatureID = 171710, },
	[2939] = { description = 'If you mat their silvery feathers down very carefully, it almost forms a mirror-like surface.', sourceText = '|cFFFFD200Pet Battle:|r Bastion|n', creatureID = 171712, },
	[2940] = { description = 'When newly-hatched, they start with colorless, white feathers. Within weeks, they flourish into a wide spectrum of beautiful colors.', sourceText = '|cFFFFD200Drop: |rBastion', creatureID = 171714, },
	[2941] = { description = 'The mane on this tiny foal reflects a majestic indigo color when the light hits it in just the right way.', sourceText = '|cFFFFD200Covenant Feature: |rPath of Ascension|n|cFFFFD200Covenant: |rKyrian', creatureID = 171716, },
	[2942] = { description = 'Few things are as adorable as a young skyfoal.', sourceText = '|cFFFFD200Adventures:|r Shadowlands', creatureID = 171719, },
	[2943] = { description = 'These flying creatures do not leave feathers out in the wild. If a feather happens to ever detach from their body, the feather glows brilliantly for a moment and then vanishes from sight.', sourceText = '|cFFFFD200Pet Battle:|r Bastion', creatureID = 171666, },
	[2944] = { description = 'The thrill of battle and the grip of war have not yet left the bones of Oonar.', sourceText = '|cFFFFD200Drop:|r Oonar\'s Arm|n|cFFFFD200Zone:|r Maldraxxus', creatureID = 171954, },
	[2945] = { description = 'A newborn bonetusk has no visible tusks, but within weeks their tusks grow to a size proportional to their body.', sourceText = '|cFFFFD200Covenant Feature: |rThe Ember Court|n|cFFFFD200Covenant: |rVenthyr|n|n|cFFFFD200Covenant Feature: |rQueen\'s Conservatory|n|cFFFFD200Covenant: |rNight Fae|n|n|cFFFFD200Covenant Feature: |rPath of Ascension|n|cFFFFD200Covenant: |rKyrian|n|n|cFFFFD200Covenant Feature: |rAbomination Factory|n|cFFFFD200Covenant: |rNecrolord', creatureID = 171982, },
	[2946] = { description = 'The tusks on these creatures are fused to their skulls in such a manner that they feel no pain when charging or ramming into anything.', sourceText = '|cFFFFD200Adventures:|r Shadowlands', creatureID = 171984, },
	[2947] = { description = 'The bright color of the luminous webspinner taunts would-be predators to chase them into their webs.', sourceText = '|cFFFFD200Treasure: |rCache of Eyes|n|cFFFFD200Zone: |rMaldraxxus', creatureID = 171985, },
	[2948] = { description = 'Maldraxxus is full of mighty and terrifying creatures, causing most denizens to be abnormally calm when around the indigenous species of giant spiders.', sourceText = '|cFFFFD200Drop: |rSister Chelicerae|n|cFFFFD200Zone: |rMaldraxxus', creatureID = 171986, },
	[2949] = { description = 'If this species spits venom at its prey, is it still technically considered venom, or just really gross?', sourceText = '|cFFFFD200Treasure: |rSprouting Growth|n|cFFFFD200Zone: |rMaldraxxus', creatureID = 171987, },
	[2950] = { description = 'That place on your back you can\'t quite scratch? Get the help of this little guy. Problem solved.', sourceText = '|cFFFFD200Pet Battle: |rMaldraxxus', creatureID = 172130, },
	[2952] = { description = 'The bubbles inside of these ooze creatures make a very satisfying pop noise when they finally surface and rupture.', sourceText = '|cFFFFD200Treasure: |rSlime-Coated Crate|n|cFFFFD200Zone: |rMaldraxxus', creatureID = 172132, },
	[3000] = { description = '', sourceText = '', creatureID = 173373, },
	[2953] = { description = 'One of Plague Deviser Marileth\'s first creations, Shy Melvin was too timid for the demands placed on slimes by the House of Plagues.', sourceText = '|cFFFFD200Drop: |rDeadly Dapperling|n|cFFFFD200Zone: |rMaldraxxus', creatureID = 172134, },
	[2954] = { description = 'A parasite that latches onto some of the larger beasts of Maldraxxus and drinks until fully-grown, one wonders how the beasts don\'t manage to notice.', sourceText = '|cFFFFD200Covenant Feature: |rThe Ember Court|n|cFFFFD200Covenant: |rVenthyr|n|n|cFFFFD200Covenant Feature: |rQueen\'s Conservatory|n|cFFFFD200Covenant: |rNight Fae|n|n|cFFFFD200Covenant Feature: |rPath of Ascension|n|cFFFFD200Covenant: |rKyrian|n|n|cFFFFD200Covenant Feature: |rAbomination Factory|n|cFFFFD200Covenant: |rNecrolord', creatureID = 172135, },
	[2955] = { description = 'Though they are quite squishy while young, the bloodlouse will eventually grow hardened plates around its entire body.', sourceText = '|cFFFFD200Drop: |rSmorgas the Feaster|n|cFFFFD200Zone: |rMaldraxxus', creatureID = 172136, },
	[2956] = { description = 'Being thrown into a pit of bloodlouse larvae is a painful fate for those that fail the great houses of Maldraxxus.', sourceText = '|cFFFFD200Drop: |rSmorgas the Feaster|n|cFFFFD200Zone: |rMaldraxxus', creatureID = 172137, },
	[2957] = { description = 'Some predators in Maldraxxus collect spines as trophies, and some of them don\'t stop moving even then.', sourceText = '|cFFFFD200Drop: |rScunner|n|cFFFFD200Zone: |rMaldraxxus', creatureID = 172139, },
	[2958] = { description = 'What it lacks in appendages, it makes up for in positive attitude.', sourceText = '|cFFFFD200Covenant Feature: |rAbomination Factory|n|cFFFFD200Covenant: |rNecrolord', creatureID = 172140, },
	[2959] = { description = 'Even the smallest bones don\'t go to waste in Maldraxxus.', sourceText = '|cFFFFD200Drop:|r Supplies of the Undying Army|n', creatureID = 172148, },
	[2960] = { description = 'Is the ooze holding the bones in, or are the bones holding the ooze together?', sourceText = '|cFFFFD200Drop: |rOily Invertebrate|n|cFFFFD200Zone: |rMaldraxxus', creatureID = 172149, },
	[2961] = { description = 'It is not advisable to hug a sludge feeler. Those stains never wash out.', sourceText = '|cFFFFD200Drop: |rMargrave Stradama|n|cFFFFD200Zone: |rPlaguefall|n|cFFFFD200Difficulty: |rMythic', creatureID = 172150, },
	[2962] = { description = 'If you can beat them, stitch them!', sourceText = '|cFFFFD200Vendor: |rNalcorn Talsen|n|cFFFFD200Zone: |rMaldraxxus|n|cFFFFD200Faction: |rThe Undying Army - Revered|n|cFFFFD200Cost: |r1765|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t', creatureID = 172151, },
	[2963] = { description = 'The soul that occupies Mu\'dud was a harsh ruler in life, and now he seeks to dominate the most violent kingdom of all.', sourceText = '|cFFFFD200Covenant Feature: |rAbomination Factory|n|cFFFFD200Covenant: |rNecrolord', creatureID = 172153, },
	[2964] = { description = 'The barbed mandibles on this species will dig deep into anything they bite, causing a lot of irritation.', sourceText = '|cFFFFD200Drop: |rRavenomous|n|cFFFFD200Zone: |rMaldraxxus', creatureID = 172155, },
	[2965] = { description = 'Unlike most spiders that drain all the fluids from their prey, the bloodfeaster has developed a taste for blood alone.', sourceText = '|cFFFFD200Covenant Feature: |rThe Ember Court|n|cFFFFD200Covenant: |rVenthyr|n|n|cFFFFD200Covenant Feature: |rQueen\'s Conservatory|n|cFFFFD200Covenant: |rNight Fae|n|n|cFFFFD200Covenant Feature: |rPath of Ascension|n|cFFFFD200Covenant: |rKyrian|n|n|cFFFFD200Covenant Feature: |rAbomination Factory|n|cFFFFD200Covenant: |rNecrolord', creatureID = 172284, },
	[2966] = { description = 'Sometimes sins of great souls are so powerful that they animate the very stones they are placed on.', sourceText = '|cFFFFD200Covenant: |rVenthyr|n|cFFFFD200Zone: |rRevendreth', creatureID = 172570, },
	[2967] = { description = 'Dredgers are the sweat (and sometimes tears) of Revendreth, and their help is absolutely critical to the workings of Revendreth.', sourceText = '|cFFFFD200Covenant Feature: |rThe Ember Court|n|cFFFFD200Covenant: |rVenthyr', creatureID = 172854, },
