	[3006] = { description = 'These dredbats are built by the Stonewright for the Court of Harvesters. They are bonded to their owners and become fierce fighters.', sourceText = '|cFFFFD200Drop:|r Court of Harvesters Supplies|n', creatureID = 173502, },
	[3007] = { description = 'Identified by the signature coloring of their many legs, these spiders are known for elaborate dances used to threaten and confuse foes.', sourceText = '|cFFFFD200Pet Battle:|r Revendreth|n', creatureID = 173506, },
	[3008] = { description = 'No longer lost, it\'s now considering a name change.', sourceText = '|cFFFFD200Drop:|r Lost Quill|n|cFFFFD200Zone:|r Revendreth', creatureID = 173508, },
	[3009] = { description = 'Roosting in the grounds of Castle Nathria, The Count did not foresee falling into an eternal stone slumber.', sourceText = '|cFFFFD200Treasure: |rThe Count|n|cFFFFD200Zone: |rRevendreth|n|cFFFFD200Cost: |r99|Hcurrency:1820|h|TINTERFACE/ICONS/INV_Jewelcrafting_LivingRuby_01.blp:0|t|h', creatureID = 173531, },
	[3010] = { description = 'Joining with their new residents in Sinfall a few of these juvenile dredbats accompanied Renathal\'s forces into the maw.', sourceText = '|cFFFFD200Drop: |r Bastion Maw Assault|n|cFFFFD200Treasure: |rSinfall Screecher Cage|n|cFFFFD200Zone: |rThe Maw', creatureID = 173533, },
	[3011] = { description = 'Roosting over Sinfall, Battie was roused one evening by the music of the first Ember Court. She has not missed a single one since.', sourceText = '|cFFFFD200Vendor: |rTemel|n|cFFFFD200Zone: |rRevendreth|n|cFFFFD200Faction: |rThe Ember Court - Revered|n|cFFFFD200Covenant: |rVenthyr|n|cFFFFD200Cost: |r250|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 173535, },
	[3012] = { description = 'Abandoned by its master in a secluded room, the little bat has been hibernating in stone for ages.', sourceText = '|cFFFFD200Treasure: |rRepaired Transport Mirror|n|cFFFFD200Covenant: |rVenthyr|n|cFFFFD200Zone: |rRevendreth', creatureID = 173536, },
	[3013] = { description = 'What vile subtance is jiggling around inside of this creature?', sourceText = '|cFFFFD200Treasure:|r Strange Growth|n|cFFFFD200Zone:|r Maldraxxus|n', creatureID = 173534, },
	[3014] = { description = 'Hiding among the reeds near muck pools, juvenile mire creepers patiently waiting for easy prey to approach.', sourceText = '|cFFFFD200Pet Battle:|r Revendreth|n', creatureID = 173555, },
	[3015] = { description = 'Subsisting on the residual anima in the waste of the Endmire, these mire creepers are withering away, yet in their famished state they are doubly dangerous.', sourceText = '|cFFFFD200Pet Battle:|r Revendreth|n', creatureID = 173556, },
	[3016] = { description = 'This particular tiny tree is infested with strange spores and doesn\'t seem to mind.', sourceText = '|cFFFFD200Vendor: |rCortinarius|n|cFFFFD200Zone: |rArdenweald|n|cFFFFD200Faction: |rMarasmius - Friendly|n|cFFFFD200Covenant: |rNight Fae|n|cFFFFD200Cost: |r250|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 173585, },
	[3017] = { description = 'These saplings grow unnaturally quickly when given enough anima, and their leaves are razor sharp when fully grown.', sourceText = '|cFFFFD200Vendor: |rMaster Clerk Salorn|n|cFFFFD200Zone: |rArdenweald|n|cFFFFD200Covenant: |rNight Fae|n|cFFFFD200Cost: |r250|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 173586, },
	[3018] = { description = 'As long as any fragment of this species is found and planted, it will regrow into the same magnificent species as it once was.', sourceText = '|cFFFFD200Covenant:|r Night Fae|n|cFFFFD200Zone:|r Ardenweald', creatureID = 173587, },
	[3019] = { description = 'Whatever you do, don\'t ask it to carry a bucket of water. We don\'t talk about what happened to that area of the Shadowlands.', sourceText = '|cFFFFD200Vendor: |rSpindlenose|n|cFFFFD200Zone: |rArdenweald|n|cFFFFD200Faction: |rCourt of Night - Friendly|n|cFFFFD200Covenant: |rNight Fae|n|cFFFFD200Cost: |r250|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 173588, },
	[3020] = { description = 'This pet has tremendous airborne agility and will always land on its feet, even when flung by a home-made spring-loaded leaper-flinger.', sourceText = '|cFFFFD200Adventures:|r Shadowlands', creatureID = 173589, },
	[3021] = { description = 'The latent energy found throughout Ardenweald\'s seeds sometimes augments small critters with unnatural abilities.', sourceText = '|cFFFFD200Pet Battle:|r Ardenweald|n', creatureID = 173590, },
	[3022] = { description = 'These critters are capable of holding a tremendous amount of food in their cheeks for easy transportation between hiding spaces.', sourceText = '|cFFFFD200Treasure: |rFaerie Trove|n|cFFFFD200Zone: |rArdenweald', creatureID = 173591, },
	[3023] = { description = 'No one is quite sure where Reginald got his or her name, but what we do know is they don\'t answer to anything else.', sourceText = '|cFFFFD200Vendor: |rCortinarius|n|cFFFFD200Zone: |rArdenweald|n|cFFFFD200Faction: |rMarasmius - Friendly|n|cFFFFD200Covenant: |rNight Fae|n|cFFFFD200Cost: |r250|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 173593, },
	[3024] = { description = 'The skies may have scorched part of Revendreth, but persistent creatures are still able to exist in these harsh conditions.', sourceText = '|cFFFFD200Drop:|r Ashen Amalgamation|n|cFFFFD200Covenant Feature:|r Ember Court|n|cFFFFD200Covenant:|r Venthyr|n|cFFFFD200Zone:|r Revendreth', creatureID = 173842, },
	[3025] = { description = 'Nobody knows why all of these skeletal hands are from the left hand. Where did the right hands go?', sourceText = '|cFFFFD200Treasure:|r Skeletal Hand Fragments|n|cFFFFD200Zone:|r Maldraxxus', creatureID = 173847, },
	[3026] = { description = 'Torghast is a tower of hardship and perpetual agony, which is a perfect environment for these creatures.', sourceText = '|cFFFFD200Drop:|r Torghast|n', creatureID = 173849, },
	[3027] = { description = 'Many have asked where these creatures came from, and how they made it to the Maw, but none that researched them have ever been heard from again.', sourceText = '|cFFFFD200Drop:|r Torghast|n|n', creatureID = 173850, },
	[3028] = { description = 'If a detached hand claps by itself inside of Torghast, can the Jailer hear it?', sourceText = '|cFFFFD200Drop:|r Torghast|n|n', creatureID = 173851, },
	[3029] = { description = 'The mysterious forces that animate this mobile mitt don\'t seem particularly evil, yet.', sourceText = '|cFFFFD200Drop:|r Torghast|n', creatureID = 173988, },
	[3030] = { description = 'Sometimes it is best to leave what strange creature you found in a mysterious jar in the depths of Torghast alone.', sourceText = '|cFFFFD200Drop:|r Torghast|n', creatureID = 173989, },
	[3032] = { description = 'Its teeth are razor sharp and will chew through just about anything.', sourceText = '|cFFFFD200Drop:|r Torghast|n', creatureID = 173990, },
	[3033] = { description = 'These guardians of Torghast are always watching for suspicious activity.', sourceText = '|cFFFFD200Drop:|r Torghast|n|n', creatureID = 173991, },
	[3034] = { description = 'That eerie feeling you get when the shadows seem to shift and follow you isn\'t just a feeling, it\'s usually one of these.', sourceText = '|cFFFFD200Drop:|r Torghast|n', creatureID = 173992, },
	[3035] = { description = 'Poking a tiny gormlng in the side makes a very satisfying, squishy noise. It is NOT advisable to poke one too many times.', sourceText = '|cFFFFD200Drop: |rGormbore|n|cFFFFD200Zone: |rArdenweald', creatureID = 173993, },
	[3036] = { description = 'Remornia has been exposed to untold amounts of evil and sin, and will siphon power from any available source to fuel its desires.', sourceText = '|cFFFFD200Drop: |rSire Denathrius|n|cFFFFD200Zone: |rCastle Nathria', creatureID = 173994, },
	[3037] = { description = 'Staring at one of these creates a deep void of emotion at the very core of your being, then fills it with a sense of dread.', sourceText = '|cFFFFD200Drop: |rEternas the Tormentor|n|cFFFFD200Zone: |rThe Maw', creatureID = 174081, },
	[3038] = { description = 'This species has a very distinct set of markings on its back that makes it easy to identify in the unending horrors of the Maw.', sourceText = '|cFFFFD200Drop:|r The Maw|n', creatureID = 174082, },
	[3039] = { description = 'Tiny flakes of skin seem to perpetually fall off of this horrible creature, leaving behind a trail and a very distinct, foul odor.', sourceText = '|cFFFFD200Drop:|r The Maw|n', creatureID = 174083, },
	[3040] = { description = 'The razor sharp claws will tear into anything with ease, making for an excellent emergency can opener.', sourceText = '|cFFFFD200Drop: |rSanngror the Torturer|n|cFFFFD200Zone: |rThe Maw', creatureID = 174084, },
	[3041] = { description = 'What horrors these eyes have seen in the tower of Torghast, with their unsleeping, unblinking gaze?', sourceText = '|cFFFFD200Achievement: |rTwisting Corridors: Layer 2|n|cFFFFD200Category: |rTorghast', creatureID = 174085, },
	[3042] = { description = 'Plucking a feather from a steward is considered a sin worthy of a trip to Revendreth.', sourceText = '|cFFFFD200Covenant: |rKyrian|n|cFFFFD200Zone: |rBastion', creatureID = 174087, },
	[3043] = { description = 'Though not very fuzzy, this slimy feline is still one of the most adorable things you\'ve ever seen.', sourceText = '|cFFFFD200Covenant: |rNecrolord|n|cFFFFD200Zone: |rMaldraxxus|n', creatureID = 174088, },
	[3044] = { description = 'While young, they have an adorable face and cute little antennae. While grown up, they gain razor sharp pinchers and are unstoppable eating machines. Life is full of compromises.', sourceText = '|cFFFFD200Drop: |rTred\'ova|n|cFFFFD200Zone: |rMists of Tirna Scithe|n|cFFFFD200Difficulty: |rMythic', creatureID = 174089, },
	[3045] = { description = 'This slime seems to change color the more you look at it, and then suddenly, it appears to be looking back at you. Perhaps it is time for a rest.', sourceText = '|cFFFFD200Treasure: |rPlaguefallen Chest|n|cFFFFD200Zone: |rMaldraxxus', creatureID = 174125, },
	[3046] = { description = 'Despite being decrepit and undead, geists move with exceptional speed and agility.', sourceText = '|cFFFFD200World Event: |rShadowlands Scourge Invasion|n|cFFFFD200Cost: |r20|Hcurrency:1754|h|TInterface/ICONS/Achievement_Reputation_ArgentCrusader.blp:0|t|h', creatureID = 174181, },
	[3047] = { description = 'You got spinebugged!', sourceText = '|cFFFFD200Treasure: |rSpinebug|n|cFFFFD200Zone: |rMaldraxxus', creatureID = 174677, },
	[3049] = { description = 'Normally just a plain white in color, their skin reflects whatever they have eaten and gains wonderful spots of color depending on the organs digested.', sourceText = '|cFFFFD200Pet Battle: |rMaldraxxus', creatureID = 175021, },
	[3050] = { description = 'Legends say that if you see a bleak skitterer in your dreams, you are destined to battle one to the death.', sourceText = '|cFFFFD200Pet Battle: |rMaldraxxus', creatureID = 175022, },
	[3051] = { description = 'These slimy creatures make the strangest suction noises when they try to climb up a wall. This is a well-known fact in Maldraxxus, for obvious reasons.', sourceText = '|cFFFFD200Pet Battle: |rMaldraxxus', creatureID = 175023, },
	[3052] = { description = 'Toxic spores rain down wherever these creatures fly, turning small, unwary critters into a gruesome feast.', sourceText = '|cFFFFD200Pet Battle: |rMaldraxxus', creatureID = 175024, },
	[3053] = { description = 'A strange whelp hatched on Netherwing Ledge, it is thought to be blessed by the waning lunar energies of Shadowmoon Valley.', sourceText = '|cFFFFD200Blizzard\'s 30th Anniversary|r ', creatureID = 175203, },
	[3054] = { description = 'A good friend to Archivist Fane since his earliest days, Janeera is ready for him to let the quill go as she feels he is getting too attached.', sourceText = '|cFFFFD200Vendor: |rArchivist Janeera|n|cFFFFD200Zone: |rRevendreth|n|cFFFFD200Faction: |rThe Avowed - Exalted|n|cFFFFD200Cost: |r1000|Hcurrency:1816|h|TInterface/ICONS/6BF_Blackrock_Nova.BLP:0|t|h', creatureID = 175220, },
	[3061] = { description = 'Stewards looking to move up in steward society know to fluff up their feathers, give out tons of juice, and always be hooting.', sourceText = '|cFFFFD200Vendor: |rZeleskos|n|cFFFFD200Covenant: |rKyrian|n|cFFFFD200Zone: |rBastion', creatureID = 175561, },
	[3062] = { description = 'Tales say that the stewards were created to aid the denizens of Bastion in their eternal duties. They don\'t seem to mind.', sourceText = '|cFFFFD200Drop: |rBastion|n|cFFFFD200Covenant: |rKyrian', creatureID = 175560, },
	[3063] = { description = 'Larion are prideful beasts, and see anyone that enters their territory as a challenge.', sourceText = '|cFFFFD200Drop: |rLarionrider Orstus|n|cFFFFD200Zone: |rBastion', creatureID = 175562, },
	[3064] = { description = 'When fully-grown, a larion that has grown up with an aspirant will never leave his or her master\'s side.', sourceText = '|cFFFFD200Drop:|r Supplies of the Ascended|n', creatureID = 175564, },
	[3431] = { description = '', sourceText = '', creatureID = 200688, },
	[3065] = { description = 'Young larions are often given to aspirants as an early test of compassion and leadership.', sourceText = '|cFFFFD200Drop:|r Courage|n|cFFFFD200Zone:|r Bastion|n', creatureID = 175563, },
	[3066] = { description = 'Lucy was sent to Revendreth, as all cats are, and somehow escaped. When questioned, local dredgers blamed it on her being a cat.', sourceText = '|cFFFFD200Drop:|r Dirty Glinting Object|n', creatureID = 175715, },
	[3067] = { description = 'Spriggans are tricky foes that live in Ardenweald and torment the Night Fae. This one has decided to play the ultimate trick--joining you.', sourceText = '|cFFFFD200Achievement: |rFamily Exorcist|n|cFFFFD200Category: |rBattle', creatureID = 175756, },
	[3068] = { description = '', sourceText = '', creatureID = 175777, },
	[3070] = { description = '', sourceText = '', creatureID = 175778, },
	[3071] = { description = '', sourceText = '', creatureID = 175779, },
	[3072] = { description = '', sourceText = '', creatureID = 175780, },
	[3073] = { description = '', sourceText = '', creatureID = 175781, },
	[3074] = { description = '', sourceText = '', creatureID = 175782, },
	[3075] = { description = '', sourceText = '', creatureID = 175783, },
	[3076] = { description = '', sourceText = '', creatureID = 175784, },
	[3077] = { description = '', sourceText = '', creatureID = 175785, },
	[3078] = { description = '', sourceText = '', creatureID = 175786, },
	[3079] = { description = 'The allegiances of the spriggan are as changing as the seasons.', sourceText = '|cFFFFD200Achievement: |rAbhorrent Adversaries of the Afterlife|n|cFFFFD200Category: |rBattle', creatureID = 175787, },
	[3080] = { description = 'Raised in the verdant groves of Ardenweald, these kits are infused with the essence of rebirth.', sourceText = '|cFFFFD200Pet Battle:|r Ardenweald|n', creatureID = 176019, },
	[3081] = { description = 'These grubs consume anima-depleted materials of all types. They aren\'t picky.', sourceText = '|cFFFFD200Pet Battle: |rArdenweald', creatureID = 176020, },
	[3082] = { description = 'The wings of this moth glitter like the starry skies above Ardenweald.', sourceText = '|cFFFFD200Pet Battle: |rArdenweald', creatureID = 176021, },
	[3083] = { description = 'For a pet That\'s all mouth, where does anything it eats go? Maldraxxus necromancers have dissected many of these pets, and have no answers.', sourceText = '|cFFFFD200Pet Battle: |rMaldraxxus', creatureID = 176024, },
	[3089] = { description = '', sourceText = '', creatureID = 176657, },
	[3090] = { description = '', sourceText = '', creatureID = 176659, },
	[3091] = { description = '', sourceText = '', creatureID = 176658, },
	[3092] = { description = 'Squibbles is engineered to never leave your side.', sourceText = '|cFFFFD200Drop:|r Grummlepouch|n', creatureID = 176662, },
	[3097] = { description = 'Baubleworms are highly prized in the Veiled Market, where those featuring few gemstone flaws can fetch extreme prices.', sourceText = '|cFFFFD200Vendor: |rKeeper Ta\'hult|n|cFFFFD200Zone: |rOribos|n|cFFFFD200Cost: |r10000|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 178216, },
	[3098] = { description = 'The fields of battle teem with Lil\'Abom parts, which are perfectly interchangable and easy to put back together.', sourceText = '|cFFFFD200Drop:|r Maldraxxus Maw Assault|n', creatureID = 179008, },
	[3099] = { description = 'Infused etherwyrms can be used as portable anima batteries, but they must be trained not to flee at the sight of combat.', sourceText = '|cFFFFD200Drop:|r Ardenweald Maw Assault|n|cFFFFD200Treasure: |rEtherwyrm Cage|n|cFFFFD200Zone: |rThe Maw|n', creatureID = 179025, },
	[3100] = { description = 'Dragons come in all types of temporal classification -- timeless, time-lost, and even infinite.', sourceText = '|cFFFFD200Vendor: |rHistorian Ma\'di|n|cFFFFD200Zone: |rTanaris|n|cFFFFD200Cost: |r200|Hcurrency:1166|h|TInterface/ICONS/PVECurrency-Justice.blp:0|t|h', creatureID = 179125, },
	[3101] = { description = 'Sly snuck aboard for a trip into the Maw. he\'s not scared, he just really wants to stay in your backpack for a while.', sourceText = '|cFFFFD200Achievement: |rA Sly Fox|n|cFFFFD200Category: |rCollect', creatureID = 179083, },
	[3102] = { description = 'A young animite will rapidly grow if fed in excess. It is suggested that no more than one average soul\'s worth of anima be consumed per year to keep them docile.', sourceText = '|cFFFFD200Pet Battle: |rKorthia', creatureID = 179131, },
	[3103] = { description = 'The brilliant scales of the etherwyrm reflect the bright skies of Bastion.', sourceText = '|cFFFFD200Drop:|r Ascended War Chest|n|cFFFFD200Zone:|r The Maw', creatureID = 179132, },
	[3104] = { description = 'The gemstones on a baubleworm will quickly deteriorate if the worm is killed. Healthy worms are kept as prized pets by wealthy Brokers.', sourceText = '|cFFFFD200Vendor: |rKeeper Ta\'hult|n|cFFFFD200Zone: |rOribos|n|cFFFFD200Cost: |r1|Hitem:36812|h|TINTERFACE/ICONS/INV_Misc_Gear_01.blp:0|t|h 1|Hitem:62072|h|TINTERFACE/ICONS/INV_Staff_11.blp:0|t|h 1|Hitem:67410|h|TINTERFACE/ICONS/INV_Stone_13.blp:0|t|h', creatureID = 179137, },
	[3105] = { description = 'Baubleworms were found by the Brokers on an unknown world, and so gorgeous were their gems, that quickly the expedition turned against one another.', sourceText = '|cFFFFD200Vendor: |rKeeper Ta\'hult|n|cFFFFD200Zone: |rOribos|n|cFFFFD200Cost: |r1|Hitem:3300|h|TINTERFACE/ICONS/INV_Gauntlets_02.blp:0|t|h 1|Hitem:3670|h|TINTERFACE/ICONS/INV_Misc_Bone_01.blp:0|t|h 1|Hitem:6150|h|TINTERFACE/ICONS/INV_Misc_Bandage_06.blp:0|t|h', creatureID = 179138, },
	[3106] = { description = 'Baubleworms have no natural predators, and grow their gemstones to attract mates.', sourceText = '|cFFFFD200Vendor: |rKeeper Ta\'hult|n|cFFFFD200Zone: |rOribos|n|cFFFFD200Cost: |r1|Hitem:11406|h|TINTERFACE/ICONS/INV_Misc_Pelt_Bear_Ruin_02.blp:0|t|h 1|Hitem:11944|h|TINTERFACE/ICONS/INV_Boots_06.blp:0|t|h 1|Hitem:25402|h|TINTERFACE/ICONS/INV_Hammer_16.blp:0|t|h', creatureID = 179139, },
	[3107] = { description = 'New species of murloc are discovered around Azeroth all the time, owing to their invasive nature and varied diet.', sourceText = '|cFFFFD200Quest: |rGurgl and the Bandit|n|cFFFFD200Zone: |rEye of Azshara', creatureID = 179140, },
	[3108] = { description = 'The Purrkin hunt the invasive skrats in the Veiled Market, keeping the population just high enough that they are still needed.', sourceText = '|cFFFFD200Pet Battle: |rTazavesh, the Veiled Market', creatureID = 179164, },
	[3109] = { description = 'Purrkins are a traditional pet of the Brokers and the two have a mutually beneficial relationship -- a necessity in Broker culture.', sourceText = '|cFFFFD200Pet Battle: |rTazavesh, the Veiled Market', creatureID = 179165, },
	[3110] = { description = 'Gizmo is considered a perfect purrkin, perhaps the best that has ever existed. He is also a ruthless murderer of small critters.', sourceText = '|cFFFFD200Drop: |rP.O.S.T. Master|n|cFFFFD200Zone: |rTazavesh, the Veiled Market', creatureID = 179166, },
	[3111] = { description = 'Skrats are poisonous, venomous, and unusually damp -- everything you want in a pet.', sourceText = '|cFFFFD200Pet Battle: |rTazavesh, the Veiled Market', creatureID = 179167, },
	[3112] = { description = 'Skrats are an especially invasive species, in that they can reproduce with only a sole skrat, and consume almost anything.', sourceText = '|cFFFFD200Pet Battle: |rTazavesh, the Veiled Market', creatureID = 179168, },
	[3113] = { description = 'One particular Broker has taken a liking to the skrat species, kept this rare specimin as a pet, and inherited the smell as a souvenir.', sourceText = '|cFFFFD200Zone:|r Tazavesh, the Veiled Market|n', creatureID = 179169, },
	[3114] = { description = 'When you\'re a Maldraxxian stitchcrafter and you have to name a lot of abominations, sometimes you just go with the obvious choice.', sourceText = '|cFFFFD200Drop:|r War Chest of the Undying Army|n|cFFFFD200Zone:|r The Maw|n', creatureID = 179171, },
	[3115] = { description = 'Not quite alive, not quite dead, simply an arm, with no head.', sourceText = '|cFFFFD200Pet Battle: |rThe Maw', creatureID = 179179, },
	[3116] = { description = 'Gorm find a way to invade just about anywhere, and the Maw is no exception.', sourceText = '|cFFFFD200Drop:|r War Chest of the Wild Hunt|n|cFFFFD200Zone:|r The Maw', creatureID = 179180, },
	[3117] = { description = 'Though the spawn of a gorm queen lost within the Maw, this pet appears to be untainted by the Jailer\'s foul magics.', sourceText = '|cFFFFD200Drop: |rGralebboih|cFFFFD200Zone: |rThe Maw', creatureID = 179181, },
	[3118] = { description = 'The nails of the mawrat can inflict one of several afterlife-ending illnesses.', sourceText = '|cFFFFD200Pet Battle: |rThe Maw', creatureID = 179182, },
	[3119] = { description = 'Fragments of souls abound in the Maw, searching for meaning, or an end to their torment. But some just want to give you a hand.', sourceText = '|cFFFFD200Pet Battle: |rThe Maw', creatureID = 179183, },
	[3120] = { description = 'These ensorcelled gauntlets are used by the mawsworn to capture escaping prisoners.', sourceText = '|cFFFFD200Pet Battle: |rThe Maw', creatureID = 179219, },
	[3121] = { description = 'Struggle all you want, it will just grip harder.', sourceText = '|cFFFFD200Drop:|r The Maw|n', creatureID = 179220, },
	[3122] = { description = 'There was once a mawsworn so vile that he was killed by his own minions. So great was his malice that a fragment of him lives on in this glove.', sourceText = '|cFFFFD200Drop: |rSoulrender Dormazain|n|cFFFFD200Zone: |rSanctum of Domination', creatureID = 179222, },
	[3123] = { description = 'A species of wild pest that exists within the Maw, no one is quite sure what it eats or why it exists, but they can\'t get rid of it.', sourceText = '|cFFFFD200Pet Battle: |rThe Maw', creatureID = 179226, },
	[3124] = { description = 'Every deathroach is technically considered vile, but these really take the cake.', sourceText = '|cFFFFD200Pet Battle: |rThe Maw', creatureID = 179227, },
	[3125] = { description = 'See reflections on the water? There\'s more than darkness in the depths.', sourceText = '|cFFFFD200Drop: |rHelsworn Soulseeker|n|cFFFFD200Zone: |rThe Maw', creatureID = 179228, },
	[3126] = { description = 'These eyes were sent to slow and debilitate the wayward souls of the Maw, so that they could be more easily captured.', sourceText = '|cFFFFD200Pet Battle: |rKorthia, The Maw', creatureID = 179229, },
	[3127] = { description = 'One of several indistinguishable pets, all named Chompy.', sourceText = '|cFFFFD200Visitor Daily: |rOozing with Character|n|cFFFFD200Zone: |rKorthia|n|n|cFFFFD200Visitor Daily: |rLocal Reagents|n|cFFFFD200Zone: |rKorthia', creatureID = 179230, },
	[3128] = { description = 'The Eye of the Jailer, diffused, is now merely an all-seeing magical bauble.', sourceText = '|cFFFFD200Drop: |rEye of the Jailer|n|cFFFFD200Zone: |rSanctum of Domination', creatureID = 179232, },
	[3129] = { description = 'The rage Zovaal harbored for his punished existence echoes through the eye.', sourceText = '|cFFFFD200Drop: |rEye of the Jailer|n|cFFFFD200Zone: |rSanctum of Domination|n|cFFFFD200Difficulty: |rMythic', creatureID = 179233, },
	[3130] = { description = 'The golden armor on these mawsworn is flimsy and easily bent -- the gilded armor is a punishment.', sourceText = '|cFFFFD200Drop:|r Adamant Vaults|n|cFFFFD200Zone:|r Torghast, Tower of the Damned|n', creatureID = 179239, },
	[3131] = { description = 'The Sanctum of Domination accepts mawsworn of all shapes, sizes, and levels of corporeality.', sourceText = '|cFFFFD200Drop: |rKel\'Thuzad|n|cFFFFD200Zone: |rSanctum of Domination', creatureID = 179240, },
	[3132] = { description = 'A ruthless tormenter of the smallest souls. Some say he was known in life as "Thermaplugg."', sourceText = '|cFFFFD200Achievement: |rMany, Many Things|n|cFFFFD200Category: |rTorghast', creatureID = 179241, },
	[3133] = { description = 'Rook never speaks, and little about them is known.', sourceText = '|cFFFFD200Drop:|r Mysterious Gift from Ve\'nari|n|cFFFFD200Zone:|r The Maw', creatureID = 179242, },
	[3134] = { description = 'You have never met a pet that is more about chewing.', sourceText = '|cFFFFD200Pet Battle: |rKorthia', creatureID = 179248, },
	[3135] = { description = 'The garnetgullet rolls in bright red flowers to enhance its color, serving as a call to similar species and a warning to enemies.', sourceText = '|cFFFFD200Pet Battle: |rKorthia', creatureID = 179250, },
	[3136] = { description = 'With Korthia torn asunder by the Jailer, the few species that remain must be archived.', sourceText = '|cFFFFD200Drop: |rZelnithop|n|cFFFFD200Zone: |rKorthia', creatureID = 179251, },
	[3137] = { description = 'The hide color of this species is more rare on Korthia, and helps it hide from predators. And mawsworn, in this case.', sourceText = '|cFFFFD200Drop:|r Death\'s Advance Supplies|n|cFFFFD200Zone:|r Korthia', creatureID = 179252, },
	[3207] = { description = 'Three parts planning, one part danger, one part noodle.', sourceText = '|cFFFFD200Profession:|r Protoform Synthesis|n', creatureID = 182735, },
	[3432] = { description = '', sourceText = '', creatureID = 200696, },
	[3138] = { description = 'A domesticated species, it is quite calm and seems hungry.', sourceText = '|cFFFFD200Vendor: |rDuchess Mynx|n|cFFFFD200Zone: |rKorthia|n|cFFFFD200Faction: |rDeath\'s Advance - Honored|n|cFFFFD200Cost: |r50|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h 1500|Hcurrency:1767|h|TINTERFACE/ICONS/INV_Stygia.BLP:0|t|h', creatureID = 179253, },
	[3139] = { description = 'Devourers exist throughout the In-Between of various types and levels of power, but always seeking anima.', sourceText = '|cFFFFD200Pet Battle: |rKorthia', creatureID = 179256, },
	[3140] = { description = 'It only has two teeth, and they both mean business.', sourceText = '|cFFFFD200Drop:|r Supplies of the Archivists\' Codex|n|cFFFFD200Zone:|r Korthia', creatureID = 179255, },
	[3141] = { description = 'Corpsefly eggs end up everywhere in Maldraxxus, and are easily transported accidentally.', sourceText = '|cFFFFD200Pet Battle:|r Korthia|n', creatureID = 179329, },
	[3153] = { description = 'Blinky just hatched, and is ready for adventure, but may need a few naps along the way.', sourceText = '|cFFFFD200In-Game Shop|r|n', creatureID = 179589, },
	[3155] = { description = '', sourceText = '', creatureID = 179966, },
	[3156] = { description = '', sourceText = '', creatureID = 179809, },
	[3158] = { description = '', sourceText = '', creatureID = 179967, },
	[3169] = { description = 'One part focus, one part core, zero parts coherent or stable shape.', sourceText = '|cFFFFD200Profession:|r Protoform Synthesis|n', creatureID = 181308, },
	[3170] = { description = 'The core of a song that all but faded long ago.', sourceText = '|cFFFFD200Profession:|r Protoform Synthesis|n', creatureID = 181335, },
	[3171] = { description = 'It could become anything. It could become *everything*. In the meantime, it\'s... this.', sourceText = '|cFFFFD200Profession:|r Protoform Synthesis', creatureID = 181336, },
	[3172] = { description = 'It\'s hard to be sure since it has no face, but this thing seems to like you.', sourceText = '|cFFFFD200Treasure: |rEphemera Orb|n|cFFFFD200Zone: |rZereth Mortis', creatureID = 181337, },
	[3173] = { description = 'The signature song of the bufonid croaker can be heard across vast distances.', sourceText = '|cFFFFD200Pet Battle:|r Zereth Mortis|n', creatureID = 181362, },
	[3174] = { description = 'One part facial sphere, one part cube manufactory, one part successful curiosity.', sourceText = '|cFFFFD200Profession:|r Protoform Synthesis|n', creatureID = 181488, },
	[3175] = { description = 'Hatch-queen of the baby murlocs, Murkastrasza spent many years battling her bitter rival, Deathy.', sourceText = '|cFFFFD200Promotion:|r Dragonflight Heroic Edition|n', creatureID = 181535, },
	[3176] = { description = 'Only newly synthesized tunneling vombata have been seen. The rest are probably happily digging underground, never to be seen again.', sourceText = '|cFFFFD200Profession:|r Protoform Synthesis|n', creatureID = 181547, },
	[3177] = { description = 'A drakonid hatchling is a curious sight, but now is the time for curious things.', sourceText = '|cFFFFD200Promotion: Dragonflight Base Edition|r ', creatureID = 181575, },
	[3178] = { description = 'One part beak, two parts wings, one part inability to stop, one part unwillingness to stop.', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis|n', creatureID = 181578, },
	[3179] = { description = 'One part rocks, two parts bigger rocks, three parts smaller rocks, one part ability to move anyway.', sourceText = '|cFFFFD200Profession:|r Protoform Synthesis|n', creatureID = 181615, },
	[3180] = { description = 'Some of the more adventurous denizens of Zereth Mortis consider venomous bufonids to be a delicacy.', sourceText = '|cFFFFD200Pet Battle:|r Zereth Mortis|n', creatureID = 182019, },
	[3181] = { description = 'One part slippery skin, one part powerful legs, one part vocal sac, one part complete self-acceptance.', sourceText = '|cFFFFD200Profession:|r Protoform Synthesis|n', creatureID = 182081, },
	[3188] = { description = '', sourceText = '', creatureID = 182140, },
	[3189] = { description = 'One part duplication, two parts duplication, four parts duplication... oops.', sourceText = '|cFFFFD200Profession:|r Protoform Synthesis', creatureID = 182183, },
	[3190] = { description = 'Some leporids are docile and cute. Others are... still cute.', sourceText = '|cFFFFD200Pet Battle: |rZereth Mortis', creatureID = 182216, },
	[3191] = { description = 'Some of the more timid leporids live deep underground. They emerge only to gather food before returning to their burrows.', sourceText = '|cFFFFD200Pet Battle: |rZereth Mortis', creatureID = 182234, },
	[3196] = { description = 'While larger proto avians are known to feed on small animals, fledglings mostly eat insects and grubs.', sourceText = '|cFFFFD200Pet Battle: |rZereth Mortis', creatureID = 182260, },
	[3197] = { description = 'One part gills, one part haste, one part whatever it needs to be.', sourceText = '|cFFFFD200Profession:|r Protoform Synthesis|n', creatureID = 182264, },
	[3200] = { description = 'The distinctive red coloration of scarlet proto avians makes it difficult for them to hide from predators, which ironically means they\'re very hard to find.', sourceText = '|cFFFFD200Pet Battle: |rZereth Mortis', creatureID = 182294, },
	[3201] = { description = 'Eight parts legs. Eight parts eyes. One part rope. One part insatiable hunger.', sourceText = '|cFFFFD200Profession:|r Protoform Synthesis|n', creatureID = 182393, },
	[3202] = { description = 'Some tarachnids prefer to lie in wait for their prey. These prefer to chase it, disable it, and devour it whole.', sourceText = '|cFFFFD200Pet Battle: |rZereth Mortis', creatureID = 182437, },
	[3203] = { description = 'When prey wanders into this creature\'s reach, it rarely wanders out.', sourceText = '|cFFFFD200Pet Battle: |rZereth Mortis', creatureID = 182473, },
	[3204] = { description = 'One part shell. One part claw. As many parts resilience as need be.', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis', creatureID = 182504, },
	[3205] = { description = 'Some of the smaller scarabids of Zereth Mortis have metallic outer shells to protect themselves. It also makes them look fabulous.', sourceText = '|cFFFFD200Pet Battle: |rZereth Mortis', creatureID = 182568, },
	[3206] = { description = 'This scarabid\'s brightly colored shell makes it more interesting to potential mates. Unfortunately, this also makes them interesting to potential predators.', sourceText = '|cFFFFD200Pet Battle: |rZereth Mortis', creatureID = 182691, },
	[3208] = { description = 'The distinctive red coloration of these viperids serves as a warning to potential predators: \'eat at your own risk!\'', sourceText = '|cFFFFD200Pet Battle: |rZereth Mortis', creatureID = 182758, },
	[3209] = { description = 'Where does a 350-pound viperid sit? Anywhere it wants.', sourceText = '|cFFFFD200Pet Battle: |rZereth Mortis', creatureID = 182760, },
	[3210] = { description = 'Viperids can sit still for hours, silently awaiting their prey.', sourceText = '|cFFFFD200Pet Battle: |rZereth Mortis', creatureID = 182768, },
	[3211] = { description = 'Two parts wings, one part dagger, five parts sadism.', sourceText = '|cFFFFD200Profession:|r Protoform Synthesis|n', creatureID = 182840, },
	[3212] = { description = 'Whether in life or the afterlife, some things are universal. Nasty blood-sucking insects are one of those things.', sourceText = '|cFFFFD200Pet Battle: |rZereth Mortis', creatureID = 182876, },
	[3213] = { description = 'Despite its vulnerable appearance, predators will often avoid attacking a juvenile vombata. It\'s not known exactly why, but many believe it has to do with the vombata\'s cute little nose.', sourceText = '|cFFFFD200Pet Battle: |rZereth Mortis', creatureID = 183142, },
	[3214] = { description = 'There are many terrifying things in Zereth Mortis, but none so terrifying (or adorable) as a vombata protecting its young.', sourceText = '|cFFFFD200Pet Battle: |rZereth Mortis', creatureID = 183158, },
	[3215] = { description = 'No one in their right mind has ever seen a Mawtouched Geomental. Even so, plenty of people have seen Mawtouched Geomentals.', sourceText = '|cFFFFD200Pet Battle: |rZereth Mortis', creatureID = 183230, },
	[3216] = { description = 'The ambystan snapper is one of the nicest creatures you can meet in Zereth Mortis... until it isn\'t.', sourceText = '|cFFFFD200Pet Battle: |rZereth Mortis', creatureID = 183277, },
	[3217] = { description = 'Most of Zereth Mortis\' aurelids are content to swim, but some are born to fly!', sourceText = '|cFFFFD200Pet Battle: |rZereth Mortis', creatureID = 183281, },
	[3218] = { description = 'Nobody calls this poultrid \'chicken.\'', sourceText = '|cFFFFD200Pet Battle: |rZereth Mortis', creatureID = 183285, },
	[3219] = { description = 'When confronted, helicids will hide in their heavily armored shells. If provoked further, they will lash out with devastating force.', sourceText = '|cFFFFD200Pet Battle: |rZereth Mortis', creatureID = 183292, },
	[3220] = { description = 'Two parts wings, one part beak, two parts talon, one part smugness.', sourceText = '|cFFFFD200Profession:|r Protoform Synthesis', creatureID = 183557, },
	[3221] = { description = 'This tiny tormentor of Torghast relishes in punishing any soul in the tower that can\'t step on it.', sourceText = '|cFFFFD200Achievement: |rThe Jailer\'s Gauntlet: Layer 1|n|cFFFFD200Category: |rTorghast', creatureID = 183772, },
	[3222] = { description = 'isn\'t she just the sweetest little helicid?', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis', creatureID = 184196, },
	[3223] = { description = 'Ambystans are swift but cowardly creatures. At the first hint of danger, a darter will hide in the nearest hole.', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis', creatureID = 184183, },
	[3224] = { description = 'The red coloring on this scarabid lets potential opponents know it is not to be trifled with.', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis', creatureID = 184184, },
	[3225] = { description = 'These poultrids are hostile, but never angry. They love every moment of every battle.', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis', creatureID = 184189, },
	[3226] = { description = 'It\'s really unclear what\'s \'multi\' about them.', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis', creatureID = 184190, },
	[3227] = { description = 'Lihuvim\'s geomental creations are the most majestic of walking rock piles.', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis', creatureID = 184191, },
	[3229] = { description = 'Just one part jelly, but refreshed over and over and over again.', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis', creatureID = 184186, },
	[3230] = { description = 'It feeds on fear, but is generally too cute to inspire terror on its own.', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis', creatureID = 184192, },
	[3231] = { description = 'This creature\'s existence strongly implies the existence of other, more advanced tickle-based aurelids out there.', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis', creatureID = 184193, },
	[3232] = { description = 'Left to their own devices, these leporids will leap high into the air at the slightest provocation. Sometimes they\'ll even come down properly, as well.', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis', creatureID = 184194, },
	[3233] = { description = 'Two parts fortress, one part eyestalks, one part slime.', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis', creatureID = 184187, },
	[3234] = { description = 'Where some viperids lie in wait for their prey, this variety stalks their intended meals for hours.', sourceText = '|cFFFFD200Profession:|r Protoform Synthesis|n', creatureID = 184197, },
	[3235] = { description = 'Since synthesized creatures come into being fully grown, why would anyone design an immature helicid?', sourceText = '|cFFFFD200Profession: |rProtoform Synthesis', creatureID = 184195, },
	[3237] = { description = 'A little ambystan that was too big, then too little, and finally just right. He followed you home!', sourceText = '|cFFFFD200Quest:|r Can I Keep Him?', creatureID = 184923, },
	[3242] = { description = 'Looks like Ben\'jin and Maka\'fon have kept busy since you last saw them. Maka\'jin is ready for adventure but will settle for a large pile of leafy greens.', sourceText = '|cFFFFD200Trading Post|r', creatureID = 185322, },
	[3243] = { description = 'He has a heart of gold. Literally. These shiny good-boys are all the rage with affluent gnomes everywhere.', sourceText = '|cFFFFD200Trading Post|r', creatureID = 183686, },
	[3244] = { description = 'Fooled aeons ago by the clever loa of death into a bargain of unceasing service, this tiny ancient being carries out his duties eternally red with rage.', sourceText = '|cFFFFD200Trading Post|r', creatureID = 185324, },
	[3246] = { description = '', sourceText = '', creatureID = 185475, },
	[3279] = { description = 'This little racing enthusiast has never missed a race and has never met a racer she didn\'t like.', sourceText = '|cFFFFD200Achievement:|r Dragon Racing Completionist - Silver|n', creatureID = 189101, },
	[3247] = { description = 'This isn\'t the real Pocopoc, but it provided this replica to you for use outside Zereth Mortis. Its details are so accurate as to be utterly convincing to anyone who hasn\'t spent lots of time with the real one.', sourceText = '|cFFFFD200Vendor: |rOlea Manu|n|cFFFFD200Zone: |rZereth Mortis|n|cFFFFD200Cost: |r500|Hcurrency:1979|h|TINTERFACE/ICONS/INV_Trinket_ProgenitorRaid_02_Blue.BLP:0|t|h', creatureID = 185477, },
	[3248] = { description = 'Ichabod began his existence as a prototype for the fearsome harvest golems terrorizing the farmers of Westfall, but a little gnomish ingenuity has since made him far less likely to attack your knees.', sourceText = '|cFFFFD200Promotion|r |n', creatureID = 183638, },
	[3249] = { description = 'Before he was known as one of the Wild Gods, Ursoc roamed the Grizzly Hills as a cub.', sourceText = '|cFFFFD200In-Game Shop|r|n', creatureID = 185586, },
	[3250] = { description = 'Some hawkstriders are destined to be great. Others are destined to be Egbob.', sourceText = '|cFFFFD200Trading Post|r', creatureID = 183708, },
	[3251] = { description = 'Some species of red bees are known to lead solitary lives. Still, this busy bee seems happy to make time for you.', sourceText = '|cFFFFD200Trading Post|r', creatureID = 185604, },
	[3252] = { description = 'Ancient Gorian blood runs through Garrlok\'s veins. As far as anyone knows, only his diminutive stature keeps his imperial ambitions in check.', sourceText = '|cFFFFD200Trading Post|r', creatureID = 185606, },
	[3253] = { description = 'Siege training begins early for kodo calflings that show a lack of inhibition towards breaking things. Mind your shins.', sourceText = '|cFFFFD200In-Game Shop|r', creatureID = 185611, },
	[3254] = { description = 'To its foes, this Watcher\'s innocent eyes belie the lethal danger of a prowling Huntress, waiting in the shadows.', sourceText = '|cFFFFD200Trading Post|r', creatureID = 185621, },
	[3255] = { description = 'His pink antlers make him the life of any party.', sourceText = '|cFFFFD200Trading Post|r', creatureID = 185756, },
	[3256] = { description = 'The master jewelcrafters of the Dragon Isles have perfected their craft so much that their gemmed statues can be brought to life. This whelp forged of obsidian is now not to be trifled with.', sourceText = '|cFFFFD200Profession:|r Jewelcrafting|n', creatureID = 186844, },
	[3257] = { description = '', sourceText = '', creatureID = 186884, },
	[3259] = { description = 'This little rabbit is cute from the tip of her button nose to the tips of her deadly claws.', sourceText = '|cFFFFD200Treasure: |rSack of Oddities', creatureID = 188709, },
	[3260] = { description = 'Nearly invisible, this creature leaps from tree to snow-covered tree.', sourceText = '|cFFFFD200Pet Battle: |rThe Azure Span', creatureID = 188792, },
	[3261] = { description = 'Hope, like this phoenix, rises from the ashes of shattered dreams.', sourceText = '|cFFFFD200Drop: |rWarden Entrix|n|cFFFFD200Zone: |rThe Forbidden Reach', creatureID = 188821, },
	[3262] = { description = 'Some very clever wizard or dragon or other magical being thought they were being clever when they decided to color an ice elemental purple for a very elaborate bit of wordplay. They were probably right.', sourceText = '|cFFFFD200Achievement:|r What Can I Say? They Love Me.|n', creatureID = 188849, },
	[3263] = { description = 'It looks like a duck and it smells like a duck and it quacks like a duck... what\'s a duck?', sourceText = '|cFFFFD200Achievement:|r Proven Pet Parent', creatureID = 188861, },
	[3264] = { description = 'A shard of a fragment of a piece of a sliver of a piece of crystal, but walking around like it owns the place. Good thing it likes you.', sourceText = '|cFFFFD200Achievement:|r Many More Mini Minions', creatureID = 188885, },
	[3265] = { description = 'Truly, the most dignified of stinky musken.', sourceText = '|cFFFFD200Achievement:|r Good Things Come in Small Packages', creatureID = 188901, },
	[3266] = { description = 'Above the vast green plains and rolling hills the slyvern fly, leaving only tiny shadows on the ground to mark their path through the sky.', sourceText = '|cFFFFD200Pet Battle: |rOhn\'ahran Plains', creatureID = 189160, },
	[3267] = { description = 'You met this Slyvern next week from your point of view, but last year from its point of view.', sourceText = '', creatureID = 189211, },
	[3268] = { description = '', sourceText = '', creatureID = 189376, },
	[3269] = { description = 'Oddly enough, you met this fish drifting through the air.', sourceText = '|cFFFFD200Treasure:|r Bubble Drifter|n|cFFFFD200Zone: |rThe Waking Shores', creatureID = 189663, },
	[3270] = { description = 'To some, this fish speaks eloquently of its survival in the most arduous conditions. To most, it says \'glub.\'', sourceText = '|cFFFFD200Vendor: |rPatchu|n|cFFFFD200Zone: |rThe Azure Span|n|cFFFFD200Cost: |r300|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 189695, },
	[3272] = { description = 'This is the most pettable pointy creature you will ever meet.', sourceText = '|cFFFFD200Pet Battle: |rOhn\'ahran Plains, Thaldraszus, The Azure Span, The Waking Shores', creatureID = 189093, },
	[3273] = { description = 'Somehow this creature manages to be hot, rocky, and slimy all at the same time.', sourceText = '|cFFFFD200Pet Battle: |rThe Waking Shores', creatureID = 189094, },
	[3274] = { description = 'When Alvin said he wanted to be the best anvil ever, everyone doubted him. If anvils could laugh, he\'d be the one laughing now.', sourceText = '|cFFFFD200Profession:|r Blacksmithing|n', creatureID = 189095, },
	[3275] = { description = 'Chestnut once found an acorn larger than he was, ate the whole thing, and doubled in size. It\'s a good thing he didn\'t eat a Trunkalumpf.', sourceText = '|cFFFFD200Treasure:|r Acorn Harvester|n|cFFFFD200Zone:|r Thaldraszus|n', creatureID = 189096, },
	[3276] = { description = 'Many have tried to boop the cute little noses of creatures like this. Most have lost fingers.', sourceText = '|cFFFFD200Pet Battle: |rOhn\'ahran Plains, Thaldraszus, The Azure Span', creatureID = 189097, },
	[3277] = { description = 'It\'s been a busy three years for this litte critter. It was born next year, rased last year, and adopted as your pet this year.', sourceText = '', creatureID = 189098, },
	[3278] = { description = 'Pistachio got her name in exactly the way you would expect.', sourceText = '|cFFFFD200Vendor: |rPatchu|n|cFFFFD200Zone: |rThe Azure Span|n|cFFFFD200Cost: |r50|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 189099, },
	[3433] = { description = '', sourceText = '', creatureID = 200682, },
	[3280] = { description = 'These elusive creatures are famous for being hard to find unless you already know where to look. Fortunately, Haniko told you where to look.', sourceText = '|cFFFFD200Pet Battle: |rThe Waking Shores', creatureID = 189102, },
	[3281] = { description = 'Between battling, bathing, and playing, these ottuks have very busy schedules.', sourceText = '|cFFFFD200Pet Battle: |rOhn\'ahran Plains, The Azure Span, The Waking Shores', creatureID = 189103, },
	[3282] = { description = 'The reverberating sound of an adult hornswog\'s belch can be heard for leagues. Less far-reaching, but far more musical, is the dainty sound of a swoglet belching.', sourceText = '|cFFFFD200Pet Battle: |rOhn\'ahran Plains, Thaldraszus, The Azure Span, The Waking Shores', creatureID = 189104, },
	[3283] = { description = 'When you feel a chill run down your spine, don\'t be afraid. It\'s probably just your pet snowlemental.', sourceText = '|cFFFFD200Pet Battle: |rThe Azure Span, The Waking Shores', creatureID = 189107, },
	[3284] = { description = 'Baby ottuks spend their days swimming, chasing butterflies, and practicing their intimidating roars.', sourceText = '|cFFFFD200Faction: |rIskaara Tuskarr|n|cFFFFD200Renown: |r9|n|cFFFFD200Vendor: |rJiq|n|cFFFFD200Location: |rIskaara', creatureID = 189105, },
	[3285] = { description = 'In its big eyes you can see your own reflection. You also see everything else because its eyes are so big.', sourceText = '|cFFFFD200Drop: |rLuttrok|n|cFFFFD200Zone: |rThe Forbidden Reach', creatureID = 189106, },
	[3286] = { description = 'His name is Mister Toots because of the melodious sounds he can make with his trunk.', sourceText = '|cFFFFD200Quest: |rSo How Did It Go?|n|cFFFFD200Zone: |rThe Waking Shores', creatureID = 189108, },
	[3287] = { description = 'A dream of the memory of fire.', sourceText = '|cFFFFD200Vendor: |rMythressa|n|cFFFFD200Zone: |rValdrakken|n|cFFFFD200Cost: |r1000|Hcurrency:2118|h|TINTERFACE/ICONS/INV_Misc_Powder_Thorium.blp:0|t|h', creatureID = 189111, },
	[3288] = { description = 'While their tiny tusks may not help much in a fight, the trunkalumpfs make up for it in sheer weight.', sourceText = '|cFFFFD200Pet Battle: |rOhn\'ahran Plains, The Azure Span', creatureID = 189110, },
	[3289] = { description = 'A spark can become an ember. An ember can become a raging fire. This creature was never a spark or an ember, it has only ever been a raging fire.', sourceText = '|cFFFFD200Drop:|r World Drop|n|cFFFFD200Weather:|r Elemental Storm (Fire)|n|cFFFFD200Zone:|r The Waking Shores, Ohn\'ahran Plains, The Azure Span, Thaldraszus', creatureID = 189112, },
	[3290] = { description = 'It sits anywhere it wants to, but it prefers to sit on laps.', sourceText = '|cFFFFD200Treasure: |rZskera Vaults, The Forbidden Reach', creatureID = 189113, },
	[3291] = { description = 'Scruffles\' lust for battle runs deep.', sourceText = '|cFFFFD200Drop: |rVraken the Hunter|n|cFFFFD200Zone: |rThe Forbidden Reach', creatureID = 189115, },
	[3292] = { description = 'Some think this creature is smoldering because it is on fire. In truth it\'s because of its burning gaze.', sourceText = '|cFFFFD200Quest: |rTale of the Phoenix|n|cFFFFD200Zone: |rThe Waking Shores', creatureID = 189117, },
	[3293] = { description = 'Red for the fires in which it was born.', sourceText = '|cFFFFD200Drop: |rBonesifter Marwak|n|cFFFFD200Zone: |rThe Forbidden Reach', creatureID = 189118, },
	[3294] = { description = 'Is it possible to rise from the ashes of time? Gerald thinks so and he will not be denied.', sourceText = '|cFFFFD200Achievement: |rFamily Battler of Zaralek Cavern|n|cFFFFD200Category: |rBattle', creatureID = 189119, },
	[3295] = { description = 'An Igneoid is formed when magma makes it to Azeroth\'s surface and is infused with dark elemental magic. The resulting creature is tiny, fierce, and very cute.', sourceText = '|cFFFFD200Pet Battle: |rThaldraszus, The Azure Span, The Waking Shores', creatureID = 189121, },
	[3296] = { description = 'This salamanther wants to be your best friend.', sourceText = '|cFFFFD200Pet Battle: |rOhn\'ahran Plains, Thaldraszus, The Azure Span, The Waking Shores', creatureID = 189122, },
	[3297] = { description = 'This Salamanther met itself while lost in time. The two hung out on a rock together for a few hours. It was nice.', sourceText = '|cFFFFD200Trading Post|r ', creatureID = 189123, },
	[3298] = { description = 'Many salamanthers have interesting colorations, but this salamanther has the coolest one.', sourceText = '', creatureID = 189128, },
	[3299] = { description = 'What the deep claims, none can reclaim.', sourceText = '|cFFFFD200Drop:|r World Drop|n|cFFFFD200Weather:|r Elemental Storm (Water)|n|cFFFFD200Zone:|r The Waking Shores, Ohn\'ahran Plains, The Azure Span, Thaldraszus', creatureID = 189130, },
	[3300] = { description = 'Though many creatures of the Dragon Isles are mysterious, the ducks are perhaps the most so. Where did they come from? Where are they going? What are they planning? We may never know.', sourceText = '|cFFFFD200Pet Battle: |rOhn\'ahran Plains, The Waking Shores', creatureID = 189131, },
	[3301] = { description = 'Quack.', sourceText = '|cFFFFD200Pet Battle: |rOhn\'ahran Plains, Thaldraszus, The Waking Shores', creatureID = 189136, },
	[3302] = { description = 'Still learning how to fly.', sourceText = '|cFFFFD200Quest: |rBut... I Am the Hero of Ducks|n|cFFFFD200Zone: |rOhn\'ahran Plains', creatureID = 189138, },
	[3303] = { description = 'What do you feed a cute little duckling? What? No, not cheese and quackers! Quackers aren\'t healthy for ducks.', sourceText = '|cFFFFD200Vendor: |rPakak|n|cFFFFD200Zone: |rValdrakken|n|cFFFFD200Cost: |r1|Hitem:197788|h|TINTERFACE/ICONS/INV_Misc_Food_Meat_Cooked_06.blp:0|t|h 1|Hitem:197789|h|TINTERFACE/ICONS/INV_Misc_Food_99.blp:0|t|h 3|Hitem:197792|h|TINTERFACE/ICONS/INV_Misc_FortuneCookie_color03.BLP:0|t|h', creatureID = 189140, },
	[3306] = { description = 'Beep. Beep. Beep. Quack.', sourceText = '|cFFFFD200Profession: |rEngineering', creatureID = 189142, },
	[3307] = { description = 'This is the terror that flaps o\'er the Dragon Isles. This is the winged nightmare that pecks at your emerald dreams. What? No it\'s not Deathwing, it\'s a duck!', sourceText = '|cFFFFD200Pet Battle: |rOhn\'ahran Plains, The Waking Shores, Valdrakken', creatureID = 189144, },
	[3308] = { description = 'As small as it is, it\'s no wonder this little duckling got lost in time.', sourceText = '', creatureID = 189145, },
	[3309] = { description = 'These beautiful, elusive birds have very erratic migratory patterns, which can be difficult to track, leading some explorers to erroneously believe ducks simply didn\'t exist.', sourceText = '|cFFFFD200Treasure:|r Slightly Chewed Duck Egg|n|cFFFFD200Zone:|r Ohn\'ahran Plains|n', creatureID = 189133, },
	[3310] = { description = 'In the mountain air, frost lingers upon the grass as a storm rolls in.', sourceText = '|cFFFFD200Drop:|r World Drop|n|cFFFFD200Weather:|r Elemental Storm (Air)|n|cFFFFD200Zone:|r The Waking Shores, Ohn\'ahran Plains, The Azure Span, Thaldraszus', creatureID = 189132, },
	[3311] = { description = 'Its eyes can see the flick of a mouse tail from leagues away.', sourceText = '|cFFFFD200Drop:|r Grand Hunt Spoils|n', creatureID = 189134, },
	[3312] = { description = 'If a duck quacks in the woods and nobody is around to hear it, does it even exist?', sourceText = '', creatureID = 189135, },
	[3313] = { description = 'Each of this creature\'s steps shakes the ground. When enough of them run together it sounds like a raging thunderstorm.', sourceText = '|cFFFFD200Pet Battle: |rOhn\'ahran Plains, Thaldraszus, The Waking Shores', creatureID = 189153, },
	[3314] = { description = 'Contrary to its name, this little creature shakes nothing as it shuffles from place to place.', sourceText = '', creatureID = 189150, },
	[3316] = { description = 'Who\'s the cutest little monster? You are!', sourceText = '|cFFFFD200Quest:|r Oh Where, Oh Where Can He Be?|n|cFFFFD200Zone:|r Thaldraszus', creatureID = 189152, },
	[3317] = { description = 'Though it\'s rare, thunder lizards have been known to bond with centaurs. And their close allies. Lucky you!', sourceText = '|cFFFFD200Vendor: |rQuartermaster Huseng|n|cFFFFD200Zone: |rOhn\'ahran Plains|n|cFFFFD200Cost: |r100|Hcurrency:2003|h|TINTERFACE/ICONS/INV_Faction_WarResources.BLP:0|t|h', creatureID = 189154, },
	[3318] = { description = 'When these creatures stampede, they sound like a thunderstorm. When these creatures stampede in a thunderstorm, they sound like a cataclysm.', sourceText = '|cFFFFD200Pet Battle: |rThe Waking Shores', creatureID = 189155, },
	[3319] = { description = 'Soon after you put down this slyvern\'s decay-twisted mother, you learned the hard way both how it got its name and why Zon\'Wogi asked you to care for it.', sourceText = '|cFFFFD200Quest: |rThe Great Shokhari|n|cFFFFD200Zone: |rThe Azure Span', creatureID = 189204, },
	[3320] = { description = 'These critters\' white coats help them hide in the snow when  predators are around.', sourceText = '|cFFFFD200Pet Battle: |rThe Azure Span', creatureID = 189209, },
	[3321] = { description = 'There is no fury quite like that of a mother Blackfeather defending her nest.', sourceText = '|cFFFFD200Treasure:|r Blackfeather Nester|n|cFFFFD200Zone:|r The Azure Span|n', creatureID = 189156, },
	[3322] = { description = 'These birds use their sharp beaks and dragon-like talons to forage for prey on tree trunks and branches. They communicate with each other by drumming on the trees with their beaks, which makes a sound that can be heard over great distances.', sourceText = '|cFFFFD200Pet Battle: |rOhn\'ahran Plains, The Azure Span', creatureID = 189157, },
	[3323] = { description = 'It has bright feathers. What else is there to say?', sourceText = '|cFFFFD200Treasure: |rZskera Vaults, The Forbidden Reach', creatureID = 189158, },
	[3325] = { description = 'Faithful and ferocious, this bakar is a hero\'s best friend.', sourceText = '|cFFFFD200Drop:|r Grand Hunt Spoils|n', creatureID = 189159, },
	[3326] = { description = 'When not lazily floating down a river, this Timbertooth is fond of snacks and snuggles.', sourceText = '|cFFFFD200Faction: |rIskaara Tuskarr|n|cFFFFD200Renown: |r9|n|cFFFFD200Vendor: |rJiq|n|cFFFFD200Location: |rIskaara', creatureID = 189655, },
	[3327] = { description = 'Hunters value these critters more for their companionship than their pelts.', sourceText = '|cFFFFD200Pet Battle: |rOhn\'ahran Plains', creatureID = 189657, },
	[3328] = { description = 'As good as gold and better. These little guys are quite frail but exceedingly brave. - Bob Bigheart', sourceText = '|cFFFFD200Pet Battle: |rOhn\'ahran Plains, The Azure Span', creatureID = 189658, },
	[3329] = { description = 'A timbertooth with lighting inside? Shocking!', sourceText = '|cFFFFD200Pet Battle: |rOhn\'ahran Plains', creatureID = 189660, },
	[3330] = { description = 'Gnawing at the roots of magic trees have given this creature the ability to transcend time itself.', sourceText = '|cFFFFD200Vendor: |rTurik|n|cFFFFD200Zone: |rThe Forbidden Reach|n|cFFFFD200Cost: |r25000|Hcurrency:2118|h|TINTERFACE/ICONS/INV_Misc_Powder_Thorium.blp:0|t|h', creatureID = 189661, },
	[3331] = { description = 'Is it normal for a fish to have fangs?', sourceText = '|cFFFFD200Profession: |rFishing|n|cFFFFD200Zone: |rZskera Vaults, The Forbidden Reach|n', creatureID = 189694, },
	[3332] = { description = 'This frillfish is a no-frills kind of frillfish.', sourceText = '|cFFFFD200Treasure: |rUnique Fish|n|cFFFFD200Zone: |rZskera Vault, The Forbidden Reach', creatureID = 189696, },
	[3333] = { description = 'The vorquin of the dragon isles have manes that come in a variety of stunning colors.', sourceText = '|cFFFFD200Treasure: |rZskera Vaults, The Forbidden Reach', creatureID = 191287, },
	[3334] = { description = 'It\'s quite possible this creature is aging backwards. It looks a bit younger every time you see it.', sourceText = '|cFFFFD200Vendor:|r Brendormi|n|cFFFFD200Cost:|r 105|Hitem:202039|h|TINTERFACE/ICONS/INV_misc_stormlordsfavor.blp:0|t|h 1500|Hcurrency:2118|h|TINTERFACE/ICONS/INV_Misc_Powder_Thorium.blp:0|t|h', creatureID = 191298, },
	[3335] = { description = 'Behold! A pale vorquin.', sourceText = '|cFFFFD200Pet Battle: |rThe Azure Span', creatureID = 191316, },
	[3336] = { description = 'It will probably grow bigger. Probably.', sourceText = '|cFFFFD200Pet Battle: |rThaldraszus, The Azure Span, The Waking Shores', creatureID = 191323, },
	[3338] = { description = 'Sharp crystal needles protrude from this creature\'s back, and yet it still wants you to pet it somehow.', sourceText = '|cFFFFD200Treasure: |rAnimate Crystalspine|n|cFFFFD200Zone: |rThe Forbidden Reach', creatureID = 191435, },
	[3342] = { description = 'This little dragon is all fired up! It looks like he\'s got some things to do.', sourceText = '|cFFFFD200Quest:|r A Dragon\'s Day Off|n', creatureID = 191381, },
	[3344] = { description = 'Sometimes you can see blue light flickering deep inside the gems that make up this strange creature.', sourceText = '|cFFFFD200Profession:|r Jewelcrafting', creatureID = 191383, },
	[3345] = { description = 'The hardest part to shape was the whelpling\'s cute little snout.', sourceText = '|cFFFFD200Profession:|r Jewelcrafting|n', creatureID = 191384, },
	[3346] = { description = 'Trade Prince Gallywix wants you to know that he already owns three of these.', sourceText = '|cFFFFD200Profession:|r Jewelcrafting|n', creatureID = 191385, },
	[3434] = { description = '', sourceText = '', creatureID = 200690, },
	[3347] = { description = 'A magically infused emerald sits at the heart of this creature.', sourceText = '|cFFFFD200Profession:|r Jewelcrafting|n', creatureID = 191386, },
	[3348] = { description = 'Raindrops whip around this creature with frightening speed, ripping and tearing anything that touches it. Experts say the best way to avoid injury is to give it a treat and some snuggles.', sourceText = '|cFFFFD200Quest:|r A Primal Threat|n|cFFFFD200Zone:|r The Dranosh\'ar Blockade (Horde), Stormwind Harbor (Alliance)|n', creatureID = 191387, },
	[3350] = { description = 'A brilliant executive assistant and apprentice, Lord Basilton also loves to stick his nose into piles of anything interesting.', sourceText = '|cFFFFD200Quest: |rCarry On, Basilton|n|cFFFFD200Zone: |rThaldraszus', creatureID = 191627, },
	[3351] = { description = 'These small bears will not normally attack people, but they have been known to steal the occasional picnic basket.', sourceText = '|cFFFFD200Pet Battle: |rThaldraszus, The Azure Span', creatureID = 194720, },
	[3352] = { description = 'It\'s not easy having red fur during the winter, but in the autumn it\'s very fashionable.', sourceText = '|cFFFFD200Pet Battle: |rThaldraszus', creatureID = 192251, },
	[3353] = { description = 'These creatures\' shells are not literally made of stone, but they\'re strong enough that they might as well be.', sourceText = '|cFFFFD200Pet Battle: |rOhn\'ahran Plains, Thaldraszus', creatureID = 192254, },
	[3354] = { description = 'A playful splash from this otter will give you quite a shock.', sourceText = '|cFFFFD200Pet Battle: |rThe Azure Span', creatureID = 192256, },
	[3355] = { description = 'Formed by the reckless use of elemental magic, the slumbering earth awakens. It is angry.', sourceText = '|cFFFFD200Drop:|r World Drop|n|cFFFFD200Weather:|r Elemental Storm (Earth)|n|cFFFFD200Zone:|r The Waking Shores, Ohn\'ahran Plains, The Azure Span, Thaldraszus', creatureID = 192258, },
	[3356] = { description = 'Despite its name, its shell is actually much tougher than chalk.', sourceText = '|cFFFFD200Pet Battle:|r The Azure Span|n', creatureID = 192260, },
	[3357] = { description = 'These creatures are highly sought after by hunters looking to make weapons from the sharp crystals on its back.', sourceText = '|cFFFFD200Pet Battle: |rThe Azure Span', creatureID = 192265, },
	[3358] = { description = 'The blood of a crimsonspine flows through the clear crystal structures that make up its body, which causes its unique coloration.', sourceText = '|cFFFFD200Pet Battle: |rThaldraszus', creatureID = 192268, },
	[3359] = { description = 'An adult Snowclaw can rattle the very trees with its roar. A cub can rattle one of the smaller branches.', sourceText = '|cFFFFD200World Event:|r Primal Bear Cub|n|cFFFFD200Zone:|r Azure Span', creatureID = 192343, },
	[3360] = { description = 'This turtle eats bugs, as you\'d expect from its name, but you wouldn\'t believe how large the bugs are.', sourceText = '|cFFFFD200World Event:|r A Small Favor for Cymre Brightblade|n|cFFFFD200Zone:|r Waking Shores|n', creatureID = 192350, },
	[3361] = { description = 'Exposure to magic, minerals, and water pressure has left this crab looking very sharp.', sourceText = '', creatureID = 192363, },
	[3362] = { description = 'These crabs build their own shells from small fragments of rocks and gems.', sourceText = '', creatureID = 192365, },
	[3363] = { description = 'Oooh, shiny!', sourceText = '', creatureID = 192366, },
	[3364] = { description = 'These creatures were bred to hunt snakes. With a low profile and a long body, Snakebiters are perfect for chasing serpents into their own holes.', sourceText = '', creatureID = 192368, },
	[3365] = { description = 'Most brightly colored frogs are incredibly poisonous. The Roseate Hoppers are different because they are only credibly poisonous.', sourceText = '|cFFFFD200Treasure: |rHidden Hornswog Hostage|n|cFFFFD200Zone: |rThe Waking Shores', creatureID = 192369, },
	[3366] = { description = 'A troublesome mage named McGwire decided to start a small fire. He summoned a flame which caused him to exclaim: \'This is much more alive than required!\'', sourceText = '|cFFFFD200Pet Battle: |rThaldraszus, The Azure Span, The Waking Shores', creatureID = 193000, },
	[3367] = { description = 'The core temperature of an Emberling is hot enough to dissolve anything organic, so make sure to wear gloves if you want to pet it.', sourceText = '|cFFFFD200Pet Battle: |rThe Waking Shores', creatureID = 193024, },
	[3368] = { description = 'Ironically, she has a warm personality.', sourceText = '|cFFFFD200Quest:|r Kill the Queen', creatureID = 194004, },
	[3376] = { description = 'Born of living stone. Reforged by the hands of Wrathion\'s agents. Sadly much too tiny to make a difference in the black dragons\' war against the djaradin.', sourceText = '|cFFFFD200Vendor: |rYries Lightfingers|n|cFFFFD200Zone: |rThe Waking Shores|n|cFFFFD200Cost: |r1|Hitem:198357|h|TINTERFACE/ICONS/INV_Shield_1H_DragonQuest_B_02.blp:0|t|h 2|Hitem:198082|h|TINTERFACE/ICONS/INV_Ore_Blackrock_Nugget.blp:0|t|h 3|Hitem:199219|h|TINTERFACE/ICONS/INV_Misc_Food_Legion_gooBloodGel_chunk.BLP:0|t|h', creatureID = 195896, },
	[3377] = { description = '', sourceText = '|cFFFFD200Pet Battle: |rThe Azure Span', creatureID = 196206, },
	[3378] = { description = 'It\'s tried to cut down, but magic is just so tasty.', sourceText = '|cFFFFD200Faction: |rValdrakken Accord|n|cFFFFD200Renown: |r18|n|cFFFFD200Vendor: |rGroundskeeper Kama|n|cFFFFD200Location: |rValdrakken', creatureID = 196304, },
	[3379] = { description = 'This proto-whelp\'s distinctive coloration is highly prized by pet trainers.', sourceText = '|cFFFFD200Faction: |rValdrakken Accord|n|cFFFFD200Renown: |r18|n|cFFFFD200Vendor: |rGroundskeeper Kama|n|cFFFFD200Location: |rValdrakken', creatureID = 196305, },
	[3380] = { description = 'After pestering the Dragonscale Basecamp for weeks, Granpap Whiskers discovered a way to catch them and use them to get rid of other more annoying insects.', sourceText = '|cFFFFD200Faction: |rDragonscale Expedition|n|cFFFFD200Renown: |r11|n|cFFFFD200Vendor: |rGranpap Whiskers|n|cFFFFD200Location: |rDragonscale Basecamp', creatureID = 196409, },
	[3381] = { description = 'Under the invitation and tutelage of Doc Nanners many Gray Marmoni have become common, but not always desired guests at the Dragonscale Expedition Basecamp.', sourceText = '|cFFFFD200Faction: |rDragonscale Expedition|n|cFFFFD200Renown: |r11|n|cFFFFD200Vendor: |rGranpap Whiskers|n|cFFFFD200Location: |rDragonscale Basecamp', creatureID = 194893, },
	[3427] = { description = 'This creature is usually known to be quite docile, but try to stick a carrot in its face and YOU WILL lose feeling in your fingers.', sourceText = '|cFFFFD200Reputation:|r Winterpelt Furbolg|n|cFFFFD200Zone:|r Winterpelt Hollow|n', creatureID = 200259, },
	[3382] = { description = 'As an egg, this proto-drake was struck by a freak bolt of lightning. Years later, the lightning still lives inside.', sourceText = '|cFFFFD200Vendor: |rMythressa|n|cFFFFD200Zone: |rValdrakken|n|cFFFFD200Cost: |r1000|Hcurrency:2118|h|TINTERFACE/ICONS/INV_Misc_Powder_Thorium.blp:0|t|h', creatureID = 196666, },
	[3384] = { description = 'Peck. Peck. Zap!', sourceText = '|cFFFFD200Pet Battle: |rThaldraszus', creatureID = 192259, },
	[3385] = { description = 'This what your mother always warned you would happen if you ate grass that was struck by lightning.', sourceText = '|cFFFFD200Pet Battle: |rThe Waking Shores', creatureID = 192029, },
	[3386] = { description = '', sourceText = '', creatureID = 192097, },
	[3387] = { description = '', sourceText = '', creatureID = 192067, },
