	[3407] = { description = 'Scout came to the Dragon Isles for adventure and excitement. After finding you, he got both.', sourceText = '|cFFFFD200Vendor: |rPatchu|n|cFFFFD200Zone: |rThe Azure Span|n|cFFFFD200Cost: |r5000|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 192108, },
	[3408] = { description = 'When the Bushbeard clan decided they were going to have a wedding, they chose Petal to be the flower girl. She was spectacular.', sourceText = '|cFFFFD200Vendor: |rPatchu|n|cFFFFD200Zone: |rThe Azure Span|n|cFFFFD200Cost: |r100|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 198077, },
	[3409] = { description = 'I wish that I could bind the tides and bend them to my will. But in its depths the sea abides and waits for no one still.', sourceText = '|cFFFFD200Drop:|r Zapthrottle Soul Inhaler|n|cFFFFD200Profession:|r Engineering', creatureID = 198269, },
	[3410] = { description = 'When it was captured and bound to a cage made of enchanted metal, this spirit managed to twist and reshape the metal into the suit of armor it now wears.', sourceText = '|cFFFFD200Drop:|r Zapthrottle Soul Inhaler|n|cFFFFD200Profession:|r Engineering', creatureID = 198271, },
	[3411] = { description = 'This is the soul of a forest fire. Though its armor keeps its flames under control, you sometimes notice it gazing longingly at dead trees and dry underbrush.', sourceText = '|cFFFFD200Drop:|r Zapthrottle Soul Inhaler|n|cFFFFD200Profession:|r Engineering|n', creatureID = 198272, },
	[3412] = { description = 'When it was the wind, this spirit carried scents to every corner of Azeroth. Sometimes you can still smell on it the barest hint of food, smoke, or the carnage of battle.', sourceText = '|cFFFFD200Drop:|r Zapthrottle Soul Inhaler|n|cFFFFD200Profession:|r Engineering', creatureID = 198273, },
	[3414] = { description = 'After finding a clutch of black proto-whelps, Wrathion took them under his wing.', sourceText = '|cFFFFD200Faction: |rWrathion|n|cFFFFD200Vendor: |rLorena Belle|n|cFFFFD200Location: |rObsidian Citadel', creatureID = 198316, },
	[3415] = { description = 'Cubbly\'s extended proximity to time magic has given him powers far beyond the average bear.', sourceText = '|cFFFFD200NPC:|r Strange Bear Cub', creatureID = 198480, },
