	[3480] = { description = 'This creature usually has a muddy appearance, but occasionally you may find one that shines a brilliant red.', sourceText = '|cFFFFD200Pet Battle: |rZaralek Cavern', creatureID = 203292, },
	[3481] = { description = 'The bright glow of a stonewhisker\'s ears is said to be the result of eating magical cheeses. This may also explain its extremely bad breath.', sourceText = '|cFFFFD200Pet Battle: |rZaralek Cavern', creatureID = 203308, },
	[3482] = { description = 'Slimy, yet also gritty. It would not make a good meal.', sourceText = '|cFFFFD200Pet Battle: |rZaralek Cavern', creatureID = 203310, },
	[3483] = { description = 'Boulderfangs are known to make cozy nests in cabinets, shoes, and other inconvenient places.', sourceText = '|cFFFFD200Pet Battle: |rZaralek Cavern', creatureID = 203311, },
	[3484] = { description = 'Despite being made mostly of rock, these creatures can fly. Some attribute this to the strength of their wings, but most agree it\'s probably just magic.', sourceText = '|cFFFFD200Pet Battle: |rZaralek Cavern', creatureID = 203312, },
	[3485] = { description = 'Not all moths spin silk. These moths spin lies.', sourceText = '|cFFFFD200Pet Battle: |rZaralek Cavern', creatureID = 203313, },
	[3486] = { description = 'The call of the lithengale is unique among birds in that it has a persistent, driving rhythm.', sourceText = '|cFFFFD200Pet Battle: |rZaralek Cavern', creatureID = 203358, },
	[3487] = { description = 'Because their dens tend to be close to drogbar settlements, these snails are both faster and stronger than those on the surface.', sourceText = '|cFFFFD200Pet Battle: |rZaralek Cavern', creatureID = 203364, },
	[3488] = { description = 'The fire at the center of the world burns within its carapace.', sourceText = '|cFFFFD200Pet Battle: |rZaralek Cavern', creatureID = 203367, },
	[3489] = { description = 'A tiny sun seems to burn in its chest.', sourceText = '|cFFFFD200Pet Battle: |rZaralek Cavern', creatureID = 203372, },
	[3490] = { description = 'Because they are often attracted to candles, these moths can be a big problem for kobolds.', sourceText = '|cFFFFD200Pet Battle: |rZaralek Cavern', creatureID = 203377, },
	[3491] = { description = 'There must always be a Lick King.', sourceText = '|cFFFFD200Drop:|r Headless Horseman|n|cFFFFD200Zone:|r Scarlet Monastery', creatureID = 203463, },
	[3493] = { description = 'If Roland has taught you anything, it\'s that friendship comes at a price. That price is lots and lots of cheese.', sourceText = '|cFFFFD200Achievement: |rThe Gift of Cheese|n|cFFFFD200Category: |rCooking', creatureID = 204036, },
	[3511] = { description = 'Like a campfire, but it wants skritches.', sourceText = '|cFFFFD200Drop: |rFyrakk Assault ', creatureID = 204217, },
