	[3513] = { description = 'Other frogs like to play, but this one is feeling too blue.', sourceText = '', creatureID = 204222, },
	[3514] = { description = 'Being green is... not easy.', sourceText = '', creatureID = 204224, },
	[3515] = { description = 'Annuna\'s distinctive coloration is due to her eating nothing but orange dragonflies.', sourceText = '|cFFFFD200Quest: |rA Scaly Pet|n|cFFFFD200Zone: |rValdrakken', creatureID = 204225, },
	[3516] = { description = 'Crimson swoglets are considered the most scholarly of all swoglets because they\'re well red.', sourceText = '|cFFFFD200Vendor: |rCelestine of the Harvest|n|cFFFFD200Zone: |rThe Waking Shores|n|cFFFFD200Zone: |rOhn\'ahran Plains|n|cFFFFD200Zone: |rThe Azure Span|n|cFFFFD200Zone: |rThaldraszus|n|cFFFFD200Cost: |r250|Hitem:207026|h|TINTERFACE/ICONS/INV_Enchant_DustStrange.blp:0|t|h', creatureID = 204226, },
	[3517] = { description = 'Three years ago this swoglet was a fully grown adult.', sourceText = '', creatureID = 204227, },
	[3518] = { description = 'Named after her favorite food.', sourceText = '', creatureID = 204237, },
	[3519] = { description = 'Its insides are a raging inferno. Its outside is happy pup.', sourceText = '', creatureID = 204240, },
	[3520] = { description = 'Imagine the size of the birds that eat these bugs.', sourceText = '', creatureID = 204261, },
	[3521] = { description = 'Lord Stantley will arrive fashionably early to any party that serves shrimp.', sourceText = '|cFFFFD200Zone: |rZaralek Cavern', creatureID = 204263, },
	[3522] = { description = 'It\'s not really all that savage, just a little crabby.', sourceText = '', creatureID = 204266, },
	[3523] = { description = 'It got its name because its favorite food is fondue.', sourceText = '|cFFFFD200Vendor: |rCelestine of the Harvest|n|cFFFFD200Zone: |rThe Waking Shores|n|cFFFFD200Zone: |rOhn\'ahran Plains|n|cFFFFD200Zone: |rThe Azure Span|n|cFFFFD200Zone: |rThaldraszus|n|cFFFFD200Cost: |r250|Hitem:207026|h|TINTERFACE/ICONS/INV_Enchant_DustStrange.blp:0|t|h', creatureID = 204268, },
	[3524] = { description = 'Fortunately it mainly devours pet treats.', sourceText = '|cFFFFD200Drop:|r Researchers Under Fire|n|cFFFFD200Zone:|r Zaralek Cavern|n', creatureID = 204269, },
	[3525] = { description = 'A warlock won this little guy at an infernal carnival, but it quickly outgrew its aquarium.', sourceText = '', creatureID = 204271, },
	[3526] = { description = 'Like Neltharion, this fish went mad listening to whispers of the Old Cods.', sourceText = '', creatureID = 204275, },
	[3527] = { description = 'How exactly does one swim through a void? This creature knows, but isn\'t telling.', sourceText = '', creatureID = 204276, },
	[3528] = { description = 'This type of beetle feeds on dragon scales. Its bright coloration warns predators against attack.', sourceText = '|cFFFFD200Vendor: |rHarlowe Marl|n|cFFFFD200Zone: |rZaralek Cavern|n|cFFFFD200Cost: |r200|Hcurrency:2003|h|TINTERFACE/ICONS/INV_Faction_WarResources.BLP:0|t|h|n', creatureID = 204286, },
	[3529] = { description = 'If bananas grew in caves, they would be Rango\'s favorite food.', sourceText = '|cFFFFD200Vendor: |rHarlowe Marl|n|cFFFFD200Zone: |rZaralek Cavern|n|cFFFFD200Cost: |r150|Hcurrency:2003|h|TINTERFACE/ICONS/INV_Faction_WarResources.BLP:0|t|h', creatureID = 204288, },
	[3530] = { description = 'Miloh likes naps. Also snacks.', sourceText = '|cFFFFD200Vendor: |rExplorer Bezzert|n|cFFFFD200Zone: |rZaralek Cavern|n|cFFFFD200Cost: |r2500|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h|n|n|cFFFFD200Vendor: |rPatchu|n|cFFFFD200Zone: |rThe Azure Span|n|cFFFFD200Cost: |r2500|Hitem:163036|h|TINTERFACE/ICONS/INV_Currency_PetBattle.BLP:0|t|h', creatureID = 204293, },
	[3531] = { description = 'Rusty\'s powerful nose could smell a needle in a haystack.', sourceText = '', creatureID = 204295, },
	[3532] = { description = 'Amador has a big heart and loves to help people, especially children and elders. He also likes belly rubs.', sourceText = '', creatureID = 204296, },
	[3533] = { description = 'Sure it\'s just a rock, but how much can it lift?', sourceText = '|cFFFFD200Treasure: |rChest of Massive Gains|n|cFFFFD200Zone: |rZaralek Cavern', creatureID = 204303, },
	[3534] = { description = 'Jerrie was once an ordinary rock mouse. He still is, in fact.', sourceText = '', creatureID = 204305, },
	[3535] = { description = 'These snails are especially prized by many drogbar snail-racers for being fast and looking fantastic.', sourceText = '|cFFFFD200Vendor: |rSaccratros|n|cFFFFD200Zone: |rZaralek Cavern|n|cFFFFD200Cost: |r50|Hitem:204727|h|TINTERFACE/ICONS/INV_Misc_CuriousCoin.BLP:0|t|h', creatureID = 204320, },
	[3536] = { description = 'While not as fast as some of the other snails in the Zaralek Cavern, Bashful makes up for it with sheer power.', sourceText = '|cFFFFD200Quest: |rA Race To The Finish|n|cFFFFD200Zone: |rZaralek Cavern', creatureID = 204321, },
	[3537] = { description = 'Known to hide money in its shell.', sourceText = '|cFFFFD200Vendor: |rPonzo|n|cFFFFD200Zone: |rZaralek Cavern|n|cFFFFD200Cost: |r45|Hitem:205188|h|TINTERFACE/ICONS/INV_Stone_WeightStone_08.blp:0|t|h', creatureID = 204323, },
	[3538] = { description = 'Layers of rocky skin make Ambershells very tough to hurt.', sourceText = '|cFFFFD200Quest: |rA Race To The Finish|n|cFFFFD200Zone: |rZaralek Cavern', creatureID = 204324, },
	[3539] = { description = 'Roseshells are known to be very agressive, but aren\'t very fast. So if you mess with one, prepare to be bitten in about five seconds.', sourceText = '', creatureID = 204325, },
	[3540] = { description = 'Somehow pretty and gross at the same time.', sourceText = '|cFFFFD200Quest: |rA Race To The Finish|n|cFFFFD200Zone: |rZaralek Cavern', creatureID = 204326, },
	[3541] = { description = 'For a creature made of rocks it hovers quite effortlessly.', sourceText = '|cFFFFD200Drop: |rKob\'rok|n|cFFFFD200Zone: |rZaralek Cavern', creatureID = 204339, },
	[3542] = { description = 'This creature hunts by lurking in dark areas, then darting out to catch its prey as it wanders by.', sourceText = '', creatureID = 204340, },
	[3543] = { description = 'This creature can eat twice its bodyweight at a single meal.', sourceText = '', creatureID = 204341, },
	[3544] = { description = 'It doesn\'t even bother to chew its food.', sourceText = '', creatureID = 204342, },
	[3545] = { description = 'Perhaps you\'d like to see how snakelike it can be!', sourceText = '|cFFFFD200Treasure: |rOuroboros Tablet and Kob\'rok\'s Luminescent Scale|n', creatureID = 204343, },
	[3546] = { description = 'Skaarn likes to coil up inside of your backpack.', sourceText = '|cFFFFD200Drop: |rKob\'rok|n|cFFFFD200Zone: |rZaralek Cavern', creatureID = 204345, },
	[3547] = { description = 'He doesn\'t want you to know this, but Mikah\'s favorite food is broccoli.', sourceText = '', creatureID = 204354, },
	[3548] = { description = 'The fury of the sea in the packge of a puddle.', sourceText = '|cFFFFD200Drop: |rAquifon|n|cFFFFD200Zone: |rZaralek Cavern', creatureID = 204359, },
	[3549] = { description = 'Their name comes from the fact that they lay eggs in the hearts of their prey.', sourceText = '', creatureID = 204360, },
	[3550] = { description = 'Undermoths are known to be some of the most intelligent moths on Azeroth. This is not saying much.', sourceText = '', creatureID = 204361, },
	[3551] = { description = 'Contrary to their name, these moths are actually quite happy.', sourceText = '|cFFFFD200Drop: |rUnderlight Queen|n|cFFFFD200Zone: |rZaralek Cavern', creatureID = 204363, },
	[3552] = { description = 'While napping in shrubbery, sometimes his snaggletooth gets caught on a leaf.', sourceText = '', creatureID = 201008, },
