	[3556] = { description = 'Kromos can often be found napping in sandboxes.', sourceText = '', creatureID = 204370, },
	[3557] = { description = 'Roggy is the kind of snail that never gives up, no matter how hard things get.', sourceText = '|cFFFFD200Achievement: |rSlow and Steady Wins the Race|n|cFFFFD200Category: |rDragon Isles', creatureID = 204621, },
