	[3578] = { description = 'Newsy delivers mail all around the Dragon Isles. She is so quick and reliable that the small holes on the letters she delivers are negligible.', sourceText = '|cFFFFD200Quest: |rSigned, Sealed, Self-Delivered', creatureID = 205359, },
	[3580] = { description = 'Also known as the Lord of Lies, Baa\'lial is a master of deceit.', sourceText = '|cFFFFD200Promotion:|r Greedy Emissary', creatureID = 205637, },
	[3581] = { description = 'The light of the new legacy illuminates the path you forge.', sourceText = '|cFFFFD200Treasure:|rWayward Motes of Nasz\'uro |n', creatureID = 205649, },
	[3582] = { description = 'Blub\'s heart has always been bigger than her tank, but once the rest of her got too big she had to find a new home. Fortunately, she can now travel with you and see all the marvels of the world!', sourceText = '', creatureID = 205795, },
	[3587] = { description = '', sourceText = '', creatureID = 206907, },
	[3588] = { description = 'Solid and dependable, Iggy is always ready for a fierce battle or a gentle metaphor.', sourceText = '|cFFFFD200Quest: |rA Solid Pet|n|cFFFFD200Zone: |rValdrakken', creatureID = 207064, },
	[3589] = { description = 'Rusziona\'s newfound courage means she can accompany you on adventures! Nothing in all of Azeroth can stop her! Not even a duck!', sourceText = '|cFFFFD200Quest: |rAnatidaephobia|n|cFFFFD200Zone: |rValdrakken', creatureID = 207176, },
	[3590] = { description = 'Bela has learned some humility since you first met her, so she likely won\'t boss you around too much.', sourceText = '|cFFFFD200Quest: |rPatience, Please|n|cFFFFD200Zone: |rValdrakken', creatureID = 207220, },
	[3596] = { description = 'Ten thousand years spent floating in a time stasis is quite a bit of time to work on material.', sourceText = '|cFFFFD200Quest: |rBy Time Fire be Purged!', creatureID = 207310, },
	[3597] = { description = 'Princess Vorquistrasza wants you to know that she appreciates you coming to her birthday party, but is underwhelmed by your gift.', sourceText = '|cFFFFD200Quest: |rA Cavalier Pet|n|cFFFFD200Zone: |rValdrakken', creatureID = 207535, },
	[3598] = { description = 'Now that he\'s no longer sneezing through time, Zhusa is ready to accompany you on your future adventures! Or past adventures! Or future adventures!', sourceText = '|cFFFFD200Quest: |rTemporal Immunity|n|cFFFFD200Zone: |rValdrakken', creatureID = 207552, },
	[3599] = { description = 'Zaleth is looking forward to accompanying you on your adventures. he\'s already got a list of books he wants to take with him, but Lasztak said he could only bring 2.', sourceText = '|cFFFFD200Quest: |rSight Isn\'t Everything|n|cFFFFD200Zone: |rValdrakken', creatureID = 207556, },
	[3600] = { description = 'Pallibrius is an amazing book because of its wealth of arcane knowledge. Also because of all the pie recipes its previous owner scribbled in the margins.', sourceText = '|cFFFFD200Quest: |rA Scholar\'s Pet|n|cFFFFD200Zone: |rValdrakken', creatureID = 207558, },
	[3601] = { description = 'Posidriss is excited to go on adventures with you, but maybe let them have a nap first?', sourceText = '|cFFFFD200Quest: |rHibernation|n|cFFFFD200Zone: |rValdrakken', creatureID = 207568, },
	[3602] = { description = 'Somnius is legendary for her ability to sleep through everything except suppertime.', sourceText = '|cFFFFD200Quest: |rA Sleepy Pet|n|cFFFFD200Zone: |rValdrakken', creatureID = 207571, },
	[3608] = { description = '', sourceText = '', creatureID = 208034, },
	[3609] = { description = '', sourceText = '', creatureID = 208058, },
	[3975] = { description = '', sourceText = '', creatureID = 208096, },
	[4237] = { description = '', sourceText = '', creatureID = 208125, },
	[4253] = { description = 'These astral phenomena are rarely sighted due to the speed of the slyvern. It is a good omen to have one following you around.', sourceText = '|cFFFFD200Trading Post|r |n', creatureID = 208436, },
	[4255] = { description = 'Loves: Broccoli, playing, and smashing things. Hates: Meat, sleeping, and mean Titan Keepers who won\'t let you smash things.', sourceText = '|cFFFFD200Drop: |rTime Rifts|n|cFFFFD200Vendor: |rSorotis|n|cFFFFD200Zone: |rTyrhold Reservoir|n|cFFFFD200Cost: |r2750|Hcurrency:2594|h|TInterface/ICONS/TimelessCoin_yellow.BLP:0|t|h', creatureID = 208624, },
	[4257] = { description = 'Gill\'dan earned his great power by drinking the blood of a demon, which would also have turned his scales green had they not already been that color.', sourceText = '|cFFFFD200Drop: |rTime Rifts|n|cFFFFD200Vendor: |rSir Finley Mrrgglton|n|cFFFFD200Zone: |rTyrhold Reservoir|n|cFFFFD200Cost: |r2750|Hcurrency:2594|h|TInterface/ICONS/TimelessCoin_yellow.BLP:0|t|h', creatureID = 208630, },
	[4258] = { description = 'This eye has witnessed untold horrors, and these fangs may be responsible for a few of their own.', sourceText = '|cFFFFD200Drop: |rTime Rifts|n|cFFFFD200Vendor: |rProvisioner Qorra|n|cFFFFD200Zone: |rTyrhold Reservoir|n|cFFFFD200Cost: |r2750|Hcurrency:2594|h|TInterface/ICONS/TimelessCoin_yellow.BLP:0|t|h', creatureID = 208631, },
	[4259] = { description = 'He seems to be very angry, but it\'s possible he\'s just hungry.', sourceText = '|cFFFFD200Drop: |rTime Rifts|n|cFFFFD200Vendor: |rFalara Nightsong|n|cFFFFD200Zone: |rTyrhold Reservoir|n|cFFFFD200Cost: |r2750|Hcurrency:2594|h|TInterface/ICONS/TimelessCoin_yellow.BLP:0|t|h', creatureID = 208634, },
	[4260] = { description = 'DEFECTIVE; REFUSES TO KILL. MARK FOR IMMEDIATE DESTRUCTION OR SALE TO LESS INTELLIGENT ENTITY.', sourceText = '|cFFFFD200Drop: |rTime Rifts|n|cFFFFD200Vendor: |rGill the Drill|n|cFFFFD200Zone: |rTyrhold Reservoir|n|cFFFFD200Cost: |r2750|Hcurrency:2594|h|TInterface/ICONS/TimelessCoin_yellow.BLP:0|t|h', creatureID = 208636, },
	[4261] = { description = 'A war-obsessed world turned her into a weapon... but they couldn\'t break her spirit.', sourceText = '|cFFFFD200Drop: |rTime Rifts|n|cFFFFD200Vendor: |rWarden Krizzik|n|cFFFFD200Zone: |rTyrhold Reservoir|n|cFFFFD200Cost: |r2750|Hcurrency:2594|h|TInterface/ICONS/TimelessCoin_yellow.BLP:0|t|h', creatureID = 208637, },
	[4262] = { description = 'A lifetime of servitude to Anub\'arak, then the Lich King, and now... you!', sourceText = '|cFFFFD200Drop: |rTime Rifts|n|cFFFFD200Vendor: |rBaron Silver|n|cFFFFD200Zone: |rTyrhold Reservoir|n|cFFFFD200Cost: |r2750|Hcurrency:2594|h|TInterface/ICONS/TimelessCoin_yellow.BLP:0|t|h', creatureID = 208639, },
	[4263] = { description = 'With a nose for secrets, Tobias is ready to be by the side of super sleuths.', sourceText = '|cFFFFD200Achievement:|r Community Rumor Mill|n|cFFFFD200Event:|r Secrets of Azeroth', creatureID = 208643, },
	[4265] = { description = 'Feel the chill nipping at your toes! A menace of Azeroth, now in festive blue and travel-size!', sourceText = '|cFFFFD200Achievement:|r WoW\'s 19th Anniversary|n|cFFFFD200Category:|r Events', creatureID = 209259, },
	[4267] = { description = 'As heir to the Lord of Outland, Maggz has always shared his family\'s love of violence and power. He just happens to be even more in love with pet treats.', sourceText = '', creatureID = 210058, },
	[4275] = { description = 'The first thing you might notice about this creature is its huge expressive eyes. This is a mistake. Instead focus on its razor-sharp claws.', sourceText = '|cFFFFD200Pet Battle: |rEmerald Dream', creatureID = 211110, },
	[4276] = { description = 'Keep it out of your backpack or it will eat all your food and then take a nap.', sourceText = '|cFFFFD200Pet Battle: |rEmerald Dream', creatureID = 211177, },
	[4277] = { description = 'Sapnibblers are always up for a game of hide and squeak.', sourceText = '|cFFFFD200Pet Battle: |rEmerald Dream', creatureID = 211182, },
	[4278] = { description = 'This little one likes cheese, but most fawn do.', sourceText = '|cFFFFD200Pet Battle: |rEmerald Dream', creatureID = 211288, },
	[4279] = { description = 'As younglings, these critters need a lot of naps. Fortunately, adventurers\' backpacks make good napping spots.', sourceText = '|cFFFFD200Pet Battle: |rEmerald Dream', creatureID = 211295, },
	[4280] = { description = 'If you think the skunks of the Emerald Dream smell bad, pray that you never smell the skunks of the Emerald Nightmare.', sourceText = '|cFFFFD200Pet Battle: |rEmerald Dream', creatureID = 211302, },
	[4285] = { description = 'When enemy combatants fly overhead, there\'s only one thing to do. Duck.', sourceText = '|cFFFFD200Achievement: |rDucks In A Row|n|cFFFFD200Category: |rDragonflight Raid', creatureID = 211939, },
	[4286] = { description = 'At a young age Aura donned her magic hat, took her crystal staff, and set off for adventure into the unknown.', sourceText = '|cFFFFD200Trading Post|r |n', creatureID = 211942, },
	[4287] = { description = 'The duck of your dreams... or perhaps someone else\'s dreams.', sourceText = '|cFFFFD200Vendor: |rElianna|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1|Hcurrency:2777|h|TINTERFACE/ICONS/INV_Legion_Faction_DreamWeavers.BLP:0|t|h', creatureID = 212192, },
	[4288] = { description = 'If you ever have an outdoor feast in the Emerald Dream, look out for wild blueloos. A blueloo won\'t steal your picinic basket, but it will politely ask for some snacks.', sourceText = '|cFFFFD200Achievement:|r Friends in Feathers|n', creatureID = 212206, },
	[4289] = { description = 'Compared to a fully grown dreamborne scarab, this is the lesser of two weevils.', sourceText = '|cFFFFD200Vendor: |rElianna|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1|Hcurrency:2777|h|TINTERFACE/ICONS/INV_Legion_Faction_DreamWeavers.BLP:0|t|h', creatureID = 212267, },
	[4290] = { description = 'Be sure to sleep with some grass under your pillow so you can give this critter a snack while you dream.', sourceText = '|cFFFFD200Vendor: |rElianna|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1|Hcurrency:2777|h|TINTERFACE/ICONS/INV_Legion_Faction_DreamWeavers.BLP:0|t|h|n', creatureID = 212273, },
	[4291] = { description = 'Since you rescued her from Larodar, this grateful little doe hasn\'t stopped following you around.', sourceText = '|cFFFFD200Achievement:|r "Don\'t Let the Doe Hit You On the Way Out"|n', creatureID = 212275, },
	[4292] = { description = 'It is said that some animals dream in black and white. This animal only dreams in green.', sourceText = '|cFFFFD200Vendor: |rElianna|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1|Hcurrency:2777|h|TINTERFACE/ICONS/INV_Legion_Faction_DreamWeavers.BLP:0|t|h', creatureID = 212315, },
	[4295] = { description = 'This creature could sleep through another Cataclysm.', sourceText = '|cFFFFD200Vendor: |rTalisa Whisperbloom|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1|Hitem:211376|h|TINTERFACE/ICONS/INV_Misc_TrailofFlowers.blp:0|t|h', creatureID = 212328, },
	[4296] = { description = 'Snoozles was once given a choice between life in the timeless beautiful snowfields and life in a warm home with pet treats. We know which one he chose.', sourceText = '|cFFFFD200Vendor: |rTalisa Whisperbloom|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1|Hitem:211376|h|TINTERFACE/ICONS/INV_Misc_TrailofFlowers.blp:0|t|h', creatureID = 212329, },
	[4297] = { description = 'Rescued from the Druids of the Flame\'s onslaught upon the Emerald Dream, this juvenile faerie dragon has taken a liking to you -- and to your food. Don\'t leave your snacks unattended!', sourceText = '|cFFFFD200Quest: |rBurning Out|n|cFFFFD200Zone: |rEmerald Dream', creatureID = 212399, },
	[4298] = { description = 'Seedles burrow beneath plants in the emerald dreams and nibble at their roots.', sourceText = '|cFFFFD200Vendor: |rTalisa Whisperbloom|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1|Hitem:211376|h|TINTERFACE/ICONS/INV_Misc_TrailofFlowers.blp:0|t|h', creatureID = 212408, },
	[4299] = { description = 'Dustites scavenge among the roots of great trees for any small leavings they can find.', sourceText = '|cFFFFD200Vendor: |rTalisa Whisperbloom|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1|Hitem:211376|h|TINTERFACE/ICONS/INV_Misc_TrailofFlowers.blp:0|t|h', creatureID = 212413, },
	[4300] = { description = 'Dream on. Dream until your dream comes true!', sourceText = '|cFFFFD200Vendor: |rElianna|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1|Hcurrency:2777|h|TINTERFACE/ICONS/INV_Legion_Faction_DreamWeavers.BLP:0|t|h|n', creatureID = 212417, },
	[4302] = { description = 'These creatures live in bushes and near trees, but they sleep so soundly they\'re practically invisible. If you want to interrupt their dreaming, you\'d best bring friends.', sourceText = '|cFFFFD200Pet Battle: |rEmerald Dream', creatureID = 212437, },
	[4303] = { description = 'Such toof. Such snaggle.', sourceText = '|cFFFFD200Pet Battle: |rEmerald Dream', creatureID = 212459, },
	[4304] = { description = 'A brown dream badger is a rare sight. Not so rare a smell though.', sourceText = '|cFFFFD200Pet Battle: |rEmerald Dream', creatureID = 212557, },
	[4305] = { description = 'Snoots has a supernatural sense of smell. It\'s not any better than normal, it\'s just supernatural.', sourceText = '|cFFFFD200Vendor: |rTalisa Whisperbloom|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1|Hitem:211376|h|TINTERFACE/ICONS/INV_Misc_TrailofFlowers.blp:0|t|h', creatureID = 212562, },
	[4306] = { description = 'Elmer once ate a whole cake all by himself. It wasn\'t even his cake.', sourceText = '|cFFFFD200Vendor: |rTalisa Whisperbloom|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1|Hitem:211376|h|TINTERFACE/ICONS/INV_Misc_TrailofFlowers.blp:0|t|h', creatureID = 212567, },
	[4307] = { description = 'What Spruce lacks in speed, she makes up for with sheer stubbornness.', sourceText = '|cFFFFD200Vendor: |rMoon Priestess Lasara|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r375|Hcurrency:2003|h|TINTERFACE/ICONS/INV_Faction_WarResources.BLP:0|t|h', creatureID = 212569, },
	[4308] = { description = 'Juniper\'s unique coloration makes it hard to hide from predators in the Emerald Dream\'s lush green foliage. She therefore made the wise decision to travel with you instead.', sourceText = '|cFFFFD200Vendor: |rMoon Priestess Lasara|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r375|Hcurrency:2003|h|TINTERFACE/ICONS/INV_Faction_WarResources.BLP:0|t|h', creatureID = 212570, },
	[4309] = { description = 'Is it a snail? Is it a dream? Is it the dream of a sleeping snail?', sourceText = '|cFFFFD200Vendor: |rElianna|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1|Hcurrency:2777|h|TINTERFACE/ICONS/INV_Legion_Faction_DreamWeavers.BLP:0|t|h', creatureID = 212877, },
	[4310] = { description = 'Wyvern whelps are sleepy by nature. The dreams of wyvern whelps are doubly so.', sourceText = '|cFFFFD200Vendor: |rElianna|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1|Hitem:210776|h|TInterface/ICONS/10_2_RaidAbility_Green.BLP:0|t|h', creatureID = 212954, },
	[4311] = { description = 'When she grows up, Mitzy wants to guide Greatfather Winter\'s sleigh just like Metzen, her famous dad. Until then, she\'s more than happy to come with you on your adventures.', sourceText = '|cFFFFD200Trading Post|r ', creatureID = 213111, },
	[3579] = { description = 'Also known as the Dreamurk, Ysergle slumbered for many long years in the Emrgrled Dream.', sourceText = '|cFFFFD200Promotion:|r', creatureID = 205467, },
	
}
